INSERT INTO "QuizQuestion" (id, question, "questionTranslation", options, "correctAnswer", explanation, "VocabularyId", "createdAt", "updatedAt") VALUES ('b3cf576b-5f6c-441b-a796-96c25580d6da', '무지개에는 일곱 가지 ______이 있어요.', 'Cầu vồng có bảy ______.', ARRAY['색깔', '모양', '크기', '길이']::text[], '색깔', '색깔 có nghĩa là ''màu sắc'', dùng để chỉ màu của vật thể hoặc hiện tượng.', '8ad08d61-e5ce-4915-8b6b-d0c59788b69f', '2026-01-05T17:07:58.342Z', '2026-01-05T17:07:58.342Z'),
  ('f7d17955-cfa0-4a68-9186-61b530196ada', '이 옷의 ______이 예뻐요.', '______ của chiếc áo này đẹp quá.', ARRAY['색', '디자인', '재질', '브랜드']::text[], '색', '색 là hình thức ngắn gọn của 색깔, thường được dùng trong văn nói hoặc khi kết hợp với các từ khác.', '8ad08d61-e5ce-4915-8b6b-d0c59788b69f', '2026-01-05T17:07:58.342Z', '2026-01-05T17:07:58.342Z'),
  ('d90f9a10-6eaa-4fd5-84e3-e5b40c19fffa', '______ 신호등은 멈추라는 뜻이에요.', 'Đèn giao thông ______ có nghĩa là dừng lại.', ARRAY['빨간색', '초록색', '노란색', '파란색']::text[], '빨간색', '빨간색 có nghĩa là ''màu đỏ'', là màu của nguy hiểm, cảnh báo, và dừng lại trong giao thông.', 'bbbc83d9-ce53-4ae0-a68e-dfd174b8ed47', '2026-01-05T17:07:58.342Z', '2026-01-05T17:07:58.342Z'),
  ('4daa05eb-a8fd-474f-8439-c3cd24f7a58c', '사랑을 상징하는 꽃은 보통 ______ 장미예요.', 'Hoa tượng trưng cho tình yêu thường là hoa hồng ______.', ARRAY['빨간색', '하얀색', '노란색', '보라색']::text[], '빨간색', '빨간색 tượng trưng cho tình yêu, đam mê, và sức mạnh trong nhiều nền văn hóa.', 'bbbc83d9-ce53-4ae0-a68e-dfd174b8ed47', '2026-01-05T17:07:58.342Z', '2026-01-05T17:07:58.342Z'),
  ('203ef6e6-4a68-43d8-b958-31da920aa253', '______ 신호등은 준비하라는 뜻이에요.', 'Đèn giao thông ______ có nghĩa là chuẩn bị.', ARRAY['노란색', '빨간색', '초록색', '파란색']::text[], '노란색', '노란색 có nghĩa là ''màu vàng'', thường tượng trưng cho sự thận trọng, cảnh báo, và ánh sáng mặt trời.', '01bb98eb-b78c-4691-98d1-23d1f89612d2', '2026-01-05T17:07:58.342Z', '2026-01-05T17:07:58.342Z'),
  ('c1aa688a-fae6-4af4-b5f6-e821fadba5de', '봄이 되면 ______ 민들레꽃이 피어요.', 'Khi mùa xuân đến, những bông hoa bồ công anh ______ nở rộ.', ARRAY['노란색', '빨간색', '파란색', '보라색']::text[], '노란색', '노란색 thường gắn liền với mùa xuân, sự ấm áp, và năng lượng tích cực.', '01bb98eb-b78c-4691-98d1-23d1f89612d2', '2026-01-05T17:07:58.342Z', '2026-01-05T17:07:58.342Z'),
  ('3f9043a8-9289-43bc-a805-53df52a965bf', '______ 바다는 매우 아름다워요.', 'Biển ______ rất đẹp.', ARRAY['파란색', '빨간색', '초록색', '검은색']::text[], '파란색', '파란색 có nghĩa là ''màu xanh da trời'', thường gắn liền với bầu trời, biển cả, và sự yên bình.', '46390c9e-9933-407f-a758-9abea83a1771', '2026-01-05T17:07:58.342Z', '2026-01-05T17:07:58.342Z'),
  ('885908f6-d5f9-4690-b0db-c8863ff6ccae', '______ 옷은 청량감을 주는 느낌이에요.', 'Quần áo ______ cho cảm giác mát mẻ, sảng khoái.', ARRAY['파란색', '빨간색', '주황색', '갈색']::text[], '파란색', '파란색 thường được cảm nhận là màu lạnh, tạo cảm giác thanh mát, yên tĩnh và đáng tin cậy.', '46390c9e-9933-407f-a758-9abea83a1771', '2026-01-05T17:07:58.342Z', '2026-01-05T17:07:58.342Z'),
  ('cec8e860-c55a-4a2c-84fe-0cd944a2df31', '______ 신호등은 건너가도 된다는 뜻이에요.', 'Đèn giao thông ______ có nghĩa là được phép đi qua.', ARRAY['초록색', '빨간색', '노란색', '파란색']::text[], '초록색', '초록색 có nghĩa là ''màu xanh lá cây'', tượng trưng cho sự an toàn, đi tiếp, và thiên nhiên.', '8a4b776c-307e-4f26-8145-ede961089d53', '2026-01-05T17:07:58.342Z', '2026-01-05T17:07:58.342Z'),
  ('764aa62b-0cb6-481d-8c0c-a6f2a32a054b', '공원의 ______ 잔디밭에서 사람들이 즐거워해요.', 'Mọi người vui vẻ trên bãi cỏ ______ ở công viên.', ARRAY['초록색', '빨간색', '파란색', '보라색']::text[], '초록색', '초록색 là màu của thiên nhiên, cây cỏ, và sự phát triển, thường mang lại cảm giác tươi mát và bình yên.', '8a4b776c-307e-4f26-8145-ede961089d53', '2026-01-05T17:07:58.342Z', '2026-01-05T17:07:58.342Z'),
  ('8994e43c-9447-4413-9935-47f9c2e97069', '결혼식에서 신부는 보통 ______ 웨딩드레스를 입어요.', 'Trong đám cưới, cô dâu thường mặc váy cưới ______.', ARRAY['하얀색', '검은색', '빨간색', '파란색']::text[], '하얀색', '하얀색 có nghĩa là ''màu trắng'', tượng trưng cho sự thuần khiết, trong sáng, và ngây thơ.', '8c77c0a2-bece-4b66-947a-6c040ad5fd51', '2026-01-05T17:07:58.342Z', '2026-01-05T17:07:58.342Z'),
