INSERT INTO "QuizQuestion" (id, question, "questionTranslation", options, "correctAnswer", explanation, "VocabularyId", "createdAt", "updatedAt") VALUES ('20b69eef-f434-46ba-84e1-832d8a2c2e28', '처음 만났을 때 무엇이라고 말하나요?', 'Khi gặp lần đầu người ta nói gì?', ARRAY['안녕히 가세요', '안녕하세요', '감사합니다', '죄송합니다']::text[], '안녕하세요', '안녕하세요 là lời chào phổ biến nhất trong tiếng Hàn, dùng khi gặp gỡ ai đó lần đầu hoặc trong tình huống trang trọng.', 'd5115eee-d508-4a44-b581-9635c9c81f3c', '2026-01-05T17:07:58.351Z', '2026-01-05T17:07:58.351Z'),
  ('ce51a8f9-3a5b-4035-843b-88a690602e4f', '선생님께 인사할 때 적절한 말은?', 'Khi chào thầy/cô, câu nào phù hợp?', ARRAY['안녕', '안녕하세요', '잘 가', '또 봐']::text[], '안녕하세요', '안녕하세요 là hình thức lịch sự, trang trọng để chào hỏi, phù hợp khi nói với thầy cô hoặc người lớn tuổi.', 'd5115eee-d508-4a44-b581-9635c9c81f3c', '2026-01-05T17:07:58.351Z', '2026-01-05T17:07:58.351Z'),
  ('9500f500-b48d-4645-acb2-12ed1a449c28', '친구가 집에 갈 때 뭐라고 하나요?', 'Khi bạn về nhà, chúng ta nói gì?', ARRAY['안녕하세요', '안녕히 가세요', '안녕히 계세요', '어서 오세요']::text[], '안녕히 가세요', '안녕히 가세요 được dùng khi chào tạm biệt người đang rời đi, trong khi bạn ở lại.', '4e0ac674-2a0a-4af3-bf70-2e2c7a07381f', '2026-01-05T17:07:58.351Z', '2026-01-05T17:07:58.351Z'),
  ('a235a49d-935f-4e65-bea0-99c572eda9a4', '손님이 가게를 나갈 때 직원이 하는 말은?', 'Khi khách rời cửa hàng, nhân viên nói gì?', ARRAY['안녕히 계세요', '안녕히 가세요', '어서 오세요', '감사합니다']::text[], '안녕히 가세요', '안녕히 가세요 là lời chào lịch sự dành cho người đang rời đi, thường dùng trong cửa hàng, nhà hàng hoặc khi tiễn khách.', '4e0ac674-2a0a-4af3-bf70-2e2c7a07381f', '2026-01-05T17:07:58.351Z', '2026-01-05T17:07:58.351Z'),
  ('01c12adb-abdc-45de-a4ae-aa90e1e2d5b3', '회사에서 먼저 퇴근할 때 동료에게 뭐라고 하나요?', 'Khi rời công ty trước đồng nghiệp, bạn nói gì?', ARRAY['안녕히 가세요', '안녕히 계세요', '안녕하세요', '수고하세요']::text[], '안녕히 계세요', '안녕히 계세요 được dùng khi bạn rời đi và người kia ở lại, ví dụ khi bạn về trước đồng nghiệp.', 'c576cbc7-e4a7-40c8-809f-30de1750e50d', '2026-01-05T17:07:58.351Z', '2026-01-05T17:07:58.351Z'),
  ('dc4e28d9-172e-4fdd-b385-a553f8df412a', '식당에서 계산하고 나올 때 주인에게 하는 말은?', 'Khi thanh toán và rời nhà hàng, bạn nói với chủ quán gì?', ARRAY['안녕하세요', '안녕히 계세요', '잘 먹었습니다', '다녀오겠습니다']::text[], '안녕히 계세요', '안녕히 계세요 là lời chào phù hợp khi khách hàng rời đi và chủ quán vẫn ở lại quán.', 'c576cbc7-e4a7-40c8-809f-30de1750e50d', '2026-01-05T17:07:58.351Z', '2026-01-05T17:07:58.351Z'),
  ('6dab5f5c-a84f-4f6f-90f4-65c52d26b7bf', '선생님께 선물을 받았을 때 뭐라고 하나요?', 'Khi nhận quà từ thầy/cô, bạn nói gì?', ARRAY['죄송합니다', '감사합니다', '네', '아니요']::text[], '감사합니다', '감사합니다 là cách nói cảm ơn trang trọng, lịch sự trong tiếng Hàn, dùng trong các tình huống chính thức hoặc với người lớn tuổi.', 'bd50d9c0-a08c-49a2-8932-96415da44b08', '2026-01-05T17:07:58.351Z', '2026-01-05T17:07:58.351Z'),
  ('8c09e02c-2656-413f-9ee9-7b0afed8e041', '누군가 도움을 준 후에 하는 말은?', 'Sau khi ai đó giúp đỡ, bạn nói gì?', ARRAY['죄송합니다', '괜찮아요', '감사합니다', '만나서 반갑습니다']::text[], '감사합니다', '감사합니다 dùng để bày tỏ lòng biết ơn khi nhận được sự giúp đỡ từ người khác.', 'bd50d9c0-a08c-49a2-8932-96415da44b08', '2026-01-05T17:07:58.351Z', '2026-01-05T17:07:58.351Z'),
  ('f716368d-dbd0-42e7-ab86-08b43cfab391', '지하철에서 누군가의 발을 밟았을 때 뭐라고 하나요?', 'Khi vô tình dẫm lên chân ai đó trên tàu điện ngầm, bạn nói gì?', ARRAY['감사합니다', '죄송합니다', '네', '안녕하세요']::text[], '죄송합니다', '죄송합니다 là cách xin lỗi trang trọng, dùng khi mắc lỗi hoặc gây ra sự bất tiện cho người khác.', '417520ea-d083-4c21-9113-81182c839d2b', '2026-01-05T17:07:58.351Z', '2026-01-05T17:07:58.351Z'),
  ('27bcfece-5bd3-44f2-841d-f0c50cb8792e', '약속에 늦었을 때 가장 먼저 해야 할 말은?', 'Khi đến muộn cuộc hẹn, điều đầu tiên bạn nên nói là gì?', ARRAY['안녕하세요', '감사합니다', '죄송합니다', '만나서 반갑습니다']::text[], '죄송합니다', '죄송합니다 là cách xin lỗi thích hợp khi bạn đến muộn hoặc vi phạm một quy tắc xã hội.', '417520ea-d083-4c21-9113-81182c839d2b', '2026-01-05T17:07:58.351Z', '2026-01-05T17:07:58.351Z'),
  ('eb82788d-1265-4042-91d4-42ac4fef7fa7', '질문에 긍정적으로 대답할 때 하는 말은?', 'Khi trả lời khẳng định một câu hỏi, bạn nói gì?', ARRAY['아니요', '네', '죄송합니다', '감사합니다']::text[], '네', '네 là cách nói ''vâng'', ''dạ'' hoặc ''đúng vậy'' trong tiếng Hàn, dùng để đồng ý hoặc xác nhận điều gì đó.', '4e259b64-c559-4fc4-b612-b0dff38eb945', '2026-01-05T17:07:58.351Z', '2026-01-05T17:07:58.351Z'),
  ('8023b30b-a9e2-472a-b1b3-5acbbb858587', '"커피 마실래요?" 라고 물었을 때 마시고 싶다면?', 'Khi được hỏi "Bạn có muốn uống cà phê không?" và bạn muốn uống, bạn trả lời thế nào?', ARRAY['아니요', '네', '괜찮아요', '죄송합니다']::text[], '네', '네 dùng để trả lời đồng ý hoặc chấp nhận một lời đề nghị, mời mọc.', '4e259b64-c559-4fc4-b612-b0dff38eb945', '2026-01-05T17:07:58.351Z', '2026-01-05T17:07:58.351Z'),
  ('204560ee-199d-4cbc-83cc-81d96d0c4668', '질문에 부정적으로 대답할 때 하는 말은?', 'Khi trả lời phủ định một câu hỏi, bạn nói gì?', ARRAY['네', '아니요', '감사합니다', '만나서 반갑습니다']::text[], '아니요', '아니요 là cách nói ''không'' hoặc ''không phải'' trong tiếng Hàn, dùng để từ chối hoặc phủ nhận điều gì đó.', '031f96ec-0e17-4f45-a13f-15a2bf4ec0fd', '2026-01-05T17:07:58.351Z', '2026-01-05T17:07:58.351Z'),
  ('2751c14c-c4ec-4096-bafd-a0b089236a82', '"저 분이 선생님이에요?" 라고 물었을 때 선생님이 아니라면?', 'Khi được hỏi "Người đó có phải là giáo viên không?" và không phải, bạn trả lời thế nào?', ARRAY['네', '아니요', '감사합니다', '죄송합니다']::text[], '아니요', '아니요 dùng để trả lời phủ định khi thông tin không chính xác hoặc khi bạn không đồng ý.', '031f96ec-0e17-4f45-a13f-15a2bf4ec0fd', '2026-01-05T17:07:58.351Z', '2026-01-05T17:07:58.351Z'),
  ('05527118-7f77-45a0-b9b4-bae2dce2bc31', '누군가 실수했을 때 화내지 않고 이해해준다는 뜻으로 하는 말은?', 'Khi ai đó mắc lỗi và bạn muốn nói rằng bạn không giận và thông cảm, bạn nói gì?', ARRAY['죄송합니다', '감사합니다', '괜찮아요', '안녕하세요']::text[], '괜찮아요', '괜찮아요 có nghĩa là ''không sao đâu'', ''ổn mà'', dùng để trấn an người khác hoặc cho biết mọi thứ vẫn ổn.', '047569b1-3bf5-46db-a308-0c8625fa54ef', '2026-01-05T17:07:58.351Z', '2026-01-05T17:07:58.351Z'),
  ('4a75011e-10bd-40bd-b237-864986a43b6d', '"미안해요" 라고 했을 때 "______" 하고 대답할 수 있어요.', 'Khi ai đó nói "Xin lỗi", bạn có thể trả lời "______".', ARRAY['감사합니다', '안녕하세요', '괜찮아요', '만나서 반갑습니다']::text[], '괜찮아요', '괜찮아요 là câu trả lời thông dụng khi ai đó xin lỗi, thể hiện sự tha thứ hoặc không có vấn đề gì.', '047569b1-3bf5-46db-a308-0c8625fa54ef', '2026-01-05T17:07:58.351Z', '2026-01-05T17:07:58.351Z'),
  ('8b346208-995c-4431-a86f-4cdb8839fba5', '새로운 친구를 만났을 때 기쁨을 표현하는 말은?', 'Khi gặp một người bạn mới, câu nào thể hiện sự vui mừng?', ARRAY['안녕히 가세요', '만나서 반갑습니다', '죄송합니다', '괜찮아요']::text[], '만나서 반갑습니다', '만나서 반갑습니다 nghĩa là ''rất vui được gặp bạn'', dùng để bày tỏ niềm vui khi gặp ai đó lần đầu.', 'a662839d-de9f-4673-9198-c2754becbc70', '2026-01-05T17:07:58.351Z', '2026-01-05T17:07:58.351Z'),
  ('2411b6e5-9192-41d3-bc6f-58b8a4937f1d', '첫 인사 후에 추가로 할 수 있는 말은?', 'Sau lời chào đầu tiên, bạn có thể nói thêm gì?', ARRAY['안녕히 계세요', '만나서 반갑습니다', '감사합니다', '죄송합니다']::text[], '만나서 반갑습니다', '만나서 반갑습니다 thường được nói sau 안녕하세요 khi gặp ai đó lần đầu, thể hiện sự thân thiện.', 'a662839d-de9f-4673-9198-c2754becbc70', '2026-01-05T17:07:58.351Z', '2026-01-05T17:07:58.351Z'),
  ('ff5241e7-14d9-4f57-8875-03a6b37c76ad', '새로운 친구에게 질문할 때 "______이 뭐예요?" 라고 물어요.', 'Khi hỏi một người bạn mới, chúng ta nói "______ là gì?"', ARRAY['나이', '이름', '전화번호', '주소']::text[], '이름', '이름 có nghĩa là ''tên'', dùng để hỏi tên của người khác trong tình huống thông thường.', '878cec59-e4b7-42be-b551-844ca2ff687c', '2026-01-05T17:07:58.351Z', '2026-01-05T17:07:58.351Z'),
  ('3665555a-b5e0-4df0-8131-a544a2ac25c1', '제 ______은 이지영입니다.', '______ của tôi là Lee Ji-young.', ARRAY['나이', '이름', '직업', '국적']::text[], '이름', '이름 dùng khi giới thiệu tên của bản thân trong các tình huống giao tiếp.', '878cec59-e4b7-42be-b551-844ca2ff687c', '2026-01-05T17:07:58.351Z', '2026-01-05T17:07:58.351Z'),
  ('078d8cb8-ddd2-4481-bdc3-10c0e90c356d', '어르신께 공손하게 물을 때 "______이 어떻게 되세요?" 라고 합니다.', 'Khi hỏi lịch sự với người lớn tuổi, chúng ta nói "______ là gì ạ?"', ARRAY['이름', '성함', '호칭', '별명']::text[], '성함', '성함 là kính ngữ của 이름, dùng để hỏi tên một cách trang trọng với người lớn tuổi hoặc trong tình huống chính thức.', 'd0ce1612-8017-4ddd-9ca1-e69c83fecfc3', '2026-01-05T17:07:58.351Z', '2026-01-05T17:07:58.351Z'),
  ('63dcecc6-51f6-49a0-a54f-f56b3215f188', '회사 면접에서 "______이 어떻게 되시나요?" 라고 물을 수 있어요.', 'Trong phỏng vấn công ty, có thể hỏi "______ của anh/chị là gì?"', ARRAY['이름', '성함', '나이', '직급']::text[], '성함', '성함 được sử dụng trong các tình huống trang trọng, chuyên nghiệp như phỏng vấn, gặp gỡ đối tác.', 'd0ce1612-8017-4ddd-9ca1-e69c83fecfc3', '2026-01-05T17:07:58.351Z', '2026-01-05T17:07:58.351Z'),
  ('048f8b3c-afff-4f82-a250-453c4cba64a8', '"______가 몇 살이에요?" 라고 물으면 상대방의 연령을 묻는 거예요.', 'Khi hỏi "______ bao nhiêu tuổi?" là đang hỏi tuổi của đối phương.', ARRAY['이름', '나이', '키', '몸무게']::text[], '나이', '나이 có nghĩa là ''tuổi'', dùng khi hỏi về độ tuổi của một người.', '0d9c3687-177b-4702-97dc-4d62bb2c2567', '2026-01-05T17:07:58.351Z', '2026-01-05T17:07:58.351Z'),
  ('9eee464f-8ab1-4386-9f57-3cb4c800160d', '어린 아이의 ______를 물을 때는 조심스럽게 해야 해요.', 'Khi hỏi ______ của trẻ nhỏ, cần phải cẩn thận.', ARRAY['이름', '나이', '성별', '취미']::text[], '나이', '나이 là chủ đề nhạy cảm trong một số văn hóa, đặc biệt khi hỏi người lớn tuổi hoặc trong môi trường chuyên nghiệp.', '0d9c3687-177b-4702-97dc-4d62bb2c2567', '2026-01-05T17:07:58.351Z', '2026-01-05T17:07:58.351Z'),
  ('ffe37ca3-574b-4507-899e-4f2a9b427c97', '저는 스물한 ______입니다.', 'Tôi 21 ______.', ARRAY['년', '살', '개월', '월']::text[], '살', '살 là đơn vị đếm tuổi trong tiếng Hàn, luôn đứng sau số từ để chỉ số tuổi.', '592218d8-574c-4a6f-91cd-b3ef690e3135', '2026-01-05T17:07:58.351Z', '2026-01-05T17:07:58.351Z'),
  ('863945ef-7ba6-43c1-a4f6-03b9e31c98bd', '아기가 열두 ______이 되었어요.', 'Em bé đã được 12 ______ rồi.', ARRAY['살', '개월', '년', '주']::text[], '살', '살 dùng để đếm tuổi của người, động từ chính là ''되다'' (trở thành) hoặc ''이다'' (là).', '592218d8-574c-4a6f-91cd-b3ef690e3135', '2026-01-05T17:07:58.351Z', '2026-01-05T17:07:58.351Z'),
