INSERT INTO "Category" (id, name, description, icon, "order", "topikLevel", "groupId", "createdAt", "updatedAt") VALUES ('0b09d52f-e62a-4cea-8fca-1588d6355993', 'Số đếm Hán-Hàn (한자 수사)', 'Từ vựng về số đếm Hán-Hàn thường dùng trong đếm số tầng, tháng, giờ, giá cả, ngày tháng', '🔢', 1, 1, '7497b4ad-ffca-4165-b264-cf7432d6cbe7', '2026-01-05T17:07:58.352Z', '2026-01-05T17:07:58.352Z'),
  ('712c9429-c97f-406d-86b9-2daf519db308', 'Số đếm Thuần-Hàn', 'Số đếm thuần Hàn từ 1 đến 10 và số 20, cách sử dụng với bộ đếm', '🔢', 2, 1, '7497b4ad-ffca-4165-b264-cf7432d6cbe7', '2026-01-05T17:07:58.352Z', '2026-01-05T17:07:58.352Z'),
  ('841e2305-8850-489e-ab51-941c9bbc489b', 'Thứ trong tuần', 'Từ vựng về các ngày trong tuần và các khái niệm thời gian liên quan trong tiếng Hàn', '📅', 3, 1, '7497b4ad-ffca-4165-b264-cf7432d6cbe7', '2026-01-05T17:07:58.352Z', '2026-01-05T17:07:58.352Z'),
  ('7a666f9a-4834-4ca8-b185-0745ba335ad9', 'Ngày và Tháng (날짜와 월)', 'Từ vựng về năm, tháng, ngày và các tháng đặc biệt trong tiếng Hàn', '📅', 4, 1, '7497b4ad-ffca-4165-b264-cf7432d6cbe7', '2026-01-05T17:07:58.352Z', '2026-01-05T17:07:58.352Z');
