INSERT INTO "QuizQuestion" (id, question, "questionTranslation", options, "correctAnswer", explanation, "VocabularyId", "createdAt", "updatedAt") VALUES ('6f143754-f388-44ba-91fe-3fb058e12f43', '휴가 때는 보통 ______와 함께 시간을 보내요.', 'Vào kỳ nghỉ, tôi thường dành thời gian với ______.', ARRAY['친구', '가족', '동료', '이웃']::text[], '가족', '가족 có nghĩa là ''gia đình'', chỉ những người có quan hệ huyết thống hoặc hôn nhân sống chung.', '6aa54038-87ba-4771-9886-18cddbacc5fd', '2026-01-05T17:07:58.354Z', '2026-01-05T17:07:58.354Z'),
  ('a974d5ac-c950-4ee6-b01c-2be513502268', '______ 사진을 찍어서 액자에 넣어 두었어요.', 'Tôi đã chụp ảnh ______ và đặt vào khung ảnh.', ARRAY['가족', '친구', '동물', '풍경']::text[], '가족', '가족 thường được dùng trong ngữ cảnh về những kỷ niệm, sinh hoạt chung của các thành viên trong nhà.', '6aa54038-87ba-4771-9886-18cddbacc5fd', '2026-01-05T17:07:58.354Z', '2026-01-05T17:07:58.354Z'),
  ('0fb71a6b-6198-40f1-b0f0-c27d2b08fd4d', '______는 어릴 때 많은 이야기를 들려주셨어요.', '______ đã kể cho tôi nghe rất nhiều câu chuyện khi tôi còn nhỏ.', ARRAY['할아버지', '어머니', '동생', '삼촌']::text[], '할아버지', '할아버지 có nghĩa là ''ông'', dùng để gọi ông nội hoặc ông ngoại một cách kính trọng.', '72011c33-3ef2-4249-abd1-4c292e1e1548', '2026-01-05T17:07:58.354Z', '2026-01-05T17:07:58.354Z'),
  ('7291ddba-653a-4661-9934-085ff31a2d80', '주말에 ______ 댁에 방문하는 것이 저의 일과예요.', 'Vào cuối tuần, việc đến thăm nhà ______ là thói quen của tôi.', ARRAY['할아버지', '친구', '선생님', '사장님']::text[], '할아버지', '할아버지 thường đi kèm với các từ thể hiện sự kính trọng như ''댁'' (nhà/quý ngài) trong văn nói lịch sự.', '72011c33-3ef2-4249-abd1-4c292e1e1548', '2026-01-05T17:07:58.354Z', '2026-01-05T17:07:58.354Z'),
  ('33010408-2083-4d31-9811-2a9afae2fceb', '______가 만들어 주신 김치는 정말 맛있어요.', 'Kimchi mà ______ làm cho thực sự rất ngon.', ARRAY['할머니', '아버지', '형', '동생']::text[], '할머니', '할머니 có nghĩa là ''bà'', dùng để gọi bà nội hoặc bà ngoại với thái độ yêu thương, kính trọng.', '7789e7aa-f5f7-45dc-a594-04511863edf7', '2026-01-05T17:07:58.354Z', '2026-01-05T17:07:58.354Z'),
  ('047ec350-5589-4c76-a40e-8b9d2a4c0e48', '어린 시절, ______ 품에서 자주 잠이 들었어요.', 'Hồi nhỏ, tôi thường ngủ thiếp đi trong vòng tay của ______.', ARRAY['할머니', '아빠', '언니', '고모']::text[], '할머니', '할머니 thường gắn liền với hình ảnh ấm áp, chăm sóc và những kỷ niệm tuổi thơ.', '7789e7aa-f5f7-45dc-a594-04511863edf7', '2026-01-05T17:07:58.354Z', '2026-01-05T17:07:58.354Z'),
  ('55d9a87b-27d7-4304-a446-49e5e32ad544', '______께서는 항상 가족을 위해 열심히 일하셨어요.', '______ luôn làm việc chăm chỉ vì gia đình.', ARRAY['아버지', '할아버지', '형', '삼촌']::text[], '아버지', '아버지 là cách gọi trang trọng, kính ngữ đối với ''bố''. ''아빠'' là cách gọi thân mật, gần gũi hơn.', 'd9db4e46-4089-43ea-a511-d7343c9d929c', '2026-01-05T17:07:58.354Z', '2026-01-05T17:07:58.354Z'),
  ('9fcbe796-df24-473d-a1c2-6df8c3f412d2', '저는 어릴 때 ______와 많이 등산을 다녔어요.', 'Hồi nhỏ, tôi đã đi leo núi rất nhiều cùng với ______.', ARRAY['아빠', '엄마', '할머니', '이모']::text[], '아빠', '아빠 là cách gọi thân mật, tự nhiên của con cái đối với cha mình, thường dùng trong sinh hoạt hàng ngày.', 'd9db4e46-4089-43ea-a511-d7343c9d929c', '2026-01-05T17:07:58.354Z', '2026-01-05T17:07:58.354Z'),
  ('b15ed0ec-4d5a-4573-993c-b41a8af675f7', '______께서는 우리에게 중요한 가르침을 주셨어요.', '______ đã dạy cho chúng tôi những bài học quan trọng.', ARRAY['어머니', '아버지', '할아버지', '고모']::text[], '어머니', '어머니 là cách gọi trang trọng, kính ngữ đối với ''mẹ''. ''엄마'' là cách gọi thân mật.', '1869a04b-adea-4751-8c3e-0d40481430f2', '2026-01-05T17:07:58.354Z', '2026-01-05T17:07:58.354Z'),
  ('513afa02-a05d-4130-b6ee-955deb34aaa0', '아플 때마다 ______가 해 주신 죽이 그리워요.', 'Mỗi khi ốm, tôi lại nhớ món cháo mà ______ nấu cho.', ARRAY['엄마', '아빠', '누나', '할아버지']::text[], '엄마', '엄마 là cách gọi đầy yêu thương, gần gũi, thường gắn với sự chăm sóc, vỗ về hàng ngày.', '1869a04b-adea-4751-8c3e-0d40481430f2', '2026-01-05T17:07:58.354Z', '2026-01-05T17:07:58.354Z'),
  ('232ec6a5-81a7-45e1-90b2-66d9595ff039', '저는 ______의 조언을 항상 소중히 들어요.', 'Tôi luôn trân trọng lời khuyên của ______.', ARRAY['형', '누나', '동생', '삼촌']::text[], '형', '형 là cách em trai gọi anh trai của mình. Nó thể hiện sự tôn trọng từ người nhỏ tuổi hơn.', 'dc2567ca-43b1-4f30-863b-545def0a3972', '2026-01-05T17:07:58.354Z', '2026-01-05T17:07:58.354Z'),
  ('b1caab8b-4903-4fc2-bae1-914990083de9', '______가 군대에 가신 후로 집이 많이 조용해졌어요.', 'Kể từ khi ______ đi nghĩa vụ quân sự, nhà tôi trở nên yên tĩnh hẳn.', ARRAY['형', '오빠', '언니', '이모']::text[], '형', 'Người nói là em trai, nên dùng ''형'' để gọi anh trai của mình.', 'dc2567ca-43b1-4f30-863b-545def0a3972', '2026-01-05T17:07:58.354Z', '2026-01-05T17:07:58.354Z'),
  ('84372f52-6417-4d83-ab35-b42206efc51c', '______는 저를 많이 챙겨주는 편이에요.', '______ là người hay chăm sóc tôi.', ARRAY['오빠', '형', '언니', '남동생']::text[], '오빠', '오빠 là cách em gái gọi anh trai của mình. Nó mang sắc thái thân mật, gần gũi.', 'c04469d1-7494-4da8-8c5f-fe1ea0c1adc0', '2026-01-05T17:07:58.354Z', '2026-01-05T17:07:58.354Z'),
  ('34e2f37b-0304-4a4e-a5a5-ecec91b7703b', '운전을 못 하는 저를 대신해 ______가 운전을 해 주셨어요.', 'Thay vì tôi không biết lái xe, ______ đã lái xe giúp.', ARRAY['오빠', '할아버지', '여동생', '이모부']::text[], '오빠', 'Người nói là em gái, đang nói về anh trai ruột của mình nên dùng ''오빠''.', 'c04469d1-7494-4da8-8c5f-fe1ea0c1adc0', '2026-01-05T17:07:58.354Z', '2026-01-05T17:07:58.354Z'),
  ('a394f2c9-3171-45ce-9b57-491958e59fee', '______ 덕분에 어려운 문제를 해결할 수 있었어요.', 'Nhờ có ______ mà tôi đã có thể giải quyết được vấn đề khó.', ARRAY['누나', '오빠', '할머니', '고모']::text[], '누나', '누나 là cách em trai gọi chị gái của mình, thể hiện sự tôn trọng và thân thiết.', 'b3e1dfba-3968-4192-b5dd-e64a7fe5ee64', '2026-01-05T17:07:58.354Z', '2026-01-05T17:07:58.354Z'),
  ('3c1f367b-f387-49b1-9a94-9a88f6cbbc67', '어릴 때는 ______가 제 숙제를 도와주곤 했어요.', 'Hồi nhỏ, ______ thường giúp tôi làm bài tập.', ARRAY['누나', '언니', '여동생', '이모']::text[], '누나', 'Người nói là em trai, đang nhớ về chị gái ruột của mình nên dùng ''누나''.', 'b3e1dfba-3968-4192-b5dd-e64a7fe5ee64', '2026-01-05T17:07:58.354Z', '2026-01-05T17:07:58.354Z'),
  ('65d36673-e942-4c8f-be2c-b22baf6d4c84', '비밀을 가장 많이 공유하는 사람은 저의 ______예요.', 'Người mà tôi chia sẻ bí mật nhiều nhất là ______ của tôi.', ARRAY['언니', '오빠', '남동생', '아저씨']::text[], '언니', '언니 là cách em gái gọi chị gái của mình. Mối quan hệ này thường rất gần gũi và tin tưởng.', '7dbe457e-4aa0-4bf5-9406-8ab848dce2d6', '2026-01-05T17:07:58.354Z', '2026-01-05T17:07:58.354Z'),
  ('819932b6-a5cd-43a5-a0b9-ce22a3e11847', '______의 옷장은 저에게는 패션 잡지와 같아요.', 'Tủ quần áo của ______ đối với tôi giống như một cuốn tạp chí thời trang vậy.', ARRAY['언니', '누나', '형', '삼촌']::text[], '언니', 'Người nói là em gái, đang nói về chị gái ruột của mình nên dùng ''언니''.', '7dbe457e-4aa0-4bf5-9406-8ab848dce2d6', '2026-01-05T17:07:58.354Z', '2026-01-05T17:07:58.354Z'),
  ('e38c4ccf-506e-408b-9e1e-9da33f0c2eb8', '______를 학교에 데려다 주는 것이 저의 아침 일과예요.', 'Việc đưa ______ đến trường là thói quen buổi sáng của tôi.', ARRAY['동생', '할아버지', '아버지', '선생님']::text[], '동생', '동생 là từ chung chỉ ''em'', dùng cho cả em trai và em gái, không phân biệt giới tính của người nói.', 'e15f0837-deed-4569-9e81-dc2959354ac9', '2026-01-05T17:07:58.354Z', '2026-01-05T17:07:58.354Z'),
  ('c172fd2c-59a3-4df1-a1a1-861e726be6b4', '우리 집에서 가장 장난을 잘 치는 사람은 ______입니다.', 'Người nghịch ngợm nhất nhà tôi là ______.', ARRAY['동생', '아버지', '할머니', '고모']::text[], '동생', '동생 thường được dùng khi nói chung về một người em trong gia đình, không cần chỉ rõ là ''남동생'' (em trai) hay ''여동생'' (em gái) nếu ngữ cảnh đã rõ.', 'e15f0837-deed-4569-9e81-dc2959354ac9', '2026-01-05T17:07:58.354Z', '2026-01-05T17:07:58.354Z'),
  ('1d44be5b-0046-4db8-b517-85570e62a675', '날씨가 맑아서 ______.', 'Thời tiết đẹp nên tâm trạng rất tốt.', ARRAY['기분이 좋아요', '피곤해요', '화나요', '졸려요']::text[], '기분이 좋아요', '''기분이 좋다'' là cụm từ cố định diễn tả cảm giác vui vẻ, thoải mái trong tâm trạng, thường do tác động bên ngoài như thời tiết đẹp.', 'e39646b9-e42b-438a-bd74-cad32293eb80', '2026-01-05T17:07:58.354Z', '2026-01-05T17:07:58.354Z'),
  ('f56aa379-b59a-4613-b370-5cba1fe1471b', '______ 노래를 들으세요.', 'Hãy nghe bài hát khiến tâm trạng tốt lên.', ARRAY['기분이 나쁜', '기분이 좋아지는', '피곤한', '화나는']::text[], '기분이 좋아지는', '''기분이 좋아지는'' là dạng định ngữ của động từ ''기분이 좋아지다'' (trở nên vui vẻ), bổ nghĩa cho danh từ ''노래''.', 'e39646b9-e42b-438a-bd74-cad32293eb80', '2026-01-05T17:07:58.354Z', '2026-01-05T17:07:58.354Z'),
  ('0f24966c-fd3d-4ec6-a23c-c73bdc29bcff', '생각만 해도 ______.', 'Chỉ nghĩ đến thôi cũng thấy tâm trạng tệ đi.', ARRAY['기쁘네요', '기분이 나빠져요', '재미있네요', '행복하네요']::text[], '기분이 나빠져요', '''기분이 나빠지다'' là trở nên khó chịu. Cụm từ ''기분이 나쁘다'' thường dùng khi có lý do cụ thể làm hỏng tâm trạng.', '462de8b0-44d1-4890-8474-0ebbbba3be05', '2026-01-05T17:07:58.354Z', '2026-01-05T17:07:58.354Z'),
  ('bf2a62b0-2dc4-4de6-9e08-7719b18523af', '누군가 ______ 농담을 했어요.', 'Ai đó đã nói một trò đùa khiến người khác khó chịu.', ARRAY['기분이 좋은', '재미있는', '기분이 나쁜', '행복한']::text[], '기분이 나쁜', '''기분이 나쁜'' là dạng định ngữ, bổ nghĩa cho ''농담'', chỉ câu chuyện khiến người nghe cảm thấy không vui.', '462de8b0-44d1-4890-8474-0ebbbba3be05', '2026-01-05T17:07:58.354Z', '2026-01-05T17:07:58.354Z'),
  ('2b3ec19f-c44e-4816-9b3b-540b739d71b6', '합격 소식을 듣고 ______.', 'Nghe tin đỗ, tôi vô cùng vui mừng.', ARRAY['화났어요', '기뻤어요', '슬펐어요', '피곤했어요']::text[], '기뻤어요', '''기쁘다'' diễn tả niềm vui mừng, hân hoan trước một tin tốt hoặc sự kiện tích cực. Dạng quá khứ là ''기뻤다''.', '5a03c1bb-2718-4564-8f61-6ddb28b770d4', '2026-01-05T17:07:58.354Z', '2026-01-05T17:07:58.354Z'),
  ('527c5901-9dd5-4825-82b9-34eae238fc9c', '______ 소식을 전해 드립니다.', 'Tôi xin thông báo một tin vui.', ARRAY['기쁜', '슬픈', '화나는', '피곤한']::text[], '기쁜', '''기쁜'' là dạng định ngữ, bổ nghĩa cho ''소식'' (tin tức), nghĩa là ''tin vui''.', '5a03c1bb-2718-4564-8f61-6ddb28b770d4', '2026-01-05T17:07:58.354Z', '2026-01-05T17:07:58.354Z'),
  ('6b99d096-b684-4d4b-89d2-86d594b84c88', '이별해서 마음이 ______.', 'Vì chia tay nên trong lòng rất buồn.', ARRAY['행복해요', '슬퍼요', '화나요', '재미있어요']::text[], '슬퍼요', '''슬프다'' diễn tả nỗi buồn sâu sắc, thường do mất mát, chia ly hoặc những chuyện đau lòng.', '48c9c546-a974-4f4c-8271-3ca5cd2bdd24', '2026-01-05T17:07:58.354Z', '2026-01-05T17:07:58.354Z'),
  ('d1be549c-7f53-45f6-80f1-d64a3dfb74a2', '그 이야기는 ______ 결말이에요.', 'Câu chuyện đó có một kết thúc buồn.', ARRAY['재미있는', '화나는', '슬픈', '기쁜']::text[], '슬픈', '''슬픈'' là dạng định ngữ, bổ nghĩa cho ''결말'' (kết thúc). ''슬픈 결말'' nghĩa là ''kết thúc buồn''.', '48c9c546-a974-4f4c-8271-3ca5cd2bdd24', '2026-01-05T17:07:58.354Z', '2026-01-05T17:07:58.354Z'),
  ('cb3f6f3b-985c-4f5a-aec1-2990adc3b658', '약속을 안 지켜서 ______.', 'Vì không giữ lời hứa nên tôi đã tức giận.', ARRAY['기뻤어요', '화났어요', '슬펐어요', '재미있었어요']::text[], '화났어요', '''화나다'' diễn tả cảm giác tức giận, bực bội khi có chuyện không như ý hoặc ai đó làm sai.', '5edf46ee-7eaa-4b39-82d3-e05347df6335', '2026-01-05T17:07:58.354Z', '2026-01-05T17:07:58.354Z'),
  ('aa74e19d-0c91-4c83-9d49-e531f004a9bb', '______ 목소리로 말하지 마세요.', 'Đừng nói với giọng điệu tức giận.', ARRAY['작은', '화난', '기쁜', '재미있는']::text[], '화난', '''화난'' là dạng định ngữ, bổ nghĩa cho ''목소리'' (giọng nói). ''화난 목소리'' là ''giọng nói giận dữ''.', '5edf46ee-7eaa-4b39-82d3-e05347df6335', '2026-01-05T17:07:58.354Z', '2026-01-05T17:07:58.354Z'),
  ('4819f703-5b8d-4940-a0a3-7b581cfdb6b1', '이 게임 정말 ______!', 'Trò chơi này thật sự rất thú vị!', ARRAY['재미있어요', '피곤해요', '아파요', '졸려요']::text[], '재미있어요', '''재미있다'' thường dùng để đánh giá một sự vật, sự việc (phim, sách, trò chơi...) mang lại cảm giác vui vẻ, hứng thú.', '1733d493-5f89-4437-97b9-6b9356865b49', '2026-01-05T17:07:58.354Z', '2026-01-05T17:07:58.354Z'),
  ('94800d96-9328-46ab-b22e-8b3432c1d57a', '______ 책을 추천해 주세요.', 'Hãy giới thiệu cho tôi một quyển sách thú vị.', ARRAY['비싼', '재미있는', '무거운', '어려운']::text[], '재미있는', '''재미있는'' là dạng định ngữ, bổ nghĩa cho ''책'' (sách). ''재미있는 책'' là ''sách hay/sách thú vị''.', '1733d493-5f89-4437-97b9-6b9356865b49', '2026-01-05T17:07:58.354Z', '2026-01-05T17:07:58.354Z'),
  ('fe8a42c7-eeb4-45e4-a07f-c77ac7c1544b', '건강하고 ______ 살고 싶어요.', 'Tôi muốn sống khỏe mạnh và hạnh phúc.', ARRAY['행복하게', '슬프게', '화나게', '피곤하게']::text[], '행복하게', '''행복하게'' là dạng phó từ của ''행복하다'', nghĩa là ''một cách hạnh phúc''. Đây là cảm giác mãn nguyện, vui sướng trọn vẹn.', '9859a67f-ff04-422a-b93b-b27ee22747a8', '2026-01-05T17:07:58.354Z', '2026-01-05T17:07:58.354Z'),
  ('70cc12d7-888a-4542-a0d0-6425fd4c2018', '세상에서 제일 ______ 순간이에요.', 'Đó là khoảnh khắc hạnh phúc nhất trên đời.', ARRAY['행복한', '화난', '지루한', '짜증나는']::text[], '행복한', '''행복한'' là dạng định ngữ, bổ nghĩa cho ''순간'' (khoảnh khắc). ''행복한 순간'' là ''khoảnh khắc hạnh phúc''.', '9859a67f-ff04-422a-b93b-b27ee22747a8', '2026-01-05T17:07:58.354Z', '2026-01-05T17:07:58.354Z'),
  ('a1f441e0-a635-485d-b97f-dbdc65c92bca', '다리가 아파서 ______.', 'Chân đau nên tôi thấy mệt mỏi.', ARRAY['배고파요', '피곤해요', '졸려요', '화나요']::text[], '피곤해요', '''피곤하다'' diễn tả trạng thái mệt mỏi về thể chất (và đôi khi tinh thần) sau khi hoạt động nhiều.', '2f00f934-32e4-4da7-82a8-a6bfb28a068b', '2026-01-05T17:07:58.354Z', '2026-01-05T17:07:58.354Z'),
  ('3cb572af-ceed-448e-97c7-ca31eaaa0f60', '______ 하루였어요.', 'Thật là một ngày mệt mỏi.', ARRAY['즐거운', '피곤한', '기쁜', '재미있는']::text[], '피곤한', '''피곤한'' là dạng định ngữ, bổ nghĩa cho ''하루'' (một ngày). ''피곤한 하루'' là ''ngày mệt mỏi''.', '2f00f934-32e4-4da7-82a8-a6bfb28a068b', '2026-01-05T17:07:58.354Z', '2026-01-05T17:07:58.354Z'),
  ('811c56da-7b74-4dab-8c02-2c5d72c581f2', '잠을 못 자서 ______.', 'Vì không ngủ được nên tôi buồn ngủ.', ARRAY['배고파요', '피곤해요', '졸려요', '아파요']::text[], '졸려요', '''졸리다'' diễn tả cảm giác buồn ngủ, mắt díu lại và muốn đi ngủ. Nó khác với ''피곤하다'' (mệt mỏi chung).', '5ba41f9c-395f-4100-ab21-073d75dbae8d', '2026-01-05T17:07:58.354Z', '2026-01-05T17:07:58.354Z'),
  ('6e811ad2-20f3-420f-a184-e72209aaaca6', '______ 눈을 비비고 있어요.', 'Tôi đang dụi đôi mắt buồn ngủ.', ARRAY['행복한', '예쁜', '졸린', '아픈']::text[], '졸린', '''졸린'' là dạng định ngữ, bổ nghĩa cho ''눈'' (mắt). ''졸린 눈'' là ''đôi mắt buồn ngủ''.', '5ba41f9c-395f-4100-ab21-073d75dbae8d', '2026-01-05T17:07:58.354Z', '2026-01-05T17:07:58.354Z'),
  ('984ad909-6f3e-414d-9acd-081cff70c92f', '배가 ______ 병원에 갔어요.', 'Vì đau bụng nên tôi đã đi bệnh viện.', ARRAY['고파서', '아파서', '부러서', '시려서']::text[], '아파서', '''아프다'' diễn tả cảm giác đau đớn ở một bộ phận cơ thể hoặc trạng thái bị ốm, bệnh.', '236b5b7d-9777-4cbe-82ca-2f352601b3d3', '2026-01-05T17:07:58.354Z', '2026-01-05T17:07:58.354Z'),
  ('ff3fddd0-5759-4f14-a025-ded572ce40fc', '______ 곳을 말씀해 주세요.', 'Hãy nói cho tôi biết chỗ nào bị đau.', ARRAY['아픈', '큰', '작은', '예쁜']::text[], '아픈', '''아픈'' là dạng định ngữ, bổ nghĩa cho ''곳'' (chỗ, vị trí). ''아픈 곳'' là ''chỗ bị đau''.', '236b5b7d-9777-4cbe-82ca-2f352601b3d3', '2026-01-05T17:07:58.354Z', '2026-01-05T17:07:58.354Z');
