INSERT INTO "QuizQuestion" (id, question, "questionTranslation", options, "correctAnswer", explanation, "VocabularyId", "createdAt", "updatedAt") VALUES ('4ac1c0a3-65ca-4d95-bfab-9a9fb3b83d5b', '______ 공기가 가장 상쾌해요.', 'Không khí ______ trong lành nhất.', ARRAY['아침', '점심', '저녁', '밤']::text[], '아침', '아침 có nghĩa là ''buổi sáng'', thường chỉ khoảng thời gian từ lúc mặt trời mọc đến trước trưa.', '07a19a89-0d9a-4232-82d6-57c390b81937', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('57864415-8703-420e-95ea-8cb5f2d71153', '______을 먹지 않고 학교에 가면 배가 고파요.', 'Nếu không ăn ______ mà đi học thì sẽ đói bụng.', ARRAY['아침', '점심', '저녁', '밤']::text[], '아침', '아침 cũng có thể được dùng để chỉ ''bữa sáng'' (아침식사), đặc biệt khi đi với động từ ''먹다'' (ăn).', '07a19a89-0d9a-4232-82d6-57c390b81937', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('c4759cb5-3aa5-4db4-a1fe-8718a6c24b87', '알람 시계가 울리면 ______.', 'Khi đồng hồ báo thức kêu thì tôi ______.', ARRAY['일어나요', '자요', '먹어요', '읽어요']::text[], '일어나요', '일어나다 có nghĩa là ''thức dậy'', chỉ hành động chuyển từ trạng thái ngủ sang trạng thái thức.', '33e95485-956b-4117-9914-d6fbc59ed65e', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('44484969-dd04-42ac-8a8c-c04758c83ff6', '늦잠을 자서 9시에 ______.', 'Vì ngủ nướng nên tôi đã ______ lúc 9 giờ.', ARRAY['일어났어요', '잤어요', '먹었어요', '웃었어요']::text[], '일어났어요', '일어나다 thường được dùng để miêu tả thời điểm bắt đầu một ngày mới sau giấc ngủ.', '33e95485-956b-4117-9914-d6fbc59ed65e', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('3c02f29a-bb9e-408c-a9ca-c1394350503f', '잠에서 깨어나면 먼저 ______.', 'Sau khi tỉnh giấc, đầu tiên tôi ______.', ARRAY['세수해요', '먹어요', '읽어요', '달려요']::text[], '세수해요', '세수하다 có nghĩa là ''rửa mặt'', thường là một trong những hoạt động đầu tiên sau khi thức dậy.', '31c8012b-8301-493f-854b-a3bd190ddabb', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('fcf1acd0-a154-48b4-896f-e8a082f93054', '______ 후에 상쾌한 느낌이 들어요.', 'Sau khi ______, tôi cảm thấy sảng khoái.', ARRAY['세수하고', '먹고', '자고', '쓰고']::text[], '세수하고', '세수하다 không chỉ làm sạch mặt mà còn giúp tỉnh táo và bắt đầu ngày mới.', '31c8012b-8301-493f-854b-a3bd190ddabb', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('a0523a8d-d459-4c1f-b8a2-d092d3e1d7b3', '하루에 세 번 ______ 위생에 좋아요.', 'Một ngày ______ ba lần thì tốt cho vệ sinh răng miệng.', ARRAY['이를 닦는 것이', '세수하는 것이', '머리 감는 것이', '손 씻는 것이']::text[], '이를 닦는 것이', '이를 닦다 có nghĩa là ''đánh răng'', một hoạt động vệ sinh cá nhân quan trọng.', '57b4fe0e-9b8d-4622-bfef-434be09609eb', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('01617757-c5fe-4f84-866f-83a098ec212e', '______ 칫솔과 치약이 필요해요.', 'Để ______ cần có bàn chải đánh răng và kem đánh răng.', ARRAY['이를 닦으려면', '세수하려면', '머리 감으려면', '씻으려면']::text[], '이를 닦으려면', '이를 닦다 luôn đi kèm với dụng cụ là 칫솔 (bàn chải đánh răng) và 치약 (kem đánh răng).', '57b4fe0e-9b8d-4622-bfef-434be09609eb', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('6a19cbce-a832-4b99-8995-1976fa877365', '______은 하루의 에너지를 공급해요.', 'Việc ______ cung cấp năng lượng cho cả ngày.', ARRAY['아침을 먹는 것이', '점심을 먹는 것이', '저녁을 먹는 것이', '간식을 먹는 것이']::text[], '아침을 먹는 것이', '아침을 먹다 có nghĩa là ''ăn sáng'', bữa ăn đầu tiên trong ngày sau một đêm dài.', 'c7923637-cabb-4fdb-b949-ee0a07ae7a85', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('9011e3dd-9508-40cf-abee-4f0cc45384bb', '______ 시간이 없어서 그냥 커피만 마셨어요.', 'Vì không có thời gian ______ nên tôi chỉ uống cà phê thôi.', ARRAY['아침을 먹을', '점심을 먹을', '저녁을 먹을', '간식을 먹을']::text[], '아침을 먹을', '아침을 먹다 thường bị bỏ qua khi mọi người vội vã đi làm hoặc đi học.', 'c7923637-cabb-4fdb-b949-ee0a07ae7a85', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('6d93b480-540c-4e53-bc7e-c8829cd78c7d', '날씨에 맞는 ______ 건강에 중요해요.', 'Việc ______ phù hợp với thời tiết rất quan trọng cho sức khỏe.', ARRAY['옷을 입는 것이', '신발을 신는 것이', '모자를 쓰는 것이', '장갑을 끼는 것이']::text[], '옷을 입는 것이', '옷을 입다 có nghĩa là ''mặc quần áo'', một hoạt động chuẩn bị cơ bản trước khi ra ngoài.', '8e79fa5c-7c34-4144-b68a-a2c1f186c8c1', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('c4de3b34-0c85-44a6-baac-00b47d360471', '______ 오늘 일정에 맞는 복장을 선택해요.', 'Khi ______, tôi chọn trang phục phù hợp với lịch trình hôm nay.', ARRAY['옷을 입을 때', '세수할 때', '이를 닦을 때', '먹을 때']::text[], '옷을 입을 때', '옷을 입다 không chỉ là mặc đồ mà còn bao gồm việc lựa chọn trang phục phù hợp.', '8e79fa5c-7c34-4144-b68a-a2c1f186c8c1', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('6879dd20-8c59-4151-9947-8db0ddf50a5f', '학생들은 월요일부터 금요일까지 ______.', 'Học sinh ______ từ thứ Hai đến thứ Sáu.', ARRAY['학교에 가요', '회사에 가요', '여행 가요', '쉬어요']::text[], '학교에 가요', '학교에 가다 có nghĩa là ''đi đến trường'', thường dùng cho học sinh, sinh viên.', '81f482cb-1f4d-4289-a625-45ab20e68d82', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('cdef2ccd-f57d-4e3c-8c33-93cafd3a64e9', '버스나 지하철을 타고 ______.', 'Tôi ______ bằng xe buýt hoặc tàu điện ngầm.', ARRAY['학교에 가요', '집에 가요', '공원에 가요', '병원에 가요']::text[], '학교에 가요', '학교에 가다 thường được kết hợp với phương tiện di chuyển như 걸어서 (đi bộ), 버스로 (bằng xe buýt).', '81f482cb-1f4d-4289-a625-45ab20e68d82', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('7f68804c-02c4-4bf1-8418-8f242484111c', '직장인들은 아침에 ______.', 'Nhân viên văn phòng ______ vào buổi sáng.', ARRAY['회사에 가요', '학교에 가요', '시장에 가요', '도서관에 가요']::text[], '회사에 가요', '회사에 가다 có nghĩa là ''đi đến công ty'', tương đương với ''đi làm'' đối với nhân viên văn phòng.', '51866cac-5680-4ec6-b13f-0f3167f9bd57', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('22d60f46-df26-48e8-818d-7a0c904e4abd', '출근 시간에는 ______ 사람들이 많아요.', 'Vào giờ đi làm, có rất nhiều người đang ______.', ARRAY['회사에 가는', '학교에 가는', '집에 가는', '쇼핑하는']::text[], '회사에 가는', '회사에 가다 thường được dùng trong bối cảnh 출근 (đi làm) và thường xảy ra vào buổi sáng các ngày trong tuần.', '51866cac-5680-4ec6-b13f-0f3167f9bd57', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('6c9889a0-98bd-4002-9842-933714b4ca82', '월요일부터 금요일까지 회사에서 ______.', 'Từ thứ Hai đến thứ Sáu, tôi ______ ở công ty.', ARRAY['쉬어요', '일해요', '놀아요', '자요']::text[], '일해요', '일하다 có nghĩa là ''làm việc'', là hoạt động chính trong ngày của nhiều người trưởng thành.', 'a0143de0-7de3-48a8-89e8-4e4e3e7294dc', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('f4c1df9c-b104-4fc7-941e-391fa04c3dc4', '저는 보통 아침 9시부터 저녁 6시까지 ______.', 'Tôi thường ______ từ 9 giờ sáng đến 6 giờ tối.', ARRAY['공부해요', '일해요', '쉬어요', '먹어요']::text[], '일해요', '일하다 thường được dùng để chỉ công việc chính thức, có giờ giấc cụ thể.', 'a0143de0-7de3-48a8-89e8-4e4e3e7294dc', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('c1e7ad56-2804-4d0d-baf8-625d15fb6a98', '시험 전에는 열심히 ______.', 'Trước kỳ thi thì tôi chăm chỉ ______.', ARRAY['놀아요', '공부해요', '자요', '먹어요']::text[], '공부해요', '공부하다 có nghĩa là ''học tập'', là hoạt động chính của học sinh, sinh viên.', '45a1971e-dc5f-457d-963b-3d76518808fe', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('cf693acd-6b28-4994-b589-6296139284dc', '한국어를 잘하고 싶어서 매일 ______.', 'Vì muốn giỏi tiếng Hàn nên tôi ______ mỗi ngày.', ARRAY['일해요', '공부해요', '쉬어요', '놀아요']::text[], '공부해요', '공부하다 thường đi với đối tượng học (한국어를 공부하다, 수학을 공부하다).', '45a1971e-dc5f-457d-963b-3d76518808fe', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('769a3c03-edba-4523-a216-1cd25d7d4517', '회사 사람들과 함께 ______.', 'Tôi ______ cùng với đồng nghiệp.', ARRAY['점심을 먹어요', '일을 해요', '놀아요', '잠을 자요']::text[], '점심을 먹어요', '점심을 먹다 là ''ăn trưa'', thường diễn ra vào khoảng 12 giờ trưa.', 'd64bbe14-f95a-4ca4-ac8a-34eaf4deb1ee', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('45d55d04-8d6b-4811-bced-b05022a0619f', '______ 후에 커피를 마셔요.', 'Sau khi ______, tôi uống cà phê.', ARRAY['일을 하고', '공부하고', '점심을 먹고', '쉬고']::text[], '점심을 먹고', '점심을 먹다 là hoạt động thường nhật, thường được nhắc đến trong lịch trình hàng ngày.', 'd64bbe14-f95a-4ca4-ac8a-34eaf4deb1ee', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('19d99cb3-8edf-44e0-8f5c-7c0e9635d0c0', '오랜만에 고등학교 ______.', 'Tôi ______ bạn cấp 3 sau một thời gian dài.', ARRAY['친구를 만나요', '친구를 찾아요', '친구를 부르요', '친구를 기다려요']::text[], '친구를 만나요', '친구를 만나다 có nghĩa là ''gặp gỡ bạn bè'', là hoạt động xã hội phổ biến.', '49644d68-7896-41e5-b7ff-2b67bcafb2db', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('c6158ff5-b997-41f3-b6ff-f84ec2f9e6c0', '주말에는 보통 ______.', 'Vào cuối tuần tôi thường ______.', ARRAY['일해요', '공부해요', '친구를 만나요', '청소해요']::text[], '친구를 만나요', '친구를 만나다 thường là hoạt động vào cuối tuần khi mọi người có thời gian rảnh.', '49644d68-7896-41e5-b7ff-2b67bcafb2db', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('c22adf26-b3f8-4d47-984d-1fd5b2885527', '전화로 오랜 시간 ______.', 'Tôi ______ lâu trên điện thoại.', ARRAY['이야기해요', '일해요', '공부해요', '자요']::text[], '이야기해요', '이야기하다 có nghĩa là ''trò chuyện, nói chuyện'', thường đi với ''와/과'' (cùng với).', '6d5b527b-3bdd-4c2f-abef-1a2206b3c8d8', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('140265fc-0354-4d0f-b564-b463b610c4f0', '중요한 문제에 대해 ______.', 'Chúng tôi ______ về vấn đề quan trọng.', ARRAY['이야기해요', '먹어요', '자요', '놀아요']::text[], '이야기해요', '이야기하다 có thể dùng trong cả ngữ cảnh thân mật và nghiêm túc.', '6d5b527b-3bdd-4c2f-abef-1a2206b3c8d8', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('1d45bc40-3cfa-4308-b03d-506d9c7950ac', '일을 많이 해서 ______.', 'Vì làm việc nhiều nên tôi muốn ______.', ARRAY['더 해요', '쉬고 싶어요', '놀고 싶어요', '먹고 싶어요']::text[], '쉬고 싶어요', '쉬다 có nghĩa là ''nghỉ ngơi'', thường dùng khi cảm thấy mệt mỏi.', '92c59ebc-137a-4f46-b701-87db8214bd47', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('a9c5d4ca-daaf-4d16-bbd4-1663176fdda4', '점심시간에 30분 ______.', 'Tôi ______ 30 phút vào giờ nghỉ trưa.', ARRAY['일해요', '쉬어요', '공부해요', '청소해요']::text[], '쉬어요', '쉬다 là hoạt động cần thiết để lấy lại năng lượng trong ngày.', '92c59ebc-137a-4f46-b701-87db8214bd47', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('21e6dfab-4aa2-48fa-8337-fd09cbcfebd8', '신선한 야채를 사려고 ______.', 'Để mua rau tươi, tôi ______.', ARRAY['시장에 가요', '백화점에 가요', '마트에 가요', '편의점에 가요']::text[], '시장에 가요', '시장에 가다 là ''đi chợ'', thường để mua thực phẩm tươi sống với giá rẻ.', '396df300-c2cf-4a64-9699-ce054bf4cd1b', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('9af950bd-aa5c-4f07-8ea4-5fd8ed363dad', '일요일 아침에 ______.', 'Vào sáng Chủ nhật, tôi ______.', ARRAY['늦게 자요', '시장에 가요', '일해요', '공부해요']::text[], '시장에 가요', '시장에 가다 thường là hoạt động cuối tuần, đặc biệt là vào buổi sáng.', '396df300-c2cf-4a64-9699-ce054bf4cd1b', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('6e35dcaa-dec6-4cb2-b77a-45213a1fd9b0', '방이 더러워서 ______.', 'Vì phòng bẩn nên tôi ______.', ARRAY['청소해요', '자요', '놀아요', '먹어요']::text[], '청소해요', '청소하다 có nghĩa là ''dọn dẹp, lau chùi'', là công việc nhà thường xuyên.', '4859ae33-1d83-4f62-a149-d63812f225a6', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('e8e37ae8-cc2b-46ac-a258-4f85bd1ea62a', '집안을 깨끗하게 ______.', 'Tôi ______ để nhà cửa sạch sẽ.', ARRAY['청소해요', '고쳐요', '만들어요', '팔아요']::text[], '청소해요', '청소하다 thường đi với đối tượng được dọn dẹp (방을 청소하다, 집을 청소하다).', '4859ae33-1d83-4f62-a149-d63812f225a6', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('e625fd6d-b398-4751-9cd4-4a101e28c20a', '피곤해서 빨리 ______.', 'Vì mệt nên tôi muốn về nhà nhanh.', ARRAY['집에 가고 싶어요', '학교에 가고 싶어요', '쇼핑하고 싶어요', '운동하고 싶어요']::text[], '집에 가고 싶어요', '''집에 가다'' nghĩa là ''về nhà'', đây là hoạt động thường làm sau khi kết thúc công việc hoặc học tập.', '0f628eea-d23c-4aab-ac3b-191d38ed8a35', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('2be91214-998e-4c9e-9190-42c4e3f3b31d', '회사에서 ______ 길에 마트에 들렀어요.', 'Trên đường về nhà từ công ty, tôi đã ghé vào siêu thị.', ARRAY['집에 오는', '학교에 가는', '출근하는', '여행하는']::text[], '집에 오는', '''집에 오다'' được dùng khi nói về việc trở về nhà của chính mình.', '0f628eea-d23c-4aab-ac3b-191d38ed8a35', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('1dd173f3-1baa-4267-bb23-522991e856bd', '보통 몇 시에 ______?', 'Bạn thường ăn tối lúc mấy giờ?', ARRAY['아침을 먹어요', '점심을 먹어요', '저녁을 먹어요', '밤을 먹어요']::text[], '저녁을 먹어요', '''저녁을 먹다'' là cụm từ cố định để chỉ hoạt động ăn bữa tối.', '4ee4313f-d3dc-4027-9fed-165bf78e7711', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('e053c521-0b48-4a63-8f1d-b4cf9d4273f8', '오늘 ______ 식당을 추천해 주세요.', 'Hãy giới thiệu cho tôi nhà hàng để ăn tối hôm nay.', ARRAY['저녁을 먹을', '아침을 먹을', '점심을 먹을', '회식을 할']::text[], '저녁을 먹을', '''저녁을 먹을'' là dạng định ngữ của động từ, bổ nghĩa cho danh từ ''식당'' (nhà hàng).', '4ee4313f-d3dc-4027-9fed-165bf78e7711', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('0354fb19-a244-4537-9015-717391979dd9', '아이들이 방에서 ______ 있어요.', 'Bọn trẻ đang làm bài tập trong phòng.', ARRAY['숙제를 하고', '놀고', '자고', '읽고']::text[], '숙제를 하고', '''숙제를 하다'' là cụm động từ chỉ hoạt động làm bài tập về nhà.', '759a8d2a-0f4e-4489-a802-02f602963446', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('f7a3fdfd-d37d-4285-b205-12b90153b6d9', '______ 많아서 시간이 없어요.', 'Vì có nhiều bài tập nên tôi không có thời gian.', ARRAY['숙제가', '일이', '친구가', '시간이']::text[], '숙제가', 'Trong câu này, ''숙제'' (bài tập) đóng vai trò chủ ngữ với trợ từ ''가''.', '759a8d2a-0f4e-4489-a802-02f602963446', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('4415ccc1-31bf-4059-96bc-9ad21159dec1', '주말에 집에서 ______.', 'Vào cuối tuần tôi xem TV ở nhà.', ARRAY['텔레비전을 봐요', '텔레비전을 만들어요', '텔레비전을 고쳐요', '텔레비전을 팔아요']::text[], '텔레비전을 봐요', '''텔레비전을 보다'' là hoạt động giải trí phổ biến, có nghĩa là xem truyền hình.', 'b498d95f-ad6f-4566-9f31-88e3fcc803e5', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('b75e20ce-f66c-4632-9069-f1780a84303b', '______ 좋아하는 프로그램이 뭐예요?', 'Chương trình TV nào bạn thích xem?', ARRAY['텔레비전에서', '라디오에서', '인터넷에서', '영화관에서']::text[], '텔레비전에서', '''텔레비전에서'' có nghĩa là ''trên TV'', chỉ phương tiện phát sóng chương trình.', 'b498d95f-ad6f-4566-9f31-88e3fcc803e5', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('2c965883-5bfb-4580-a03a-11fdd241fd1f', '잠들기 전에 ______.', 'Trước khi ngủ tôi đọc sách.', ARRAY['책을 읽어요', '책을 써요', '책을 사요', '책을 빌려요']::text[], '책을 읽어요', '''책을 읽다'' là hoạt động đọc sách, thường được làm vào buổi tối để thư giãn.', '9f4ddb7d-3d57-4971-936d-bb34ea2b0402', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('aaaf38c9-8ce5-4487-9bc8-4fecfa27fd11', '______ 시간이 부족해요.', 'Tôi thiếu thời gian để đọc sách.', ARRAY['책을 읽을', '영화를 볼', '음악을 들을', '운동할']::text[], '책을 읽을', '''책을 읽을'' là dạng định ngữ, bổ nghĩa cho danh từ ''시간'' (thời gian).', '9f4ddb7d-3d57-4971-936d-bb34ea2b0402', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('71ca0a97-ad4f-401a-9d26-830ef1636e08', '더워서 하루에 두 번 ______.', 'Vì nóng nên tôi tắm hai lần một ngày.', ARRAY['샤워해요', '씻어요', '목욕해요', '청소해요']::text[], '샤워해요', '''샤워하다'' là động từ Hán-Hàn chỉ việc tắm bằng vòi sen, khác với ''목욕하다'' (tắm bồn).', 'b0717e9a-9b34-41da-8201-a4c5996d8401', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('d153e85f-0393-4646-943c-695f6ff169f7', '______ 물이 차가워요.', 'Nước tắm lạnh.', ARRAY['샤워', '세탁', '요리', '청소']::text[], '샤워', '''샤워'' ở đây là danh từ, ''샤워 물'' có nghĩa là ''nước để tắm''.', 'b0717e9a-9b34-41da-8201-a4c5996d8401', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('b9500c71-873b-4893-9137-b62e08a28a95', '건강을 위해 매일 ______.', 'Để khỏe mạnh, tôi tập thể dục mỗi ngày.', ARRAY['운동해요', '먹어요', '자요', '놀아요']::text[], '운동해요', '''운동하다'' là hoạt động rèn luyện thân thể, tốt cho sức khỏe.', 'd54bad10-afa3-4963-87ec-6f162dc10637', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('4cd2a9ab-35b3-4834-94da-c41cf3c4a3e4', '______ 좋은 장소를 알고 싶어요.', 'Tôi muốn biết địa điểm tốt để tập thể dục.', ARRAY['운동할', '쇼핑할', '공부할', '일할']::text[], '운동할', '''운동할'' là dạng định ngữ, bổ nghĩa cho danh từ ''장소'' (địa điểm).', 'd54bad10-afa3-4963-87ec-6f162dc10637', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('108c354e-c05d-4e4a-8f00-2b500ea023db', '어제 밤에 잘 못 ______.', 'Tối qua tôi không ngủ được.', ARRAY['잡니다', '잡시다', '자요', '잤어요']::text[], '잤어요', '''자다'' chia quá khứ thành ''잤다'', phù hợp với trạng từ ''어제 밤에'' (tối qua).', '3c026015-3f33-4c6c-b196-edff1c0d1ca8', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('b779a681-1423-4124-9b0b-dc0ee4c36671', '______ 전에 차 한 잔 마시지 마세요.', 'Đừng uống trà trước khi ngủ.', ARRAY['자는', '잘', '자고', '자면']::text[], '잘', '''잘'' là dạng gốc của ''자다'' khi kết hợp với ''전에'' (trước khi) để tạo cụm ''잘 전에'' (trước khi ngủ).', '3c026015-3f33-4c6c-b196-edff1c0d1ca8', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z');
INSERT INTO "LearningGroup" (id, name, icon, "topikLevel", "createdAt", "updatedAt") VALUES ('12626874-0059-49e0-b221-fc8fdff3dbf5', 'Khoa học & Công nghệ (과학 및 기술)', NULL, 3, '2026-01-05T17:07:58.358Z', '2026-01-05T17:07:58.358Z');
INSERT INTO "Category" (id, name, description, icon, "order", "topikLevel", "groupId", "createdAt", "updatedAt") VALUES ('a8ad6055-c840-4160-aaa5-a3410c9fdebe', 'Công nghệ thông tin & AI (IT 기술 및 인공지능)', 'Từ vựng về các công nghệ thông tin cốt lõi, trí tuệ nhân tạo và học máy', '🤖', 1, 3, '12626874-0059-49e0-b221-fc8fdff3dbf5', '2026-01-05T17:07:58.358Z', '2026-01-05T17:07:58.358Z'),
  ('9db553aa-cd9b-490b-b3a7-61cc819dfab5', 'Khoa học Đời sống & Công nghệ Sinh học (생명 과학 및 바이오 기술)', 'Từ vựng về các khái niệm nền tảng và công nghệ tiên tiến trong lĩnh vực sinh học và công nghệ sinh học.', '🧬', 2, 3, '12626874-0059-49e0-b221-fc8fdff3dbf5', '2026-01-05T17:07:58.358Z', '2026-01-05T17:07:58.358Z'),
  ('379a6be1-bc23-47e2-92bf-14f7a2d695d7', 'Vật lý & Vũ trụ học (물리학 및 우주론)', 'Từ vựng về các lý thuyết vật lý cơ bản, khái niệm vũ trụ học và hiện tượng thiên văn học lớn', '🌌', 3, 3, '12626874-0059-49e0-b221-fc8fdff3dbf5', '2026-01-05T17:07:58.358Z', '2026-01-05T17:07:58.358Z'),
  ('064843ae-5b21-4cd5-8cd1-379a54a6ae93', 'Khoa học Môi trường (환경 과학)', 'Từ vựng về các vấn đề môi trường, biến đổi khí hậu và bảo tồn thiên nhiên', '🌱', 4, 3, '12626874-0059-49e0-b221-fc8fdff3dbf5', '2026-01-05T17:07:58.358Z', '2026-01-05T17:07:58.358Z'),
  ('fb16f2ee-c916-43bc-a9b2-3b9cd6b2036a', 'Khoa học Não bộ & Tâm lý học Nhận thức (뇌 과학 및 인지 심리학)', 'Từ vựng về cấu trúc, chức năng của não bộ và các quá trình tinh thần như nhận thức, học tập, ra quyết định.', '🧠', 5, 3, '12626874-0059-49e0-b221-fc8fdff3dbf5', '2026-01-05T17:07:58.358Z', '2026-01-05T17:07:58.358Z'),
  ('d9f4687c-1bde-4fe2-8172-cddec1fcdade', 'Y học & Dược học (의학 및 약학)', 'Từ vựng về các khái niệm y học hiện đại, nghiên cứu dược phẩm, bệnh lý và phương pháp điều trị tiên tiến', '🩺', 6, 3, '12626874-0059-49e0-b221-fc8fdff3dbf5', '2026-01-05T17:07:58.358Z', '2026-01-05T17:07:58.358Z');
INSERT INTO "Vocabulary" (id, korean, hanja, vietnamese, pronunciation, example, "exampleTranslation", "categoryId", "createdAt", "updatedAt") VALUES ('b8d5d058-c0a6-481a-a47d-03caada5af84', '머신러닝', NULL, 'Học máy (Machine Learning)', 'meosilleoning', '머신러닝은 데이터로부터 패턴을 학습하여 스스로 성능을 개선하는 기술이다.', 'Học máy là công nghệ tự cải thiện hiệu suất bằng cách học các mẫu từ dữ liệu.', 'a8ad6055-c840-4160-aaa5-a3410c9fdebe', '2026-01-05T17:07:58.358Z', '2026-01-05T17:07:58.358Z'),
  ('8da631de-e9f1-495d-b59f-14a2ac0a3622', '딥러닝', NULL, 'Học sâu (Deep Learning)', 'dimneoning', '딥러닝 기술은 이미지 인식과 자연어 처리 분야에서 획기적인 발전을 이끌었다.', 'Công nghệ học sâu đã dẫn đến sự phát triển đột phá trong lĩnh vực nhận dạng hình ảnh và xử lý ngôn ngữ tự nhiên.', 'a8ad6055-c840-4160-aaa5-a3410c9fdebe', '2026-01-05T17:07:58.358Z', '2026-01-05T17:07:58.358Z'),
  ('6498f4b5-8165-4a9f-bbe6-836af08ffe4d', '블록체인', NULL, 'Chuỗi khối (Blockchain)', 'beullokchein', '블록체인 기술은 금융 거래의 투명성과 보안성을 높일 수 있다.', 'Công nghệ blockchain có thể nâng cao tính minh bạch và bảo mật của các giao dịch tài chính.', 'a8ad6055-c840-4160-aaa5-a3410c9fdebe', '2026-01-05T17:07:58.358Z', '2026-01-05T17:07:58.358Z'),
  ('52681d01-7a14-4cdd-af5b-6ebe9ed56b1d', '알고리즘', NULL, 'Thuật toán', 'algorijeum', '추천 시스템은 정교한 알고리즘을 기반으로 사용자에게 맞춤형 콘텐츠를 제공한다.', 'Hệ thống đề xuất cung cấp nội dung được cá nhân hóa cho người dùng dựa trên các thuật toán tinh vi.', 'a8ad6055-c840-4160-aaa5-a3410c9fdebe', '2026-01-05T17:07:58.358Z', '2026-01-05T17:07:58.358Z'),
  ('b511d5e3-7faf-4a27-96fb-135eb3628390', '자연어 처리 (NLP)', '自然語 處理', 'Xử lý ngôn ngữ tự nhiên', 'jayeon-eo cheori', '챗봇은 자연어 처리 기술을 활용하여 사람과 대화하는 인공지능 프로그램이다.', 'Chatbot là một chương trình trí tuệ nhân tạo đối thoại với con người bằng cách sử dụng công nghệ xử lý ngôn ngữ tự nhiên.', 'a8ad6055-c840-4160-aaa5-a3410c9fdebe', '2026-01-05T17:07:58.358Z', '2026-01-05T17:07:58.358Z'),
  ('e621995e-f7ef-4125-b4a2-342de69afdb7', '유전 공학', NULL, 'Kỹ thuật di truyền', 'yujeon gonghak', '유전 공학 기술을 통해 특정 질병에 강한 작물을 개발할 수 있다.', 'Có thể phát triển các loại cây trồng kháng bệnh cụ thể thông qua kỹ thuật di truyền.', '9db553aa-cd9b-490b-b3a7-61cc819dfab5', '2026-01-05T17:07:58.358Z', '2026-01-05T17:07:58.358Z'),
  ('0f7d0026-7296-42cf-81f6-be4a4597627e', '줄기세포', NULL, 'Tế bào gốc', 'julgisepo', '줄기세포 연구는 손상된 조직을 재생시키는 등 난치병 치료의 가능성을 열었다.', 'Nghiên cứu tế bào gốc đã mở ra khả năng điều trị các bệnh nan y, chẳng hạn như tái tạo các mô bị tổn thương.', '9db553aa-cd9b-490b-b3a7-61cc819dfab5', '2026-01-05T17:07:58.358Z', '2026-01-05T17:07:58.358Z'),
  ('c5617952-92ff-4705-bc25-d9db24ee894f', '유전자 변형 (GMO)', NULL, 'Biến đổi gen (GMO)', 'yujeonja byeonhyeong', '유전자 변형 식품의 안전성에 대한 논쟁은 계속되고 있다.', 'Cuộc tranh cãi về sự an toàn của thực phẩm biến đổi gen vẫn đang tiếp diễn.', '9db553aa-cd9b-490b-b3a7-61cc819dfab5', '2026-01-05T17:07:58.358Z', '2026-01-05T17:07:58.358Z'),
  ('318a6837-2feb-49eb-b566-c1f55e7274a9', '복제', NULL, 'Nhân bản', 'bokje', '동물 복제는 심각한 윤리적 문제를 야기하며 사회적 합의가 필요하다.', 'Nhân bản động vật gây ra những vấn đề đạo đức nghiêm trọng và cần có sự đồng thuận của xã hội.', '9db553aa-cd9b-490b-b3a7-61cc819dfab5', '2026-01-05T17:07:58.358Z', '2026-01-05T17:07:58.358Z'),
  ('74f44205-16d8-4139-a22b-2b6b8128507b', 'DNA (디옥시리보핵산)', NULL, 'DNA (Axit deoxyribonucleic)', 'di-ok-si-ri-bo-haek-san', 'DNA 분석을 통해 개인의 신원을 확인하거나 유전 질환을 예측할 수 있다.', 'Thông qua phân tích DNA, có thể xác nhận danh tính cá nhân hoặc dự đoán các bệnh di truyền.', '9db553aa-cd9b-490b-b3a7-61cc819dfab5', '2026-01-05T17:07:58.358Z', '2026-01-05T17:07:58.358Z'),
