INSERT INTO "QuizQuestion" (id, question, "questionTranslation", options, "correctAnswer", explanation, "VocabularyId", "createdAt", "updatedAt") VALUES ('39e276d3-9b4d-48b3-9019-1be0aa1f9720', '입법권, 사법권, 행정권이 서로 독립적으로 운영되는 민주주의 원칙은?', 'Nguyên tắc dân chủ mà quyền lập pháp, tư pháp và hành pháp vận hành độc lập với nhau là gì?', ARRAY['일당제', '삼권분립', '중앙집권', '지방분권']::text[], '삼권분립', '삼권분립 là ''tam quyền phân lập'', 한국 헌법 제1조에 명시된 대의민주주의의 기본 원리로, 국회(입법), 법원(사법), 정부(행정)가 상호 견제와 균형을 이룹니다.', 'c559c5be-c6f5-4add-aa9f-e6942fba544b', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('838291a5-f1cd-4ce1-8993-bae02cd70f85', '대한민국 헌법의 기본 구조를 이루는 가장 중요한 원리는?', 'Nguyên tắc quan trọng nhất tạo nên cấu trúc cơ bản của Hiến pháp Đại Hàn Dân Quốc là gì?', ARRAY['왕정주의', '삼권분립', '군주제', '일인독재']::text[], '삼권분립', '삼권분립은 한국 민주주의의 핵심으로, 1987년 헌법 개정 이후 강화되어 현재의 정치 체제를 유지하고 있습니다.', 'c559c5be-c6f5-4add-aa9f-e6942fba544b', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('cf17ebe6-bf04-4ce2-8f27-ced74f21ad66', '대한민국의 입법 기관으로 국민의 대표로 구성된 기관은?', 'Cơ quan lập pháp của Đại Hàn Dân Quốc, được cấu thành bởi đại diện của nhân dân là gì?', ARRAY['대법원', '국회', '청와대', '감사원']::text[], '국회', '국회 là ''Quốc hội'', 4년 임기의 국회의원 300명으로 구성되며, 여의도에 위치한 국회의사당에서 활동합니다. 단원제 체제를 운영합니다.', '87d2f654-e7b9-4d77-8dd4-416678720b98', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('2b2065b8-13ef-4a6a-b0dc-71efffc0cdd5', '한국에서 법률을 만들고 예산을 심의하는 최고 입법부는?', 'Cơ quan lập pháp tối cao tại Hàn Quốc tạo ra luật pháp và xem xét ngân sách là gì?', ARRAY['행정부', '국회', '사법부', '헌법재판소']::text[], '국회', '국회의 주요 기능으로는 법률 제정, 예산 심의, 국정 감사, 국무총리 및 대법원장 임명 동의 등이 있습니다.', '87d2f654-e7b9-4d77-8dd4-416678720b98', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('59f405a5-62cb-482d-9bd2-d03644fdb6f2', '대통령을 선출하기 위해 실시하는 전국적인 선거를 뭐라고 하나요?', 'Cuộc bầu cử toàn quốc được tiến hành để bầu tổng thống được gọi là gì?', ARRAY['총선', '대선', '지방선거', '재보궐선거']::text[], '대선', '대선은 ''bầu cử tổng thống'', 5년마다 실시되며, 만 19세 이상 모든 국민이 참여하는 직접 선거입니다. 최근에는 3월에 실시되는 것이 관례입니다.', '857a24bd-53c2-41a7-918f-8daa9d803851', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('ec23a635-f902-42ef-86d4-61f23b43c38e', '한국에서 ''선거의 계절''이라고 불리며 가장 관심을 받는 선거는?', 'Cuộc bầu cử được gọi là ''mùa bầu cử'' và nhận được sự quan tâm nhiều nhất tại Hàn Quốc là gì?', ARRAY['대선', '총선', '지방선거', '교육감선거']::text[], '대선', '대선은 국가의 최고 지도자를 뽑는 선거로, 한국 사회 전체에 큰 영향을 미치며, 텔레비전 토론과 각종 여론 조사가 활발하게 진행됩니다.', '857a24bd-53c2-41a7-918f-8daa9d803851', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('130e96c0-4ca6-4216-a9d4-663767602407', '유사한 정치적 이념과 목표를 가진 사람들이 모여 권력을 획득하고 정책을 실현하기 위해 만든 조직은?', 'Tổ chức được thành lập bởi những người có hệ tư tưởng và mục tiêu chính trị tương tự để giành quyền lực và thực hiện chính sách là gì?', ARRAY['시민단체', '정당', '재단', '협회']::text[], '정당', '정당은 ''chính đảng'', 한국의 주요 정당으로 더불어민주당, 국민의힘, 정의당 등이 있으며, 정당법에 따라 등록된 정당만 선거에 참여할 수 있습니다.', 'efd8b9cb-378c-4922-a756-261c0b126031', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('71b03afb-4733-4701-8d21-63cff0df3d08', '국회에서 심의되고 표결을 거쳐 법률로 제정되기 위한 안건을 뭐라고 하나요?', 'Vấn đề được Quốc hội xem xét và thông qua biểu quyết để ban hành thành luật được gọi là gì?', ARRAY['정책', '법안', '규정', '지침']::text[], '법안', '법안 là ''dự luật'', 의원 발의와 정부 제출 법안으로 구분되며, 상임위원회 심사 → 본회의 심의 → 표결 과정을 거쳐 법률로 제정됩니다.', '99308612-0c9f-421f-b119-92764dfe3771', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('a4f316dc-b81a-47c2-a4b1-f69b8c3678e0', '공통의 안보나 외교 목적을 위해 국가 간에 체결된 협력 관계를 뭐라고 하나요?', 'Mối quan hệ hợp tác giữa các quốc gia được thiết lập vì mục đích an ninh hoặc ngoại giao chung được gọi là gì?', ARRAY['경쟁 관계', '동맹', '중립 관계', '적대 관계']::text[], '동맹', '동맹 là ''đồng minh'', 한국의 가장 중요한 동맹은 한미동맹으로, 1953년 상호방위조약에 기초하여 북한 위협에 대응하고 지역 안정을 유지합니다.', '27e33ffe-1546-43f8-831d-2298b5b47a89', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('28bcaf2d-5a40-4471-b1db-212b3c5a3c73', '한국 외교안보 정책의 핵심이자 ''한반도의 평화와 번영의 축''으로 불리는 것은?', 'Trụ cột của chính sách đối ngoại và an ninh Hàn Quốc, được gọi là ''trục hòa bình và thịnh vượng của bán đảo Triều Tiên'' là gì?', ARRAY['한중 관계', '한미동맹', '한일 관계', '한러 관계']::text[], '한미동맹', '한미동맹은 한국 외교의 핵심으로, 군사적 협력, 경제적 파트너십, 가치 동맹으로 발전해 왔으며, 주한미군이 상시 주둔하고 있습니다.', '27e33ffe-1546-43f8-831d-2298b5b47a89', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('837c0b0b-14b5-4870-9099-6c4441c8d059', '국가 간의 이해관계 충돌로 발생하는 갈등을 뭐라고 하나요?', 'Xung đột xảy ra do mâu thuẫn lợi ích giữa các quốc gia được gọi là gì?', ARRAY['국내 갈등', '국제 분쟁', '지역 문제', '문화 충돌']::text[], '국제 분쟁', '국제 분쟁은 ''tranh chấp quốc tế'', 한국과 관련된 대표적 분쟁으로 독도 문제, 북핵 문제, 동중국해 및 남중국해 영유권 분쟁 등이 있습니다.', '11325908-f6f8-44a5-b799-e7eef3572660', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('c06152b4-670d-43bc-a60d-54ee0780dc5c', '국가 간에 체결되어 국제법상 구속력을 가지는 공식적인 협정을 뭐라고 하나요?', 'Thỏa thuận chính thức được ký kết giữa các quốc gia và có hiệu lực ràng buộc theo luật pháp quốc tế được gọi là gì?', ARRAY['각서', '조약', '성명', '선언']::text[], '조약', '조약 là ''hiệp ước'', 한국은 180개 이상의 국가와 다양한 조약을 체결했으며, 국회의 비준 동의를 받아 효력을 발생합니다.', '843a555a-56a8-42a4-baf7-713c72db07b8', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('70487724-09e2-41dc-b0f3-3aebc79436a9', '국가 원수나 정부 수반들이 직접 모여 중요한 국제 문제를 논의하는 회의를 뭐라고 하나요?', 'Cuộc họp mà nguyên thủ quốc gia hoặc người đứng đầu chính phủ trực tiếp tập hợp để thảo luận các vấn đề quốc tế quan trọng được gọi là gì?', ARRAY['장관회의', '정상회담', '실무협의', '전문가회의']::text[], '정상회담', '정상회담 là ''hội nghị thượng đỉnh'', G20, ASEAN+3, 한미정상회담 등 한국은 다양한 다자 및 양자 정상회담에 적극 참여하고 있습니다.', 'c02c7f97-41bd-4a0c-a6da-5d026fef0f73', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('2eb21be2-2744-4316-9c54-e5a8fe0cbd4b', '문재인 전 대통령과 김정은 위원장의 2018년 만남은 어떤 종류의 외교 행사였나요?', 'Cuộc gặp năm 2018 giữa cựu Tổng thống Moon Jae-in và Chủ tịch Kim Jong-un là loại sự kiện ngoại giao nào?', ARRAY['장관회담', '정상회담', '실무회담', '민간교류']::text[], '정상회담', '2018년 판문점에서 열린 문재인-김정은 정상회담은 남북 정상회담으로, 평화와 번영의 새로운 한반도 구상을 논의한 역사적 회담이었습니다.', 'c02c7f97-41bd-4a0c-a6da-5d026fef0f73', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('e7487fcf-056e-49db-ba46-0303dc79de02', '국제 관계에서 상대방이 제공한 혜택에 상응하는 대가를 지불하는 원칙을 뭐라고 하나요?', 'Nguyên tắc trong quan hệ quốc tế về việc trả giá tương xứng với lợi ích mà đối phương cung cấp được gọi là gì?', ARRAY['일방주의', '상호주의', '중립주의', '보호주의']::text[], '상호주의', '상호주의 là ''chủ nghĩa tương hỗ'', 한국 외교의 기본 원칙으로, 관세 인하, 비자 면제, 투자 보호 등 다양한 분야에서 상호 호혜적 관계를 추구합니다.', '537b9c02-defe-4049-82e9-630d1552581b', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('3d2a57f8-65b2-4415-adc7-284ad8b5c22e', '법을 해석하고 적용하며 재판을 통해 분쟁을 해결하는 국가 기관들의 체계를 뭐라고 하나요?', 'Hệ thống các cơ quan nhà nước giải thích và áp dụng pháp luật, giải quyết tranh chấp thông qua xét xử được gọi là gì?', ARRAY['행정 제도', '입법 제도', '사법 제도', '선거 제도']::text[], '사법 제도', '사법 제도 (judicial system) bao gồm các tòa án và thẩm phán, có chức năng xét xử các vụ án hình sự, dân sự và giải thích hiến pháp, pháp luật.', 'd05051e5-c2b5-46df-aaec-85b03f1f5313', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('27549609-cc62-4ed3-a747-0a7e8ddf32f3', '"______의 독립"은 재판관이 외부 압력 없이 공정하게 재판할 수 있어야 한다는 원칙입니다.', '"Độc lập của ______" là nguyên tắc đảm bảo thẩm phán có thể xét xử công bằng mà không có áp lực từ bên ngoài.', ARRAY['사법', '행정', '입법', '정당']::text[], '사법', '사법부 독립 (사법의 독립) là nguyên tắc cơ bản trong nhà nước pháp quyền, đảm bảo tòa án hoạt động một cách khách quan, không bị chi phối bởi các nhánh quyền lực khác (hành pháp, lập pháp).', 'd05051e5-c2b5-46df-aaec-85b03f1f5313', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('27ae724a-f8a6-4969-9ac3-ad33e74c3b19', '민사 소송에서 재판을 시작하기 위해 소장을 제출하는 당사자를 뭐라고 하나요?', 'Trong vụ kiện dân sự, bên đệ đơn khởi kiện, nộp đơn kiện lên tòa án được gọi là gì?', ARRAY['피고', '원고', '증인', '변호사']::text[], '원고', '원고 (plaintiff) là người khởi kiện, người nộp đơn kiện (소장) lên tòa án để yêu cầu giải quyết một tranh chấp hoặc đòi bồi thường.', '2e2cf6cb-28fa-4a0b-b519-cbea7457bc79', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('a17609d8-3945-431f-ada0-6526c0268ae8', '민사 소송에서 고소당하거나 소송을 당한 상대방을 뭐라고 하나요?', 'Trong vụ kiện dân sự, người bị kiện, bên bị đưa ra tòa được gọi là gì?', ARRAY['원고', '피고', '검사', '판사']::text[], '피고', '피고 (defendant) là bị đơn, người bị nguyên đơn (원고) kiện và phải ra tòa để trả lời các cáo buộc.', '2e2cf6cb-28fa-4a0b-b519-cbea7457bc79', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('eb0c6707-dc0b-4936-9a00-55112148d111', '법원에 의뢰하여 분쟁을 해결하는 절차나 그런 사건 자체를 뭐라고 하나요?', 'Thủ tục giải quyết tranh chấp thông qua tòa án, hoặc chính vụ việc đó, được gọi là gì?', ARRAY['협상', '중재', '소송', '화해']::text[], '소송', '소송 (lawsuit, litigation) là quá trình pháp lý chính thức mà một bên (원고) kiện một bên khác (피고) trước tòa án để đòi quyền lợi hoặc khắc phục thiệt hại.', '838c9997-e852-410a-a313-206ec3c79c98', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('0c302ac3-2f58-40dc-ac3a-8c1515f25320', '"______을/를 제기하다"는 법원에 공식적으로 사건을 등록하는 행위입니다.', '"______을/를 제기하다" là hành động đăng ký một vụ án chính thức tại tòa án.', ARRAY['계약', '소송', '청원', '신고']::text[], '소송', '''소송을 제기하다'' có nghĩa là khởi kiện, bắt đầu một vụ kiện bằng cách nộp đơn kiện (소장) lên tòa án có thẩm quyền.', '838c9997-e852-410a-a313-206ec3c79c98', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('093b7210-0084-4924-9526-d411391c4075', '1심 법원의 판결에 불만족하는 당사자가 더 높은 법원에 다시 재판을 요구하는 것을 뭐라고 하나요?', 'Việc một bên không hài lòng với phán quyết của tòa án sơ thẩm, yêu cầu một tòa án cấp cao hơn xem xét lại được gọi là gì?', ARRAY['기소하다', '항소하다', '판결하다', '선고하다']::text[], '항소하다', '항소하다 (to appeal) là hành động của nguyên đơn hoặc bị đơn yêu cầu tòa án cấp trên (항소법원) xem xét lại phán quyết của tòa án cấp dưới vì cho rằng phán quyết đó có sai sót về mặt pháp lý hoặc thủ tục.', 'e8126ab6-0716-4682-8153-4bb2eea97e9b', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('d3039bf2-77c3-4142-81a2-312a2cf5246f', '대한민국 법원 체계에서 1심 다음 단계는 보통 ______심입니다.', 'Trong hệ thống tòa án Hàn Quốc, cấp tiếp theo sau sơ thẩm thường là tòa án ______.', ARRAY['1', '2', '3', '4']::text[], '2', 'Hệ thống tòa án Hàn Quốc thường có ba cấp: 1심 (tòa sơ thẩm), 2심 (항소심 - tòa phúc thẩm), và 3심 (상고심 - Tòa án Tối cao). 항소는 lên 2심.', 'e8126ab6-0716-4682-8153-4bb2eea97e9b', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('d6d7d27d-a02e-4319-a272-61ab6291cc42', '법률이나 행정 행위가 국가의 최고 법인 헌법에 위배되는 것을 뭐라고 하나요?', 'Việc một đạo luật hoặc hành vi hành chính vi phạm hiến pháp - luật tối cao của quốc gia - được gọi là gì?', ARRAY['위법', '위헌', '부당', '무효']::text[], '위헌', '위헌 (unconstitutional) có nghĩa là trái với hiến pháp. Chỉ có cơ quan có thẩm quyền (như 헌법재판소 ở Hàn Quốc) mới có quyền tuyên bố một đạo luật là 위헌.', '424188d7-d570-4663-8c79-f337acb1ed4f', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('88cce8b4-ae5a-4e21-bb94-4294263c9c24', '______ 결정이 나면 해당 법률은 효력을 잃게 됩니다.', 'Nếu có quyết định ______, đạo luật đó sẽ mất hiệu lực.', ARRAY['합헌', '위헌', '적법', '유효']::text[], '위헌', 'Khi Tòa án Hiến pháp (헌법재판소) ra phán quyết một đạo luật là 위헌 (vi hiến), đạo luật đó hoặc các điều khoản vi hiến sẽ bị vô hiệu hóa và không còn được áp dụng.', '424188d7-d570-4663-8c79-f337acb1ed4f', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('d7140534-8ae8-4314-a3ae-43ff8fcb435c', '국민의 생명, 신체, 재산 등 중요한 법익을 침해하는 범죄 행위와 그에 대한 형벌을 정한 법률은?', 'Đạo luật quy định về các hành vi phạm tội xâm phạm các lợi ích pháp lý quan trọng của công dân như tính mạng, thân thể, tài sản và hình phạt đối với chúng là gì?', ARRAY['민법', '헌법', '형법', '상법']::text[], '형법', '형법 có nghĩa là ''Luật hình sự'' (Criminal law). Đây là một ngành luật cơ bản quy định thế nào là tội phạm (범죄) và các hình phạt (형벌) tương ứng như ''징역'' (tù), ''벌금'' (tiền phạt). Mục đích của 형법 là bảo vệ trật tự xã hội, ngăn ngừa tội phạm và cải tạo phạm nhân.', '02df74f4-7931-4d4f-bb3c-62e4b59b637c', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('39ee92b6-1c89-4297-9eb1-6b4de3aa0eae', '재판에서 사건의 진실을 밝히거나 특정 사실을 입증하기 위해 제출되는 물건이나 진술 등을 뭐라고 하나요?', 'Những vật phẩm, lời khai... được xuất trình tại phiên tòa để làm sáng tỏ sự thật vụ án hoặc chứng minh một sự việc cụ thể được gọi là gì?', ARRAY['증인', '증거', '진술서', '판결문']::text[], '증거', '증거 có nghĩa là ''bằng chứng'' (Evidence). Trong tố tụng hình sự, ''증거'' là yếu tố then chốt để xác định ''유죄'' hay ''무죄''. Các loại 증거 bao gồm ''물적 증거'' (bằng chứng vật chất), ''증인 진술'' (lời khai nhân chứng), ''감정 결과'' (kết quả giám định). ''결정적인 증거'' (bằng chứng mang tính quyết định) có sức nặng rất lớn.', 'ac9f89e4-1490-4baa-9557-77a7e76f0c6f', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('32700d49-ab0c-4f6d-9877-3d606e59ee6a', '법정에서 피고인의 죄가 입증되지 않았다고 판단하거나, 반대로 죄가 입증되었다고 판단하는 것을 각각 뭐라고 하나요?', 'Việc tại tòa án, nhận định rằng tội của bị cáo chưa được chứng minh, hoặc ngược lại, nhận định tội đã được chứng minh, lần lượt được gọi là gì?', ARRAY['유죄 / 무죄', '무죄 / 유죄', '기소 / 불기소', '선고 / 판결']::text[], '무죄 / 유죄', '무죄 có nghĩa là ''vô tội'' (Not guilty/Acquittal). 유죄 có nghĩa là ''có tội'' (Guilty/Conviction). Đây là hai kết quả cơ bản của một phiên tòa hình sự. Nguyên tắc ''의심스러울 때는 피고인의 이익으로'' (in dubio pro reo - khi còn nghi ngờ, phải nghiêng về bị cáo) nhằm bảo vệ người vô tội, vì vậy để tuyên ''유죄'' cần có ''증거'' vững chắc.', 'b84a20f7-3d05-4d0c-b098-8560f583ed60', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('4c549662-2b4e-4bd5-bcae-d941bce6f606', '법원이 범죄자에게 선고하는, 교도소에 수용되어 노역을 하는 자유형의 일종은?', 'Một loại hình phạt tước tự do do tòa án tuyên đối với người phạm tội, theo đó phải bị giam giữ trong trại giam và lao động là gì?', ARRAY['벌금', '구류', '징역', '사회봉사']::text[], '징역', '징역 có nghĩa là ''phạt tù có lao động'' hoặc ''tù giam'' (Imprisonment with labor). Đây là hình phạt chính (주형) phổ biến nhất trong ''형법'' Hàn Quốc đối với các tội nghiêm trọng. Thời hạn ''징역'' được ''선고'' (tuyên án) bởi thẩm phán, ví dụ ''징역 10년'' (10 năm tù). Có cả ''징역형'' (tù có lao động) và ''금고형'' (tù không lao động).', '3b10ff53-78c6-45fc-9d7e-bfc249ec2d99', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('752e0110-3932-4dd3-bcfc-683d43010dc0', '범죄 행위가 있은 후 일정 기간이 지나면 국가의 형사소추권이 소멸되는 제도를 뭐라고 하나요?', 'Chế định theo đó quyền truy tố hình sự của nhà nước sẽ hết hiệu lực sau một khoảng thời gian nhất định kể từ khi hành vi phạm tội xảy ra được gọi là gì?', ARRAY['재심 기간', '공소시효', '상소 기간', '집행 유예']::text[], '공소시효', '공소시효 có nghĩa là ''thời hiệu truy cứu trách nhiệm hình sự'' hoặc ''thời hiệu khởi tố'' (Statute of limitations). Mỗi tội danh có một thời hạn 공소시효 khác nhau (từ 5 năm đến 25 năm hoặc không có thời hiệu đối với tội nghiêm trọng). Sau khi thời hạn này ''지나다'' (trôi qua), cơ quan công tố (검찰) không thể ''기소'' (khởi tố) và tòa án không thể ''처벌'' (trừng phạt) người phạm tội được nữa, nhằm ổn định các quan hệ pháp luật.', 'fb1974b4-e625-472c-979f-ebdbfec950e7', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('694cce4b-0b68-4960-9ec3-ec753bbc86aa', '개인이 자신의 재산을 소유하고, 사용하고, 처분할 수 있는 권리를 통틀어 무엇이라고 하나요?', 'Quyền của cá nhân được sở hữu, sử dụng và định đoạt tài sản của mình được gọi chung là gì?', ARRAY['인권', '재산권', '선거권', '노동권']::text[], '재산권', '재산권 có nghĩa là ''quyền tài sản''. Đây là một trong những 권리 (quyền) cơ bản được 헌법 (Hiến pháp) 보장하다 (bảo đảm). Quyền này bao gồm 소유권 (quyền sở hữu), 사용권 (quyền sử dụng) và 처분권 (quyền định đoạt).', 'cf8e127d-c990-407d-a68b-1da3a58f56ba', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('63719907-119c-4a0e-a36a-85114da8b64e', '다른 사람의 불법 행위나 계약 위반 등으로 입은 재산적·정신적 손실을 금전 등으로 메워주는 것을 뭐라고 하나요?', 'Việc bù đắp bằng tiền hoặc các hình thức khác cho những thiệt hại về tài sản hoặc tinh thần phát sinh từ hành vi bất hợp pháp hoặc vi phạm hợp đồng của người khác được gọi là gì?', ARRAY['보험 가입', '손해 배상', '법정 소송', '화해 협상']::text[], '손해 배상', '손해 배상 có nghĩa là ''bồi thường thiệt hại''. Người gây ra 손해 (thiệt hại) phải 배상하다 (bồi thường) cho 피해자 (nạn nhân). 손해 배상 책임 (trách nhiệm bồi thường) có thể phát sinh từ 불법 행위 (hành vi bất hợp pháp) hoặc 계약 위반 (vi phạm hợp đồng).', '3c177592-03dd-4847-a118-c2e18f71b024', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('c05ddeff-fa10-4a4f-b063-3948487b6ae5', '사망한 사람의 재산과 권리·의무가 법정 순위에 따라 생존한 사람에게 이전되는 것을 무엇이라고 하나요?', 'Việc tài sản, quyền và nghĩa vụ của người đã chết được chuyển giao cho người còn sống theo một trật tự pháp định được gọi là gì?', ARRAY['증여', '상속', '매매', '투자']::text[], '상속', '상속 có nghĩa là ''sự thừa kế''. Người để lại tài sản là 상속인 (người để lại thừa kế), người nhận tài sản là 상속인 (người thừa kế). 상속인은 상속세를 내야 할 수도 있다 (người thừa kế có thể phải nộp thuế thừa kế).', 'a9489915-fa87-4ebc-92c3-473b0355093a', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('08803c4d-9f37-4a81-9311-0382e79b2806', '계약서에 명시된 내용을 지키지 않거나 이행하지 않는 행위를 무엇이라고 하나요?', 'Hành động không tuân thủ hoặc không thực hiện các điều khoản đã được ghi trong hợp đồng được gọi là gì?', ARRAY['계약 체결', '계약 위반', '계약 갱신', '계약 해지']::text[], '계약 위반', '계약 위반 có nghĩa là ''vi phạm hợp đồng'', hay ''bội ước''. Khi 발생하면, 피해 당사자 (bên bị thiệt hại)는 손해 배상을 청구할 수 있고, 위반한 당사자는 위약금 (tiền phạt vi phạm)을 지불해야 할 수 있습니다. 이는 법적 책임을 야기한다 (dẫn đến trách nhiệm pháp lý).', 'fa5024dd-794b-446f-859b-c2c2c5343b52', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('8bdc43b6-4247-4dfa-a356-a73747aa6840', '특정 재산에 대해 소유하고, 사용하고, 수익하고, 처분할 수 있는 포괄적이고 배타적인 권리를 무엇이라고 하나요?', 'Quyền toàn diện và độc quyền đối với một tài sản cụ thể, bao gồm quyền sở hữu, sử dụng, hưởng lợi và định đoạt, được gọi là gì?', ARRAY['사용권', '소유권', '전세권', '저당권']::text[], '소유권', '소유권 có nghĩa là ''quyền sở hữu''. Đây là 권리 중 가장 완전한 권리로, 등기 (đăng ký)를 통해 공시 (công bố)하고 보호받는다. 예를 들어, 부동산의 소유권은 등기부등본 (bản sao sổ đăng ký)으로 확인한다.', '4c660f40-17c9-4cf7-b7bb-2e5fb127a517', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('5367308d-a04e-4f5e-850b-4455690cc049', '한 여성이 평생 낳는 평균 자녀 수(합계출산율)가 2.1명 미만으로 인구를 유지하기 힘든 현상을 뭐라고 하나요?', 'Hiện tượng số con trung bình một phụ nữ sinh trong đời (tổng tỷ suất sinh) dưới 2.1, khó duy trì dân số được gọi là gì?', ARRAY['다산', '인구 폭발', '저출산', '고출산']::text[], '저출산', '저출산 (low birth rate) là hiện tượng tỷ lệ sinh giảm xuống dưới mức thay thế (2.1 con/phụ nữ), dẫn đến dân số già đi nhanh chóng và thiếu hụt lực lượng lao động trong tương lai.', 'f14efdb0-2780-47ad-841e-d7d0fcf87df8', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('65e8fddd-b126-4ca7-957b-9e10d5d03ebc', '______ 현상이 지속되면 미래에 경제 성장에 부정적인 영향을 미칠 수 있어요.', 'Nếu hiện tượng ______ tiếp diễn, có thể ảnh hưởng tiêu cực đến tăng trưởng kinh tế trong tương lai.', ARRAY['고령화', '저출산', '양극화', '모두 정답']::text[], '모두 정답', '저출산 (sinh thấp) dẫn trực tiếp đến 고령화 사회 (dân số già), và cả hai cùng làm trầm trọng thêm 소득 양극화 (phân cực thu nhập). Cả ba đều là những thách thức nghiêm trọng đối với kinh tế và xã hội.', 'f14efdb0-2780-47ad-841e-d7d0fcf87df8', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('20d02036-cb6c-4eda-ac07-57cb5f19303f', '전체 인구에서 65세 이상 노인 인구의 비율이 높아지는 사회를 뭐라고 하나요?', 'Xã hội mà tỷ lệ dân số từ 65 tuổi trở lên trong tổng dân số ngày càng tăng được gọi là gì?', ARRAY['청년 사회', '고령화 사회', '다문화 사회', '인구 과잉 사회']::text[], '고령화 사회', '고령화 사회 (aging society) là giai đoạn khi tỷ lệ dân số từ 65 tuổi trở lên vượt quá 7% tổng dân số. Hàn Quốc đã trở thành ''초고령 사회'' (siêu già hóa) khi tỷ lệ này vượt 20%.', '910428bc-3610-49c0-b0d6-ebb69f33db3d', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('61b33c5a-3a68-4d52-84ea-1a1d6a880c7c', '______가 진행되면 연금과 의료비 부담이 커지고 경제 활력이 떨어질 수 있어요.', 'Khi ______ diễn ra, gánh nặng lương hưu và chi phí y tế tăng lên, sức sống kinh tế có thể giảm sút.', ARRAY['고령화', '국제화', '도시화', '디지털화']::text[], '고령화', '고령화 (sự già hóa dân số) gây áp lực lớn lên hệ thống phúc lợi xã hội (lương hưu, y tế) và làm giảm quy mô lực lượng lao động, ảnh hưởng đến tăng trưởng kinh tế tiềm năng.', '910428bc-3610-49c0-b0d6-ebb69f33db3d', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('aa88c4cf-c571-4747-956e-b31453952c60', '소득이나 부, 기회 등이 한쪽으로는 매우 높고 다른 쪽으로는 매우 낮아져 중간층이 사라지는 현상을 뭐라고 하나요?', 'Hiện tượng thu nhập, của cải, cơ hội, v.v. trở nên rất cao ở một bên và rất thấp ở bên kia, khiến tầng lớp trung lưu biến mất được gọi là gì?', ARRAY['평준화', '양극화', '통합화', '균형화']::text[], '양극화', '양극화 (polarization) là sự phân cực, phân hóa sâu sắc trong xã hội, thường được nói đến dưới dạng 소득 양극화 (phân cực thu nhập) hay 부의 양극화 (phân cực của cải).', '8a51c229-8238-4074-99f5-974542950c8f', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('ac91bebd-45b1-4be1-883f-ab70060922a6', '______가 심해지면 사회 안정과 통합에 악영향을 미칩니다.', 'Nếu ______ trở nên nghiêm trọng, sẽ ảnh hưởng xấu đến sự ổn định và đoàn kết xã hội.', ARRAY['문화 다양성', '양극화', '국제 협력', '기술 발전']::text[], '양극화', 'Sự phân cực nghiêm trọng (심한 양극화) làm tăng bất bình đẳng, làm xói mòn niềm tin xã hội, và có thể dẫn đến xung đột giai cấp hoặc bất ổn xã hội.', '8a51c229-8238-4074-99f5-974542950c8f', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('0f7313a5-bc7f-46f8-bda4-3aed4c857b9f', '성별에 근거한 편견이나 차별 대우를 뭐라고 하나요?', 'Sự thành kiến hoặc đối xử khác biệt dựa trên giới tính được gọi là gì?', ARRAY['인종차별', '성차별', '연령차별', '장애차별']::text[], '성차별', '성차별 (sexism, gender discrimination) là sự phân biệt đối xử dựa trên giới tính, thường chỉ việc phụ nữ bị đối xử bất công so với nam giới trong các lĩnh vực như việc làm, lương, thăng tiến.', 'cca85d1c-c558-4161-8b69-e655e9976b7c', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('99652427-ebba-4260-9317-284ab13a452d', '______을/를 금지하는 법과 평등을 촉진하는 정책이 필요합니다.', 'Cần có luật pháp cấm ______ và các chính sách thúc đẩy bình đẳng.', ARRAY['성차별', '국제 무역', '기술 혁신', '문화 교류']::text[], '성차별', 'Nhiều quốc gia đã ban hành luật như 남녀고용평등법 (Luật bình đẳng việc làm nam nữ) để cấm 성차별 tại nơi làm việc và thúc đẩy cơ hội bình đẳng.', 'cca85d1c-c558-4161-8b69-e655e9976b7c', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('34fc9c94-5ba9-49ec-9c0d-c97855c8123e', '서로 다른 사회 집단이나 계층 사이에 발생하는 이해관계나 가치관의 충돌을 뭐라고 하나요?', 'Sự xung đột về lợi ích hoặc giá trị giữa các nhóm hoặc tầng lớp xã hội khác nhau được gọi là gì?', ARRAY['사회적 통합', '사회적 갈등', '사회적 합의', '사회적 안정']::text[], '사회적 갈등', '사회적 갈등 (social conflict) là tình trạng xung đột, bất đồng giữa các nhóm trong xã hội, có thể xuất phát từ sự khác biệt về kinh tế, chính trị, tư tưởng, văn hóa hoặc thế hệ.', '6cd46e2b-f713-4297-9aff-701785fa7acb', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('089bd58f-360c-4a04-b28b-daf274a3adca', '노사 갈등이나 지역 감정 등은 ______의 예입니다.', 'Xung đột giữa chủ và lao động (노사 갈등) hay tình cảm địa phương (지역 감정) là ví dụ của ______.', ARRAY['국제 협력', '사회적 갈등', '가족 사랑', '직장 우정']::text[], '사회적 갈등', '노사 갈등 (xung đột lao động) và 지역 감정 (tâm lý địa phương, phân biệt vùng miền) là hai dạng điển hình của 사회적 갈등 ở Hàn Quốc và nhiều nơi khác.', '6cd46e2b-f713-4297-9aff-701785fa7acb', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('d199363f-7c40-4af9-9e43-7fdd99fdff3b', '실업, 질병, 노령 등 사회적 위험에 직면한 사람들을 보호하기 위한 제도적 장치를 총칭하는 말은?', 'Từ bao quát chỉ các thiết chế nhằm bảo vệ những người đối mặt với các rủi ro xã hội như thất nghiệp, bệnh tật, tuổi già là gì?', ARRAY['경제 성장', '사회 안전망', '시장 경쟁', '개인 책임']::text[], '사회 안전망', '사회 안전망 có nghĩa là ''mạng lưới an sinh xã hội'' (Social safety net). Đây là hệ thống các chính sách và chương trình (국민연금, 건강보험, 실업급여 등) do nhà nước hoặc xã hội thiết lập để ''맞춤형 지원'' (hỗ trợ theo đối tượng) cho người dân khi họ gặp khó khăn, ngăn chặn họ rơi xuống dưới mức nghèo khó.', '1be3daec-b751-490e-ba66-e66b9a02c914', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('1a15c6fc-b8ce-484a-b221-e54f2705702f', '국민의 노후 생활을 보장하기 위해 가입 기간 동안 보험료를 납부하고 노령기에 연금을 지급하는 공적 연금 제도는?', 'Chế độ lương hưu công do nhà nước quản lý, nơi người tham gia đóng phí bảo hiểm trong thời gian làm việc và nhận lương hưu khi về già để đảm bảo cuộc sống tuổi già là gì?', ARRAY['개인 연금', '국민연금', '퇴직금', '생활보조금']::text[], '국민연금', '국민연금 có nghĩa là ''lương hưu quốc dân'' (National Pension). Đây là trụ cột chính trong ''사회 안전망'' của Hàn Quốc dành cho tuổi già. Hầu hết người lao động và người sử dụng lao động đều phải tham gia và đóng ''국민연금 보험료''. Sau khi đến tuổi hưởng lương hưu (''연금 수령 연령''), người tham gia sẽ nhận được ''노령 연금'' (lương hưu tuổi già) định kỳ.', 'b9c0027b-f131-49a8-afaa-546e29c3e641', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('1686d6fb-401e-4c33-8f4c-5e242ac18b69', '국가가 소득 조사를 통해 선정한 빈곤층에게 최저 생활을 보장하기 위해 현금이나 현물로 지원하는 제도는?', 'Chế độ do nhà nước hỗ trợ bằng tiền mặt hoặc hiện vật cho người nghèo được lựa chọn thông qua điều tra thu nhập để đảm bảo mức sống tối thiểu là gì?', ARRAY['공적 개발 원조', '공공 부조', '사회 보험', '자선 기부']::text[], '공공 부조', '공공 부조 có nghĩa là ''trợ cấp công'' (Public assistance/Social assistance). Đây là chương trình phúc lợi có tính ''선별적'' (lựa chọn đối tượng), dành riêng cho những người có thu nhập dưới mức nghèo khó (저소득층). Hình thức hỗ trợ chính là ''기초생활수급제도'' (Chế độ hưởng trợ cấp sinh hoạt cơ bản), cung cấp tiền mặt cho các nhu cầu cơ bản như ăn, ở, y tế.', 'd4ba8266-59a9-4059-a8c5-6de61573a911', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('aaab8364-840e-4bb0-8b1e-8a72739265ec', '다양한 사정으로 인해 정부의 사회 복지 제도가 정상적으로 지원되지 못하는 사람이나 집단이 존재하는 영역을 뭐라고 하나요?', 'Khu vực tồn tại những cá nhân hoặc nhóm người không nhận được sự hỗ trợ bình thường từ hệ thống phúc lợi xã hội của chính phủ do nhiều hoàn cảnh khác nhau được gọi là gì?', ARRAY['복지 선진국', '복지 사각지대', '복지 중복지원', '복지 혜택']::text[], '복지 사각지대', '복지 사각지대 có nghĩa là ''vùng mù phúc lợi'' hay ''khoảng trống phúc lợi'' (Welfare blind spot). Những người thuộc ''복지 사각지대'' thường có thu nhập cao hơn một chút so với tiêu chuẩn được nhận ''공공 부조'', nhưng lại quá thấp để có cuộc sống ổn định. Họ cũng có thể là những người không biết đến các chương trình hỗ trợ hoặc gặp khó khăn trong thủ tục. ''해소'' (giải quyết) vấn đề này là một thách thức lớn.', 'c30019b6-540d-48c2-8287-cb58af1d5fae', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('8e168c0a-19f0-4c1d-babb-97029951b931', '소득이나 재산에 관계없이 모든 국민에게 동등하게 혜택을 제공하는 복지 정책 모델은?', 'Mô hình chính sách phúc lợi cung cấp lợi ích bình đẳng cho mọi công dân, bất kể thu nhập hay tài sản, là gì?', ARRAY['선별적 복지', '보편적 복지', '잔여적 복지', '기업 복지']::text[], '보편적 복지', '보편적 복지 có nghĩa là ''phúc lợi phổ quát'' (Universal welfare). Đây là mô hình chính sách cung cấp các dịch vụ phúc lợi cơ bản (ví dụ: giáo dục miễn phí, y tế cơ bản, trợ cấp trẻ em) cho tất cả mọi người mà không cần kiểm tra điều kiện kinh tế. Mục tiêu là giảm bớt sự kỳ thị (낙인) khi nhận trợ cấp, tăng cường đoàn kết xã hội, nhưng thường đi kèm với gánh nặng thuế lớn hơn.', '240ea851-43db-4b66-a077-f77ef0989677', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('18cfd9b3-445c-4841-8bdb-7911a36ab546', '장기간에 걸쳐 지구의 기온, 강수량, 바람 패턴 등이 바뀌는 현상을 뭐라고 하나요?', 'Hiện tượng nhiệt độ, lượng mưa, hình thái gió... của Trái Đất thay đổi trong một khoảng thời gian dài được gọi là gì?', ARRAY['계절 변화', '기후 변화', '일기 예보', '대기 오염']::text[], '기후 변화', '기후 변화 có nghĩa là ''biến đổi khí hậu''. Đây là một trong những 환경 문제 lớn nhất hiện nay, chủ yếu do 인간 활동 (hoạt động của con người) như 온실가스 배출 (phát thải khí nhà kính) gây ra, dẫn đến 지구 온난화 (hiện tượng nóng lên toàn cầu).', 'fd13dc18-7037-47f0-b472-c3717c6ca4be', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('e8bfeb89-460c-4390-bad5-f8b83f1ebae8', '지구 대기 중에 열을 가두어 지구 온난화를 일으키는 주된 원인이 되는 가스(예: 이산화탄소, 메탄)들을 통틀어 뭐라고 하나요?', 'Các loại khí (ví dụ: CO2, CH4) trong khí quyển Trái Đất giữ nhiệt và là nguyên nhân chính gây ra hiện tượng nóng lên toàn cầu được gọi chung là gì?', ARRAY['산소', '청정 가스', '온실가스', '질소']::text[], '온실가스', '온실가스 có nghĩa là ''khí nhà kính''. 대표적인 온실가스로는 이산화탄소 (CO2), 메탄 (CH4), 아산화질소 (N2O)가 있다. 이들 가스의 배출량 (lượng phát thải)을 감축하다 (cắt giảm)는 것이 기후 변화 대응의 핵심이다.', 'ebc8b5f1-9dc4-4617-822a-af244ed2f404', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('129fa9e4-5483-45d0-8932-a902f24a46bf', '태양광, 풍력, 수력 등 사용해도 계속해서 자연적으로 보충되는 에너지원을 뭐라고 하나요?', 'Các nguồn năng lượng như quang năng, phong năng, thủy năng... dù có sử dụng vẫn tiếp tục được bổ sung một cách tự nhiên được gọi là gì?', ARRAY['화석 연료', '원자력', '재생 에너지', '바이오매스']::text[], '재생 에너지', '재생 에너지 có nghĩa là ''năng lượng tái tạo'' hay ''năng lượng tái sinh''. 이는 친환경적이다 (thân thiện với môi trường) và 화석 연료 (nhiên liệu hóa thạch)를 대체하다 (thay thế)하여 온실가스 배출을 줄이는 데 기여한다.', '6b9a4b0b-d60a-4ea3-9548-29a026d5347c', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('688fbeb7-b423-4f9b-985c-59188fdfe47f', '특정 지역에서 살고 있는 모든 생물(동식물)과 그들이 상호작용하는 무생물 환경을 포함한 전체를 무엇이라고 하나요?', 'Toàn bộ bao gồm tất cả sinh vật (động thực vật) sống trong một khu vực cụ thể và môi trường vô sinh mà chúng tương tác được gọi là gì?', ARRAY['공동체', '생태계', '서식지', '자연 경관']::text[], '생태계', '생태계 có nghĩa là ''hệ sinh thái''. 이는 매우 섬세한 균형 (sự cân bằng tinh tế)을 유지하고 있다. 환경 오염, 삼림 벌채 (chặt phá rừng) 등은 이 균형을 교란시키고 (làm xáo trộn) 생태계를 파괴할 수 있다.', 'eb906400-1dd6-4f0f-b05a-914f7ae54fa7', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('13098029-fe2d-470a-81c8-5ec0de5435b5', '현재 세대의 필요를 충족시키면서도 미래 세대가 그들의 필요를 충족시킬 능력을 저해하지 않는 발전 방식을 무엇이라고 하나요?', 'Phương thức phát triển đáp ứng nhu cầu của thế hệ hiện tại mà không làm tổn hại đến khả năng đáp ứng nhu cầu của các thế hệ tương lai được gọi là gì?', ARRAY['급속 성장', '무분별한 개발', '지속 가능한 발전', '산업화']::text[], '지속 가능한 발전', '지속 가능한 발전 có nghĩa là ''phát triển bền vững''. 이는 경제적 발전, 사회적 포용 (hòa nhập xã hội), 환경 보호를 균형 있게 추구하는 개념이다. 유엔의 지속가능발전목표 (SDGs)가 대표적인 예이다.', 'a89888d1-b894-45f4-a102-96978f11d79b', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('466a4e7b-a5d9-4552-80e8-f71a398216d7', '좋은 학교나 대학에 들어가기 위해 치열하게 경쟁하는 현상을 뭐라고 하나요?', 'Hiện tượng cạnh tranh khốc liệt để vào được những trường học hoặc đại học tốt được gọi là gì?', ARRAY['취업 경쟁', '입시 경쟁', '체육 경쟁', '문화 경쟁']::text[], '입시 경쟁', '입시 경쟁 (admissions competition) là sự cạnh tranh khốc liệt giữa các học sinh để đạt điểm số cao trong các kỳ thi tuyển sinh (수능, 내신) và được nhận vào các trường đại học danh tiếng.', '195e0dd5-e384-440f-bc63-f04b51dc134f', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('22e9f157-b647-47bd-822c-238944fd94ab', '______으로/로 인해 학생들은 어린 나이부터 엄청난 공부 부담을 안게 됩니다.', 'Do ______, học sinh phải chịu gánh nặng học tập khổng lồ từ khi còn rất nhỏ.', ARRAY['입시 경쟁', '스포츠 활동', '문화 체험', '여행']::text[], '입시 경쟁', '입시 경쟁 gay gắt thường được coi là nguyên nhân chính dẫn đến tình trạng học tập quá tải, căng thẳng và thiếu thời gian nghỉ ngơi cho học sinh Hàn Quốc.', '195e0dd5-e384-440f-bc63-f04b51dc134f', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('1e6d65c3-989d-4302-acbd-34d00188f7a9', '학교 정규 교육 과정 외에 학원, 과외 등에서 이루어지는 교육을 통틀어 뭐라고 하나요?', 'Giáo dục diễn ra bên ngoài chương trình giảng dạy chính quy của trường, như ở các học viện, gia sư, được gọi chung là gì?', ARRAY['공교육', '사교육', '평생교육', '직업교육']::text[], '사교육', '사교육 (private education) chỉ tất cả các hình thức giáo dục có trả phí bên ngoài trường học công lập, bao gồm học thêm tại học viện (학원), gia sư (과외), và các lớp học trực tuyến.', '3a5e9075-326a-47ec-9140-f095227d1a3c', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('8ffc33d9-b000-4f52-9009-32872581388d', '______ 비용이 가계 경제에 큰 부담으로 작용하는 경우가 많아요.', 'Chi phí ______ thường trở thành gánh nặng lớn đối với kinh tế gia đình.', ARRAY['주거', '의료', '사교육', '식비']::text[], '사교육', '사교육비 (chi phí học thêm) là một trong những khoản chi lớn nhất của các hộ gia đình Hàn Quốc có con trong độ tuổi đi học, và được coi là một vấn đề xã hội nghiêm trọng.', '3a5e9075-326a-47ec-9140-f095227d1a3c', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('67f03b7a-0011-4bd3-b0af-49d8548e569e', '지역, 소득, 계층에 따라 교육의 기회와 질에서 나타나는 차이를 뭐라고 하나요?', 'Sự chênh lệch về cơ hội và chất lượng giáo dục theo vùng miền, thu nhập, tầng lớp được gọi là gì?', ARRAY['교육 평등', '교육 격차', '교육 확대', '교육 혁신']::text[], '교육 격차', '교육 격차 (educational gap/inequality) là sự bất bình đẳng trong tiếp cận các nguồn lực và kết quả giáo dục, thường do chênh lệch về kinh tế (소득) hoặc địa lý (지역) gây ra.', 'b0803709-238f-4063-8bab-ea12d022dd72', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('2bd56fd0-eefa-467e-995d-5dcc04addc57', '______이/가 심할수록 사회 이동성이 낮아지고 빈곤의 대물림 가능성이 높아져요.', '______ càng nghiêm trọng thì khả năng di động xã hội càng thấp và khả năng nghèo đói được truyền từ đời này sang đời khác càng cao.', ARRAY['교육 격차', '교통 발전', '의료 보장', '문화 생활']::text[], '교육 격차', '교육 격차 làm cố định hoặc làm trầm trọng thêm bất bình đẳng xã hội, vì giáo dục thường là con đường chính để thoát nghèo và thăng tiến xã hội. Khoảng cách này hạn chế cơ hội của trẻ em từ các gia đình nghèo.', 'b0803709-238f-4063-8bab-ea12d022dd72', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('dbe65d42-2c60-4240-8dc5-fbcc99b5cc20', '개인이 교육 기관에서 공부하고 취득한 학위나 학업 성취 수준을 통틀어 뭐라고 하나요?', 'Trình độ học vấn, bằng cấp hoặc mức độ thành tích học tập mà một cá nhân đạt được từ các cơ sở giáo dục được gọi chung là gì?', ARRAY['경력', '학력', '능력', '자격']::text[], '학력', '학력 (academic background, educational attainment) thường đề cập đến trường đại học cao nhất mà một người đã tốt nghiệp (예: 서울대학교 학력) hoặc bằng cấp họ sở hữu.', 'a2eaad3c-d875-4dd3-ab9c-c9ad84b43f9e', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('cb3e3e8c-a7ed-47c6-ab48-6f73219c9b44', '한국에서는 취업 시 ______이/가 중요한 평가 기준이 되는 경우가 많아요.', 'Ở Hàn Quốc, khi xin việc, ______ thường trở thành tiêu chí đánh giá quan trọng.', ARRAY['외모', '학력', '취미', '혈액형']::text[], '학력', '학력주의 (chủ nghĩa bằng cấp) là hiện tượng phổ biến ở Hàn Quốc, nơi trường đại học danh tiếng mà ứng viên tốt nghiệp thường được các nhà tuyển dụng coi trọng quá mức, đôi khi hơn cả kinh nghiệm hay kỹ năng thực tế.', 'a2eaad3c-d875-4dd3-ab9c-c9ad84b43f9e', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('c1163399-5a5c-4e47-b4c9-6372d32bcd88', '학교 교육을 마친 후에도 직업, 자기 개발, 취미 등을 위해 계속해서 배우는 것을 뭐라고 하나요?', 'Việc tiếp tục học tập sau khi kết thúc giáo dục nhà trường, vì công việc, phát triển bản thân, sở thích, v.v. được gọi là gì?', ARRAY['정규 교육', '평생 교육', '조기 교육', '전문 교육']::text[], '평생 교육', '평생 교육 (lifelong education) là khái niệm học tập liên tục trong suốt cuộc đời, không giới hạn độ tuổi, bao gồm giáo dục người lớn, đào tạo lại, học tập cá nhân để thích ứng với xã hội và công việc luôn thay đổi.', '57d4a918-e981-4302-b193-7f570a725a95', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('0b5692d7-5274-4262-ad51-1b785d176d1d', '기술이 빠르게 발전하는 4차 산업 혁명 시대에는 ______이/가 필수적입니다.', 'Trong thời đại Cách mạng Công nghiệp 4.0 với công nghệ phát triển nhanh chóng, ______ là điều cần thiết.', ARRAY['휴식', '평생 교육', '고정관념', '은퇴']::text[], '평생 교육', 'Để theo kịp tốc độ thay đổi của công việc và kỹ năng trong kỷ nguyên số, các cá nhân cần liên tục học hỏi và nâng cao kỹ năng (재교육, upskilling/reskilling), đó chính là tinh thần của 평생 교육.', '57d4a918-e981-4302-b193-7f570a725a95', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('101efc2b-42b2-4e2a-8aaa-ab3bf5e8be6e', '사회 구성원들 사이에서 어떤 공공 문제에 대해 형성된 집단적 의견이나 태도를 뭐라고 하나요?', 'Ý kiến hoặc thái độ tập thể được hình thành giữa các thành viên trong xã hội về một vấn đề công cộng nào đó được gọi là gì?', ARRAY['개인적 의견', '여론', '정부 발표', '기업 광고']::text[], '여론', '여론 có nghĩa là ''dư luận'' (Public opinion). Đây là tập hợp các quan điểm của đa số công chúng về các vấn đề xã hội, chính trị, kinh tế. ''여론'' có sức mạnh to lớn, có thể tác động đến quyết định của chính trị gia, thay đổi chính sách và định hình văn hóa. ''여론 조사'' (thăm dò dư luận) là công cụ để đo lường ''여론''.', '4df056d6-a0db-4906-84a3-0df6991664cc', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('8991f52e-dd43-4d40-9eea-fa39cedd6ed8', '사실과 다르게 조작되거나 근거 없이 만들어져 마치 사실인 것처럼 유포되는 정보는?', 'Thông tin bị bóp méo khác với sự thật hoặc được tạo ra không có căn cứ nhưng lan truyền như thể đó là sự thật là gì?', ARRAY['공식 발표', '허위 정보', '기밀 문서', '연구 보고서']::text[], '허위 정보', '허위 정보 (Thông tin sai sự thật/False information) hay ''가짜 뉴스'' (Tin giả/Fake news) là một vấn đề nghiêm trọng trong thời đại kỹ thuật số. Loại thông tin này thường được tạo ra và lan truyền (확산) trên ''소셜 미디어'' với mục đích gây nhầm lẫn, thao túng ''여론'', kiếm tiền từ lượt xem, hoặc tấn công chính trị. Nó làm xói mòn niềm tin vào ''언론'' và gây chia rẽ xã hội.', 'b3da47fe-f8cd-42e5-904d-3c0c86a73b4b', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('b7570cbd-5a52-41a4-b359-b0d0b82e9f47', '언론과 출판이 정부의 검열이나 간섭 없이 자유롭게 정보와 의견을 발표할 수 있는 권리는?', 'Quyền được tự do công bố thông tin và ý kiến mà không bị kiểm duyệt hoặc can thiệp từ chính phủ của báo chí và xuất bản là gì?', ARRAY['사생활 보호권', '언론의 자유', '재산권', '평등권']::text[], '언론의 자유', '언론의 자유 có nghĩa là ''tự do báo chí'' (Freedom of the press). Đây là một quyền cơ bản trong ''민주주의'' (dân chủ), được ghi nhận trong Hiến pháp Hàn Quốc. Nó cho phép ''언론 매체'' (cơ quan truyền thông) giám sát quyền lực nhà nước, phơi bày sự thật, và cung cấp thông tin đa dạng cho công chúng. Tuy nhiên, quyền này cũng đi kèm với trách nhiệm (책임) như tránh ''명예훼손'' (phỉ báng).', '80067cf1-8d79-42e8-8338-929f945a6069', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('4c312ba1-af45-49ca-96fe-7c710c279551', '보도나 의견이 객관적이고 중립적이지 못하고 특정 정치적 입장이나 이해 관계에 치우치는 성향을 뭐라고 하나요?', 'Xu hướng trong việc đưa tin hoặc ý kiến không khách quan và trung lập, mà nghiêng về một lập trường chính trị hoặc lợi ích cụ thể nào đó, được gọi là gì?', ARRAY['객관성', '편향성', '정확성', '신속성']::text[], '편향성', '편향성 có nghĩa là ''tính thiên vị'' (Bias). Khi ''언론'' có ''편향성'', họ có thể lựa chọn, nhấn mạnh, hoặc diễn giải sự kiện theo hướng có lợi cho một phe phái nhất định. ''편향적인 보도'' (bài đưa tin thiên vị) có thể làm méo mó sự thật, củng cố định kiến, và làm gia tăng sự phân cực (양극화) trong xã hội. Người tiêu dùng truyền thông cần có ''미디어 리터러시'' (năng lực thông tin) để nhận biết điều này.', '5f616f9d-b4c8-4f1b-bb27-fdabca5f9fda', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('4c77b682-f7b9-40d0-a6ae-66985dec5e4d', '미디어가 어떤 문제를 중요하게 다루고 보도함으로써 대중이 그 문제를 중요하게 생각하도록 만드는 효과는?', 'Hiệu ứng mà truyền thông tạo ra khiến công chúng coi trọng một vấn đề nào đó bằng cách tập trung đưa tin và xử lý vấn đề đó một cách nghiêm túc là gì?', ARRAY['여론 형성', '의제 설정', '정보 확산', '오락 제공']::text[], '의제 설정', '의제 설정 (Agenda-setting) có nghĩa là ''thiết lập chương trình nghị sự''. Đây là một lý thuyết quan trọng trong truyền thông học. ''미디어'' không bảo mọi người ''nghĩ gì'' (생각), nhưng có thể quyết định mọi người ''nghĩ về cái gì'' (무엇에 대해 생각). Bằng cách lặp đi lặp lại đưa tin về một chủ đề nào đó (예: 기후 변화, 부동산 문제), truyền thông khiến công chúng và cả chính trị gia coi đó là vấn đề ưu tiên cần giải quyết.', 'd0423385-e4f1-4ca8-ac23-02a0927429f7', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('b6ac0b94-e06e-45c0-b5b5-f102e2495ed3', '1950년부터 1953년까지 남한과 북한 사이에서 벌어진 전쟁을 무엇이라고 하나요?', 'Cuộc chiến diễn ra từ năm 1950 đến 1953 giữa Nam Hàn và Bắc Hàn được gọi là gì?', ARRAY['세계 대전', '한국 전쟁', '베트남 전쟁', '독립 전쟁']::text[], '한국 전쟁', '한국 전쟁 có nghĩa là ''Chiến tranh Triều Tiên'' (hay Chiến tranh Hàn Quốc). Đây là một cuộc chiến 대리 (cuộc chiến ủy nhiệm) trong thời kỳ Chiến tranh Lạnh, dẫn đến 휴전 (đình chiến) chứ không phải 평화 조약 (hiệp ước hòa bình), nên 한반도 (bán đảo Triều Tiên) vẫn chia cắt cho đến ngày nay.', 'b3692230-d056-4918-ad3e-1369dfb63b32', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('eb1fe134-8b44-43b1-852e-d9c1c177eb74', '군인이 국가 권력을 장악하여 민간 통치를 대체한 정부 형태를 무엇이라고 하나요?', 'Hình thức chính quyền mà quân đội nắm giữ quyền lực nhà nước, thay thế chính quyền dân sự được gọi là gì?', ARRAY['왕정', '공화정', '군사 정권', '민주 정부']::text[], '군사 정권', '군사 정권 có nghĩa là ''chính quyền quân sự''. Trong lịch sử hiện đại Hàn Quốc, 박정희 정권 (chính quyền Park Chung-hee) và 전두환 정권 (chính quyền Chun Doo-hwan) là những ví dụ điển hình. Các chính quyền này thường 통제 (kiểm soát) 강력하게 (mạnh mẽ) và 민주주의를 억압하다 (đàn áp dân chủ).', 'daf3d2ac-ed64-4f6f-9861-70879efcc832', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('b75e39c9-1a15-4ccf-a3f9-e70eb0784318', '군사 독재 정권에 맞서 자유, 민주주의, 인권 등을 요구하며 벌인 국민적 저항 운동을 뭐라고 하나요?', 'Phong trào đấu tranh quốc dân chống lại chế độ độc tài quân sự, đòi hỏi tự do, dân chủ, nhân quyền... được gọi là gì?', ARRAY['산업화 운동', '민주화 운동', '독립 운동', '환경 운동']::text[], '민주화 운동', '민주화 운동 có nghĩa là ''phong trào dân chủ hóa''. Các phong trào lớn trong lịch sử Hàn Quốc bao gồm 4.19 혁명 (Cách mạng 19/4), 부마 민주 항쟁 (Phong trào Dân chủ Busan-Masan), 그리고 특히 1980년의 5.18 광주 민주화 운동 (Phong trào Dân chủ hóa Gwangju 18/5).', '5d02f19e-9697-434a-9de8-8701ec5ecae4', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('105d94fa-df71-46ae-a6b7-35f94dafd94d', '전쟁의 폐허에서 시작하여 고도 성장을 통해 세계적인 경제 강국으로 도약한 한국의 발전 과정을 무엇이라고 하나요?', 'Quá trình phát triển của Hàn Quốc, bắt đầu từ đống đổ nát sau chiến tranh, vươn lên thành cường quốc kinh tế thế giới thông qua tăng trưởng cao được gọi là gì?', ARRAY['문화 발전', '경제 개발', '정치 개혁', '군사 강화']::text[], '경제 개발', '경제 개발 có nghĩa là ''phát triển kinh tế''. Đây được gọi là 한강의 기적 (Kỳ tích sông Hàn). 1960-70년대 박정희 정권의 경제 개발 5개년 계획 등을 통해 수출 주도형 공업화 (công nghiệp hóa theo hướng xuất khẩu)를 이루어 냈다.', '9c32e26f-5cac-4a71-8f9f-4960d85404f7', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('5c9230e1-2528-414d-a56c-4730e22671ed', '대한민국(남한)과 조선민주주의인민공화국(북한) 사이의 정치적, 경제적, 사회적 관계를 총칭하여 무엇이라고 하나요?', 'Mối quan hệ chính trị, kinh tế, xã hội giữa Đại Hàn Dân Quốc (Nam Hàn) và Cộng hòa Dân chủ Nhân dân Triều Tiên (Bắc Hàn) được gọi chung là gì?', ARRAY['국제 관계', '남북 관계', '동맹 관계', '교류 관계']::text[], '남북 관계', '남북 관계 có nghĩa là ''quan hệ Nam-Bắc''. 이는 분단 (chia cắt) 현실에서 매우 복잡한 양상을 띠며, 정전 상태 (tình trạng đình chiến) 속에서 대화, 협력, 갈등, 긴장이 반복된다. 주요 사건으로는 2000년 남북 정상 회담 (hội nghị thượng đỉnh liên Triều) 등이 있다.', '3cf31d75-b949-4345-a0d2-c9583f3afb09', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('f4f4d575-224f-4e1b-a70b-8dadb602b63e', '헌법에 명시된, 인간이라면 누구나 태어나면서부터 가지는 불가침의 기본적 권리는?', 'Những quyền cơ bản bất khả xâm phạm mà bất cứ con người nào cũng có ngay từ khi sinh ra, được ghi trong hiến pháp là gì?', ARRAY['시민권', '기본권', '특권', '재산권']::text[], '기본권', '기본권 (fundamental rights) là những quyền cơ bản của con người được hiến pháp công nhận và bảo vệ, như quyền sống, quyền tự do, quyền bình đẳng, quyền mưu cầu hạnh phúc.', 'c87d0762-e499-4247-adde-f6a4bc2cf303', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('390cda3f-ed6e-4584-a2a2-3e136bce6d7a', '______은/는 국가 권력으로부터도 보호받아야 합니다.', '______ cũng phải được bảo vệ khỏi quyền lực nhà nước.', ARRAY['기본권', '의무', '세금', '징병']::text[], '기본권', '기본권 có chức năng như một rào cản chống lại sự xâm phạm tùy tiện từ phía nhà nước. Nhà nước có nghĩa vụ tôn trọng, bảo vệ và thực hiện các quyền này.', 'c87d0762-e499-4247-adde-f6a4bc2cf303', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('32069ee7-3fe8-4dc2-af4a-500e1b71bb94', '인종, 성별, 종교, 사회적 신분 등에 관계없이 법적으로 동등하게 대우받을 권리를 뭐라고 하나요?', 'Quyền được đối xử bình đẳng trước pháp luật, không phân biệt chủng tộc, giới tính, tôn giáo, địa vị xã hội, v.v. được gọi là gì?', ARRAY['자유권', '참정권', '평등권', '생존권']::text[], '평등권', '평등권 (right to equality) là quyền cơ bản đảm bảo mọi người đều bình đẳng trước pháp luật và được pháp luật bảo vệ như nhau, không bị phân biệt đối xử.', '7c29142b-9612-437d-b81f-83279055de19', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('eecb0321-8283-4cdd-857c-5b5816f14cc8', '"법 앞의 ______"은 민주주의 사회의 기본 원칙입니다.', '"______ trước pháp luật" là nguyên tắc cơ bản của xã hội dân chủ.', ARRAY['평등', '자유', '특권', '차별']::text[], '평등', '"법 앞의 평등" (equality before the law) là nguyên tắc pháp quyền quan trọng, có nghĩa là tất cả mọi người đều phải tuân theo cùng một luật và được xét xử bởi cùng một hệ thống tòa án.', '7c29142b-9612-437d-b81f-83279055de19', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('536e69cf-4e11-49e5-8887-ab4ce2f8f7f2', '개인이나 국가가 인간의 기본적 권리와 자유를 부정하거나 침해하는 행위는?', 'Hành động của cá nhân hoặc nhà nước phủ nhận hoặc xâm phạm các quyền và tự do cơ bản của con người là gì?', ARRAY['인권 증진', '인권 침해', '인권 보호', '인권 존중']::text[], '인권 침해', '인권 침해 (human rights violation) là bất kỳ hành động nào tước đoạt hoặc hạn chế quyền con người của một cá nhân hoặc nhóm người, từ phân biệt đối xử đến tra tấn, bắt giữ tùy tiện, v.v.', 'fd4d27a1-fd2a-450c-8d17-424340825faf', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('91d4a40b-556a-4c3e-8b8f-11b86dcc8116', '국제 사회는 심각한 ______이 발생하는 국가에 대해 제재를 가하기도 합니다.', 'Cộng đồng quốc tế đôi khi áp đặt các biện pháp trừng phạt đối với các quốc gia nơi xảy ra ______ nghiêm trọng.', ARRAY['경제 발전', '인권 침해', '문화 교류', '기술 협력']::text[], '인권 침해', 'Bảo vệ nhân quyền là mối quan tâm toàn cầu. Các tổ chức như UN có thể điều tra và các quốc gia có thể áp dụng biện pháp trừng phạt ngoại giao hoặc kinh tế để phản ứng lại các vi phạm nhân quyền có hệ thống và nghiêm trọng.', 'fd4d27a1-fd2a-450c-8d17-424340825faf', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('a18771a0-f71e-4615-b77b-2f680cc451f9', '인종, 종교, 성적 지향, 장애 등으로 인해 사회에서 주류가 아니거나 차별을 받을 수 있는 집단을 뭐라고 하나요?', 'Nhóm người trong xã hội không thuộc nhóm chủ đạo hoặc có thể bị phân biệt đối xử do chủng tộc, tôn giáo, xu hướng tính dục, khuyết tật, v.v. được gọi là gì?', ARRAY['사회적 다수자', '사회적 소수자', '지배 계층', '엘리트 집단']::text[], '사회적 소수자', '사회적 소수자 (social minorities) là những nhóm người có đặc điểm khác biệt (văn hóa, dân tộc, tôn giáo, giới tính, v.v.) so với đa số và thường dễ bị tổn thương, bị loại trừ hoặc phân biệt đối xử trong xã hội.', '8b687e15-2f8b-43a0-9392-0d7450cea28b', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('a8851b46-a8db-4ef7-84bb-7b79bd1e8c3f', '______의 인권 보호는 포용적이고 다양성을 존중하는 사회를 만드는 데 중요합니다.', 'Việc bảo vệ nhân quyền của ______ rất quan trọng để xây dựng một xã hội bao trùm và tôn trọng sự đa dạng.', ARRAY['기업', '정부', '사회적 소수자', '군인']::text[], '사회적 소수자', 'Một xã hội lành mạnh và công bằng cần đảm bảo rằng 모든 thành viên, đặc biệt là những nhóm thiểu số dễ bị tổn thương, đều được hưởng đầy đủ các quyền con người và có cơ hội bình đẳng.', '8b687e15-2f8b-43a0-9392-0d7450cea28b', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('1cafd341-4256-44e6-a74b-c1e365378bd1', '자신의 생각, 의견, 신념을 말, 글, 예술 등 다양한 방법으로 공개적으로 표현할 수 있는 권리를 뭐라고 하나요?', 'Quyền được bày tỏ công khai suy nghĩ, ý kiến, niềm tin của mình thông qua lời nói, văn bản, nghệ thuật và nhiều cách thức khác được gọi là gì?', ARRAY['종교의 자유', '표현의 자유', '거주의 자유', '직업 선택의 자유']::text[], '표현의 자유', '표현의 자유 (freedom of expression) là một quyền con người cơ bản, bao gồm tự do tìm kiếm, tiếp nhận và truyền bá thông tin, ý tưởng dưới mọi hình thức, bất chấp biên giới.', 'c3613b97-52a2-4629-a836-4086d0683dae', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('b20350d7-feb4-4471-9a18-0993bb1c41ea', '______은/는 민주주의가 제대로 기능하기 위한 필수 조건이지만, 명예 훼손이나 혐오 발언과 같은 합리적 제한이 있을 수 있습니다.', '______ là điều kiện tiên quyết để nền dân chủ vận hành đúng đắn, nhưng có thể có những hạn chế hợp lý như phỉ báng hoặc phát ngôn thù ghét.', ARRAY['표현의 자유', '신체의 자유', '재산권', '투표권']::text[], '표현의 자유', '표현의 자유 không phải là quyền tuyệt đối. Nó có thể bị hạn chế vì những lý do chính đáng như bảo vệ an ninh quốc gia, trật tự công cộng, sức khỏe cộng đồng, đạo đức, hoặc quyền và danh tiếng của người khác.', 'c3613b97-52a2-4629-a836-4086d0683dae', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('d0321f37-f5c5-4332-8fa6-efd72489c6df', '한 국가가 다른 국가나 국제 사회와의 관계에서 추구하는 목표와 이를 달성하기 위한 원칙 및 행동 방침을 총칭하는 것은?', 'Tổng thể các mục tiêu, nguyên tắc và phương hướng hành động mà một quốc gia theo đuổi trong quan hệ với các quốc gia khác và cộng đồng quốc tế được gọi là gì?', ARRAY['국내 정책', '외교 정책', '경제 정책', '사회 정책']::text[], '외교 정책', '외교 정책 có nghĩa là ''chính sách đối ngoại'' (Foreign policy). Đây là chiến lược tổng thể của một quốc gia trong việc tương tác với phần còn lại của thế giới. ''외교 정책'' được hình thành dựa trên ''국익'' (lợi ích quốc gia) và các giá trị cốt lõi, bao gồm các lĩnh vực như an ninh, kinh tế, văn hóa. ''외교 정책 기조'' là chủ đạo, phương hướng lớn của chính sách đó.', '9497e547-da92-4673-a05e-a2df292c9a18', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('2c4a79cf-7ee6-4d85-9868-af5b811594a5', '한 국가의 생존, 안보, 경제적 번영, 국제적 위상 등 국가 전체의 이익을 무엇이라고 하나요?', 'Lợi ích của toàn bộ quốc gia, bao gồm sự tồn vong, an ninh, thịnh vượng kinh tế và vị thế quốc tế được gọi là gì?', ARRAY['개인적 이익', '기업 이익', '국익', '지역 이익']::text[], '국익', '국익 có nghĩa là ''lợi ích quốc gia'' (National interest). Đây là nền tảng và mục tiêu cao nhất của mọi ''외교 정책''. Quan hệ ngoại giao, liên minh, hay thậm chí xung đột đều được đánh giá dựa trên việc chúng có phục vụ và tối đa hóa ''국익'' hay không. ''국익'' thường được chia thành lợi ích cốt lõi (생존, 주권) và lợi ích thứ yếu (경제적 이득, 문화적 영향력).', 'ac6ded44-e4ff-4514-acd2-6cab5ff72017', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('dda3eb66-2aa8-4e2e-a8db-775db5083dc4', '세 개 이상의 국가가 국제 기구나 국제 회의를 통해 공동의 문제를 협의하고 해결하는 외교 방식은?', 'Phương thức ngoại giao trong đó ba quốc gia trở lên cùng thảo luận và giải quyết các vấn đề chung thông qua các tổ chức quốc tế hoặc hội nghị quốc tế là gì?', ARRAY['양자외교', '다자외교', '특사 외교', '원조 외교']::text[], '다자외교', '다자외교 có nghĩa là ''ngoại giao đa phương'' (Multilateral diplomacy). Đây là hoạt động ngoại giao được tiến hành trong khuôn khổ các thể chế, tổ chức quốc tế như UN (유엔), WTO, ASEAN, G20. 다자외교 giúp giải quyết các vấn đề toàn cầu (글로벌 이슈) như biến đổi khí hậu, đại dịch, an ninh hạt nhân một cách có quy tắc và cùng có lợi, nâng cao vai trò và ảnh hưởng (국제적 위상) của một quốc gia.', 'a86bb787-3426-402d-a4af-480f93011c2a', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('5c654d02-1d5c-49d2-87c4-dd31b4eab1ae', '이데올로기나 감정보다는 국가의 실질적인 경제적, 안보적 이득을 우선시하는 외교 노선은?', 'Đường lối ngoại giao ưu tiên lợi ích kinh tế, an ninh thực chất của quốc gia hơn là ý thức hệ hay tình cảm là gì?', ARRAY['이념 외교', '실리 외교', '인도적 외교', '감정적 외교']::text[], '실리 외교', '실리 외교 có nghĩa là ''ngoại giao thực dụng'' hay ''ngoại giao vì lợi ích thực tế'' (Pragmatic diplomacy). Trong ''실리 외교'', các quyết định đối ngoại được đưa ra dựa trên tính toán lạnh lùng về lợi ích và chi phí (국익), thay vì bị ràng buộc bởi các nguyên tắc ý thức hệ (이념) cứng nhắc hoặc tình cảm lịch sử. Ví dụ, thiết lập quan hệ ngoại giao với một quốc gia bất chấp sự khác biệt về thể chế chính trị nếu điều đó mang lại lợi ích kinh tế lớn.', '700cf6ea-3fad-49b7-84c8-b26084a05312', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('c80933da-8976-4876-b0af-4ef0b815ca21', '상대국에 대한 제재나 군사적 위협(압박)과 협상 테이블(대화)을 함께 활용하는 이중적 접근법을 뭐라고 하나요?', 'Phương pháp tiếp cận kép sử dụng đồng thời cả biện pháp trừng phạt hoặc đe dọa quân sự (gây áp lực) lẫn bàn đàm phán (đối thoại) đối với một quốc gia khác được gọi là gì?', ARRAY['회피 정책', '압박과 대화', '강경 일변도', '유화 일변도']::text[], '압박과 대화', '압박과 대화 có nghĩa là ''gây áp lực và đối thoại'' (Pressure and dialogue). Đây là một chiến lược ngoại giao phổ biến, đặc biệt trong các cuộc đàm phán khó khăn (예: 북핵 문제). ''압박'' (thông qua ''제재'' - trừng phạt, ''군사 훈련'' - tập trận) nhằm tạo đòn bẩy và khiến đối phương quay lại bàn đàm phán. ''대화'' (thông qua ''회담'' - hội đàm, ''협상'' - thương lượng) nhằm tìm kiếm giải pháp ngoại giao. Việc ''병행하다'' (tiến hành song song) hai mặt này đòi hỏi sự cân bằng tinh tế.', 'f46996c3-b4aa-4a5d-96f4-184f73b3e288', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('3577ccf3-c3fa-4741-a9f9-b9a131433af1', '인구와 경제 활동이 농촌 지역에서 도시 지역으로 집중되는 인구·사회적 변화 과정을 뭐라고 하나요?', 'Quá trình thay đổi nhân khẩu - xã hội khi dân số và hoạt động kinh tế tập trung từ khu vực nông thôn vào khu vực thành thị được gọi là gì?', ARRAY['공업화', '도시화', '세계화', '정보화']::text[], '도시화', '도시화 có nghĩa là ''đô thị hóa''. 이는 현대 사회의 중요한 현상으로, 특히 한국은 20세기 후반에 급격한 도시화를 경험하여 서울 등 대도시의 인구가 폭발적으로 증가했다. 이로 인해 다양한 도시 문제가 발생했다.', 'e5d20f6e-820d-4fc2-b1f2-c4842d6b378d', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('5c0b191d-5fed-4b40-b0b4-92261d753b37', '인구 증가나 도시화로 인해 주택의 수요가 공급을 크게 앞서면서 발생하는 사회적 문제를 무엇이라고 하나요?', 'Vấn đề xã hội nảy sinh khi nhu cầu về nhà ở vượt xa nguồn cung do dân số tăng hoặc quá trình đô thị hóa được gọi là gì?', ARRAY['식량난', '주택난', '교통난', '취업난']::text[], '주택난', '주택난 có nghĩa là ''tình trạng thiếu nhà'', ''khủng hoảng nhà ở''. 이로 인해 집값 (giá nhà)과 전세비 (tiền cọc thuê nhà)가 급등하고, 서민들의 주거 부담이 커진다. 정부는 공공 주택 (nhà ở công cộng) 건설이나 다양한 주택 정책을 통해 이를 해결하려 노력한다.', 'ff457e47-0986-458e-b18c-65bb45fbdfe7', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('b698a6d4-c837-48c9-bf8e-c667d04bbfd7', '차량이 과도하게 많아져 도로의 소통 능력을 넘어서면서 차량의 흐름이 느려지거나 멈추는 현상을 뭐라고 하나요?', 'Hiện tượng dòng xe chậm lại hoặc dừng hẳn do số lượng xe vượt quá khả năng thông hành của đường phố được gọi là gì?', ARRAY['교통 사고', '교통 체증', '교통 편의', '교통 규제']::text[], '교통 체증', '교통 체증 có nghĩa là ''sự tắc nghẽn giao thông''. 특히 대도시에서 출퇴근 시간 (rush hour)에 심각하다. 이는 시간 낭비, 연료 낭비, 대기 오염 등을 일으킨다. 이를 해결하기 위해 대중교통 (giao thông công cộng) 확충, 교통 수요 관리 등의 정책이 시행된다.', '23f4590c-90f9-4b93-a2e7-d56b50bc4b26', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('04e15eee-2253-4a11-8179-68bf7ecec22e', '낙후된 도시 지역에서 주택이 노후화되고 생활 환경이 열악해지며 빈곤 주민이 집중되는 현상을 뭐라고 하나요?', 'Hiện tượng nhà cửa xuống cấp, môi trường sống tồi tàn đi và cư dân nghèo tập trung tại các khu vực thành thị lạc hậu được gọi là gì?', ARRAY['재개발', '슬럼화', '고급화', '상업화']::text[], '슬럼화', '슬럼화 có nghĩa là ''hiện tượng trở thành khu ổ chuột'' (slum). 이러한 지역에서는 주택이 낡고 (주택 노후화), 기초 시설이 부족하며, 범죄율이 높을 수 있다. 이는 심각한 사회 문제로, 도시 재생 사업의 주요 대상이 된다.', '50c554b4-52df-46af-b3b1-5d300716d515', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('c3688765-1528-42dc-9c47-933422fcf282', '노후화되거나 쇠퇴한 도시 지역을 물리적으로 정비하고, 경제·사회·문화적으로 활성화시키는 종합적인 사업을 뭐라고 하나요?', 'Dự án tổng hợp nhằm cải tạo mặt bằng vật chất và phục hồi về mặt kinh tế, xã hội, văn hóa cho các khu vực đô thị đã xuống cấp hoặc suy thoái được gọi là gì?', ARRAY['도시 확장', '도시 재생', '신도시 건설', '공단 조성']::text[], '도시 재생', '도시 재생 có nghĩa là ''sự tái sinh/tái tạo đô thị''. 이는 기존의 재개발 (tái phát triển)과 달리, 철거와 신축보다는 기존 자원을 활용하고 지역 공동체를 살리는 데 중점을 둔다. 문화 시설 조성, 공원 조성, 가로 환경 정비 등을 포함한다.', 'cb804903-3204-4eaa-9182-4b288c6a012a', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('4d40105f-a926-4386-b231-8ca2765896a4', '여러 다른 민족, 문화, 종교, 언어를 가진 사람들이 함께 살아가는 사회를 뭐라고 하나요?', 'Xã hội nơi những người thuộc nhiều dân tộc, văn hóa, tôn giáo, ngôn ngữ khác nhau cùng chung sống được gọi là gì?', ARRAY['단일 문화 사회', '다문화 사회', '고립 사회', '전통 사회']::text[], '다문화 사회', '다문화 사회 (multicultural society) là xã hội có sự đa dạng về văn hóa và sắc tộc, thường hình thành do làn sóng nhập cư hoặc lịch sử di cư lâu dài.', '5fadf180-abff-4d79-ae95-043f3c1c66a6', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('8fb900f1-79ed-4a7f-87c8-fe3ef6535adf', '______에서는 다양한 문화적 배경을 가진 사람들이 서로를 존중하며 살아가는 것이 중요해요.', 'Trong ______, điều quan trọng là những người có nền tảng văn hóa đa dạng phải sống tôn trọng lẫn nhau.', ARRAY['다문화 사회', '군사 사회', '농업 사회', '산업 사회']::text[], '다문화 사회', 'Xây dựng một 다문화 사회 thành công đòi hỏi không chỉ sự chung sống đơn thuần mà còn cần sự 존중 (tôn trọng), 이해 (thấu hiểu), 및 사회적 통합 (social integration) thực sự giữa các cộng đồng.', '5fadf180-abff-4d79-ae95-043f3c1c66a6', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('bcb412dd-9f2d-4dc5-b9fb-bde07bda712b', '생계, 일자리, 더 나은 삶을 위해 다른 나라나 지역으로 이동하여 사는 사람들을 통틀어 뭐라고 하나요?', 'Những người di chuyển đến một quốc gia hoặc khu vực khác để sinh sống, tìm việc làm, hoặc có cuộc sống tốt hơn được gọi chung là gì?', ARRAY['관광객', '이주민', '출장자', '유학생']::text[], '이주민', '이주민 (migrants) là thuật ngữ chung chỉ những người di cư, bao gồm 외국인 근로자 (lao động nước ngoài), 결혼 이민자 (người nhập cư qua hôn nhân), 난민 (tị nạn), v.v.', 'f5102a48-e221-498e-9d44-cad384268644', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('6b915e0f-126a-4927-b402-1265b838a445', '______들을 위한 한국어 교육이나 법률 상담 서비스가 제공되고 있어요.', 'Các dịch vụ như giáo dục tiếng Hàn hoặc tư vấn pháp luật đang được cung cấp cho ______.', ARRAY['관광객', '이주민', '출장자', '유학생']::text[], '이주민', 'Để hỗ trợ 이주민 hội nhập, chính phủ và các tổ chức phi chính phủ thường cung cấp các chương trình 한국어 교육, 문화적 정착 지원 (hỗ trợ định cư văn hóa), và 다양한 dịch vụ xã hội.', 'f5102a48-e221-498e-9d44-cad384268644', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('5c616761-de50-4a41-b6b3-1adb5485e635', '다른 문화를 제대로 이해하지 않고 그 문화에 대해 갖게 되는 부정적이고 경직된 생각을 뭐라고 하나요?', 'Những suy nghĩ tiêu cực và cứng nhắc về một nền văn hóa khác mà không thực sự hiểu biết về nó được gọi là gì?', ARRAY['문화적 이해', '문화적 편견', '문화 상대주의', '문화 교류']::text[], '문화적 편견', '문화적 편견 (cultural prejudice) là những định kiến, thành kiến tiêu cực dựa trên sự khác biệt văn hóa, thường dẫn đến 고정관념 (stereotype) và 차별 (phân biệt đối xử).', '5964b035-3320-4388-b704-f644432b988a', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('cdd94edd-c9d8-4b4e-96c2-14498efc01d5', '______을/를 극복하려면 다른 문화에 대한 공부와 직접적인 교류가 필요해요.', 'Để vượt qua ______, cần có sự học hỏi và giao tiếp trực tiếp với các nền văn hóa khác.', ARRAY['문화적 편견', '경제적 발전', '기술적 혁신', '정치적 안정']::text[], '문화적 편견', 'Giáo dục đa văn hóa (다문화 교육) và tiếp xúc tích cực (긍정적 접촉) giữa các nhóm văn hóa khác nhau là những cách hiệu quả để giảm thiểu 문화적 편견 và thúc đẩy sự hiểu biết lẫn nhau.', '5964b035-3320-4388-b704-f644432b988a', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('5dd0877c-cb8d-4a7e-a09e-f28fdc646f7a', '자신의 문화 기준으로 다른 문화를 평가하기보다, 그 문화가 가진 맥락과 가치를 이해하고 존중하려는 태도는?', 'Thái độ cố gắng hiểu và tôn trọng bối cảnh và giá trị của một nền văn hóa thay vì đánh giá nó bằng tiêu chuẩn của văn hóa mình là gì?', ARRAY['문화 절대주의', '문화 사대주의', '문화 상대주의', '문화 우월주의']::text[], '문화 상대주의', '문화 상대주의 (cultural relativism) là quan điểm cho rằng niềm tin, giá trị và thực hành của một người nên được hiểu dựa trên ngữ cảnh văn hóa của chính họ, chứ không phải bị đánh giá bởi tiêu chuẩn của một nền văn hóa khác.', 'd328a5b0-4ffa-47bb-bba2-41a92acc1362', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('656eacce-cc30-4a6e-b703-a81a9d5089c6', '______에 따르면, ''옳고 그름''은 문화마다 다를 수 있으므로 우리의 기준을 강요해서는 안 됩니다.', 'Theo ______, ''đúng và sai'' có thể khác nhau tùy theo văn hóa, vì vậy không nên áp đặt tiêu chuẩn của chúng ta.', ARRAY['문화 상대주의', '문화 제국주의', '인종 중심주의', '문화 통합']::text[], '문화 상대주의', '문화 상대주의 khuyến khích sự 겸허 (khiêm tốn), 개방성 (cởi mở) và 관용 (khoan dung) trong giao tiếp liên văn hóa. Tuy nhiên, nó cũng có giới hạn khi đối mặt với những hành vi vi phạm nhân quyền phổ quát.', 'd328a5b0-4ffa-47bb-bba2-41a92acc1362', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('5d744176-9759-4259-8e2b-75de7289a750', '서로 다른 배경을 가진 개인이나 집단이 사회의 구성원으로서 동등한 권리와 기회를 누리며 조화를 이루는 과정을 뭐라고 하나요?', 'Quá trình mà các cá nhân hoặc nhóm có nền tảng khác nhau trở thành thành viên của xã hội, tận hưởng các quyền và cơ hội bình đẳng, và đạt được sự hài hòa được gọi là gì?', ARRAY['사회 분열', '사회 배제', '사회 통합', '사회 계층화']::text[], '사회 통합', '사회 통합 (social integration) là quá trình hai chiều: xã hội chủ đạo tạo cơ hội bình đẳng và chào đón người mới, trong khi người mới nỗ lực thích nghi và tham gia vào xã hội đó.', 'c8a20210-eb07-4f60-96d8-cb5f74059b2d', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z'),
  ('64b2e0f9-ade6-4775-bef3-a517df5392d4', '______을/를 위해서는 언어 교육, 고용 기회 보장, 차별 금지 등이 필요합니다.', 'Để đạt được ______, cần có giáo dục ngôn ngữ, đảm bảo cơ hội việc làm, cấm phân biệt đối xử, v.v.', ARRAY['문화적 분리', '사회 통합', '경제적 불평등', '정치적 대립']::text[], '사회 통합', '사회 통합 thành công đòi hỏi các chính sách và nỗ lực toàn diện ở nhiều cấp độ: ngôn ngữ, giáo dục, việc làm, nhà ở, y tế, và quan trọng nhất là thay đổi thái độ và nhận thức xã hội.', 'c8a20210-eb07-4f60-96d8-cb5f74059b2d', '2026-01-05T17:07:58.361Z', '2026-01-05T17:07:58.361Z');
INSERT INTO "LearningGroup" (id, name, icon, "topikLevel", "createdAt", "updatedAt") VALUES ('30f88657-353e-4f1d-a6bd-ca93e4d00f5f', 'Kinh tế & Kinh doanh (경제 및 경영)', NULL, 3, '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z');
INSERT INTO "Category" (id, name, description, icon, "order", "topikLevel", "groupId", "createdAt", "updatedAt") VALUES ('cfc47ac0-c8db-40a5-90c0-9a64d7aa54ae', 'Thị trường Tài chính', 'Từ vựng chuyên ngành về thị trường tài chính, đầu tư và các công cụ tài chính tại Hàn Quốc', '📈', 1, 3, '30f88657-353e-4f1d-a6bd-ca93e4d00f5f', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('b49d0f13-7224-410f-a8e9-0e10f8cb4a5c', 'Phân tích Doanh nghiệp', 'Từ vựng về các khái niệm, công cụ và chỉ số tài chính dùng để đánh giá và phân tích tình hình kinh doanh của một công ty', '📈', 2, 3, '30f88657-353e-4f1d-a6bd-ca93e4d00f5f', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('40b895c7-c566-46da-add4-24f138a3ca34', 'Thương mại Quốc tế', 'Từ vựng chuyên ngành về thương mại quốc tế, xuất nhập khẩu và các hiệp định thương mại', '🌐', 3, 3, '30f88657-353e-4f1d-a6bd-ca93e4d00f5f', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('1d328329-5dfd-42a4-b152-e9d85e619ef7', 'Marketing & Thương hiệu', 'Từ vựng về chiến lược marketing, quản lý thương hiệu và hoạt động truyền thông tại Hàn Quốc', '📢', 4, 3, '30f88657-353e-4f1d-a6bd-ca93e4d00f5f', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('697fb86a-08f3-4189-af70-a2d70718f371', 'Quản trị Nhân sự', 'Từ vựng về các chính sách, chiến lược và quy trình liên quan đến quản lý và phát triển con người trong tổ chức', '👥', 5, 3, '30f88657-353e-4f1d-a6bd-ca93e4d00f5f', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('9ff52c35-2d2b-4dd1-b97a-afff3854abe1', 'Sản xuất & Logistics', 'Từ vựng về các quy trình sản xuất, quản lý chuỗi cung ứng và hậu cần trong doanh nghiệp', '🚚', 6, 3, '30f88657-353e-4f1d-a6bd-ca93e4d00f5f', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('48c10c2c-6094-4e44-8d93-6aa6fcc912fe', 'Khởi nghiệp & Đầu tư mạo hiểm (스타트업 및 벤처 투자)', 'Từ vựng về hệ sinh thái khởi nghiệp, huy động vốn và đầu tư mạo hiểm', '🚀', 7, 3, '30f88657-353e-4f1d-a6bd-ca93e4d00f5f', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('b76816f2-b8d9-43bd-b003-f750a2bfcefb', 'Bất động sản & Đầu tư (부동산 및 투자)', 'Từ vựng về đầu tư bất động sản, tài chính bất động sản và thị trường nhà đất', '🏠', 8, 3, '30f88657-353e-4f1d-a6bd-ca93e4d00f5f', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('a7b30dab-2c23-49ec-a2ec-1e35ac0bbaaa', 'Sáp nhập & Mua lại (M&A)', 'Từ vựng về các giao dịch doanh nghiệp, chiến lược mua bán và sáp nhập công ty trong tài chính doanh nghiệp', '🤝', 9, 3, '30f88657-353e-4f1d-a6bd-ca93e4d00f5f', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('59234f5c-93cf-43d2-a8c1-6845347165e4', 'Kinh tế Vĩ mô (거시 경제)', 'Từ vựng về các khái niệm, chỉ số và chính sách kinh tế ở cấp độ quốc gia và toàn cầu', '📈', 10, 3, '30f88657-353e-4f1d-a6bd-ca93e4d00f5f', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('2c4ce7ac-2adf-4caf-a2b3-381e13129c18', 'Kinh tế Vi mô (미시 경제)', 'Từ vựng về các nguyên lý kinh tế vi mô, hành vi người tiêu dùng và doanh nghiệp', '📊', 11, 3, '30f88657-353e-4f1d-a6bd-ca93e4d00f5f', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('ea93f977-c648-4348-af26-23dd5282c070', 'Luật Thương mại', 'Từ vựng về các khái niệm pháp lý, quyền sở hữu trí tuệ và cơ cấu tổ chức doanh nghiệp theo luật pháp Hàn Quốc', '⚖️', 12, 3, '30f88657-353e-4f1d-a6bd-ca93e4d00f5f', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('a957fa96-c89c-40c4-a159-73d07e3c83c2', 'Quảng cáo & Tâm lý người tiêu dùng (광고 및 소비자 심리)', 'Từ vựng về các khái niệm tiếp thị, cơ chế tâm lý ảnh hưởng đến hành vi mua sắm và các hình thức quảng cáo', '🎯', 13, 3, '30f88657-353e-4f1d-a6bd-ca93e4d00f5f', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('0a7b4579-e689-4572-98cd-0ca542214ccb', 'Kinh tế Toàn cầu (세계 경제)', 'Từ vựng về các khái niệm và tổ chức kinh tế quốc tế, toàn cầu hóa và thị trường toàn cầu', '🌍', 14, 3, '30f88657-353e-4f1d-a6bd-ca93e4d00f5f', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('52f708bf-245d-4d31-a5eb-497aad1d6d5f', 'Quan hệ Lao động', 'Từ vựng về quan hệ giữa người lao động và người sử dụng lao động, các tổ chức công đoàn, đàm phán và tranh chấp lao động', '⚖️', 15, 3, '30f88657-353e-4f1d-a6bd-ca93e4d00f5f', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z');
