INSERT INTO "QuizQuestion" (id, question, "questionTranslation", options, "correctAnswer", explanation, "VocabularyId", "createdAt", "updatedAt") VALUES ('9aa1344a-d688-4706-a982-dcf81e09122a', '일자리를 찾는 과정을 뭐라고 하나요?', 'Quá trình tìm kiếm việc làm được gọi là gì?', ARRAY['구직', '퇴직', '이직', '취업']::text[], '구직', '구직 có nghĩa là ''tìm việc'', chỉ hoạt động tìm kiếm công việc mới.', '1259cc77-6b5d-4d77-ba3c-da7818c0730c', '2026-01-05T17:07:58.367Z', '2026-01-05T17:07:58.367Z'),
  ('9baca2eb-4a02-4f94-9eaf-c6f46ddf67fe', '회사에 지원할 때 개인의 학력과 경력을 정리한 서류는?', 'Khi ứng tuyển vào công ty, tài liệu tổng hợp trình độ học vấn và kinh nghiệm cá nhân là gì?', ARRAY['자기소개서', '이력서', '자격증', '추천서']::text[], '이력서', '이력서 là hồ sơ cá nhân ghi lại thông tin học vấn, kinh nghiệm làm việc và kỹ năng.', '45547918-d360-4f92-9e20-33a78f86e9cf', '2026-01-05T17:07:58.367Z', '2026-01-05T17:07:58.367Z'),
  ('91eda603-f815-428e-b91d-2c0eb8933d8c', '지원자의 장점과 포부를 표현하는 서류는?', 'Tài liệu thể hiện ưu điểm và nguyện vọng của ứng viên là gì?', ARRAY['이력서', '자기소개서', '자격증', '경력증명서']::text[], '자기소개서', '자기소개서 là thư giới thiệu bản thân, nơi ứng viên trình bày động lực, điểm mạnh và mục tiêu nghề nghiệp.', '509474ab-1bf8-4704-be33-aef6b717f8da', '2026-01-05T17:07:58.367Z', '2026-01-05T17:07:58.367Z'),
  ('26bafb7c-d2cd-4015-89eb-f3a056adb4a3', '회사에서 지원자와 직접 만나서 대화하는 과정은?', 'Quá trình công ty gặp gỡ và trò chuyện trực tiếp với ứng viên là gì?', ARRAY['시험', '면접', '토론', '상담']::text[], '면접', '면접 là buổi phỏng vấn trực tiếp giữa nhà tuyển dụng và ứng viên để đánh giá phẩm chất và năng lực.', 'cca3bc47-edc3-4279-9808-a518df82917c', '2026-01-05T17:07:58.367Z', '2026-01-05T17:07:58.367Z'),
  ('1a61c49e-cba4-4b4d-8332-14aee54130a3', '회사의 공고에 나의 관심을 표하고 해당 직무를 원한다는 의사를 밝히는 것은?', 'Việc bày tỏ sự quan tâm đến thông báo tuyển dụng của công ty và mong muốn được làm công việc đó là gì?', ARRAY['포기하다', '지원하다', '거절하다', '연기하다']::text[], '지원하다', '지원하다 có nghĩa là ''ứng tuyển'', chỉ hành động nộp hồ sơ xin việc cho một vị trí cụ thể.', 'cbe7062f-f0c9-432d-befb-a6bbebe86b07', '2026-01-05T17:07:58.367Z', '2026-01-05T17:07:58.367Z'),
  ('94d1f867-242e-4acb-8aab-416770d91385', '회사의 채용 과정을 통과하여 뽑히는 것을 뭐라고 하나요?', 'Việc vượt qua quy trình tuyển dụng của công ty và được chọn gọi là gì?', ARRAY['불합격하다', '합격하다', '지원하다', '포기하다']::text[], '합격하다', '합격하다 có nghĩa là ''trúng tuyển'' hoặc ''đỗ'', chỉ việc được chấp nhận sau quá trình tuyển dụng.', '432f3287-63c9-42c6-9d69-9cd58cedfb57', '2026-01-05T17:07:58.367Z', '2026-01-05T17:07:58.367Z'),
  ('b9a1de75-f983-4ae2-8e6e-2fc6422d6f2c', '회사의 채용 과정에서 탈락하는 것을 뭐라고 하나요?', 'Việc bị loại trong quy trình tuyển dụng của công ty gọi là gì?', ARRAY['합격하다', '불합격하다', '지원하다', '포기하다']::text[], '불합격하다', '불합격하다 có nghĩa là ''trượt'' hoặc ''không trúng tuyển'', là từ trái nghĩa của 합격하다.', '11629853-f158-49c1-aa99-6c3dab06ca57', '2026-01-05T17:07:58.367Z', '2026-01-05T17:07:58.367Z'),
