INSERT INTO "Category" (id, name, description, icon, "order", "topikLevel", "groupId", "createdAt", "updatedAt") VALUES ('5717021a-167d-4bd2-87f4-9255796bd125', 'Xem phim', 'Từ vựng về rạp chiếu phim, thể loại phim và các thuật ngữ điện ảnh tại Hàn Quốc', '🎬', 1, 2, '3130b65f-2ad0-445c-9984-cd600b607a98', '2026-01-05T17:07:58.373Z', '2026-01-05T17:07:58.373Z'),
  ('91d4e8a2-cc8f-427e-b37a-1118baf02c39', 'Âm nhạc K-Pop', 'Từ vựng về ngành công nghiệp K-Pop, các nhóm nhạc và văn hóa fan tại Hàn Quốc', '🎤', 2, 2, '3130b65f-2ad0-445c-9984-cd600b607a98', '2026-01-05T17:07:58.373Z', '2026-01-05T17:07:58.373Z'),
  ('f0471031-f95d-4e7e-b81d-ec7eb5c00ddf', 'Du lịch trong nước', 'Từ vựng về lên kế hoạch, đặt chỗ, tham quan và trải nghiệm khi đi du lịch trong Hàn Quốc', '🗺️', 3, 2, '3130b65f-2ad0-445c-9984-cd600b607a98', '2026-01-05T17:07:58.373Z', '2026-01-05T17:07:58.373Z'),
  ('6219f8cf-8998-4532-9b72-14078f609f74', 'Các ngày lễ của Hàn Quốc', 'Từ vựng về các ngày lễ tết truyền thống, phong tục và hoạt động đặc trưng của Hàn Quốc', '🎎', 4, 2, '3130b65f-2ad0-445c-9984-cd600b607a98', '2026-01-05T17:07:58.373Z', '2026-01-05T17:07:58.373Z'),
  ('d93f0a8e-64c2-47ea-87dc-81af125f12aa', 'Mạng xã hội & Internet (SNS 및 인터넷)', 'Từ vựng về các hoạt động, thuật ngữ và xu hướng trên mạng xã hội và internet', '📱', 5, 2, '3130b65f-2ad0-445c-9984-cd600b607a98', '2026-01-05T17:07:58.373Z', '2026-01-05T17:07:58.373Z'),
  ('a92d0d8a-78eb-41ea-86bf-19e37075f3f1', 'Đọc sách & Văn học', 'Từ vựng về các thể loại văn học, quá trình xuất bản và hoạt động đọc sách tại Hàn Quốc', '📖', 6, 2, '3130b65f-2ad0-445c-9984-cd600b607a98', '2026-01-05T17:07:58.373Z', '2026-01-05T17:07:58.373Z'),
  ('c23b8efd-b6be-44cb-957a-d1fc46a15174', 'Biểu diễn nghệ thuật', 'Từ vựng về các loại hình biểu diễn nghệ thuật sân khấu và văn hóa xem kịch tại Hàn Quốc', '🎭', 7, 2, '3130b65f-2ad0-445c-9984-cd600b607a98', '2026-01-05T17:07:58.373Z', '2026-01-05T17:07:58.373Z'),
  ('646d138d-7f77-459b-8a07-a83e698b6fb3', 'Hoạt động ngoài trời', 'Từ vựng về các hoạt động thể thao, giải trí và vui chơi diễn ra ở không gian bên ngoài', '🏞️', 8, 2, '3130b65f-2ad0-445c-9984-cd600b607a98', '2026-01-05T17:07:58.373Z', '2026-01-05T17:07:58.373Z'),
  ('73df67dd-4f82-4603-88b7-683666666918', 'Du lịch nước ngoài', 'Từ vựng về thủ tục, chuẩn bị và trải nghiệm khi đi du lịch ra nước ngoài', '✈️', 9, 2, '3130b65f-2ad0-445c-9984-cd600b607a98', '2026-01-05T17:07:58.373Z', '2026-01-05T17:07:58.373Z'),
  ('4ae26ee4-d763-4fcd-a09e-6cf76356e9a2', 'Triển lãm & Bảo tàng (전시회 및 박물관)', 'Từ vựng về các địa điểm văn hóa, nghệ thuật và các hoạt động tham quan', '🖼️', 10, 2, '3130b65f-2ad0-445c-9984-cd600b607a98', '2026-01-05T17:07:58.373Z', '2026-01-05T17:07:58.373Z'),
  ('c30eeb44-99de-4731-9499-f642964747bf', 'Chơi game', 'Từ vựng về các thuật ngữ game, quán net và văn hóa game tại Hàn Quốc', '🎮', 11, 2, '3130b65f-2ad0-445c-9984-cd600b607a98', '2026-01-05T17:07:58.373Z', '2026-01-05T17:07:58.373Z'),
  ('b648b238-c3bf-4a27-abae-d4981408b0ef', 'Các lớp học văn hóa', 'Từ vựng về các lớp học, khóa học văn hóa, sở thích và kỹ năng tại các trung tâm cộng đồng', '🎨', 12, 2, '3130b65f-2ad0-445c-9984-cd600b607a98', '2026-01-05T17:07:58.373Z', '2026-01-05T17:07:58.373Z'),
