INSERT INTO "QuizQuestion" (id, question, "questionTranslation", options, "correctAnswer", explanation, "VocabularyId", "createdAt", "updatedAt") VALUES ('6a2078fd-63de-4b9b-8464-2c28c0795b93', '처음 만났을 때 무엇이라고 말하나요?', 'Khi gặp lần đầu người ta nói gì?', ARRAY['안녕히 가세요', '안녕하세요', '감사합니다', '죄송합니다']::text[], '안녕하세요', '안녕하세요 là lời chào phổ biến nhất trong tiếng Hàn, dùng khi gặp gỡ ai đó lần đầu hoặc trong tình huống trang trọng.', '75b27359-13d9-4441-8c29-3eac63cc646d', '2026-01-11T09:51:05.037Z', '2026-01-11T09:51:05.037Z'),
