INSERT INTO "LearningGroup" (id, name, icon, "order", "topikLevel", "createdAt", "updatedAt") VALUES ('13dbbc0e-8431-4974-91a7-688c5f2deca2', 'Gia đình & Cảm xúc', '👨‍👩‍👧‍👦', 2, 1, '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z');
INSERT INTO "Category" (id, name, description, icon, "order", "topikLevel", "groupId", "createdAt", "updatedAt") VALUES ('193bb13c-b7f8-4251-860d-f698f02c27a0', 'Thành viên gia đình', 'Từ vựng về các thành viên trong gia đình Hàn Quốc và cách xưng hô theo quan hệ', '👨‍👩‍👧‍👦', 1, 1, '13dbbc0e-8431-4974-91a7-688c5f2deca2', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('ebd23089-2b8d-4070-b22b-3505f0350b56', 'Cảm xúc cơ bản (기본 감정)', 'Từ vựng về các trạng thái cảm xúc, cảm giác và tâm trạng phổ biến', '😊', 2, 1, '13dbbc0e-8431-4974-91a7-688c5f2deca2', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z');
INSERT INTO "Vocabulary" (id, korean, hanja, vietnamese, pronunciation, example, "exampleTranslation", "categoryId", "createdAt", "updatedAt") VALUES ('5156dd32-b13f-4c0c-9933-12ab7d45bde1', '가족', '家族', 'Gia đình', 'gajok', '우리 가족은 모두 4명이에요.', 'Gia đình tôi có tất cả 4 người.', '193bb13c-b7f8-4251-860d-f698f02c27a0', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('6b139698-86ea-4edd-b060-e29ea0d8eb81', '할아버지', NULL, 'Ông', 'harabeoji', '할아버지는 시골에 사세요.', 'Ông tôi sống ở quê.', '193bb13c-b7f8-4251-860d-f698f02c27a0', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('0c56b1d9-a27b-4d25-a2d2-46accdfb1ea3', '할머니', NULL, 'Bà', 'halmeoni', '할머니는 요리를 잘하세요.', 'Bà tôi nấu ăn rất ngon.', '193bb13c-b7f8-4251-860d-f698f02c27a0', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('c44cc327-46fa-48a6-ba8f-7eeeb4794a98', '아버지 (아빠)', NULL, 'Bố', 'abeoji (appa)', '아버지는 회사에 가셨어요.', 'Bố đã đi đến công ty.', '193bb13c-b7f8-4251-860d-f698f02c27a0', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('ac87f71a-edfd-4d4c-bd56-294c29e3268d', '어머니 (엄마)', NULL, 'Mẹ', 'eomeoni (eomma)', '어머니는 시장에서 장을 봐요.', 'Mẹ đang đi chợ.', '193bb13c-b7f8-4251-860d-f698f02c27a0', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('33bae84b-2409-4975-9ee7-8c5868309d94', '형', '兄', 'Anh trai (em trai gọi)', 'hyeong', '형하고 같이 축구를 했어요.', 'Tôi đã chơi đá bóng cùng anh trai.', '193bb13c-b7f8-4251-860d-f698f02c27a0', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('b37b675e-e8a5-4bac-869d-dbe1b3f83fae', '오빠', NULL, 'Anh trai (em gái gọi)', 'oppa', '오빠가 선물을 사줬어요.', 'Anh trai đã mua quà cho tôi.', '193bb13c-b7f8-4251-860d-f698f02c27a0', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('348c9017-9a8d-4f06-b569-656d79e90eee', '누나', NULL, 'Chị gái (em trai gọi)', 'nuna', '누나는 대학교에 다녀요.', 'Chị gái tôi đang học đại học.', '193bb13c-b7f8-4251-860d-f698f02c27a0', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('a324e140-fee4-41e2-bb13-6dc3e0e89abf', '언니', NULL, 'Chị gái (em gái gọi)', 'eonni', '언니랑 쇼핑하러 갈 거예요.', 'Tôi sẽ đi mua sắm với chị gái.', '193bb13c-b7f8-4251-860d-f698f02c27a0', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('2b7a776a-53c1-47f5-9472-2d2484c3ba76', '동생', '同生', 'Em', 'dongsaeng', '제 동생은 아직 학생이에요.', 'Em tôi vẫn còn là học sinh.', '193bb13c-b7f8-4251-860d-f698f02c27a0', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('c44dc5f1-0348-4254-a655-dd695080e825', '기분이 좋다', '氣分이 좋다', 'Tâm trạng tốt, Vui vẻ', 'gibuni jota', '오늘 시험을 잘 봐서 기분이 좋아요.', 'Hôm nay thi tốt nên tâm trạng tôi rất vui.', 'ebd23089-2b8d-4070-b22b-3505f0350b56', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('2b5df14f-a4a8-4e80-bec7-9bb273793c41', '기분이 나쁘다', '氣分이 나쁘다', 'Tâm trạng không tốt, Khó chịu', 'gibuni nappeuda', '친구랑 싸워서 기분이 나빠요.', 'Tôi cãi nhau với bạn nên tâm trạng không tốt.', 'ebd23089-2b8d-4070-b22b-3505f0350b56', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('6e71069f-c0c3-4581-86a6-0f5a25b1797d', '기쁘다', '기쁘다', 'Vui mừng', 'gippeuda', '선물을 받아서 정말 기뻐요.', 'Tôi thật sự vui mừng vì nhận được quà.', 'ebd23089-2b8d-4070-b22b-3505f0350b56', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('f67e2a47-b222-4adc-83b3-76ffb6d04c9a', '슬프다', '슬프다', 'Buồn', 'seulpeuda', '슬픈 영화를 보고 울었어요.', 'Tôi đã xem một bộ phim buồn và khóc.', 'ebd23089-2b8d-4070-b22b-3505f0350b56', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('b8e875e2-5a53-4151-ac24-04f089011098', '화나다', '화나다', 'Tức giận', 'hwanada', '동생이 제 옷을 입어서 화났어요.', 'Em tôi mặc áo của tôi nên tôi đã tức giận.', 'ebd23089-2b8d-4070-b22b-3505f0350b56', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('c7daf1a2-2e7b-4d64-9a90-d4ea38b34a1c', '재미있다', '재미있다', 'Thú vị', 'jaemiitda', '한국 드라마는 아주 재미있어요.', 'Phim truyền hình Hàn Quốc rất thú vị.', 'ebd23089-2b8d-4070-b22b-3505f0350b56', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('f56e4f88-0a1d-4371-806e-ff8c4720a7d0', '행복하다', '幸福하다', 'Hạnh phúc', 'haengbokhada', '가족과 함께 있어서 행복해요.', 'Tôi hạnh phúc vì được ở bên gia đình.', 'ebd23089-2b8d-4070-b22b-3505f0350b56', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('25e62a3e-c4a3-4d90-9390-095f07127c8d', '피곤하다', '피곤하다', 'Mệt mỏi', 'pigonhada', '하루 종일 일해서 피곤해요.', 'Tôi mệt vì đã làm việc cả ngày.', 'ebd23089-2b8d-4070-b22b-3505f0350b56', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('9227f6d6-346a-4915-a00b-4e10b0278b50', '졸리다', '졸리다', 'Buồn ngủ', 'jollida', '너무 졸려서 자고 싶어요.', 'Tôi buồn ngủ quá nên muốn đi ngủ.', 'ebd23089-2b8d-4070-b22b-3505f0350b56', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('b9c77041-c3e9-407d-8818-0a96492f1407', '아프다', '아프다', 'Đau, ốm', 'apeuda', '머리가 아파서 약을 먹었어요.', 'Tôi bị đau đầu nên đã uống thuốc.', 'ebd23089-2b8d-4070-b22b-3505f0350b56', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z');
INSERT INTO "QuizQuestion" (id, question, "questionTranslation", options, "correctAnswer", explanation, "VocabularyId", "createdAt", "updatedAt") VALUES ('28a189d0-b436-4c21-befe-34209e4ea01e', '휴가 때는 보통 ______와 함께 시간을 보내요.', 'Vào kỳ nghỉ, tôi thường dành thời gian với ______.', ARRAY['친구', '가족', '동료', '이웃']::text[], '가족', '가족 có nghĩa là ''gia đình'', chỉ những người có quan hệ huyết thống hoặc hôn nhân sống chung.', '5156dd32-b13f-4c0c-9933-12ab7d45bde1', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('ddb6885c-f2cf-4a05-884c-b00c76be5b23', '______ 사진을 찍어서 액자에 넣어 두었어요.', 'Tôi đã chụp ảnh ______ và đặt vào khung ảnh.', ARRAY['가족', '친구', '동물', '풍경']::text[], '가족', '가족 thường được dùng trong ngữ cảnh về những kỷ niệm, sinh hoạt chung của các thành viên trong nhà.', '5156dd32-b13f-4c0c-9933-12ab7d45bde1', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('daf4df50-0ec7-4c0f-92bf-c1c3089079cd', '______는 어릴 때 많은 이야기를 들려주셨어요.', '______ đã kể cho tôi nghe rất nhiều câu chuyện khi tôi còn nhỏ.', ARRAY['할아버지', '어머니', '동생', '삼촌']::text[], '할아버지', '할아버지 có nghĩa là ''ông'', dùng để gọi ông nội hoặc ông ngoại một cách kính trọng.', '6b139698-86ea-4edd-b060-e29ea0d8eb81', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('c60aaa6a-85b8-47cb-86f1-cfc166611025', '주말에 ______ 댁에 방문하는 것이 저의 일과예요.', 'Vào cuối tuần, việc đến thăm nhà ______ là thói quen của tôi.', ARRAY['할아버지', '친구', '선생님', '사장님']::text[], '할아버지', '할아버지 thường đi kèm với các từ thể hiện sự kính trọng như ''댁'' (nhà/quý ngài) trong văn nói lịch sự.', '6b139698-86ea-4edd-b060-e29ea0d8eb81', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('b11f97ac-3d81-4f35-9d92-5c606914fa47', '______가 만들어 주신 김치는 정말 맛있어요.', 'Kimchi mà ______ làm cho thực sự rất ngon.', ARRAY['할머니', '아버지', '형', '동생']::text[], '할머니', '할머니 có nghĩa là ''bà'', dùng để gọi bà nội hoặc bà ngoại với thái độ yêu thương, kính trọng.', '0c56b1d9-a27b-4d25-a2d2-46accdfb1ea3', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('220dde2b-5193-4b24-b80a-019fcc629810', '어린 시절, ______ 품에서 자주 잠이 들었어요.', 'Hồi nhỏ, tôi thường ngủ thiếp đi trong vòng tay của ______.', ARRAY['할머니', '아빠', '언니', '고모']::text[], '할머니', '할머니 thường gắn liền với hình ảnh ấm áp, chăm sóc và những kỷ niệm tuổi thơ.', '0c56b1d9-a27b-4d25-a2d2-46accdfb1ea3', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('fbdd42ca-eb6d-43d4-8e15-ab31cba1cf6f', '______께서는 항상 가족을 위해 열심히 일하셨어요.', '______ luôn làm việc chăm chỉ vì gia đình.', ARRAY['아버지', '할아버지', '형', '삼촌']::text[], '아버지', '아버지 là cách gọi trang trọng, kính ngữ đối với ''bố''. ''아빠'' là cách gọi thân mật, gần gũi hơn.', 'c44cc327-46fa-48a6-ba8f-7eeeb4794a98', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('2cf748d3-bc31-4b8a-bb26-7d9c38836b8e', '저는 어릴 때 ______와 많이 등산을 다녔어요.', 'Hồi nhỏ, tôi đã đi leo núi rất nhiều cùng với ______.', ARRAY['아빠', '엄마', '할머니', '이모']::text[], '아빠', '아빠 là cách gọi thân mật, tự nhiên của con cái đối với cha mình, thường dùng trong sinh hoạt hàng ngày.', 'c44cc327-46fa-48a6-ba8f-7eeeb4794a98', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('dd2d942a-e32c-4f84-912e-862212a381ca', '______께서는 우리에게 중요한 가르침을 주셨어요.', '______ đã dạy cho chúng tôi những bài học quan trọng.', ARRAY['어머니', '아버지', '할아버지', '고모']::text[], '어머니', '어머니 là cách gọi trang trọng, kính ngữ đối với ''mẹ''. ''엄마'' là cách gọi thân mật.', 'ac87f71a-edfd-4d4c-bd56-294c29e3268d', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('c4853a4b-fadb-46ce-ac5b-01aa5f7bdd28', '아플 때마다 ______가 해 주신 죽이 그리워요.', 'Mỗi khi ốm, tôi lại nhớ món cháo mà ______ nấu cho.', ARRAY['엄마', '아빠', '누나', '할아버지']::text[], '엄마', '엄마 là cách gọi đầy yêu thương, gần gũi, thường gắn với sự chăm sóc, vỗ về hàng ngày.', 'ac87f71a-edfd-4d4c-bd56-294c29e3268d', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('1964ab92-93c3-4ecf-8880-fbdb3de984e6', '저는 ______의 조언을 항상 소중히 들어요.', 'Tôi luôn trân trọng lời khuyên của ______.', ARRAY['형', '누나', '동생', '삼촌']::text[], '형', '형 là cách em trai gọi anh trai của mình. Nó thể hiện sự tôn trọng từ người nhỏ tuổi hơn.', '33bae84b-2409-4975-9ee7-8c5868309d94', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('2db63fba-0ef4-49f1-b613-c45af827845d', '______가 군대에 가신 후로 집이 많이 조용해졌어요.', 'Kể từ khi ______ đi nghĩa vụ quân sự, nhà tôi trở nên yên tĩnh hẳn.', ARRAY['형', '오빠', '언니', '이모']::text[], '형', 'Người nói là em trai, nên dùng ''형'' để gọi anh trai của mình.', '33bae84b-2409-4975-9ee7-8c5868309d94', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('6f9d8f22-6f3d-4860-a449-46d63cdc9178', '______는 저를 많이 챙겨주는 편이에요.', '______ là người hay chăm sóc tôi.', ARRAY['오빠', '형', '언니', '남동생']::text[], '오빠', '오빠 là cách em gái gọi anh trai của mình. Nó mang sắc thái thân mật, gần gũi.', 'b37b675e-e8a5-4bac-869d-dbe1b3f83fae', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('6dbd0a8b-cedf-4ae5-955b-3d68a576e6ab', '운전을 못 하는 저를 대신해 ______가 운전을 해 주셨어요.', 'Thay vì tôi không biết lái xe, ______ đã lái xe giúp.', ARRAY['오빠', '할아버지', '여동생', '이모부']::text[], '오빠', 'Người nói là em gái, đang nói về anh trai ruột của mình nên dùng ''오빠''.', 'b37b675e-e8a5-4bac-869d-dbe1b3f83fae', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('b12f6033-2670-46c9-b55c-7560ca12a62b', '______ 덕분에 어려운 문제를 해결할 수 있었어요.', 'Nhờ có ______ mà tôi đã có thể giải quyết được vấn đề khó.', ARRAY['누나', '오빠', '할머니', '고모']::text[], '누나', '누나 là cách em trai gọi chị gái của mình, thể hiện sự tôn trọng và thân thiết.', '348c9017-9a8d-4f06-b569-656d79e90eee', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('c14227b4-3576-4833-8079-d9adf7e5cd14', '어릴 때는 ______가 제 숙제를 도와주곤 했어요.', 'Hồi nhỏ, ______ thường giúp tôi làm bài tập.', ARRAY['누나', '언니', '여동생', '이모']::text[], '누나', 'Người nói là em trai, đang nhớ về chị gái ruột của mình nên dùng ''누나''.', '348c9017-9a8d-4f06-b569-656d79e90eee', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('63d86105-3fe2-45cc-97bc-034361bfae7c', '비밀을 가장 많이 공유하는 사람은 저의 ______예요.', 'Người mà tôi chia sẻ bí mật nhiều nhất là ______ của tôi.', ARRAY['언니', '오빠', '남동생', '아저씨']::text[], '언니', '언니 là cách em gái gọi chị gái của mình. Mối quan hệ này thường rất gần gũi và tin tưởng.', 'a324e140-fee4-41e2-bb13-6dc3e0e89abf', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('9f9c8bf3-7413-4a8b-a66e-e39be9e7e122', '______의 옷장은 저에게는 패션 잡지와 같아요.', 'Tủ quần áo của ______ đối với tôi giống như một cuốn tạp chí thời trang vậy.', ARRAY['언니', '누나', '형', '삼촌']::text[], '언니', 'Người nói là em gái, đang nói về chị gái ruột của mình nên dùng ''언니''.', 'a324e140-fee4-41e2-bb13-6dc3e0e89abf', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('549a8563-5f8d-46c0-8d6f-4f90e389d319', '______를 학교에 데려다 주는 것이 저의 아침 일과예요.', 'Việc đưa ______ đến trường là thói quen buổi sáng của tôi.', ARRAY['동생', '할아버지', '아버지', '선생님']::text[], '동생', '동생 là từ chung chỉ ''em'', dùng cho cả em trai và em gái, không phân biệt giới tính của người nói.', '2b7a776a-53c1-47f5-9472-2d2484c3ba76', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('3a1d296a-0f6a-48b6-b978-a226a6c84a51', '우리 집에서 가장 장난을 잘 치는 사람은 ______입니다.', 'Người nghịch ngợm nhất nhà tôi là ______.', ARRAY['동생', '아버지', '할머니', '고모']::text[], '동생', '동생 thường được dùng khi nói chung về một người em trong gia đình, không cần chỉ rõ là ''남동생'' (em trai) hay ''여동생'' (em gái) nếu ngữ cảnh đã rõ.', '2b7a776a-53c1-47f5-9472-2d2484c3ba76', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('f9845f0c-eabf-480b-8e46-1678b9f166b3', '날씨가 맑아서 ______.', 'Thời tiết đẹp nên tâm trạng rất tốt.', ARRAY['기분이 좋아요', '피곤해요', '화나요', '졸려요']::text[], '기분이 좋아요', '''기분이 좋다'' là cụm từ cố định diễn tả cảm giác vui vẻ, thoải mái trong tâm trạng, thường do tác động bên ngoài như thời tiết đẹp.', 'c44dc5f1-0348-4254-a655-dd695080e825', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('a88d3e04-76ed-4d70-b5ed-8e4397bd822b', '______ 노래를 들으세요.', 'Hãy nghe bài hát khiến tâm trạng tốt lên.', ARRAY['기분이 나쁜', '기분이 좋아지는', '피곤한', '화나는']::text[], '기분이 좋아지는', '''기분이 좋아지는'' là dạng định ngữ của động từ ''기분이 좋아지다'' (trở nên vui vẻ), bổ nghĩa cho danh từ ''노래''.', 'c44dc5f1-0348-4254-a655-dd695080e825', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('2ecb2109-25a6-4099-be16-7c186b3f1f53', '생각만 해도 ______.', 'Chỉ nghĩ đến thôi cũng thấy tâm trạng tệ đi.', ARRAY['기쁘네요', '기분이 나빠져요', '재미있네요', '행복하네요']::text[], '기분이 나빠져요', '''기분이 나빠지다'' là trở nên khó chịu. Cụm từ ''기분이 나쁘다'' thường dùng khi có lý do cụ thể làm hỏng tâm trạng.', '2b5df14f-a4a8-4e80-bec7-9bb273793c41', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('088b4e13-457b-4a0b-8813-1b3a7281366e', '누군가 ______ 농담을 했어요.', 'Ai đó đã nói một trò đùa khiến người khác khó chịu.', ARRAY['기분이 좋은', '재미있는', '기분이 나쁜', '행복한']::text[], '기분이 나쁜', '''기분이 나쁜'' là dạng định ngữ, bổ nghĩa cho ''농담'', chỉ câu chuyện khiến người nghe cảm thấy không vui.', '2b5df14f-a4a8-4e80-bec7-9bb273793c41', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('cd5ba77c-2023-45ee-99a2-b35b7bef457d', '합격 소식을 듣고 ______.', 'Nghe tin đỗ, tôi vô cùng vui mừng.', ARRAY['화났어요', '기뻤어요', '슬펐어요', '피곤했어요']::text[], '기뻤어요', '''기쁘다'' diễn tả niềm vui mừng, hân hoan trước một tin tốt hoặc sự kiện tích cực. Dạng quá khứ là ''기뻤다''.', '6e71069f-c0c3-4581-86a6-0f5a25b1797d', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('a2197c17-d1c0-453f-9e28-beac0b07f6e1', '______ 소식을 전해 드립니다.', 'Tôi xin thông báo một tin vui.', ARRAY['기쁜', '슬픈', '화나는', '피곤한']::text[], '기쁜', '''기쁜'' là dạng định ngữ, bổ nghĩa cho ''소식'' (tin tức), nghĩa là ''tin vui''.', '6e71069f-c0c3-4581-86a6-0f5a25b1797d', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('9947afae-3e41-4e87-a2e2-1b5eaf8a1475', '이별해서 마음이 ______.', 'Vì chia tay nên trong lòng rất buồn.', ARRAY['행복해요', '슬퍼요', '화나요', '재미있어요']::text[], '슬퍼요', '''슬프다'' diễn tả nỗi buồn sâu sắc, thường do mất mát, chia ly hoặc những chuyện đau lòng.', 'f67e2a47-b222-4adc-83b3-76ffb6d04c9a', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('a3aee61b-35f5-4ab8-95cf-369c9f174a95', '그 이야기는 ______ 결말이에요.', 'Câu chuyện đó có một kết thúc buồn.', ARRAY['재미있는', '화나는', '슬픈', '기쁜']::text[], '슬픈', '''슬픈'' là dạng định ngữ, bổ nghĩa cho ''결말'' (kết thúc). ''슬픈 결말'' nghĩa là ''kết thúc buồn''.', 'f67e2a47-b222-4adc-83b3-76ffb6d04c9a', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('868903b9-d641-4d16-b671-cf16c618f24b', '약속을 안 지켜서 ______.', 'Vì không giữ lời hứa nên tôi đã tức giận.', ARRAY['기뻤어요', '화났어요', '슬펐어요', '재미있었어요']::text[], '화났어요', '''화나다'' diễn tả cảm giác tức giận, bực bội khi có chuyện không như ý hoặc ai đó làm sai.', 'b8e875e2-5a53-4151-ac24-04f089011098', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('d11bed04-7ce3-481f-aa93-53a7a06004fe', '______ 목소리로 말하지 마세요.', 'Đừng nói với giọng điệu tức giận.', ARRAY['작은', '화난', '기쁜', '재미있는']::text[], '화난', '''화난'' là dạng định ngữ, bổ nghĩa cho ''목소리'' (giọng nói). ''화난 목소리'' là ''giọng nói giận dữ''.', 'b8e875e2-5a53-4151-ac24-04f089011098', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('8fa91e44-49e6-4ae8-8426-5fb26b2532a9', '이 게임 정말 ______!', 'Trò chơi này thật sự rất thú vị!', ARRAY['재미있어요', '피곤해요', '아파요', '졸려요']::text[], '재미있어요', '''재미있다'' thường dùng để đánh giá một sự vật, sự việc (phim, sách, trò chơi...) mang lại cảm giác vui vẻ, hứng thú.', 'c7daf1a2-2e7b-4d64-9a90-d4ea38b34a1c', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('5cf469e6-18ae-48a1-806a-cee1c14879bd', '______ 책을 추천해 주세요.', 'Hãy giới thiệu cho tôi một quyển sách thú vị.', ARRAY['비싼', '재미있는', '무거운', '어려운']::text[], '재미있는', '''재미있는'' là dạng định ngữ, bổ nghĩa cho ''책'' (sách). ''재미있는 책'' là ''sách hay/sách thú vị''.', 'c7daf1a2-2e7b-4d64-9a90-d4ea38b34a1c', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('bdbae38a-22b1-403c-8852-e5dbdc1c9d9b', '건강하고 ______ 살고 싶어요.', 'Tôi muốn sống khỏe mạnh và hạnh phúc.', ARRAY['행복하게', '슬프게', '화나게', '피곤하게']::text[], '행복하게', '''행복하게'' là dạng phó từ của ''행복하다'', nghĩa là ''một cách hạnh phúc''. Đây là cảm giác mãn nguyện, vui sướng trọn vẹn.', 'f56e4f88-0a1d-4371-806e-ff8c4720a7d0', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('6b1c658a-73c3-4b93-b024-63cbc592f387', '세상에서 제일 ______ 순간이에요.', 'Đó là khoảnh khắc hạnh phúc nhất trên đời.', ARRAY['행복한', '화난', '지루한', '짜증나는']::text[], '행복한', '''행복한'' là dạng định ngữ, bổ nghĩa cho ''순간'' (khoảnh khắc). ''행복한 순간'' là ''khoảnh khắc hạnh phúc''.', 'f56e4f88-0a1d-4371-806e-ff8c4720a7d0', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('44a30995-ad32-483d-89df-5b1877b7842d', '다리가 아파서 ______.', 'Chân đau nên tôi thấy mệt mỏi.', ARRAY['배고파요', '피곤해요', '졸려요', '화나요']::text[], '피곤해요', '''피곤하다'' diễn tả trạng thái mệt mỏi về thể chất (và đôi khi tinh thần) sau khi hoạt động nhiều.', '25e62a3e-c4a3-4d90-9390-095f07127c8d', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('656acfff-ed99-47a7-826f-96f29ffda7eb', '______ 하루였어요.', 'Thật là một ngày mệt mỏi.', ARRAY['즐거운', '피곤한', '기쁜', '재미있는']::text[], '피곤한', '''피곤한'' là dạng định ngữ, bổ nghĩa cho ''하루'' (một ngày). ''피곤한 하루'' là ''ngày mệt mỏi''.', '25e62a3e-c4a3-4d90-9390-095f07127c8d', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('b08e402c-689a-4cd6-84dd-59c23bf5044b', '잠을 못 자서 ______.', 'Vì không ngủ được nên tôi buồn ngủ.', ARRAY['배고파요', '피곤해요', '졸려요', '아파요']::text[], '졸려요', '''졸리다'' diễn tả cảm giác buồn ngủ, mắt díu lại và muốn đi ngủ. Nó khác với ''피곤하다'' (mệt mỏi chung).', '9227f6d6-346a-4915-a00b-4e10b0278b50', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('99a70e86-17e8-42fe-9ef0-80175bc7d934', '______ 눈을 비비고 있어요.', 'Tôi đang dụi đôi mắt buồn ngủ.', ARRAY['행복한', '예쁜', '졸린', '아픈']::text[], '졸린', '''졸린'' là dạng định ngữ, bổ nghĩa cho ''눈'' (mắt). ''졸린 눈'' là ''đôi mắt buồn ngủ''.', '9227f6d6-346a-4915-a00b-4e10b0278b50', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('e26fd950-e55c-405e-b9ae-d79f2834ec96', '배가 ______ 병원에 갔어요.', 'Vì đau bụng nên tôi đã đi bệnh viện.', ARRAY['고파서', '아파서', '부러서', '시려서']::text[], '아파서', '''아프다'' diễn tả cảm giác đau đớn ở một bộ phận cơ thể hoặc trạng thái bị ốm, bệnh.', 'b9c77041-c3e9-407d-8818-0a96492f1407', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z'),
  ('3b658bf4-379d-4125-ac48-d686928f93a1', '______ 곳을 말씀해 주세요.', 'Hãy nói cho tôi biết chỗ nào bị đau.', ARRAY['아픈', '큰', '작은', '예쁜']::text[], '아픈', '''아픈'' là dạng định ngữ, bổ nghĩa cho ''곳'' (chỗ, vị trí). ''아픈 곳'' là ''chỗ bị đau''.', 'b9c77041-c3e9-407d-8818-0a96492f1407', '2026-01-11T09:51:05.040Z', '2026-01-11T09:51:05.040Z');
