INSERT INTO "QuizQuestion" (id, question, "questionTranslation", options, "correctAnswer", explanation, "VocabularyId", "createdAt", "updatedAt") VALUES ('4ac1c0a3-65ca-4d95-bfab-9a9fb3b83d5b', '______ 공기가 가장 상쾌해요.', 'Không khí ______ trong lành nhất.', ARRAY['아침', '점심', '저녁', '밤']::text[], '아침', '아침 có nghĩa là ''buổi sáng'', thường chỉ khoảng thời gian từ lúc mặt trời mọc đến trước trưa.', '07a19a89-0d9a-4232-82d6-57c390b81937', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('57864415-8703-420e-95ea-8cb5f2d71153', '______을 먹지 않고 학교에 가면 배가 고파요.', 'Nếu không ăn ______ mà đi học thì sẽ đói bụng.', ARRAY['아침', '점심', '저녁', '밤']::text[], '아침', '아침 cũng có thể được dùng để chỉ ''bữa sáng'' (아침식사), đặc biệt khi đi với động từ ''먹다'' (ăn).', '07a19a89-0d9a-4232-82d6-57c390b81937', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('c4759cb5-3aa5-4db4-a1fe-8718a6c24b87', '알람 시계가 울리면 ______.', 'Khi đồng hồ báo thức kêu thì tôi ______.', ARRAY['일어나요', '자요', '먹어요', '읽어요']::text[], '일어나요', '일어나다 có nghĩa là ''thức dậy'', chỉ hành động chuyển từ trạng thái ngủ sang trạng thái thức.', '33e95485-956b-4117-9914-d6fbc59ed65e', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('44484969-dd04-42ac-8a8c-c04758c83ff6', '늦잠을 자서 9시에 ______.', 'Vì ngủ nướng nên tôi đã ______ lúc 9 giờ.', ARRAY['일어났어요', '잤어요', '먹었어요', '웃었어요']::text[], '일어났어요', '일어나다 thường được dùng để miêu tả thời điểm bắt đầu một ngày mới sau giấc ngủ.', '33e95485-956b-4117-9914-d6fbc59ed65e', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('3c02f29a-bb9e-408c-a9ca-c1394350503f', '잠에서 깨어나면 먼저 ______.', 'Sau khi tỉnh giấc, đầu tiên tôi ______.', ARRAY['세수해요', '먹어요', '읽어요', '달려요']::text[], '세수해요', '세수하다 có nghĩa là ''rửa mặt'', thường là một trong những hoạt động đầu tiên sau khi thức dậy.', '31c8012b-8301-493f-854b-a3bd190ddabb', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('fcf1acd0-a154-48b4-896f-e8a082f93054', '______ 후에 상쾌한 느낌이 들어요.', 'Sau khi ______, tôi cảm thấy sảng khoái.', ARRAY['세수하고', '먹고', '자고', '쓰고']::text[], '세수하고', '세수하다 không chỉ làm sạch mặt mà còn giúp tỉnh táo và bắt đầu ngày mới.', '31c8012b-8301-493f-854b-a3bd190ddabb', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('a0523a8d-d459-4c1f-b8a2-d092d3e1d7b3', '하루에 세 번 ______ 위생에 좋아요.', 'Một ngày ______ ba lần thì tốt cho vệ sinh răng miệng.', ARRAY['이를 닦는 것이', '세수하는 것이', '머리 감는 것이', '손 씻는 것이']::text[], '이를 닦는 것이', '이를 닦다 có nghĩa là ''đánh răng'', một hoạt động vệ sinh cá nhân quan trọng.', '57b4fe0e-9b8d-4622-bfef-434be09609eb', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('01617757-c5fe-4f84-866f-83a098ec212e', '______ 칫솔과 치약이 필요해요.', 'Để ______ cần có bàn chải đánh răng và kem đánh răng.', ARRAY['이를 닦으려면', '세수하려면', '머리 감으려면', '씻으려면']::text[], '이를 닦으려면', '이를 닦다 luôn đi kèm với dụng cụ là 칫솔 (bàn chải đánh răng) và 치약 (kem đánh răng).', '57b4fe0e-9b8d-4622-bfef-434be09609eb', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('6a19cbce-a832-4b99-8995-1976fa877365', '______은 하루의 에너지를 공급해요.', 'Việc ______ cung cấp năng lượng cho cả ngày.', ARRAY['아침을 먹는 것이', '점심을 먹는 것이', '저녁을 먹는 것이', '간식을 먹는 것이']::text[], '아침을 먹는 것이', '아침을 먹다 có nghĩa là ''ăn sáng'', bữa ăn đầu tiên trong ngày sau một đêm dài.', 'c7923637-cabb-4fdb-b949-ee0a07ae7a85', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('9011e3dd-9508-40cf-abee-4f0cc45384bb', '______ 시간이 없어서 그냥 커피만 마셨어요.', 'Vì không có thời gian ______ nên tôi chỉ uống cà phê thôi.', ARRAY['아침을 먹을', '점심을 먹을', '저녁을 먹을', '간식을 먹을']::text[], '아침을 먹을', '아침을 먹다 thường bị bỏ qua khi mọi người vội vã đi làm hoặc đi học.', 'c7923637-cabb-4fdb-b949-ee0a07ae7a85', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
  ('6d93b480-540c-4e53-bc7e-c8829cd78c7d', '날씨에 맞는 ______ 건강에 중요해요.', 'Việc ______ phù hợp với thời tiết rất quan trọng cho sức khỏe.', ARRAY['옷을 입는 것이', '신발을 신는 것이', '모자를 쓰는 것이', '장갑을 끼는 것이']::text[], '옷을 입는 것이', '옷을 입다 có nghĩa là ''mặc quần áo'', một hoạt động chuẩn bị cơ bản trước khi ra ngoài.', '8e79fa5c-7c34-4144-b68a-a2c1f186c8c1', '2026-01-05T17:07:58.357Z', '2026-01-05T17:07:58.357Z'),
