INSERT INTO "QuizQuestion" (id, question, "questionTranslation", options, "correctAnswer", explanation, "VocabularyId", "createdAt", "updatedAt") VALUES ('837787b5-1760-4986-916c-74c3a2231075', '______ 공기가 가장 상쾌해요.', 'Không khí ______ trong lành nhất.', ARRAY['아침', '점심', '저녁', '밤']::text[], '아침', '아침 có nghĩa là ''buổi sáng'', thường chỉ khoảng thời gian từ lúc mặt trời mọc đến trước trưa.', 'cc7f434a-702a-47b3-9183-3c0fbcd3ed38', '2026-01-11T09:51:05.043Z', '2026-01-11T09:51:05.043Z'),
  ('2e35b3da-e235-48a2-a463-d5927302b98c', '______을 먹지 않고 학교에 가면 배가 고파요.', 'Nếu không ăn ______ mà đi học thì sẽ đói bụng.', ARRAY['아침', '점심', '저녁', '밤']::text[], '아침', '아침 cũng có thể được dùng để chỉ ''bữa sáng'' (아침식사), đặc biệt khi đi với động từ ''먹다'' (ăn).', 'cc7f434a-702a-47b3-9183-3c0fbcd3ed38', '2026-01-11T09:51:05.043Z', '2026-01-11T09:51:05.043Z'),
  ('92896623-1566-40de-ba2c-c6f4424d36c3', '알람 시계가 울리면 ______.', 'Khi đồng hồ báo thức kêu thì tôi ______.', ARRAY['일어나요', '자요', '먹어요', '읽어요']::text[], '일어나요', '일어나다 có nghĩa là ''thức dậy'', chỉ hành động chuyển từ trạng thái ngủ sang trạng thái thức.', '0941c7f6-167f-4e94-86a3-84588adee20d', '2026-01-11T09:51:05.043Z', '2026-01-11T09:51:05.043Z'),
  ('72551d35-800e-422c-b5b5-23ded591bce1', '늦잠을 자서 9시에 ______.', 'Vì ngủ nướng nên tôi đã ______ lúc 9 giờ.', ARRAY['일어났어요', '잤어요', '먹었어요', '웃었어요']::text[], '일어났어요', '일어나다 thường được dùng để miêu tả thời điểm bắt đầu một ngày mới sau giấc ngủ.', '0941c7f6-167f-4e94-86a3-84588adee20d', '2026-01-11T09:51:05.043Z', '2026-01-11T09:51:05.043Z'),
  ('ed1aacfc-a6f7-44c6-9add-4bd0e635537f', '잠에서 깨어나면 먼저 ______.', 'Sau khi tỉnh giấc, đầu tiên tôi ______.', ARRAY['세수해요', '먹어요', '읽어요', '달려요']::text[], '세수해요', '세수하다 có nghĩa là ''rửa mặt'', thường là một trong những hoạt động đầu tiên sau khi thức dậy.', '3081043f-26b3-462c-8232-793f8b0d2041', '2026-01-11T09:51:05.043Z', '2026-01-11T09:51:05.043Z'),
  ('cd3b09da-0a8c-4e01-9a59-e8f979323899', '______ 후에 상쾌한 느낌이 들어요.', 'Sau khi ______, tôi cảm thấy sảng khoái.', ARRAY['세수하고', '먹고', '자고', '쓰고']::text[], '세수하고', '세수하다 không chỉ làm sạch mặt mà còn giúp tỉnh táo và bắt đầu ngày mới.', '3081043f-26b3-462c-8232-793f8b0d2041', '2026-01-11T09:51:05.043Z', '2026-01-11T09:51:05.043Z'),
  ('6f573b4c-0fca-4480-9fc9-1bef6a99486e', '하루에 세 번 ______ 위생에 좋아요.', 'Một ngày ______ ba lần thì tốt cho vệ sinh răng miệng.', ARRAY['이를 닦는 것이', '세수하는 것이', '머리 감는 것이', '손 씻는 것이']::text[], '이를 닦는 것이', '이를 닦다 có nghĩa là ''đánh răng'', một hoạt động vệ sinh cá nhân quan trọng.', '84f56382-52ec-4f47-84a2-587bc14190b7', '2026-01-11T09:51:05.043Z', '2026-01-11T09:51:05.043Z'),
  ('77085e20-1d14-46b2-b74e-837453c5f6b7', '______ 칫솔과 치약이 필요해요.', 'Để ______ cần có bàn chải đánh răng và kem đánh răng.', ARRAY['이를 닦으려면', '세수하려면', '머리 감으려면', '씻으려면']::text[], '이를 닦으려면', '이를 닦다 luôn đi kèm với dụng cụ là 칫솔 (bàn chải đánh răng) và 치약 (kem đánh răng).', '84f56382-52ec-4f47-84a2-587bc14190b7', '2026-01-11T09:51:05.043Z', '2026-01-11T09:51:05.043Z'),
  ('8eeb9be9-5b4e-48be-8a61-4f2bb374d9e6', '______은 하루의 에너지를 공급해요.', 'Việc ______ cung cấp năng lượng cho cả ngày.', ARRAY['아침을 먹는 것이', '점심을 먹는 것이', '저녁을 먹는 것이', '간식을 먹는 것이']::text[], '아침을 먹는 것이', '아침을 먹다 có nghĩa là ''ăn sáng'', bữa ăn đầu tiên trong ngày sau một đêm dài.', 'ebeba5d3-4779-4ce8-830a-4ada88dac11c', '2026-01-11T09:51:05.043Z', '2026-01-11T09:51:05.043Z'),
  ('222b018c-a7c9-4926-99b5-f6cddb2647f1', '______ 시간이 없어서 그냥 커피만 마셨어요.', 'Vì không có thời gian ______ nên tôi chỉ uống cà phê thôi.', ARRAY['아침을 먹을', '점심을 먹을', '저녁을 먹을', '간식을 먹을']::text[], '아침을 먹을', '아침을 먹다 thường bị bỏ qua khi mọi người vội vã đi làm hoặc đi học.', 'ebeba5d3-4779-4ce8-830a-4ada88dac11c', '2026-01-11T09:51:05.043Z', '2026-01-11T09:51:05.043Z'),
  ('ab71c34a-5d40-462e-8032-b76f7b1c07c8', '날씨에 맞는 ______ 건강에 중요해요.', 'Việc ______ phù hợp với thời tiết rất quan trọng cho sức khỏe.', ARRAY['옷을 입는 것이', '신발을 신는 것이', '모자를 쓰는 것이', '장갑을 끼는 것이']::text[], '옷을 입는 것이', '옷을 입다 có nghĩa là ''mặc quần áo'', một hoạt động chuẩn bị cơ bản trước khi ra ngoài.', '0a5d46c8-7474-4263-b0c1-c17819bea59a', '2026-01-11T09:51:05.043Z', '2026-01-11T09:51:05.043Z'),
