INSERT INTO "QuizQuestion" (id, question, "questionTranslation", options, "correctAnswer", explanation, "VocabularyId", "createdAt", "updatedAt") VALUES ('f9ba4c8b-45f7-4e39-b421-90f2599e7a01', '한국인들의 주식은 ______이에요.', 'Lương thực chính của người Hàn Quốc là ______.', ARRAY['밥', '빵', '국수', '고기']::text[], '밥', '밥 không chỉ có nghĩa là ''cơm'' mà còn thường được dùng để chỉ ''bữa ăn'' nói chung trong tiếng Hàn.', '25b396fc-941f-4044-9225-272dc3463c7b', '2026-01-11T09:51:05.041Z', '2026-01-11T09:51:05.041Z'),
  ('e8105830-fdfe-441e-b26b-47a2eef3535a', '"______ 먹자"는 식사하자는 초대의 의미예요.', '"______ 먹자" có nghĩa là mời nhau đi ăn cơm.', ARRAY['밥', '김치', '라면', '과일']::text[], '밥', 'Trong văn hóa Hàn Quốc, ''밥 먹자'' là cách mời ăn thân mật và phổ biến, thể hiện sự quan tâm.', '25b396fc-941f-4044-9225-272dc3463c7b', '2026-01-11T09:51:05.041Z', '2026-01-11T09:51:05.041Z'),
  ('f80a620d-cbf7-4b36-bcef-8ee7876b233a', '아픈 후에 ______을 많이 먹어야 해요.', 'Sau khi ốm nên ăn nhiều ______.', ARRAY['국', '김치', '과자', '아이스크림']::text[], '국', '국 (canh) thường được coi là món ăn bổ dưỡng và dễ tiêu hóa, phù hợp khi không khỏe.', 'eb34e4e5-c3b2-473a-8b61-b3f1903524f1', '2026-01-11T09:51:05.041Z', '2026-01-11T09:51:05.041Z'),
  ('283c3a68-ddb6-487d-a091-263e1aa952e3', '한국 식사에는 보통 ______이 함께 나와요.', 'Trong bữa ăn Hàn Quốc thường có kèm theo ______.', ARRAY['국', '케이크', '피자', '햄버거']::text[], '국', '국 là phần không thể thiếu trong bữa cơm truyền thống Hàn Quốc, thường được dùng cùng với 밥 (cơm).', 'eb34e4e5-c3b2-473a-8b61-b3f1903524f1', '2026-01-11T09:51:05.041Z', '2026-01-11T09:51:05.041Z'),
  ('8fd1b524-3462-4ecb-b533-dc49dcf7cb74', '______는 한국의 대표적인 발효 음식이에요.', '______ là món ăn lên men tiêu biểu của Hàn Quốc.', ARRAY['김치', '피자', '스시', '타코']::text[], '김치', '김치 là món ăn truyền thống làm từ rau cải thảo muối chua với gia vị, có lịch sử hàng nghìn năm.', '77781e9b-2c7d-463b-833c-58dce9efe89c', '2026-01-11T09:51:05.041Z', '2026-01-11T09:51:05.041Z'),
  ('be53bdf1-2681-4b7b-8ab4-7e7b0796eed0', '한국 식탁에는 항상 ______이 올라가요.', 'Trên bàn ăn Hàn Quốc luôn có ______.', ARRAY['김치', '초콜릿', '치즈', '버터']::text[], '김치', '김치 được phục vụ trong hầu hết các bữa ăn Hàn Quốc như một món ăn kèm không thể thiếu.', '77781e9b-2c7d-463b-833c-58dce9efe89c', '2026-01-11T09:51:05.041Z', '2026-01-11T09:51:05.041Z'),
  ('af9613f3-9697-4693-b451-f7f0f290df69', '______은(는) 달콤짭짤한 소스에 재운 고기를 구운 음식이에요.', '______ là món thịt được ướp trong nước sốt mặn ngọt rồi nướng.', ARRAY['불고기', '비빔밥', '김밥', '라면']::text[], '불고기', '불고기 là món thịt bò (hoặc thịt heo) thái mỏng, ướp gia vị đặc trưng và nướng trên vỉ hoặc chảo.', 'b1bddf4d-578c-426c-b8de-6bbcb4c78849', '2026-01-11T09:51:05.041Z', '2026-01-11T09:51:05.041Z'),
  ('32e21e70-293b-48e7-84bc-e1f1f852f27e', '외국인들에게 인기 있는 한국 음식 중 하나는 ______이에요.', 'Một trong những món ăn Hàn Quốc được người nước ngoài yêu thích là ______.', ARRAY['불고기', '순대', '홍어', '번데기']::text[], '불고기', '불고기 có hương vị phù hợp với khẩu vị quốc tế nên rất được ưa chuộng ở nước ngoài.', 'b1bddf4d-578c-426c-b8de-6bbcb4c78849', '2026-01-11T09:51:05.041Z', '2026-01-11T09:51:05.041Z'),
  ('9ff8617d-dac8-40f9-8e06-01f2e27dc24c', '______은(는) 밥 위에 다양한 나물과 고춧장을 올려 비벼 먹는 음식이에요.', '______ là món cơm được trộn với các loại rau củ và tương ớt đặt trên cơm.', ARRAY['비빔밥', '불고기', '김치찌개', '떡볶이']::text[], '비빔밥', '비빔밥 nghĩa đen là ''cơm trộn'', gồm cơm trắng với nhiều loại rau củ, thịt, trứng và tương ớt gochujang.', '7c8d1817-dddb-466f-8d0a-fc9439375b39', '2026-01-11T09:51:05.041Z', '2026-01-11T09:51:05.041Z'),
  ('c1a07027-d4cd-400b-80ab-44f5be2304c4', '전주 ______은(는) 특히 유명해요.', '______ của Jeonju đặc biệt nổi tiếng.', ARRAY['비빔밥', '불고기', '김밥', '라면']::text[], '비빔밥', '비빔밥 Jeonju được coi là đặc sản và phiên bản ngon nhất của món này tại Hàn Quốc.', '7c8d1817-dddb-466f-8d0a-fc9439375b39', '2026-01-11T09:51:05.041Z', '2026-01-11T09:51:05.041Z'),
  ('a0ad9a28-0ff4-459d-a45a-13950836a08d', '______은(는) 매운 고추장 소스에 떡을 볶은 한국의 대표적인 길거리 음식이에요.', '______ là món ăn đường phố tiêu biểu của Hàn Quốc, gồm bánh gạo xào trong nước sốt tương ớt cay.', ARRAY['떡볶이', '김밥', '불고기', '비빔밥']::text[], '떡볶이', '떡볶i làm từ bánh gạo hình trụ nhỏ, xào với nước sốt gochujang cay, thường có thêm cá viên, trứng và hành lá.', 'e35436c1-5f79-4f38-b8c2-51c85fd7d149', '2026-01-11T09:51:05.041Z', '2026-01-11T09:51:05.041Z'),
  ('97270fcf-1c0e-4bd0-acce-12daff724d39', '분식점에서 가장 인기 있는 메뉴는 ______이에요.', 'Món được yêu thích nhất ở các quán ăn nhẹ (bunsik) là ______.', ARRAY['떡볶이', '스테이크', '파스타', '샐러드']::text[], '떡볶이', '떡볶i là món chủ lực tại các quán 분식 (bunsik) - quán ăn nhẹ giá rẻ phục vụ món Hàn đơn giản.', 'e35436c1-5f79-4f38-b8c2-51c85fd7d149', '2026-01-11T09:51:05.041Z', '2026-01-11T09:51:05.041Z'),
  ('011327a8-fe16-4b8f-8950-3d0ad6cd062f', '______은(는) 밥과 다양한 재료를 김에 말아 만든 한국식 롤이에요.', '______ là cuốn cơm kiểu Hàn, gồm cơm và nhiều nguyên liệu cuộn trong lá rong biển.', ARRAY['김밥', '비빔밥', '불고기', '라면']::text[], '김밥', '김밥 tương tự sushi nhưng nhân đa dạng hơn (cà rốt, dưa leo, trứng, thanh cua, củ cải muối) và cơm thường trộn dầu mè.', '65e5d6ee-df4a-41cc-a804-a1755f205ea9', '2026-01-11T09:51:05.041Z', '2026-01-11T09:51:05.041Z'),
  ('3524ffd3-7029-4225-9236-3b7ae952fc62', '______은(는) 소풍 음식으로 가장 적합해요.', '______ rất thích hợp làm đồ ăn cho chuyến dã ngoại.', ARRAY['김밥', '국', '찌개', '전']::text[], '김밥', '김밥 dễ mang theo, không cần hâm nóng, và ăn được ngay nên là lựa chọn lý tưởng cho picnic.', '65e5d6ee-df4a-41cc-a804-a1755f205ea9', '2026-01-11T09:51:05.041Z', '2026-01-11T09:51:05.041Z'),
  ('a80aaafa-9bb3-451e-accb-726d4547c84e', '______은(는) 한국에서 가장 간편한 식사 대용품이에요.', '______ là món ăn thay thế bữa ăn tiện lợi nhất ở Hàn Quốc.', ARRAY['라면', '비빔밥', '불고기', '삼계탕']::text[], '라면', '라면 (mì gói) rất phổ biến ở Hàn Quốc, thường được ăn khi vội, khuya hoặc tiết kiệm.', '6a031372-b4fa-4647-864d-cb00f8af8dd8', '2026-01-11T09:51:05.041Z', '2026-01-11T09:51:05.041Z'),
  ('4ebd8952-1328-4383-96ee-abe5239378f3', '______에 계란을 풀어 넣으면 더 맛있어져요.', 'Nếu đập trứng vào ______ thì sẽ ngon hơn.', ARRAY['라면', '김치', '밥', '국']::text[], '라면', 'Trứng là topping phổ biến khi nấu 라면, làm nước dùng đậm đà và bổ dưỡng hơn.', '6a031372-b4fa-4647-864d-cb00f8af8dd8', '2026-01-11T09:51:05.041Z', '2026-01-11T09:51:05.041Z'),
  ('152a03b8-fa91-4bce-a843-55c37df2b81a', '______은(는) 국보다 걸쭉하고 진한 한국의 스튜예요.', '______ là món hầm Hàn Quốc đặc và đậm đà hơn canh.', ARRAY['찌개', '국', '탕', '전골']::text[], '찌개', '찌개 là món hầm đặc, thường có vị cay và được nấu chung trong nồi đất (뚝배기) và ăn nóng.', '205ee315-c3c5-4b94-90c1-93b9accdd4f1', '2026-01-11T09:51:05.041Z', '2026-01-11T09:51:05.041Z'),
  ('571488f4-c6ae-4866-824e-f07980e8a573', '추운 날에 ______을(를) 먹으면 몸이 따뜻해져요.', 'Vào ngày lạnh, ăn ______ sẽ làm cơ thể ấm lên.', ARRAY['찌개', '아이스크림', '샐러드', '빙수']::text[], '찌개', 'Do đặc tính nóng, cay và được ăn khi còn nóng, 찌개 là lựa chọn lý tưởng cho mùa đông.', '205ee315-c3c5-4b94-90c1-93b9accdd4f1', '2026-01-11T09:51:05.041Z', '2026-01-11T09:51:05.041Z'),
  ('529d1221-de88-42f2-bf77-7360eef0ece6', '한국 식사에는 여러 가지 ______이(가) 밥과 함께 나와요.', 'Trong bữa ăn Hàn Quốc, nhiều loại ______ được dọn ra cùng với cơm.', ARRAY['반찬', '디저트', '음료', '빵']::text[], '반찬', '반찬 chỉ các món ăn phụ nhỏ được bày cùng bữa chính, có thể từ 3-12 món tùy dịp.', 'fa03c277-c778-4e06-a152-7d9255d7cac8', '2026-01-11T09:51:05.041Z', '2026-01-11T09:51:05.041Z'),
  ('2d14e254-02b9-4e25-bf47-1b38d06b9bfb', '______은(는) 무료로 리필되는 경우가 많아요.', '______ thường được cho thêm miễn phí.', ARRAY['반찬', '고기', '생선', '디저트']::text[], '반찬', 'Ở nhiều nhà hàng Hàn Quốc, một số 반찬 như kim chi, củ cải muối thường được refill miễn phí theo yêu cầu.', 'fa03c277-c778-4e06-a152-7d9255d7cac8', '2026-01-11T09:51:05.041Z', '2026-01-11T09:51:05.041Z'),
  ('8153d7a9-42be-42b2-9fb0-f86daee75c6e', '운동한 후에는 ______을/를 마셔야 해요.', 'Sau khi tập thể dục, phải uống ______.', ARRAY['맥주', '물', '커피', '소주']::text[], '물', '물 là ''nước'', đồ uống cơ bản và cần thiết nhất cho cơ thể, đặc biệt sau khi vận động.', '53c86105-09c0-482a-a392-c1da410df542', '2026-01-11T09:51:05.041Z', '2026-01-11T09:51:05.041Z'),
  ('17abad22-ebf1-452b-bc47-fcd621b6b9af', '건강을 위해 하루에 ______을/를 많이 마셔요.', 'Để tốt cho sức khỏe, tôi uống nhiều ______ mỗi ngày.', ARRAY['콜라', '물', '주스', '맥주']::text[], '물', '물 là đồ uống lành mạnh, được khuyên dùng hàng ngày để duy trì sức khỏe.', '53c86105-09c0-482a-a392-c1da410df542', '2026-01-11T09:51:05.041Z', '2026-01-11T09:51:05.041Z'),
  ('5f28d148-e71a-46ed-a668-4e554e12d5b1', '아이들은 뼈가 자라기 위해 ______을/를 마셔야 해요.', 'Trẻ em cần uống ______ để xương phát triển.', ARRAY['우유', '커피', '소주', '콜라']::text[], '우유', '우유 là ''sữa'', giàu canxi và tốt cho sự phát triển xương của trẻ em.', '889a5116-ae25-4333-b31a-ab560f7feecf', '2026-01-11T09:51:05.041Z', '2026-01-11T09:51:05.041Z'),
  ('165902d7-6e80-49d9-830c-73f08f317633', '______와/과 시리얼을 같이 먹어요.', 'Tôi ăn ngũ cốc cùng với ______.', ARRAY['물', '우유', '주스', '차']::text[], '우유', '우유 thường được dùng kèm với ngũ cốc (시리얼) trong bữa sáng.', '889a5116-ae25-4333-b31a-ab560f7feecf', '2026-01-11T09:51:05.041Z', '2026-01-11T09:51:05.041Z'),
  ('8d1bdb12-d75a-4060-b928-1bac02c5adc0', '비타민을 섭취하려면 신선한 ______을/를 마셔요.', 'Để hấp thụ vitamin, hãy uống ______ tươi.', ARRAY['주스', '콜라', '맥주', '소주']::text[], '주스', '주스 là ''nước ép trái cây'', cung cấp vitamin tự nhiên từ hoa quả.', '43d595a2-0c95-4241-9712-2750bf089c6d', '2026-01-11T09:51:05.041Z', '2026-01-11T09:51:05.041Z'),
  ('6ab2f783-a2bd-40fe-a155-ff9ccb8096ff', '아침에 오렌지 ______ 한 잔을 마셔요.', 'Tôi uống một ly ______ cam vào buổi sáng.', ARRAY['주스', '물', '차', '우유']::text[], '주스', '주스 thường được kết hợp với tên trái cây để chỉ loại nước ép cụ thể (오렌지 주스, 사과 주스).', '43d595a2-0c95-4241-9712-2750bf089c6d', '2026-01-11T09:51:05.041Z', '2026-01-11T09:51:05.041Z'),
  ('b9c03cdc-016c-4754-b898-d8c44c41e2f7', '아침에 잠이 깨도록 ______ 한 잔을 마셔요.', 'Tôi uống một ly ______ để tỉnh táo vào buổi sáng.', ARRAY['물', '커피', '우유', '주스']::text[], '커피', '커피 là ''cà phê'', thường được uống vào buổi sáng hoặc khi cần tỉnh táo do chứa caffeine.', '715eec0f-08d0-4b1d-92ec-2dc8deaa0411', '2026-01-11T09:51:05.041Z', '2026-01-11T09:51:05.041Z'),
  ('b31ad512-9dd9-44d7-8d41-1fae9d66fd12', '회사 동료들과 ______를 마시며 이야기해요.', 'Tôi trò chuyện với đồng nghiệp trong khi uống ______.', ARRAY['소주', '맥주', '커피', '콜라']::text[], '커피', '커피 thường là đồ uống trong các cuộc gặp gỡ, trò chuyện thân mật hoặc công việc.', '715eec0f-08d0-4b1d-92ec-2dc8deaa0411', '2026-01-11T09:51:05.041Z', '2026-01-11T09:51:05.041Z'),
  ('eb9c07a3-89a2-4767-aa9a-7cdd8070560d', '한국의 전통 ______인 녹차를 마셔 봤어요?', 'Bạn đã thử uống ______ truyền thống của Hàn Quốc là trà xanh chưa?', ARRAY['차', '술', '음료', '주스']::text[], '차', '차 là ''trà'', bao gồm cả trà truyền thống như 녹차 (trà xanh) và các loại trà hiện đại.', 'c9cc9cc0-4c8f-45b8-8950-d3ec8b49478d', '2026-01-11T09:51:05.041Z', '2026-01-11T09:51:05.041Z'),
  ('f7da3149-79e9-49bd-8bd9-06d91280fe9d', '감기에 걸렸을 때 생강 ______을/를 마셔요.', 'Khi bị cảm, tôi uống ______ gừng.', ARRAY['차', '주스', '물', '맥주']::text[], '차', '차 có thể kết hợp với các thành phần khác để tạo thành trà thảo mộc (생강차 - trà gừng).', 'c9cc9cc0-4c8f-45b8-8950-d3ec8b49478d', '2026-01-11T09:51:05.041Z', '2026-01-11T09:51:05.041Z'),
