INSERT INTO "QuizQuestion" (id, question, "questionTranslation", options, "correctAnswer", explanation, "VocabularyId", "createdAt", "updatedAt") VALUES ('a8b262e5-0a13-400d-b64c-ae15e8d46670', '저는 ______에서 쉬어요.', 'Tôi nghỉ ngơi trong ______.', ARRAY['거실', '방', '주방', '화장실']::text[], '방', '방 là danh từ chỉ ''phòng'', thường là phòng ngủ hoặc không gian riêng tư.', '205d4242-f892-4638-848f-07bb219e28b4', '2026-01-05T17:07:58.349Z', '2026-01-05T17:07:58.349Z'),
