INSERT INTO "QuizQuestion" (id, question, "questionTranslation", options, "correctAnswer", explanation, "VocabularyId", "createdAt", "updatedAt") VALUES ('c2dd5e28-a7ae-4bc4-96ad-6d8b34ee2073', '저는 ______에서 쉬어요.', 'Tôi nghỉ ngơi trong ______.', ARRAY['거실', '방', '주방', '화장실']::text[], '방', '방 là danh từ chỉ ''phòng'', thường là phòng ngủ hoặc không gian riêng tư.', 'b52c3696-2cd2-4e18-aa3f-807a1a8e19ca', '2026-01-11T09:51:05.036Z', '2026-01-11T09:51:05.036Z'),
  ('f89cc646-0372-4808-ae36-3d5458a8da62', '______이 좁지만 아늑해요.', '______ tuy hẹp nhưng ấm cúng.', ARRAY['집', '방', '마을', '도시']::text[], '방', '방 có thể được mô tả về kích thước (좁다 - hẹp) và cảm giác (아늑하다 - ấm cúng).', 'b52c3696-2cd2-4e18-aa3f-807a1a8e19ca', '2026-01-11T09:51:05.036Z', '2026-01-11T09:51:05.036Z'),
