INSERT INTO "QuizQuestion" (id, question, "questionTranslation", options, "correctAnswer", explanation, "VocabularyId", "createdAt", "updatedAt") VALUES ('8bf7c93a-be06-42aa-ae8e-05b716dd3f56', '무지개에는 일곱 가지 ______이 있어요.', 'Cầu vồng có bảy ______.', ARRAY['색깔', '모양', '크기', '길이']::text[], '색깔', '색깔 có nghĩa là ''màu sắc'', dùng để chỉ màu của vật thể hoặc hiện tượng.', '1d536e64-3ebf-4471-9040-8e891cd92701', '2026-01-11T09:51:05.029Z', '2026-01-11T09:51:05.029Z'),
  ('8a04c5b9-9a38-475f-8ae6-5d4d62df2b32', '이 옷의 ______이 예뻐요.', '______ của chiếc áo này đẹp quá.', ARRAY['색', '디자인', '재질', '브랜드']::text[], '색', '색 là hình thức ngắn gọn của 색깔, thường được dùng trong văn nói hoặc khi kết hợp với các từ khác.', '1d536e64-3ebf-4471-9040-8e891cd92701', '2026-01-11T09:51:05.029Z', '2026-01-11T09:51:05.029Z'),
  ('8f73300b-2068-445c-a3fd-1839e3d11cad', '______ 신호등은 멈추라는 뜻이에요.', 'Đèn giao thông ______ có nghĩa là dừng lại.', ARRAY['빨간색', '초록색', '노란색', '파란색']::text[], '빨간색', '빨간색 có nghĩa là ''màu đỏ'', là màu của nguy hiểm, cảnh báo, và dừng lại trong giao thông.', '3886856d-30b0-4b98-a7b1-3fd9f6943266', '2026-01-11T09:51:05.029Z', '2026-01-11T09:51:05.029Z'),
  ('36abb598-f5b7-4fbd-bac4-7f0aa99a99e1', '사랑을 상징하는 꽃은 보통 ______ 장미예요.', 'Hoa tượng trưng cho tình yêu thường là hoa hồng ______.', ARRAY['빨간색', '하얀색', '노란색', '보라색']::text[], '빨간색', '빨간색 tượng trưng cho tình yêu, đam mê, và sức mạnh trong nhiều nền văn hóa.', '3886856d-30b0-4b98-a7b1-3fd9f6943266', '2026-01-11T09:51:05.029Z', '2026-01-11T09:51:05.029Z'),
  ('16811c24-9821-4d8c-9d53-b169c1e06037', '______ 신호등은 준비하라는 뜻이에요.', 'Đèn giao thông ______ có nghĩa là chuẩn bị.', ARRAY['노란색', '빨간색', '초록색', '파란색']::text[], '노란색', '노란색 có nghĩa là ''màu vàng'', thường tượng trưng cho sự thận trọng, cảnh báo, và ánh sáng mặt trời.', '5385966a-5670-484a-ab90-9492666d793d', '2026-01-11T09:51:05.029Z', '2026-01-11T09:51:05.029Z'),
  ('efb9f246-aa73-49d0-9815-69f790b4bc4d', '봄이 되면 ______ 민들레꽃이 피어요.', 'Khi mùa xuân đến, những bông hoa bồ công anh ______ nở rộ.', ARRAY['노란색', '빨간색', '파란색', '보라색']::text[], '노란색', '노란색 thường gắn liền với mùa xuân, sự ấm áp, và năng lượng tích cực.', '5385966a-5670-484a-ab90-9492666d793d', '2026-01-11T09:51:05.029Z', '2026-01-11T09:51:05.029Z'),
  ('f98c9802-77c8-40a1-9e76-f50fd91deb7a', '______ 바다는 매우 아름다워요.', 'Biển ______ rất đẹp.', ARRAY['파란색', '빨간색', '초록색', '검은색']::text[], '파란색', '파란색 có nghĩa là ''màu xanh da trời'', thường gắn liền với bầu trời, biển cả, và sự yên bình.', '23b6e487-66d3-4cf5-ad60-eb536daac5b4', '2026-01-11T09:51:05.029Z', '2026-01-11T09:51:05.029Z'),
  ('c08acdaf-ba66-441c-9b90-625d1922e195', '______ 옷은 청량감을 주는 느낌이에요.', 'Quần áo ______ cho cảm giác mát mẻ, sảng khoái.', ARRAY['파란색', '빨간색', '주황색', '갈색']::text[], '파란색', '파란색 thường được cảm nhận là màu lạnh, tạo cảm giác thanh mát, yên tĩnh và đáng tin cậy.', '23b6e487-66d3-4cf5-ad60-eb536daac5b4', '2026-01-11T09:51:05.029Z', '2026-01-11T09:51:05.029Z'),
  ('8b596411-13a7-428f-be47-4ea1f42efcc8', '______ 신호등은 건너가도 된다는 뜻이에요.', 'Đèn giao thông ______ có nghĩa là được phép đi qua.', ARRAY['초록색', '빨간색', '노란색', '파란색']::text[], '초록색', '초록색 có nghĩa là ''màu xanh lá cây'', tượng trưng cho sự an toàn, đi tiếp, và thiên nhiên.', 'ba1e87b4-bfbb-4c05-9c67-d888945e2dd9', '2026-01-11T09:51:05.029Z', '2026-01-11T09:51:05.029Z'),
  ('6a6b363c-d4f0-4d83-8bdb-2b6617858e93', '공원의 ______ 잔디밭에서 사람들이 즐거워해요.', 'Mọi người vui vẻ trên bãi cỏ ______ ở công viên.', ARRAY['초록색', '빨간색', '파란색', '보라색']::text[], '초록색', '초록색 là màu của thiên nhiên, cây cỏ, và sự phát triển, thường mang lại cảm giác tươi mát và bình yên.', 'ba1e87b4-bfbb-4c05-9c67-d888945e2dd9', '2026-01-11T09:51:05.029Z', '2026-01-11T09:51:05.029Z'),
  ('be440e83-a5b0-4331-9bcd-abc40d8c86bc', '결혼식에서 신부는 보통 ______ 웨딩드레스를 입어요.', 'Trong đám cưới, cô dâu thường mặc váy cưới ______.', ARRAY['하얀색', '검은색', '빨간색', '파란색']::text[], '하얀색', '하얀색 có nghĩa là ''màu trắng'', tượng trưng cho sự thuần khiết, trong sáng, và ngây thơ.', '2a2eef39-cbb6-4add-acda-0a0c9a4e09a0', '2026-01-11T09:51:05.029Z', '2026-01-11T09:51:05.029Z'),
