INSERT INTO "Category" (id, name, description, icon, "order", "topikLevel", "groupId", "createdAt", "updatedAt") VALUES ('bc0cd68d-8b4e-4fbe-bbd7-a90301503eef', 'Gặp gỡ & Hẹn hò', 'Từ vựng về các mối quan hệ, hẹn hò và văn hóa tình cảm của giới trẻ Hàn Quốc', '💕', 1, 2, 'bc4d20ce-2413-40af-b0ce-55e8123e6afd', '2026-01-05T17:07:58.369Z', '2026-01-05T17:07:58.369Z'),
  ('da58e52e-a8b1-4bc2-8ca9-4de2f1795bae', 'Tính cách (Phần 1)', 'Từ vựng về các đặc điểm tính cách cơ bản, liên quan đến hướng ngoại/hướng nội và thái độ sống', '😊', 2, 2, 'bc4d20ce-2413-40af-b0ce-55e8123e6afd', '2026-01-05T17:07:58.369Z', '2026-01-05T17:07:58.369Z'),
  ('73713c0a-d02d-4649-a100-488f3845bb6d', 'Tính cách (Phần 2)', 'Từ vựng về các đặc điểm tính cách chi tiết hơn, liên quan đến cách làm việc, ứng xử và cảm xúc', '🤔', 3, 2, 'bc4d20ce-2413-40af-b0ce-55e8123e6afd', '2026-01-05T17:07:58.369Z', '2026-01-05T17:07:58.369Z'),
  ('3949c468-854d-47f8-9e9c-17a84637f7ca', 'Lời mời & Các cuộc gặp gỡ (초대 및 모임)', 'Từ vựng về việc mời mọc, tham dự và các loại hình tụ tập xã hội', '📅', 4, 2, 'bc4d20ce-2413-40af-b0ce-55e8123e6afd', '2026-01-05T17:07:58.369Z', '2026-01-05T17:07:58.369Z'),
  ('0322e57e-d706-4ecd-8624-7217e0a6ce57', 'Lễ cưới & Các sự kiện gia đình', 'Từ vựng về các nghi lễ truyền thống và sự kiện gia đình quan trọng trong văn hóa Hàn Quốc', '👨‍👩‍👧‍👦', 5, 2, 'bc4d20ce-2413-40af-b0ce-55e8123e6afd', '2026-01-05T17:07:58.369Z', '2026-01-05T17:07:58.369Z'),
  ('da2888af-dfa8-406f-a064-f9f5add9366d', 'Khen ngợi & Phê bình', 'Từ vựng về cách thức đưa ra và nhận phản hồi mang tính tích cực, động viên hoặc góp ý xây dựng trong giao tiếp', '💬', 6, 2, 'bc4d20ce-2413-40af-b0ce-55e8123e6afd', '2026-01-05T17:07:58.369Z', '2026-01-05T17:07:58.369Z'),
  ('9995bcf4-58c8-4184-8223-d9d1e45757d6', 'Từ chối & Xin lỗi (거절과 사과)', 'Từ vựng liên quan đến hành động từ chối, xin lỗi, giải quyết mâu thuẫn và ứng xử', '🙏', 7, 2, 'bc4d20ce-2413-40af-b0ce-55e8123e6afd', '2026-01-05T17:07:58.369Z', '2026-01-05T17:07:58.369Z'),
  ('1538a452-80df-4cb0-b791-969dda655f6a', 'Giải quyết xung đột (갈등 해결)', 'Từ vựng về các dạng xung đột, cách thức giải quyết và hòa giải', '⚖️', 8, 2, 'bc4d20ce-2413-40af-b0ce-55e8123e6afd', '2026-01-05T17:07:58.369Z', '2026-01-05T17:07:58.369Z'),
  ('b6475cbe-6fd3-4c1a-a0bc-827ce0528c88', 'Các kiểu người', 'Từ vựng về các loại tính cách, cá tính và phong cách sống của con người trong xã hội Hàn Quốc', '👥', 9, 2, 'bc4d20ce-2413-40af-b0ce-55e8123e6afd', '2026-01-05T17:07:58.369Z', '2026-01-05T17:07:58.369Z'),
  ('51aecf7e-4c8a-4c60-9423-dd5abc30772a', 'Tình bạn', 'Từ vựng về mối quan hệ bạn bè, tình cảm và các đặc trưng của một tình bạn đích thực', '👫', 10, 2, 'bc4d20ce-2413-40af-b0ce-55e8123e6afd', '2026-01-05T17:07:58.369Z', '2026-01-05T17:07:58.369Z');
INSERT INTO "Vocabulary" (id, korean, hanja, vietnamese, pronunciation, example, "exampleTranslation", "categoryId", "createdAt", "updatedAt") VALUES ('582dba12-a400-46ed-b312-577e2bc152d5', '썸 타다', NULL, 'Giai đoạn tìm hiểu, "thả thính"', 'sseom tada', '우리 아직 사귀는 건 아니고 썸 타는 중이에요.', 'Chúng tôi chưa hẹn hò, chỉ đang trong giai đoạn tìm hiểu thôi.', 'bc0cd68d-8b4e-4fbe-bbd7-a90301503eef', '2026-01-05T17:07:58.369Z', '2026-01-05T17:07:58.369Z'),
  ('9283ed0e-406b-4cc3-a193-c160c09844ab', '소개팅', '紹介팅', 'Buổi xem mắt (do bạn bè giới thiệu)', 'sogaeting', '친구가 소개팅을 주선해 줬어요.', 'Bạn tôi đã sắp xếp cho tôi một buổi xem mắt.', 'bc0cd68d-8b4e-4fbe-bbd7-a90301503eef', '2026-01-05T17:07:58.369Z', '2026-01-05T17:07:58.369Z'),
  ('cbd0fcfd-7f66-4734-859b-196bcb28a744', '고백하다', '告白하다', 'Tỏ tình', 'gobaekhada', '용기를 내서 좋아하는 사람에게 고백했어요.', 'Tôi đã lấy hết can đảm để tỏ tình với người mình thích.', 'bc0cd68d-8b4e-4fbe-bbd7-a90301503eef', '2026-01-05T17:07:58.369Z', '2026-01-05T17:07:58.369Z'),
  ('88249951-a147-45ae-b41a-d7f8ffccdbac', '사귀다', NULL, 'Hẹn hò, quen nhau', 'sagwida', '우리 사귄 지 1년 됐어요.', 'Chúng tôi đã hẹn hò được 1 năm rồi.', 'bc0cd68d-8b4e-4fbe-bbd7-a90301503eef', '2026-01-05T17:07:58.369Z', '2026-01-05T17:07:58.369Z'),
  ('0e52c6d8-3a0c-4864-a2ca-1f8c44390bcf', '헤어지다', NULL, 'Chia tay', 'he-eojida', '그 커플은 결국 헤어졌어요.', 'Cặp đôi đó cuối cùng đã chia tay.', 'bc0cd68d-8b4e-4fbe-bbd7-a90301503eef', '2026-01-05T17:07:58.369Z', '2026-01-05T17:07:58.369Z'),
  ('542281ac-d244-4f0a-bdd6-4ee4becb4e6e', '차이다', NULL, 'Bị "đá", bị chia tay', 'cha-ida', '어제 남자친구한테 차였어요.', 'Hôm qua tôi đã bị bạn trai "đá".', 'bc0cd68d-8b4e-4fbe-bbd7-a90301503eef', '2026-01-05T17:07:58.369Z', '2026-01-05T17:07:58.369Z'),
