INSERT INTO "Category" (id, name, description, icon, "order", "topikLevel", "groupId", "createdAt", "updatedAt") VALUES ('c072d000-c53b-48dd-b26e-d871844b46e1', 'Đời sống đại học', 'Từ vựng về các hoạt động, thuật ngữ và quy trình liên quan đến việc học tập, sinh hoạt tại trường đại học Hàn Quốc', '🎓', 1, 2, '4bccf693-c08b-4ab4-bf71-4894ce02ae3d', '2026-01-05T17:07:58.367Z', '2026-01-05T17:07:58.367Z'),
  ('ce84d141-2856-489d-b078-ba16ba2a491d', 'Các môn học & Chuyên ngành (과목 및 전공)', 'Từ vựng về hệ thống môn học, phân loại và các lĩnh vực học thuật chính', '📚', 2, 2, '4bccf693-c08b-4ab4-bf71-4894ce02ae3d', '2026-01-05T17:07:58.367Z', '2026-01-05T17:07:58.367Z'),
  ('3abb1dd4-ba62-44bb-a947-82916ca8fee9', 'Thi cử & Điểm số', 'Từ vựng về các kỳ thi, phương pháp học và cách đánh giá kết quả học tập trong môi trường đại học Hàn Quốc', '📚', 3, 2, '4bccf693-c08b-4ab4-bf71-4894ce02ae3d', '2026-01-05T17:07:58.367Z', '2026-01-05T17:07:58.367Z'),
  ('49df27f4-e1bb-40bc-b522-af87dad64f79', 'Học ngoại ngữ (외국어 학습)', 'Từ vựng về việc học ngoại ngữ, đặc biệt là tiếng Hàn, gồm thi cử, phương pháp và mục tiêu', '🗣️', 4, 2, '4bccf693-c08b-4ab4-bf71-4894ce02ae3d', '2026-01-05T17:07:58.367Z', '2026-01-05T17:07:58.367Z'),
  ('4597c0ff-4b63-4ecc-b182-372080001865', 'Du học (유학)', 'Từ vựng liên quan đến du học và cuộc sống du học sinh', '✈️', 5, 2, '4bccf693-c08b-4ab4-bf71-4894ce02ae3d', '2026-01-05T17:07:58.367Z', '2026-01-05T17:07:58.367Z'),
  ('5ba0a907-0fea-4615-9715-af2100f45043', 'Hoạt động câu lạc bộ', 'Từ vựng về các câu lạc bộ sinh viên, hoạt động ngoại khóa và tương tác xã hội trong trường đại học Hàn Quốc', '🎭', 6, 2, '4bccf693-c08b-4ab4-bf71-4894ce02ae3d', '2026-01-05T17:07:58.367Z', '2026-01-05T17:07:58.367Z'),
  ('12647628-3f16-46ed-a3d8-2f4844ab8ed4', 'Làm thêm (아르바이트)', 'Từ vựng về việc làm thêm, tìm việc và các điều kiện lao động cơ bản', '💼', 7, 2, '4bccf693-c08b-4ab4-bf71-4894ce02ae3d', '2026-01-05T17:07:58.367Z', '2026-01-05T17:07:58.367Z'),
  ('a96c74fd-6e1d-4fab-b1db-a188dc1e63f9', 'Tốt nghiệp & Định hướng tương lai (졸업 및 진로)', 'Từ vựng về tốt nghiệp và lập kế hoạch cho tương lai', '🎓', 8, 2, '4bccf693-c08b-4ab4-bf71-4894ce02ae3d', '2026-01-05T17:07:58.367Z', '2026-01-05T17:07:58.367Z');
