INSERT INTO "LearningGroup" (id, name, icon, "order", "topikLevel", "createdAt", "updatedAt") VALUES ('5c2c1c86-89ec-49a1-acbc-7fa17d2d5a3e', 'Kinh tế & Tiêu dùng (경제 및 소비)', NULL, 7, 2, '2026-01-11T09:51:05.055Z', '2026-01-11T09:51:05.055Z');
INSERT INTO "Category" (id, name, description, icon, "order", "topikLevel", "groupId", "createdAt", "updatedAt") VALUES ('6f9a1614-6d3c-48d4-b274-6de4307faeb5', 'Tiêu dùng thông minh (합리적 소비)', 'Từ vựng về thói quen tiêu dùng và mua sắm hợp lý', '💰', 1, 2, '5c2c1c86-89ec-49a1-acbc-7fa17d2d5a3e', '2026-01-11T09:51:05.055Z', '2026-01-11T09:51:05.055Z'),
  ('54ef66d5-fbb8-4ab8-ba24-4d7097d90346', 'Mua sắm online', 'Từ vựng về quy trình, giao dịch và các tình huống thường gặp khi mua sắm trên các trang thương mại điện tử', '🛒', 2, 2, '5c2c1c86-89ec-49a1-acbc-7fa17d2d5a3e', '2026-01-11T09:51:05.055Z', '2026-01-11T09:51:05.055Z'),
  ('346c7ff5-0c08-4879-9c38-372eb68d3fd1', 'Ngân hàng & Tín dụng (은행 및 신용)', 'Từ vựng về dịch vụ ngân hàng, các loại thẻ, hình thức thanh toán và tín dụng cá nhân', '🏦', 3, 2, '5c2c1c86-89ec-49a1-acbc-7fa17d2d5a3e', '2026-01-11T09:51:05.055Z', '2026-01-11T09:51:05.055Z'),
  ('a7310ced-d006-4ed7-be38-1776245b43fc', 'Tiết kiệm & Đầu tư (저축 및 투자)', 'Từ vựng về tiết kiệm, đầu tư và quản lý tài chính cá nhân', '📈', 4, 2, '5c2c1c86-89ec-49a1-acbc-7fa17d2d5a3e', '2026-01-11T09:51:05.055Z', '2026-01-11T09:51:05.055Z'),
  ('0ee686ef-0b9a-4e1f-a42f-0c527e8fb9ce', 'Thuế & Bảo hiểm', 'Từ vựng về các loại thuế, nghĩa vụ tài chính với nhà nước và các loại hình bảo hiểm cá nhân, xã hội', '💳', 5, 2, '5c2c1c86-89ec-49a1-acbc-7fa17d2d5a3e', '2026-01-11T09:51:05.055Z', '2026-01-11T09:51:05.055Z'),
  ('437dec19-4555-4839-bb39-143fda3b381b', 'Các vấn đề của người tiêu dùng (소비자 문제)', 'Từ vựng về quyền lợi người tiêu dùng, khiếu nại và giải quyết tranh chấp mua sắm', '🛡️', 6, 2, '5c2c1c86-89ec-49a1-acbc-7fa17d2d5a3e', '2026-01-11T09:51:05.055Z', '2026-01-11T09:51:05.055Z'),
  ('21227412-951f-4c7d-b2db-e62b844fb701', 'Thị trường và giá cả (시장과 물가)', 'Từ vựng về kinh tế thị trường, giá cả và các khái niệm kinh tế cơ bản', '🏪', 7, 2, '5c2c1c86-89ec-49a1-acbc-7fa17d2d5a3e', '2026-01-11T09:51:05.055Z', '2026-01-11T09:51:05.055Z'),
  ('0b979a64-5830-4c62-89c8-50a8e07ff55d', 'Xu hướng tiêu dùng', 'Từ vựng về các xu hướng, mô hình và đặc điểm mới trong hành vi tiêu dùng hiện đại, đặc biệt là ở thế hệ trẻ Hàn Quốc', '📱', 8, 2, '5c2c1c86-89ec-49a1-acbc-7fa17d2d5a3e', '2026-01-11T09:51:05.055Z', '2026-01-11T09:51:05.055Z');
INSERT INTO "Vocabulary" (id, korean, hanja, vietnamese, pronunciation, example, "exampleTranslation", "categoryId", "createdAt", "updatedAt") VALUES ('588241af-6868-4bb0-888a-70e91dc9bfd7', '소비', '消費', 'Tiêu dùng', 'sobi', '최근에 소비가 많이 줄었어요.', 'Gần đây việc tiêu dùng đã giảm đi nhiều.', '6f9a1614-6d3c-48d4-b274-6de4307faeb5', '2026-01-11T09:51:05.055Z', '2026-01-11T09:51:05.055Z'),
  ('f17f11f5-02ff-4c84-aada-bd808ad4d89d', '가격을 비교하다', '價格을 比較하다', 'So sánh giá', 'gagyeogeul bigyohada', '인터넷에서 가격을 비교하고 주문했어요.', 'Tôi đã so sánh giá trên mạng rồi mới đặt hàng.', '6f9a1614-6d3c-48d4-b274-6de4307faeb5', '2026-01-11T09:51:05.055Z', '2026-01-11T09:51:05.055Z'),
  ('e06a8ecc-33d1-4968-bc03-5d141880bb16', '할인', '割引', 'Giảm giá', 'harin', '지금 세일 기간이라서 50% 할인을 해요.', 'Bây giờ là thời gian giảm giá nên họ đang giảm 50%.', '6f9a1614-6d3c-48d4-b274-6de4307faeb5', '2026-01-11T09:51:05.055Z', '2026-01-11T09:51:05.055Z'),
  ('085d800f-af19-4775-84e7-c5573f14f7ef', '충동구매', '衝動購買', 'Mua sắm bốc đồng', 'chungdong-gume', '어제 충동구매를 해서 돈을 다 썼어요.', 'Hôm qua tôi đã mua sắm bốc đồng nên đã tiêu hết tiền.', '6f9a1614-6d3c-48d4-b274-6de4307faeb5', '2026-01-11T09:51:05.055Z', '2026-01-11T09:51:05.055Z'),
  ('c269c3c8-3588-44d7-8a2f-fcc22f30a4f8', '가성비', '價性比', 'Hiệu quả chi phí (Giá tốt so với chất lượng)', 'gaseongbi', '이 식당은 가성비가 좋기로 유명해요.', 'Nhà hàng này nổi tiếng là có hiệu quả chi phí tốt.', '6f9a1614-6d3c-48d4-b274-6de4307faeb5', '2026-01-11T09:51:05.055Z', '2026-01-11T09:51:05.055Z'),
  ('265b56cb-edbd-4f1d-91ec-f5c7c547ba14', '절약하다', '節約하다', 'Tiết kiệm', 'jeoryakhada', '돈을 모으기 위해 생활비를 절약하고 있어요.', 'Tôi đang tiết kiệm sinh hoạt phí để dành tiền.', '6f9a1614-6d3c-48d4-b274-6de4307faeb5', '2026-01-11T09:51:05.055Z', '2026-01-11T09:51:05.055Z'),
  ('a578395a-d394-4772-a745-6604a84db605', '장바구니', '', 'Giỏ hàng', 'jangbaguni', '사고 싶은 물건을 장바구니에 담았어요.', 'Tôi đã cho món đồ muốn mua vào giỏ hàng.', '54ef66d5-fbb8-4ab8-ba24-4d7097d90346', '2026-01-11T09:51:05.055Z', '2026-01-11T09:51:05.055Z'),
  ('7ff74cdb-7d3e-42b1-aa48-6cda8948fcf1', '결제하다', '決濟하다', 'Thanh toán', 'gyeoljehada', '카드로 결제하시겠어요?', 'Quý khách có muốn thanh toán bằng thẻ không ạ?', '54ef66d5-fbb8-4ab8-ba24-4d7097d90346', '2026-01-11T09:51:05.055Z', '2026-01-11T09:51:05.055Z'),
  ('b4d3fce4-7705-4c04-9698-413224fd278b', '배송', '配送', 'Giao hàng', 'baesong', '배송은 보통 2~3일 정도 걸려요.', 'Việc giao hàng thường mất khoảng 2-3 ngày.', '54ef66d5-fbb8-4ab8-ba24-4d7097d90346', '2026-01-11T09:51:05.055Z', '2026-01-11T09:51:05.055Z'),
  ('616c44ca-9181-4f55-a007-6b5ba31facb9', '무료 배송', '無料 配送', 'Miễn phí giao hàng', 'muryo baesong', '3만 원 이상 사면 무료 배송이에요.', 'Nếu mua trên 30,000 won sẽ được miễn phí giao hàng.', '54ef66d5-fbb8-4ab8-ba24-4d7097d90346', '2026-01-11T09:51:05.055Z', '2026-01-11T09:51:05.055Z'),
  ('09c74cb7-8654-4ff1-ac74-766078571301', '반품하다', '返品하다', 'Trả lại hàng', 'banpumhada', '옷이 마음에 안 들어서 반품했어요.', 'Tôi đã trả lại hàng vì không vừa ý với chiếc áo.', '54ef66d5-fbb8-4ab8-ba24-4d7097d90346', '2026-01-11T09:51:05.055Z', '2026-01-11T09:51:05.055Z'),
  ('edf8d870-2d2d-4740-9ed3-7e48a00b61b6', '교환하다', '交換하다', 'Đổi hàng', 'gyohwanhada', '사이즈가 안 맞아서 다른 것으로 교환했어요.', 'Kích cỡ không vừa nên tôi đã đổi sang cái khác.', '54ef66d5-fbb8-4ab8-ba24-4d7097d90346', '2026-01-11T09:51:05.055Z', '2026-01-11T09:51:05.055Z'),
  ('c91150cd-04ad-4da6-bf35-2fc50797a8a7', '후기', '後記', 'Đánh giá, review', 'hugi', '물건을 사기 전에 다른 사람들의 후기를 읽어봐요.', 'Tôi đọc review của người khác trước khi mua đồ.', '54ef66d5-fbb8-4ab8-ba24-4d7097d90346', '2026-01-11T09:51:05.055Z', '2026-01-11T09:51:05.055Z'),
