INSERT INTO "LearningGroup" (id, name, icon, "topikLevel", "createdAt", "updatedAt") VALUES ('30f88657-353e-4f1d-a6bd-ca93e4d00f5f', 'Kinh tế & Kinh doanh (경제 및 경영)', NULL, 3, '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z');
INSERT INTO "Category" (id, name, description, icon, "order", "topikLevel", "groupId", "createdAt", "updatedAt") VALUES ('cfc47ac0-c8db-40a5-90c0-9a64d7aa54ae', 'Thị trường Tài chính', 'Từ vựng chuyên ngành về thị trường tài chính, đầu tư và các công cụ tài chính tại Hàn Quốc', '📈', 1, 3, '30f88657-353e-4f1d-a6bd-ca93e4d00f5f', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('b49d0f13-7224-410f-a8e9-0e10f8cb4a5c', 'Phân tích Doanh nghiệp', 'Từ vựng về các khái niệm, công cụ và chỉ số tài chính dùng để đánh giá và phân tích tình hình kinh doanh của một công ty', '📈', 2, 3, '30f88657-353e-4f1d-a6bd-ca93e4d00f5f', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('40b895c7-c566-46da-add4-24f138a3ca34', 'Thương mại Quốc tế', 'Từ vựng chuyên ngành về thương mại quốc tế, xuất nhập khẩu và các hiệp định thương mại', '🌐', 3, 3, '30f88657-353e-4f1d-a6bd-ca93e4d00f5f', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('1d328329-5dfd-42a4-b152-e9d85e619ef7', 'Marketing & Thương hiệu', 'Từ vựng về chiến lược marketing, quản lý thương hiệu và hoạt động truyền thông tại Hàn Quốc', '📢', 4, 3, '30f88657-353e-4f1d-a6bd-ca93e4d00f5f', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('697fb86a-08f3-4189-af70-a2d70718f371', 'Quản trị Nhân sự', 'Từ vựng về các chính sách, chiến lược và quy trình liên quan đến quản lý và phát triển con người trong tổ chức', '👥', 5, 3, '30f88657-353e-4f1d-a6bd-ca93e4d00f5f', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('9ff52c35-2d2b-4dd1-b97a-afff3854abe1', 'Sản xuất & Logistics', 'Từ vựng về các quy trình sản xuất, quản lý chuỗi cung ứng và hậu cần trong doanh nghiệp', '🚚', 6, 3, '30f88657-353e-4f1d-a6bd-ca93e4d00f5f', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('48c10c2c-6094-4e44-8d93-6aa6fcc912fe', 'Khởi nghiệp & Đầu tư mạo hiểm (스타트업 및 벤처 투자)', 'Từ vựng về hệ sinh thái khởi nghiệp, huy động vốn và đầu tư mạo hiểm', '🚀', 7, 3, '30f88657-353e-4f1d-a6bd-ca93e4d00f5f', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('b76816f2-b8d9-43bd-b003-f750a2bfcefb', 'Bất động sản & Đầu tư (부동산 및 투자)', 'Từ vựng về đầu tư bất động sản, tài chính bất động sản và thị trường nhà đất', '🏠', 8, 3, '30f88657-353e-4f1d-a6bd-ca93e4d00f5f', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('a7b30dab-2c23-49ec-a2ec-1e35ac0bbaaa', 'Sáp nhập & Mua lại (M&A)', 'Từ vựng về các giao dịch doanh nghiệp, chiến lược mua bán và sáp nhập công ty trong tài chính doanh nghiệp', '🤝', 9, 3, '30f88657-353e-4f1d-a6bd-ca93e4d00f5f', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('59234f5c-93cf-43d2-a8c1-6845347165e4', 'Kinh tế Vĩ mô (거시 경제)', 'Từ vựng về các khái niệm, chỉ số và chính sách kinh tế ở cấp độ quốc gia và toàn cầu', '📈', 10, 3, '30f88657-353e-4f1d-a6bd-ca93e4d00f5f', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('2c4ce7ac-2adf-4caf-a2b3-381e13129c18', 'Kinh tế Vi mô (미시 경제)', 'Từ vựng về các nguyên lý kinh tế vi mô, hành vi người tiêu dùng và doanh nghiệp', '📊', 11, 3, '30f88657-353e-4f1d-a6bd-ca93e4d00f5f', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('ea93f977-c648-4348-af26-23dd5282c070', 'Luật Thương mại', 'Từ vựng về các khái niệm pháp lý, quyền sở hữu trí tuệ và cơ cấu tổ chức doanh nghiệp theo luật pháp Hàn Quốc', '⚖️', 12, 3, '30f88657-353e-4f1d-a6bd-ca93e4d00f5f', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('a957fa96-c89c-40c4-a159-73d07e3c83c2', 'Quảng cáo & Tâm lý người tiêu dùng (광고 및 소비자 심리)', 'Từ vựng về các khái niệm tiếp thị, cơ chế tâm lý ảnh hưởng đến hành vi mua sắm và các hình thức quảng cáo', '🎯', 13, 3, '30f88657-353e-4f1d-a6bd-ca93e4d00f5f', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('0a7b4579-e689-4572-98cd-0ca542214ccb', 'Kinh tế Toàn cầu (세계 경제)', 'Từ vựng về các khái niệm và tổ chức kinh tế quốc tế, toàn cầu hóa và thị trường toàn cầu', '🌍', 14, 3, '30f88657-353e-4f1d-a6bd-ca93e4d00f5f', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('52f708bf-245d-4d31-a5eb-497aad1d6d5f', 'Quan hệ Lao động', 'Từ vựng về quan hệ giữa người lao động và người sử dụng lao động, các tổ chức công đoàn, đàm phán và tranh chấp lao động', '⚖️', 15, 3, '30f88657-353e-4f1d-a6bd-ca93e4d00f5f', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z');
INSERT INTO "Vocabulary" (id, korean, hanja, vietnamese, pronunciation, example, "exampleTranslation", "categoryId", "createdAt", "updatedAt") VALUES ('5475de4b-3321-4e6c-b7ed-6bbe6e44f8a9', '유동성', '流動性', 'Tính thanh khoản', 'yudongseong', '중앙은행은 시장에 유동성을 공급하기로 결정했다.', 'Ngân hàng trung ương đã quyết định cung cấp thanh khoản cho thị trường.', 'cfc47ac0-c8db-40a5-90c0-9a64d7aa54ae', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('61e64e7d-454b-4c87-9a71-cfec88d356e1', '파생 상품', '派生 商品', 'Sản phẩm phái sinh', 'pasaeng sangpum', '파생 상품 투자는 높은 위험을 수반한다.', 'Đầu tư sản phẩm phái sinh đi kèm với rủi ro cao.', 'cfc47ac0-c8db-40a5-90c0-9a64d7aa54ae', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('0ee77d64-980f-4585-ae0b-e51cc0221c47', '채권', '債券', 'Trái phiếu', 'chaegwon', '국채는 가장 안전한 투자 자산 중 하나로 여겨진다.', 'Trái phiếu chính phủ được coi là một trong những tài sản đầu tư an toàn nhất.', 'cfc47ac0-c8db-40a5-90c0-9a64d7aa54ae', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('f81f9ed9-de1b-4103-8264-2606e9f207b7', '자산', '資産', 'Tài sản', 'jasan', '그는 부동산과 주식 등 다양한 자산을 보유하고 있다.', 'Ông ấy sở hữu nhiều tài sản đa dạng như bất động sản và cổ phiếu.', 'cfc47ac0-c8db-40a5-90c0-9a64d7aa54ae', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('44e93d71-33c1-4806-99a0-3c811a95e46f', '부채', '負債', 'Nợ phải trả, công nợ', 'buchae', '과도한 부채는 기업의 재무 건전성을 악화시킨다.', 'Nợ phải trả quá mức làm suy yếu sức khỏe tài chính của doanh nghiệp.', 'cfc47ac0-c8db-40a5-90c0-9a64d7aa54ae', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('c92508e2-1d20-49dc-a67b-c088120985cb', '재무제표', '財務諸表', 'Báo cáo tài chính', 'jaemujepyo', '투자자들은 재무제표를 통해 기업의 상태를 파악한다.', 'Các nhà đầu tư nắm bắt tình hình của doanh nghiệp thông qua báo cáo tài chính.', 'b49d0f13-7224-410f-a8e9-0e10f8cb4a5c', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('2899e740-b326-41ea-9afa-47e1b45c3ea3', '현금 흐름', '現金 흐름', 'Dòng tiền', 'hyeon-geum heureum', '안정적인 현금 흐름은 기업 생존의 필수 조건이다.', 'Dòng tiền ổn định là điều kiện thiết yếu cho sự tồn tại của doanh nghiệp.', 'b49d0f13-7224-410f-a8e9-0e10f8cb4a5c', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('2e0d7780-94fe-4a9c-81cd-d6e082d85dab', '손익계산서', '損益計算書', 'Báo cáo kết quả hoạt động kinh doanh (P&L)', 'son-ikgyesanseo', '손익계산서를 보면 회사의 수익성을 알 수 있다.', 'Nhìn vào báo cáo kết quả kinh doanh có thể biết được khả năng sinh lời của công ty.', 'b49d0f13-7224-410f-a8e9-0e10f8cb4a5c', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('a14c17db-8d2b-4799-9ed0-4aa49241c0e2', '경쟁 우위', '競爭 優位', 'Lợi thế cạnh tranh', 'gyeongjaeng u-wi', '기술력은 그 회사의 핵심적인 경쟁 우위이다.', 'Năng lực kỹ thuật là lợi thế cạnh tranh cốt lõi của công ty đó.', 'b49d0f13-7224-410f-a8e9-0e10f8cb4a5c', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('adb9b461-7695-4817-b8b0-c119346dd483', '기업 가치', '企業 價値', 'Giá trị doanh nghiệp', 'gi-eop gachi', 'M&A를 통해 기업 가치를 극대화할 수 있다.', 'Có thể tối đa hóa giá trị doanh nghiệp thông qua M&A.', 'b49d0f13-7224-410f-a8e9-0e10f8cb4a5c', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('ee6e5b18-4b57-4214-ba16-d029b7dd92c9', '흑자 / 적자', '黑字 / 赤字', 'Thặng dư / Thâm hụt', 'heukja / jeokja', '올해 무역 수지는 큰 폭의 흑자를 기록했다.', 'Cán cân thương mại năm nay đã ghi nhận mức thặng dư lớn.', '40b895c7-c566-46da-add4-24f138a3ca34', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('3e37f5ed-3df0-4411-8d91-6ec1def2ba86', '비관세 장벽', '非關稅 障壁', 'Rào cản phi thuế quan', 'bigwanse jangbyeok', '위생 검역은 대표적인 비관세 장벽 중 하나이다.', 'Kiểm dịch vệ sinh là một trong những rào cản phi thuế quan tiêu biểu.', '40b895c7-c566-46da-add4-24f138a3ca34', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('d3d20d76-5eaa-468f-b393-a85efb05d66e', '덤핑', NULL, 'Bán phá giá', 'deomping', '그 회사는 덤핑 혐의로 조사를 받고 있다.', 'Công ty đó đang bị điều tra vì nghi ngờ bán phá giá.', '40b895c7-c566-46da-add4-24f138a3ca34', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('8403246f-a140-4a4a-8cd8-adbd231d2d0b', '원산지 규정', '原産地 規定', 'Quy tắc xuất xứ', 'wonsanji gyujeong', 'FTA 혜택을 받으려면 원산지 규정을 준수해야 한다.', 'Để được hưởng ưu đãi FTA, phải tuân thủ quy tắc xuất xứ.', '40b895c7-c566-46da-add4-24f138a3ca34', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('e18f4db3-728c-4ffa-825e-23c8918a418e', '상계관세', '相計關稅', 'Thuế đối kháng', 'sang-gyegwanse', '정부는 불공정한 보조금을 받은 수입품에 상계관세를 부과했다.', 'Chính phủ đã áp thuế đối kháng lên hàng hóa nhập khẩu nhận trợ cấp không công bằng.', '40b895c7-c566-46da-add4-24f138a3ca34', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('aa866566-1590-46e0-88db-a32019b0c528', '시장 세분화', '市場 細分化', 'Phân khúc thị trường', 'sijang sebunhwa', '효과적인 마케팅을 위해 시장 세분화는 필수적이다.', 'Phân khúc thị trường là cần thiết cho hoạt động marketing hiệu quả.', '1d328329-5dfd-42a4-b152-e9d85e619ef7', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('107b795d-c99d-4ff7-aa6e-27b04d959f50', '브랜드 충성도', NULL, 'Mức độ trung thành với thương hiệu', 'beuraendeu chungseongdo', '높은 브랜드 충성도는 안정적인 수익의 기반이 된다.', 'Mức độ trung thành với thương hiệu cao trở thành nền tảng của lợi nhuận ổn định.', '1d328329-5dfd-42a4-b152-e9d85e619ef7', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('7a589632-47a1-4f9d-a532-e9040900e066', '포지셔닝', NULL, 'Định vị (thương hiệu)', 'pojisyeoning', '우리는 프리미엄 시장을 타겟으로 포지셔닝 전략을 세웠다.', 'Chúng tôi đã xây dựng chiến lược định vị nhắm đến thị trường cao cấp.', '1d328329-5dfd-42a4-b152-e9d85e619ef7', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('c66f0d34-b50f-46df-8ffd-1aad19f1e61d', '구전 마케팅', '口傳 마케팅', 'Marketing truyền miệng', 'gujeon maketing', '만족한 고객들의 구전 마케팅 효과는 매우 크다.', 'Hiệu quả của marketing truyền miệng từ những khách hàng hài lòng là rất lớn.', '1d328329-5dfd-42a4-b152-e9d85e619ef7', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('c2186a9a-60dd-4edb-b13e-6cbf8ee34fbc', '인지도를 높이다', '認知度를 높이다', 'Nâng cao độ nhận diện', 'injidoreul nopida', '광고는 브랜드 인지도를 높이는 데 효과적이다.', 'Quảng cáo có hiệu quả trong việc nâng cao độ nhận diện thương hiệu.', '1d328329-5dfd-42a4-b152-e9d85e619ef7', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
  ('8266a215-2bea-49d1-866b-c341c5b17e07', '성과급', '成果給', 'Lương theo hiệu suất, thưởng', 'seong-gwageup', '회사는 개인의 성과에 따라 차등적으로 성과급을 지급한다.', 'Công ty chi trả tiền thưởng một cách khác biệt dựa trên hiệu suất của cá nhân.', '697fb86a-08f3-4189-af70-a2d70718f371', '2026-01-05T17:07:58.365Z', '2026-01-05T17:07:58.365Z'),
