INSERT INTO "LearningGroup" (id, name, icon, "order", "topikLevel", "createdAt", "updatedAt") VALUES ('3c0c6118-9525-4c1b-a6c7-b4105784a45f', 'Chính trị, Xã hội & Pháp luật (정치, 사회 및 법률)', NULL, 2, 3, '2026-01-11T09:51:05.050Z', '2026-01-11T09:51:05.050Z');
INSERT INTO "Category" (id, name, description, icon, "order", "topikLevel", "groupId", "createdAt", "updatedAt") VALUES ('92e010c1-6063-4efe-99e4-091457c94a2d', 'Hệ thống Chính trị & Bầu cử', 'Từ vựng về cấu trúc chính trị, bầu cử và các thiết chế nhà nước theo Hiến pháp Hàn Quốc', '🏛️', 1, 3, '3c0c6118-9525-4c1b-a6c7-b4105784a45f', '2026-01-11T09:51:05.050Z', '2026-01-11T09:51:05.050Z'),
  ('8b1f4487-8817-4844-b865-217cce202b3b', 'Quan hệ Quốc tế & Ngoại giao', 'Từ vựng về chính sách đối ngoại, quan hệ quốc tế và ngoại giao của Hàn Quốc', '🤝', 2, 3, '3c0c6118-9525-4c1b-a6c7-b4105784a45f', '2026-01-11T09:51:05.050Z', '2026-01-11T09:51:05.050Z'),
  ('c1415487-cdcc-4f3d-b09d-04004b6cb869', 'Pháp luật & Tòa án (사법 제도 및 재판)', 'Từ vựng về hệ thống tư pháp, các thủ tục pháp lý và tòa án', '⚖️', 3, 3, '3c0c6118-9525-4c1b-a6c7-b4105784a45f', '2026-01-11T09:51:05.050Z', '2026-01-11T09:51:05.050Z'),
  ('02f56696-8e43-4df8-9649-091b2ba62f2f', 'Luật Hình sự & Tội phạm', 'Từ vựng về các khái niệm pháp lý trong lĩnh vực hình sự, quy trình tố tụng và các loại tội phạm', '⚖️', 4, 3, '3c0c6118-9525-4c1b-a6c7-b4105784a45f', '2026-01-11T09:51:05.050Z', '2026-01-11T09:51:05.050Z'),
  ('37c36173-723d-48e7-b823-108c959548c5', 'Luật Dân sự (민법)', 'Từ vựng về các khái niệm cơ bản trong luật dân sự như quyền tài sản, hợp đồng, thừa kế và bồi thường', '⚖️', 5, 3, '3c0c6118-9525-4c1b-a6c7-b4105784a45f', '2026-01-11T09:51:05.050Z', '2026-01-11T09:51:05.050Z'),
  ('18b2cb42-34ad-4d0a-b390-600ed6591aee', 'Các vấn đề Xã hội (사회 문제)', 'Từ vựng về các vấn đề xã hội phổ biến và nhức nhối', '🧑‍🤝‍🧑', 6, 3, '3c0c6118-9525-4c1b-a6c7-b4105784a45f', '2026-01-11T09:51:05.050Z', '2026-01-11T09:51:05.050Z'),
  ('d0bfe729-f674-4f69-ac85-9746e3103ca8', 'Phúc lợi Xã hội', 'Từ vựng về hệ thống phúc lợi, an sinh xã hội, chính sách hỗ trợ và bảo vệ các nhóm yếu thế trong xã hội Hàn Quốc', '🏛️', 7, 3, '3c0c6118-9525-4c1b-a6c7-b4105784a45f', '2026-01-11T09:51:05.050Z', '2026-01-11T09:51:05.050Z'),
  ('28c9f569-fdb0-470c-9dd6-8272f3e40df4', 'Môi trường & Biến đổi Khí hậu (환경과 기후 변화)', 'Từ vựng về các vấn đề môi trường toàn cầu, nguyên nhân, hậu quả và các giải pháp phát triển bền vững', '🌍', 8, 3, '3c0c6118-9525-4c1b-a6c7-b4105784a45f', '2026-01-11T09:51:05.050Z', '2026-01-11T09:51:05.050Z'),
  ('5dfd00b2-379e-4214-8825-e14c7d4c0652', 'Giáo dục & Cạnh tranh (교육 및 경쟁)', 'Từ vựng về hệ thống giáo dục, thi cử và cạnh tranh trong giáo dục', '📚', 9, 3, '3c0c6118-9525-4c1b-a6c7-b4105784a45f', '2026-01-11T09:51:05.050Z', '2026-01-11T09:51:05.050Z'),
  ('fe256a11-aa58-4f05-a3ba-0135062d403f', 'Truyền thông & Dư luận', 'Từ vựng về vai trò, quyền lực và các hiện tượng liên quan đến truyền thông đại chúng và dư luận xã hội trong thời đại kỹ thuật số', '📰', 10, 3, '3c0c6118-9525-4c1b-a6c7-b4105784a45f', '2026-01-11T09:51:05.050Z', '2026-01-11T09:51:05.050Z'),
  ('8bc7f864-0de6-4ecc-a76e-68788acd6e69', 'Lịch sử Hiện đại Hàn Quốc (한국 현대사)', 'Từ vựng về các sự kiện, giai đoạn và khái niệm chính trị - xã hội quan trọng trong lịch sử Hàn Quốc từ thế kỷ 20 đến nay', '📜', 11, 3, '3c0c6118-9525-4c1b-a6c7-b4105784a45f', '2026-01-11T09:51:05.050Z', '2026-01-11T09:51:05.050Z'),
  ('9f0aafe8-89b4-4089-85ba-e3e881e1886d', 'Nhân quyền (인권)', 'Từ vựng về quyền con người cơ bản, tự do và bình đẳng', '🕊️', 12, 3, '3c0c6118-9525-4c1b-a6c7-b4105784a45f', '2026-01-11T09:51:05.050Z', '2026-01-11T09:51:05.050Z'),
  ('5b78c626-0745-45da-a620-417b14c2a77e', 'Chính sách Đối ngoại', 'Từ vựng về các nguyên tắc, phương thức và chiến lược trong quan hệ quốc tế và hoạt động ngoại giao của một quốc gia', '🌏', 13, 3, '3c0c6118-9525-4c1b-a6c7-b4105784a45f', '2026-01-11T09:51:05.050Z', '2026-01-11T09:51:05.050Z'),
  ('97a48b0f-503f-4b81-975c-3e2bf1dcd295', 'Đô thị hóa & Các vấn đề Đô thị (도시화 및 도시 문제)', 'Từ vựng về quá trình đô thị hóa, các thách thức và chính sách quản lý đô thị hiện đại', '🏙️', 14, 3, '3c0c6118-9525-4c1b-a6c7-b4105784a45f', '2026-01-11T09:51:05.050Z', '2026-01-11T09:51:05.050Z'),
  ('978b609c-2af9-4b56-a03f-c8872d7e73e2', 'Đa dạng văn hóa (다문화 사회)', 'Từ vựng về xã hội đa văn hóa, người di cư, hội nhập và hiểu biết liên văn hóa', '🌐', 15, 3, '3c0c6118-9525-4c1b-a6c7-b4105784a45f', '2026-01-11T09:51:05.050Z', '2026-01-11T09:51:05.050Z');
INSERT INTO "Vocabulary" (id, korean, hanja, vietnamese, pronunciation, example, "exampleTranslation", "categoryId", "createdAt", "updatedAt") VALUES ('41d59c25-55e0-439e-8cc7-af34c592816e', '삼권분립', '三權分立', 'Tam quyền phân lập', 'samgwonbullip', '삼권분립은 권력의 남용을 막기 위한 민주주의의 핵심 원리이다.', 'Tam quyền phân lập là nguyên tắc cốt lõi của dân chủ nhằm ngăn chặn lạm dụng quyền lực.', '92e010c1-6063-4efe-99e4-091457c94a2d', '2026-01-11T09:51:05.050Z', '2026-01-11T09:51:05.050Z'),
  ('bafe5092-503e-41d5-9556-2940fd79a2ad', '국회', '國會', 'Quốc hội', 'gukhoe', '국회는 법률을 제정하거나 개정하는 역할을 한다.', 'Quốc hội đóng vai trò xây dựng hoặc sửa đổi pháp luật.', '92e010c1-6063-4efe-99e4-091457c94a2d', '2026-01-11T09:51:05.050Z', '2026-01-11T09:51:05.050Z'),
  ('4fc81188-e238-4877-abd6-423dcb3b72fa', '대선 (대통령 선거)', '大選 (大統領 選擧)', 'Bầu cử tổng thống', 'daeseon', '5년마다 대선이 치러진다.', 'Cứ 5 năm một lần, cuộc bầu cử tổng thống lại được tổ chức.', '92e010c1-6063-4efe-99e4-091457c94a2d', '2026-01-11T09:51:05.050Z', '2026-01-11T09:51:05.050Z'),
  ('12771b42-9598-41e3-a4d1-d43c7b2616fd', '정당', '政黨', 'Chính đảng', 'jeongdang', '그는 새로운 정당을 창당하겠다고 발표했다.', 'Ông ấy đã tuyên bố sẽ thành lập một chính đảng mới.', '92e010c1-6063-4efe-99e4-091457c94a2d', '2026-01-11T09:51:05.050Z', '2026-01-11T09:51:05.050Z'),
  ('8e147419-2f1d-449c-966f-ecaabde101f0', '법안', '法案', 'Dự luật', 'beoban', '그 법안은 국회에서 격렬한 토론 끝에 통과되었다.', 'Dự luật đó đã được thông qua tại Quốc hội sau một cuộc tranh luận nảy lửa.', '92e010c1-6063-4efe-99e4-091457c94a2d', '2026-01-11T09:51:05.050Z', '2026-01-11T09:51:05.050Z'),
  ('6b41e8a6-b16c-45ac-8fe4-79cf4506856e', '동맹', '同盟', 'Đồng minh', 'dongmaeng', '양국은 70년간 굳건한 동맹 관계를 유지해 왔다.', 'Hai nước đã duy trì mối quan hệ đồng minh vững chắc trong 70 năm.', '8b1f4487-8817-4844-b865-217cce202b3b', '2026-01-11T09:51:05.050Z', '2026-01-11T09:51:05.050Z'),
  ('bc946dc4-6ae6-4617-96e7-43e494b9e2b4', '국제 분쟁', '國際 紛爭', 'Tranh chấp quốc tế', 'gukje bunjaeng', '영토 문제는 종종 국제 분쟁의 원인이 된다.', 'Vấn đề lãnh thổ thường trở thành nguyên nhân của các tranh chấp quốc tế.', '8b1f4487-8817-4844-b865-217cce202b3b', '2026-01-11T09:51:05.050Z', '2026-01-11T09:51:05.050Z'),
  ('8637349c-c5fb-4a76-ab8a-ab4308bd7037', '조약', '條約', 'Hiệp ước', 'joyak', '양국 정상은 평화 조약에 서명했다.', 'Lãnh đạo hai nước đã ký kết hiệp ước hòa bình.', '8b1f4487-8817-4844-b865-217cce202b3b', '2026-01-11T09:51:05.050Z', '2026-01-11T09:51:05.050Z'),
  ('f7a32557-dfb9-4764-ab5f-0627354a9772', '정상회담', '頂上會談', 'Hội nghị thượng đỉnh', 'jeongsang-hoedam', '다자간 정상회담이 다음 달에 열릴 예정이다.', 'Hội nghị thượng đỉnh đa phương dự kiến sẽ được tổ chức vào tháng sau.', '8b1f4487-8817-4844-b865-217cce202b3b', '2026-01-11T09:51:05.050Z', '2026-01-11T09:51:05.050Z'),
  ('d25c3f89-5627-47c2-a1d4-dcb219ab4e9f', '상호주의', '相互主義', 'Chủ nghĩa tương hỗ, có đi có lại', 'sanghoju-ui', '외교 관계는 상호주의 원칙에 기반해야 한다.', 'Quan hệ ngoại giao phải dựa trên nguyên tắc tương hỗ.', '8b1f4487-8817-4844-b865-217cce202b3b', '2026-01-11T09:51:05.050Z', '2026-01-11T09:51:05.050Z'),
  ('581619b7-9b9b-4efd-844f-656be788209f', '사법 제도', '司法 制度', 'Hệ thống tư pháp', 'sabeop jedo', '사법 제도의 독립성은 민주주의의 근간이다.', 'Sự độc lập của hệ thống tư pháp là nền tảng của dân chủ.', 'c1415487-cdcc-4f3d-b09d-04004b6cb869', '2026-01-11T09:51:05.050Z', '2026-01-11T09:51:05.050Z'),
  ('6afd0535-68dc-406c-9288-6e3422221c89', '원고 / 피고', '原告 / 被告', 'Nguyên đơn / Bị đơn', 'won-go / pigo', '원고는 피고에게 손해 배상을 청구했다.', 'Nguyên đơn đã yêu cầu bị đơn bồi thường thiệt hại.', 'c1415487-cdcc-4f3d-b09d-04004b6cb869', '2026-01-11T09:51:05.050Z', '2026-01-11T09:51:05.050Z'),
  ('15a7b118-5fb1-4cec-9f03-00840397110c', '소송', '訴訟', 'Tố tụng, vụ kiện', 'sosong', '그는 회사를 상대로 부당 해고 소송을 제기했다.', 'Anh ấy đã khởi kiện công ty về việc sa thải không công bằng.', 'c1415487-cdcc-4f3d-b09d-04004b6cb869', '2026-01-11T09:51:05.050Z', '2026-01-11T09:51:05.050Z'),
  ('352a3e80-5637-4053-8e54-73689b46de04', '항소하다', '抗訴하다', 'Kháng cáo', 'hangsohada', '피고인은 1심 판결에 불복하여 항소했다.', 'Bị đơn không chấp nhận phán quyết sơ thẩm và đã kháng cáo.', 'c1415487-cdcc-4f3d-b09d-04004b6cb869', '2026-01-11T09:51:05.050Z', '2026-01-11T09:51:05.050Z'),
  ('1eb7321d-d327-4f80-b917-ee07dde3df49', '위헌', '違憲', 'Vi hiến', 'wihyeon', '헌법재판소는 그 법률이 위헌이라고 결정했다.', 'Tòa án Hiến pháp đã quyết định rằng luật đó là vi hiến.', 'c1415487-cdcc-4f3d-b09d-04004b6cb869', '2026-01-11T09:51:05.050Z', '2026-01-11T09:51:05.050Z'),
  ('8e4c96b8-c7e1-4dc2-8bcf-60f77fcb57cc', '형법', '刑法', 'Luật hình sự', 'hyeongbeop', '형법은 범죄와 형벌을 규정하는 법이다.', 'Luật hình sự là luật quy định về tội phạm và hình phạt.', '02f56696-8e43-4df8-9649-091b2ba62f2f', '2026-01-11T09:51:05.050Z', '2026-01-11T09:51:05.050Z'),
  ('d7c23c1f-381c-47c7-8172-dac15987198f', '증거', '證據', 'Bằng chứng', 'jeung-geo', '검찰은 결정적인 증거를 확보하지 못했다.', 'Công tố đã không thể 확보 được bằng chứng mang tính quyết định.', '02f56696-8e43-4df8-9649-091b2ba62f2f', '2026-01-11T09:51:05.050Z', '2026-01-11T09:51:05.050Z'),
  ('e68e0229-4d68-4e00-8e8c-ade1d95ba3ce', '무죄 / 유죄', '無罪 / 有罪', 'Vô tội / Có tội', 'mujoe / yujoe', '배심원단은 피고인에게 무죄 평결을 내렸다.', 'Bồi thẩm đoàn đã đưa ra phán quyết vô tội cho bị cáo.', '02f56696-8e43-4df8-9649-091b2ba62f2f', '2026-01-11T09:51:05.050Z', '2026-01-11T09:51:05.050Z'),
  ('7577c061-23f6-4f86-94c5-273b8f810e00', '징역', '懲役', 'Phạt tù, tù giam', 'jing-yeok', '그는 사기 혐의로 징역 5년을 선고받았다.', 'Anh ta đã bị kết án 5 năm tù vì tội lừa đảo.', '02f56696-8e43-4df8-9649-091b2ba62f2f', '2026-01-11T09:51:05.050Z', '2026-01-11T09:51:05.050Z'),
  ('c7aa9a7a-a507-4c68-9943-60d1a8943334', '공소시효', '公訴時效', 'Thời hiệu truy cứu', 'gongso-sihyo', '그 사건은 이미 공소시효가 지나 처벌할 수 없다.', 'Vụ án đó đã quá thời hiệu truy cứu nên không thể trừng phạt.', '02f56696-8e43-4df8-9649-091b2ba62f2f', '2026-01-11T09:51:05.050Z', '2026-01-11T09:51:05.050Z'),
  ('5a57c153-fef5-45a3-b5a1-658ba3c04c3e', '재산권', NULL, 'Quyền tài sản', 'jaesangwon', '헌법은 국민의 재산권을 보장한다.', 'Hiến pháp đảm bảo quyền tài sản của công dân.', '37c36173-723d-48e7-b823-108c959548c5', '2026-01-11T09:51:05.050Z', '2026-01-11T09:51:05.050Z'),
  ('2ab6db2c-be74-4bfc-b36e-3bb279f035c6', '손해 배상', NULL, 'Bồi thường thiệt hại', 'sonhae baesang', '그는 교통사고 피해에 대한 손해 배상을 청구했다.', 'Anh ấy đã yêu cầu bồi thường thiệt hại cho vụ tai nạn giao thông.', '37c36173-723d-48e7-b823-108c959548c5', '2026-01-11T09:51:05.050Z', '2026-01-11T09:51:05.050Z'),
  ('92c6bb76-4efe-4e08-ad4b-38b154f956c8', '상속', NULL, 'Thừa kế', 'sangsok', '그는 아버지로부터 막대한 재산을 상속받았다.', 'Anh ấy đã được thừa kế một tài sản khổng lồ từ cha mình.', '37c36173-723d-48e7-b823-108c959548c5', '2026-01-11T09:51:05.050Z', '2026-01-11T09:51:05.050Z'),
