INSERT INTO "LearningGroup" (id, name, icon, "order", "topikLevel", "createdAt", "updatedAt") VALUES ('c125696d-c367-4638-9ce1-8a0dbd0327fd', 'Văn học, Nghệ thuật & Triết học (문학, 예술 및 철학)', NULL, 3, 3, '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z');
INSERT INTO "Category" (id, name, description, icon, "order", "topikLevel", "groupId", "createdAt", "updatedAt") VALUES ('8fd6a45c-f62f-44b1-ba2b-c6b601aea716', 'Lý luận Phê bình Văn học (문학 비평 이론)', 'Từ vựng về các khái niệm, phương pháp và lý thuyết trong phê bình văn học', '📖', 1, 3, 'c125696d-c367-4638-9ce1-8a0dbd0327fd', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('331c8f6e-4a24-4516-8209-50950db25775', 'Lịch sử Văn học Hàn Quốc', 'Từ vựng về các thể loại, tác phẩm và trào lưu văn học chủ yếu trong lịch sử phát triển văn học Hàn Quốc', '📖', 2, 3, 'c125696d-c367-4638-9ce1-8a0dbd0327fd', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('8f3bfc96-5682-484e-b842-cfa7ab9a870d', 'Thành ngữ Hán-Hàn (한자 성어)', 'Từ vựng về các thành ngữ gốc Hán (Hán tự) thường dùng trong tiếng Hàn, mang ý nghĩa triết lý, đạo đức và xã hội sâu sắc', '📖', 3, 3, 'c125696d-c367-4638-9ce1-8a0dbd0327fd', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('bc7579de-60f2-4968-8a98-532012336f6d', 'Tục ngữ & Cách ngôn (속담 및 격언)', 'Từ vựng về các câu tục ngữ, thành ngữ tiếng Hàn chứa đựng triết lý và kinh nghiệm sống', '💬', 4, 3, 'c125696d-c367-4638-9ce1-8a0dbd0327fd', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('7bc3d1e3-ce7f-4737-a499-5fc08aad4f78', 'Lịch sử Triết học Đông-Tây', 'Từ vựng về các khái niệm, tư tưởng cốt lõi của các trường phái triết học chủ yếu trong lịch sử triết học phương Đông và phương Tây', '🧠', 5, 3, 'c125696d-c367-4638-9ce1-8a0dbd0327fd', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('89b69f86-d833-4284-a1cc-f0b4000fa584', 'Mỹ học & Lịch sử Nghệ thuật (미학 및 미술사)', 'Từ vựng về lý thuyết thẩm mỹ, các phong trào nghệ thuật quan trọng và thuật ngữ phân tích tác phẩm nghệ thuật', '🎨', 6, 3, 'c125696d-c367-4638-9ce1-8a0dbd0327fd', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('10625e4c-5fa4-4983-953a-f77ad8b094f1', 'Lý thuyết Điện ảnh (영화 이론)', 'Từ vựng về các khái niệm, kỹ thuật và lý thuyết cơ bản trong phân tích và phê bình điện ảnh', '🎬', 7, 3, 'c125696d-c367-4638-9ce1-8a0dbd0327fd', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('b913978b-deea-4171-9c3d-9d4b701ab606', 'Âm nhạc Cổ điển & Hiện đại', 'Từ vựng về các thể loại, hình thức và thành tố cấu thành của âm nhạc cổ điển phương Tây và âm nhạc hiện đại', '🎵', 8, 3, 'c125696d-c367-4638-9ce1-8a0dbd0327fd', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('eb4ad100-641a-4b50-b1ea-4a5376f13fd4', 'Ngôn ngữ học (언어학)', 'Từ vựng về các ngành nghiên cứu chính trong lĩnh vực ngôn ngữ học, bao gồm cấu trúc, ý nghĩa và sử dụng ngôn ngữ', '🔤', 9, 3, 'c125696d-c367-4638-9ce1-8a0dbd0327fd', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('f39d5845-9e27-4b11-8e16-8d6707737b49', 'Thần thoại & Tôn giáo (신화 및 종교)', 'Từ vựng về thần thoại, các hệ thống tôn giáo và tín ngưỡng', '🕍', 10, 3, 'c125696d-c367-4638-9ce1-8a0dbd0327fd', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('28a71764-7102-4bb5-bb4e-45c4e8ac1b81', 'Kiến trúc (건축)', 'Từ vựng về các khái niệm, phong cách và nguyên lý cơ bản trong lĩnh vực kiến trúc.', '🏛️', 11, 3, 'c125696d-c367-4638-9ce1-8a0dbd0327fd', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('906a4180-0fa7-4ad5-b17f-740be245a928', 'Nghệ thuật Truyền thống Hàn Quốc (한국 전통 예술)', 'Từ vựng về các loại hình nghệ thuật biểu diễn, mỹ thuật và thủ công mỹ nghệ đặc trưng của Hàn Quốc', '🇰🇷', 12, 3, 'c125696d-c367-4638-9ce1-8a0dbd0327fd', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z');
INSERT INTO "Vocabulary" (id, korean, hanja, vietnamese, pronunciation, example, "exampleTranslation", "categoryId", "createdAt", "updatedAt") VALUES ('c4458d7c-0e3e-4884-8177-629f795881bb', '서사 구조', '敍事 構造', 'Cấu trúc tự sự', 'seosa gujo', '이 소설은 비선형적인 서사 구조를 통해 독자에게 새로운 경험을 제공한다.', 'Cuốn tiểu thuyết này mang đến trải nghiệm mới cho độc giả thông qua cấu trúc tự sự phi tuyến tính.', '8fd6a45c-f62f-44b1-ba2b-c6b601aea716', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('1aec8332-d97e-46d1-98ae-0988bb6b6e03', '아이러니', NULL, 'Sự mỉa mai, trớ trêu', 'aireoni', '작가는 비극적 아이러니를 사용하여 운명의 장난을 묘사했다.', 'Tác giả đã sử dụng sự trớ trêu bi thảm để miêu tả trò đùa của số phận.', '8fd6a45c-f62f-44b1-ba2b-c6b601aea716', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('468dce4e-3a28-4843-96e8-c6ec03037507', '작가 의식', '作家 意識', 'Ý thức của tác giả', 'jakga uisik', '작품을 깊이 이해하려면 당시 작가 의식을 파악해야 한다.', 'Để hiểu sâu sắc một tác phẩм, cần phải nắm bắt được ý thức của tác giả thời đó.', '8fd6a45c-f62f-44b1-ba2b-c6b601aea716', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('98806251-ffdd-4a5c-b8da-99142f045c9d', '실존주의', '實存主義', 'Chủ nghĩa hiện sinh', 'siljonju-ui', '이 작품에는 인간의 고독과 선택을 강조하는 실존주의 사상이 담겨 있다.', 'Tác phẩm này chứa đựng tư tưởng của chủ nghĩa hiện sinh, nhấn mạnh sự cô độc và lựa chọn của con người.', '8fd6a45c-f62f-44b1-ba2b-c6b601aea716', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('8e475669-7dc4-4191-b62d-210098cc28d5', '심리주의 비평', '心理主義 批評', 'Phê bình tâm lý học', 'simniju-ui bipyeong', '심리주의 비평은 등장인물의 무의식과 행동 동기를 분석한다.', 'Phê bình tâm lý học phân tích vô thức và động cơ hành động của nhân vật.', '8fd6a45c-f62f-44b1-ba2b-c6b601aea716', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('aecc73b2-ed5b-459d-9aa9-2f9d24d5edf5', '향가', '鄕歌', 'Hương ca (thơ thời Silla)', 'hyang-ga', '''제망매가''는 신라 시대 향가의 대표적인 작품이다.', '''Tế Vong Muội Ca'' là một tác phẩm tiêu biểu của thể loại Hương ca thời Silla.', '331c8f6e-4a24-4516-8209-50950db25775', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('180239e2-5aac-4616-bd44-04d0ed1be95b', '판소리계 소설', '판소리系 小說', 'Tiểu thuyết hệ Pansori', 'pansori-gye soseol', '''춘향전''은 판소리계 소설의 대표작으로, 서민들의 삶과 저항 정신을 담고 있다.', '''Xuân Hương truyện'' là tác phẩm tiêu biểu của tiểu thuyết hệ Pansori, chứa đựng cuộc sống và tinh thần phản kháng của người dân thường.', '331c8f6e-4a24-4516-8209-50950db25775', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('00c10749-8512-43a5-9b0d-019002ed7ed2', '신소설', '新小說', 'Tân tiểu thuyết (thời kỳ khai sáng)', 'sinsoseol', '이광수의 ''무정''은 한국 최초의 근대 장편 신소설로 평가받는다.', '''Vô tình'' của Lee Gwang-su được đánh giá là tân tiểu thuyết dài kỳ hiện đại đầu tiên của Hàn Quốc.', '331c8f6e-4a24-4516-8209-50950db25775', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('491b85d9-179f-40ba-b99f-55a8a8105167', '참여 문학', '參與 文學', 'Văn học tham dự, văn học dấn thân', 'chamyeo munhak', '1970년대에는 현실 비판적인 참여 문학이 주를 이루었다.', 'Vào những năm 1970, văn học tham dự mang tính phê phán hiện thực đã chiếm ưu thế.', '331c8f6e-4a24-4516-8209-50950db25775', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('8bd0d71b-5414-455f-a272-1f384256c80e', '순수 문학', '純粹 文學', 'Văn học thuần túy', 'sunsu munhak', '그는 정치적 이념에서 벗어나 언어의 예술성을 추구하는 순수 문학을 지향했다.', 'Ông ấy hướng đến văn học thuần túy, theo đuổi tính nghệ thuật của ngôn ngữ và thoát khỏi các ý thức hệ chính trị.', '331c8f6e-4a24-4516-8209-50950db25775', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('fffe3781-4ef5-47da-9d64-53229542f380', '권선징악 (勸善懲惡)', NULL, 'Khuyến thiện trừng ác', 'gwonseonjing-ak', '대부분의 고전 소설은 권선징악이라는 주제를 담고 있다.', 'Hầu hết các tiểu thuyết cổ điển đều chứa đựng chủ đề khuyến thiện trừng ác.', '8f3bfc96-5682-484e-b842-cfa7ab9a870d', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('c1527c8c-03d4-4e94-8bab-ece105a94677', '고정관념 (固定觀念)', NULL, 'Định kiến, khuôn mẫu cố định', 'gojeong-gwan-nyeom', '우리는 성별에 대한 고정관념에서 벗어나야 한다.', 'Chúng ta phải thoát khỏi những định kiến về giới tính.', '8f3bfc96-5682-484e-b842-cfa7ab9a870d', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('f829bea2-e86f-4139-ace6-1b8ad909b18f', '모순 (矛盾)', NULL, 'Mâu thuẫn', 'mosun', '그의 말과 행동에는 모순이 많다.', 'Có rất nhiều mâu thuẫn trong lời nói và hành động của anh ấy.', '8f3bfc96-5682-484e-b842-cfa7ab9a870d', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('6e8d09eb-85c3-4bbf-8de2-159c42269d21', '유비무환 (有備無患)', NULL, 'Hữu bị vô hoạn (có chuẩn bị thì không lo gặp họa)', 'yubimuhwan', '재난에 대비하여 유비무환의 자세를 가져야 한다.', 'Chúng ta phải có thái độ ''hữu bị vô hoạn'' để chuẩn bị cho thảm họa.', '8f3bfc96-5682-484e-b842-cfa7ab9a870d', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('8bd6a68c-d2b9-4f18-9dc2-d5d2a112d35c', '천고마비 (天高馬肥)', NULL, 'Thiên cao mã phì (trời cao ngựa béo - chỉ mùa thu)', 'cheon-go-mabi', '천고마비의 계절인 가을은 독서하기에 가장 좋은 때이다.', 'Mùa thu, mùa ''thiên cao mã phì'', là thời điểm tốt nhất để đọc sách.', '8f3bfc96-5682-484e-b842-cfa7ab9a870d', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('2e7f00b6-4f5a-437a-8ad6-66a3c7e12dda', '원숭이도 나무에서 떨어진다', NULL, 'Khỉ cũng có lúc rơi từ trên cây xuống (Dù là chuyên gia cũng có lúc mắc sai lầm)', 'wonsungido namueseo tteoreojinda', '아무리 전문가라도 실수할 수 있으니 ''원숭이도 나무에서 떨어진다''는 말을 기억해야 한다.', 'Dù là chuyên gia đến mấy cũng có thể mắc lỗi, vì vậy hãy nhớ câu "khỉ cũng có lúc rơi từ trên cây xuống".', 'bc7579de-60f2-4968-8a98-532012336f6d', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('a2af6fc8-024f-445a-852d-d0059637ec1c', '우물 안 개구리', NULL, 'Ếch ngồi đáy giếng', 'umul an gaeguri', '넓은 세상을 경험하지 못하면 우물 안 개구리가 되기 쉽다.', 'Nếu không trải nghiệm thế giới rộng lớn, rất dễ trở thành ếch ngồi đáy giếng.', 'bc7579de-60f2-4968-8a98-532012336f6d', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('6f126fd8-ce05-409b-a4c2-3299b3787b0c', '시작이 반이다', NULL, 'Khởi đầu là một nửa (Vạn sự khởi đầu nan)', 'sijagi banida', '너무 걱정하지 마세요. 시작이 반이라는 말도 있잖아요.', 'Đừng quá lo lắng. Chẳng phải cũng có câu nói rằng "khởi đầu là một nửa" đó sao.', 'bc7579de-60f2-4968-8a98-532012336f6d', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('2d3e822b-feb7-487a-be41-db0e15bed6a6', '공든 탑이 무너지랴', NULL, 'Tòa tháp xây bằng công sức liệu có sụp đổ? (Nỗ lực chân chính sẽ không vô ích)', 'gongdeun tabi muneojirya', '지금까지 열심히 노력했으니 좋은 결과가 있을 거예요. 공든 탑은 무너지지 않아요.', 'Vì đến giờ bạn đã nỗ lực hết mình nên sẽ có kết quả tốt thôi. Công sức sẽ không đổ sông đổ biển đâu.', 'bc7579de-60f2-4968-8a98-532012336f6d', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('47054cf3-7381-4d43-a601-8fc65c2bdb8f', '말 한마디에 천 냥 빚도 갚는다', NULL, 'Một lời nói có thể trả món nợ ngàn vàng (Lời nói khéo léo có sức mạnh to lớn)', 'mal hanmadie cheon nyang bitdo gamneunda', '부드러운 말 한마디가 어려운 상황을 해결할 수 있듯이, ''말 한마디에 천 냥 빚도 갚는다''.', 'Giống như một lời nói nhẹ nhàng có thể giải quyết tình huống khó khăn, "một lời nói có thể trả món nợ ngàn vàng".', 'bc7579de-60f2-4968-8a98-532012336f6d', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('fba40a6d-6e76-4eef-bb8f-baf5196e8de3', '인의예지 (仁義禮智)', '仁義禮智', 'Nhân, Nghĩa, Lễ, Trí (tứ đức của Nho giáo)', 'in-ui-ye-ji', '유교에서는 인의예지를 인간이 갖추어야 할 기본 덕목으로 보았다.', 'Trong Nho giáo, Nhân-Nghĩa-Lễ-Trí được coi là những đức tính cơ bản mà con người phải có.', '7bc3d1e3-ce7f-4737-a499-5fc08aad4f78', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('85e52b11-df2c-4b48-b71a-a581d509a4ca', '해탈 (解脫)', '解脫', 'Giải thoát, Niết bàn', 'haetal', '불교의 궁극적인 목표는 번뇌에서 벗어나 해탈의 경지에 이르는 것이다.', 'Mục tiêu cuối cùng của Phật giáo là thoát khỏi phiền não và đạt đến cảnh giới giải thoát.', '7bc3d1e3-ce7f-4737-a499-5fc08aad4f78', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('862930b0-edc2-4c16-b8bb-b353f5f80e76', '이데아 (Idea)', '', 'Ý niệm, thế giới ý niệm (của Plato)', 'idea', '플라톤은 현실 세계는 이데아 세계의 불완전한 그림자일 뿐이라고 주장했다.', 'Plato cho rằng thế giới hiện thực chỉ là cái bóng không hoàn hảo của thế giới ý niệm.', '7bc3d1e3-ce7f-4737-a499-5fc08aad4f78', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('57b22354-0227-4b6e-8bd8-7bfd6c108576', '허무주의 (虛無主義)', '虛無主義', 'Chủ nghĩa hư vô (Nihilism)', 'heomuju-ui', '니체는 전통적인 가치가 붕괴된 시대의 허무주의를 논했다.', 'Nietzsche đã luận bàn về chủ nghĩa hư vô trong thời đại mà các giá trị truyền thống sụp đổ.', '7bc3d1e3-ce7f-4737-a499-5fc08aad4f78', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('75022cf9-c2a7-4c3b-8af0-b84af393db07', '실용주의 (實用主義)', '實用主義', 'Chủ nghĩa thực dụng', 'siryongju-ui', '실용주의는 사상의 가치를 실제적인 결과로 판단한다.', 'Chủ nghĩa thực dụng đánh giá giá trị của tư tưởng bằng kết quả thực tế.', '7bc3d1e3-ce7f-4737-a499-5fc08aad4f78', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('919e29e7-f183-4e5f-b370-8b73bc285ceb', '미학 (美學)', NULL, 'Mỹ học', 'mihak', '미학은 아름다움의 본질과 예술을 탐구하는 학문이다.', 'Mỹ học là ngành học thuật khám phá bản chất của cái đẹp và nghệ thuật.', '89b69f86-d833-4284-a1cc-f0b4000fa584', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('660694dc-52f4-4ae6-bef2-e54c1dd51004', '구도 (構圖)', NULL, 'Bố cục, cấu đồ', 'gudo', '이 그림은 안정적인 삼각형 구도를 사용하여 균형감을 준다.', 'Bức tranh này sử dụng bố cục tam giác ổn định để tạo cảm giác cân bằng.', '89b69f86-d833-4284-a1cc-f0b4000fa584', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('8d132ebd-9d0c-4cfa-a060-1471ee85c300', '인상주의 (印象主義)', NULL, 'Chủ nghĩa Ấn tượng', 'insangju-ui', '인상주의 화가들은 빛의 변화에 따른 순간적인 인상을 포착하고자 했다.', 'Các họa sĩ theo trường phái Ấn tượng đã tìm cách nắm bắt ấn tượng tức thời theo sự thay đổi của ánh sáng.', '89b69f86-d833-4284-a1cc-f0b4000fa584', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('5c335524-2435-4949-8645-3d5ae33953e8', '입체주의 (立體主義)', NULL, 'Chủ nghĩa Lập thể', 'ipcheju-ui', '피카소는 입체주의를 통해 대상을 여러 시점에서 동시에 표현했다.', 'Picasso đã thể hiện đối tượng đồng thời từ nhiều góc nhìn thông qua chủ nghĩa Lập thể.', '89b69f86-d833-4284-a1cc-f0b4000fa584', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('88d0dfbd-a260-472f-8849-2abe6bd638e7', '추상 미술 (抽象美術)', NULL, 'Nghệ thuật trừu tượng', 'chusang misul', '추상 미술은 구체적인 형태를 재현하기보다는 색, 선, 면으로 감정을 표현한다.', 'Nghệ thuật trừu tượng biểu đạt cảm xúc bằng màu sắc, đường nét, và bề mặt thay vì tái hiện các hình dạng cụ thể.', '89b69f86-d833-4284-a1cc-f0b4000fa584', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('41782982-d282-4dba-a49f-7379aafffe16', '미장센 (Mise-en-scène)', NULL, 'Bố trí cảnh quay', 'mijangsen', '봉준호 감독은 정교한 미장센을 통해 영화의 주제를 암시한다.', 'Đạo diễn Bong Joon-ho ngụ ý chủ đề của bộ phim thông qua việc bố trí cảnh quay tinh xảo.', '10625e4c-5fa4-4983-953a-f77ad8b094f1', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('32f75dd7-a2f3-4bac-b2cc-a1fdaa62372a', '몽타주 (Montage)', NULL, 'Dựng phim, montage', 'mongtaju', '몽타주 기법은 여러 장면을 빠르게 교차하여 시간의 흐름을 압축적으로 보여준다.', 'Kỹ thuật montage cho thấy dòng chảy của thời gian một cách cô đọng bằng cách xen kẽ nhanh nhiều cảnh quay.', '10625e4c-5fa4-4983-953a-f77ad8b094f1', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('5ab37f3c-d67e-47aa-958f-601fc226d85e', '장르 (Genre)', NULL, 'Thể loại', 'jangneu', '이 영화는 스릴러 장르의 관습을 따르면서도 독창적인 변주를 보여준다.', 'Bộ phim này thể hiện sự biến tấu độc đáo trong khi vẫn tuân theo các quy ước của thể loại kinh dị.', '10625e4c-5fa4-4983-953a-f77ad8b094f1', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('f4ecbc04-88af-4907-b6d0-7ab8a947b4e6', '작가주의', '作家主義', 'Chủ nghĩa tác giả (Auteurism)', 'jakgaju-ui', '작가주의 비평은 감독을 영화의 최종적인 저자로 간주한다.', 'Phê bình theo chủ nghĩa tác giả coi đạo diễn là tác giả cuối cùng của bộ phim.', '10625e4c-5fa4-4983-953a-f77ad8b094f1', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('50467619-75db-48ee-be27-b0dbb1c4e1d1', '촬영술', '撮影術', 'Kỹ thuật quay phim', 'chwaryeongsul', '이 영화는 혁신적인 촬영술로 영상미를 극대화했다.', 'Bộ phim này đã tối đa hóa vẻ đẹp hình ảnh bằng kỹ thuật quay phim đột phá.', '10625e4c-5fa4-4983-953a-f77ad8b094f1', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('beb14e81-a0a9-4b57-8bc9-aaf00353c12e', '교향곡 (交響曲)', '交響曲', 'Giao hưởng (Symphony)', 'gyohyang-gok', '베토벤의 9번 교향곡 ''합창''은 인류의 위대한 유산이다.', 'Bản giao hưởng số 9 ''Hợp xướng'' của Beethoven là một di sản vĩ đại của nhân loại.', 'b913978b-deea-4171-9c3d-9d4b701ab606', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('4f612908-c866-41f5-b578-2423487b0d95', '협주곡 (協奏曲)', '協奏曲', 'Concerto', 'hyeopju-gok', '그녀는 오케스트라와 함께 라흐마니노프 피아노 협주곡을 연주했다.', 'Cô ấy đã biểu diễn bản concerto cho piano của Rachmaninoff cùng với dàn nhạc giao hưởng.', 'b913978b-deea-4171-9c3d-9d4b701ab606', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('4dd84c44-a153-4fc9-9efa-350ac995c8f8', '소나타 (Sonata)', '', 'Sonate', 'sonata', '모차르트의 피아노 소나타는 단순하면서도 깊이가 있다.', 'Các bản sonate cho piano của Mozart vừa đơn giản lại vừa có chiều sâu.', 'b913978b-deea-4171-9c3d-9d4b701ab606', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('d1ceae32-0e8f-45e1-932e-1ed7faf25b4b', '화성 (和聲)', '和聲', 'Hòa âm (Harmony)', 'hwaseong', '현대 음악에서는 불협화음을 사용한 복잡한 화성 진행이 나타난다.', 'Trong âm nhạc hiện đại, xuất hiện những tiến trình hòa âm phức tạp sử dụng âm nghịch.', 'b913978b-deea-4171-9c3d-9d4b701ab606', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('ed3d9dab-1a94-410b-8a93-2762eca87e74', '선율 (旋律)', '旋律', 'Giai điệu (Melody)', 'seon-yul', '이 곡은 누구나 쉽게 따라 부를 수 있는 아름다운 선율을 가지고 있다.', 'Bản nhạc này có một giai điệu đẹp mà bất cứ ai cũng có thể dễ dàng hát theo.', 'b913978b-deea-4171-9c3d-9d4b701ab606', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('a42db334-50cd-4790-b311-8483a51c118e', '음운론 (音韻論)', NULL, 'Âm vị học', 'eum-un-non', '음운론은 말소리의 체계와 규칙을 연구하는 학문이다.', 'Âm vị học là ngành nghiên cứu hệ thống và quy tắc của âm thanh lời nói.', 'eb4ad100-641a-4b50-b1ea-4a5376f13fd4', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('5b3e6bb4-9e5b-436e-81d0-8e53474d15cb', '형태론 (形態論)', NULL, 'Hình thái học', 'hyeongtae-ron', '형태론은 단어의 내부 구조와 형성 원리를 다룬다.', 'Hình thái học nghiên cứu cấu trúc bên trong của từ và nguyên lý hình thành từ.', 'eb4ad100-641a-4b50-b1ea-4a5376f13fd4', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('dccb1d9d-5b10-42ed-a9f2-6e892d7bf04d', '통사론 (統辭論)', NULL, 'Cú pháp học', 'tongsa-ron', '통사론은 단어들이 결합하여 문장을 이루는 규칙을 분석한다.', 'Cú pháp học phân tích các quy tắc mà theo đó các từ kết hợp để tạo thành câu.', 'eb4ad100-641a-4b50-b1ea-4a5376f13fd4', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('e0278d87-f32f-4f2d-a1fe-9fbf7f2d3611', '의미론 (意味論)', NULL, 'Ngữ nghĩa học', 'uimi-ron', '의미론은 단어와 문장의 의미를 연구한다.', 'Ngữ nghĩa học nghiên cứu ý nghĩa của từ và câu.', 'eb4ad100-641a-4b50-b1ea-4a5376f13fd4', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('a0cccc91-697e-46f6-bf77-85538e70f82a', '사회 언어학 (社會言語學)', NULL, 'Ngôn ngữ học xã hội', 'sahoe eon-eohak', '사회 언어학은 사회적 맥락 속에서 언어가 어떻게 사용되는지를 탐구한다.', 'Ngôn ngữ học xã hội khám phá cách ngôn ngữ được sử dụng trong bối cảnh xã hội.', 'eb4ad100-641a-4b50-b1ea-4a5376f13fd4', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('7b2e3c50-b13d-4593-8d2e-b913cf28c728', '창조 신화', '創造神話', 'Thần thoại sáng thế', 'changjo sinhwa', '단군 신화는 한국의 대표적인 창조 신화이다.', 'Thần thoại Dangun là thần thoại sáng thế tiêu biểu của Hàn Quốc.', 'f39d5845-9e27-4b11-8e16-8d6707737b49', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('9062365d-313e-4358-a27d-d9da3c4c0736', '유일신교', '唯一神教', 'Độc thần giáo (Monotheism)', 'yu-ilsin-gyo', '기독교와 이슬람교는 대표적인 유일신교이다.', 'Kitô giáo và Hồi giáo là những tôn giáo độc thần tiêu biểu.', 'f39d5845-9e27-4b11-8e16-8d6707737b49', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('bc3121fa-29b9-451f-a779-f266e1aa72e5', '다신교', '多神教', 'Đa thần giáo (Polytheism)', 'dasin-gyo', '고대 그리스 종교는 제우스를 비롯한 여러 신을 섬기는 다신교였다.', 'Tôn giáo Hy Lạp cổ đại là một tôn giáo đa thần, thờ phụng nhiều vị thần bao gồm cả Zeus.', 'f39d5845-9e27-4b11-8e16-8d6707737b49', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('83344b18-0202-45d4-bd65-3ce03b9ab5c3', '세속주의', '世俗主義', 'Chủ nghĩa thế tục', 'sesokju-ui', '현대 국가는 종교와 정치를 분리하는 세속주의를 채택한다.', 'Các quốc gia hiện đại áp dụng chủ nghĩa thế tục, tách biệt tôn giáo và chính trị.', 'f39d5845-9e27-4b11-8e16-8d6707737b49', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('d4a8e063-9f6c-473a-8aaa-6ab21e520847', '교리', '敎理', 'Giáo lý, tín điều', 'gyori', '각 종교는 고유한 교리 체계를 가지고 있다.', 'Mỗi tôn giáo đều có hệ thống giáo lý riêng của mình.', 'f39d5845-9e27-4b11-8e16-8d6707737b49', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('5592a306-c28f-43e3-af4c-3369e7275292', '건축 양식 (建築樣式)', NULL, 'Phong cách kiến trúc', 'geonchuk yangsik', '고딕 건축 양식은 높은 천장과 뾰족한 아치가 특징이다.', 'Phong cách kiến trúc Gothic có đặc trưng là trần nhà cao và các mái vòm nhọn.', '28a71764-7102-4bb5-bb4e-45c4e8ac1b81', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('999f6c0a-6175-4bce-8969-734bb483bb45', '공간 (空間)', NULL, 'Không gian', 'gong-gan', '현대 건축은 열린 공간과 유기적인 형태를 중시한다.', 'Kiến trúc hiện đại coi trọng không gian mở và hình thái hữu cơ.', '28a71764-7102-4bb5-bb4e-45c4e8ac1b81', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('0ad23498-71d4-464a-879a-a4f26b2df216', '기능성 (機能性)', NULL, 'Tính công năng, tính chức năng', 'gineungseong', '바우하우스는 미적인 것보다 기능성을 강조했다.', 'Trường phái Bauhaus đã nhấn mạnh tính công năng hơn là tính thẩm mỹ.', '28a71764-7102-4bb5-bb4e-45c4e8ac1b81', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('21bc0ab2-bc38-4a9e-934e-c98a82d07684', '유기적 건축', NULL, 'Kiến trúc hữu cơ', 'yugijeok geonchuk', '프랭크 로이드 라이트는 자연과 조화를 이루는 유기적 건축을 추구했다.', 'Frank Lloyd Wright đã theo đuổi kiến trúc hữu cơ, hòa hợp với thiên nhiên.', '28a71764-7102-4bb5-bb4e-45c4e8ac1b81', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('4fe632f7-bc9b-4ab7-9900-c20a0a4ab313', '비대칭', NULL, 'Bất đối xứng', 'bidaeching', '그의 건축물은 의도적인 비대칭을 통해 역동적인 느낌을 준다.', 'Công trình kiến trúc của ông ấy tạo cảm giác năng động thông qua sự bất đối xứng có chủ đích.', '28a71764-7102-4bb5-bb4e-45c4e8ac1b81', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('18b9bd90-37ac-4d73-b2d5-1293a5df439e', '판소리', NULL, 'Pansori (loại hình kể chuyện bằng âm nhạc)', 'pansori', '판소리는 소리꾼 한 명과 고수 한 명이 이야기를 이끌어가는 공연 예술이다.', 'Pansori là một nghệ thuật biểu diễn trong đó một người kể chuyện (sorikkun) và một người đánh trống (gosu) dẫn dắt câu chuyện.', '906a4180-0fa7-4ad5-b17f-740be245a928', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('feb837f7-8cb1-4352-a5bd-398f2d6e4d6b', '민화 (民畫)', NULL, 'Tranh dân gian', 'minhwa', '민화에는 서민들의 소박한 꿈과 해학이 담겨 있다.', 'Tranh dân gian chứa đựng những ước mơ giản dị và sự hài hước của người dân thường.', '906a4180-0fa7-4ad5-b17f-740be245a928', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('feb9ab0a-3180-4cd7-aa3a-58627d22c56b', '청자 (靑瓷)', NULL, 'Gốm men ngọc (Celadon)', 'cheongja', '고려청자는 비취색의 아름다운 빛깔로 유명하다.', 'Gốm men ngọc Goryeo nổi tiếng với màu sắc đẹp như ngọc bích.', '906a4180-0fa7-4ad5-b17f-740be245a928', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('1a87ab04-9a39-4ddc-9298-b55c4dd321eb', '백자 (白瓷)', NULL, 'Sứ trắng', 'baekja', '조선 시대 선비들은 백자의 순수하고 절제된 아름다움을 사랑했다.', 'Các học giả thời Joseon yêu thích vẻ đẹp thuần khiết và có chừng mực của sứ trắng.', '906a4180-0fa7-4ad5-b17f-740be245a928', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('4c427109-5435-4c07-b19c-9808c6ee3bf2', '수묵화 (水墨畫)', NULL, 'Tranh thủy mặc', 'sumukhwa', '수묵화는 먹의 농담을 조절하여 대상의 정신을 표현한다.', 'Tranh thủy mặc biểu đạt tinh thần của đối tượng bằng cách điều chỉnh độ đậm nhạt của mực.', '906a4180-0fa7-4ad5-b17f-740be245a928', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z');
INSERT INTO "QuizQuestion" (id, question, "questionTranslation", options, "correctAnswer", explanation, "VocabularyId", "createdAt", "updatedAt") VALUES ('807c1876-a8ae-4b27-8abc-43ae945f2576', '이야기가 시간, 공간, 인과 관계 등에 따라 배열되고 구성되는 방식을 뭐라고 하나요?', 'Cách thức mà một câu chuyện được sắp xếp và tổ chức dựa trên thời gian, không gian, quan hệ nhân quả, v.v. được gọi là gì?', ARRAY['문체', '서사 구조', '주제', '등장인물']::text[], '서사 구조', '서사 구조 (narrative structure) là khuôn khổ hay kiểu tổ chức cốt truyện, bao gồm trình tự các sự kiện, sự phân bố thời gian (현행, 회상, 예시 등), và mối quan hệ giữa các yếu tố trong tác phẩm.', 'c4458d7c-0e3e-4884-8177-629f795881bb', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('63433eeb-6dca-46e8-a522-0defb64a147f', '"인물, 갈등, 위기, 절정, 결말" 등은 전통적인 ______의 요소입니다.', '"Nhân vật, xung đột, khủng hoảng, cao trào, kết thúc" là những yếu tố của ______ truyền thống.', ARRAY['문학 장르', '서사 구조', '시적 표현', '비평 방법']::text[], '서사 구조', 'Đây là các giai đoạn cơ bản của 서사 구조 trong cốt truyện cổ điển (발단, 전개, 위기, 절정, 결말), thường được gọi là ''Freytag''s Pyramid''.', 'c4458d7c-0e3e-4884-8177-629f795881bb', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('9c6d39ba-0740-4180-b219-56f9276ff614', '말의 표면적 의미와 실제 의미가 반대이거나, 의도한 결과와 실제 결과가 정반대인 상황을 나타내는 수사법은?', 'Biện pháp tu từ biểu thị tình huống mà nghĩa bề mặt của lời nói trái ngược với nghĩa thực tế, hoặc kết quả dự định trái ngược với kết quả thực tế là gì?', ARRAY['은유', '아이러니', '과장', '반복']::text[], '아이러니', '아이러니 (irony) là một kỹ thuật tu từ hoặc một tình huống trong đó có sự chênh lệch, đối lập giữa cái dự kiến/cái xuất hiện và cái thực tế/cái thật sự, nhằm tạo ra sự hài hước sâu sắc, châm biếm hoặc bi kịch.', '1aec8332-d97e-46d1-98ae-0988bb6b6e03', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('7f5bdca9-e828-4da9-8d82-27348260c597', '등장인물이 모르는 사실을 독자만 아는 상황은 ______의 예입니다.', 'Tình huống mà chỉ độc giả biết sự thật còn nhân vật thì không là ví dụ của ______.', ARRAY['직설법', '드라마적 아이러니', '비유', '직유']::text[], '드라마적 아이러니', '드라마적 아이러니 (dramatic irony) là một dạng của 아이러니, xảy ra khi khán giả/độc giả biết một thông tin quan trọng mà nhân vật trong tác phẩm không biết, tạo nên sự kịch tính và cảm xúc đặc biệt.', '1aec8332-d97e-46d1-98ae-0988bb6b6e03', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('77a507e2-88f1-4688-ad67-737c38a6bacb', '작가가 자신의 시대, 사회, 역사에 대해 가지고 있는 생각, 신념, 태도, 가치관 등을 통칭하여 뭐라고 하나요?', 'Toàn bộ suy nghĩ, niềm tin, thái độ, giá trị quan mà một tác giả có về thời đại, xã hội, lịch sử của mình được gọi chung là gì?', ARRAY['작가 의식', '독자 반응', '텍스트 구조', '언어 유희']::text[], '작가 의식', '작가 의식 (authorial consciousness) là hệ thống tư tưởng và thế giới quan của tác giả, được hình thành bởi hoàn cảnh lịch sử-xã hội cá nhân, và có ảnh hưởng sâu sắc đến chủ đề, nội dung và thông điệp của tác phẩm.', '468dce4e-3a28-4843-96e8-c6ec03037507', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('dcd24aa7-d830-4f55-94a4-ce782c9e1516', '비평가들은 사회비평이나 역사비평에서 ______를 중요한 분석 도구로 삼습니다.', 'Các nhà phê bình sử dụng ______ như một công cụ phân tích quan trọng trong phê bình xã hội hoặc phê bình lịch sử.', ARRAY['독자의 상상력', '작가 의식', '텍스트의 객관성', '출판사의 의도']::text[], '작가 의식', 'Việc nghiên cứu 작가 의식 giúp đặt tác phẩm vào bối cảnh lịch sử, văn hóa, xã hội cụ thể, từ đó hiểu rõ hơn về thông điệp và hàm ý của tác giả đối với các vấn đề đương thời.', '468dce4e-3a28-4843-96e8-c6ec03037507', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('c7571339-105f-47dc-a8cc-8f200d763070', '인간의 실존, 자유, 책임, 고독, 불안 등을 주요 주제로 삼는 20세기 철학·문학 사조는?', 'Trào lưu triết học-văn học thế kỷ 20 lấy sự tồn tại, tự do, trách nhiệm, cô đơn, lo âu của con người làm chủ đề chính là gì?', ARRAY['낭만주의', '리얼리즘', '실존주의', '포스트모더니즘']::text[], '실존주의', '실존주의 (existentialism) là một trào lưu tư tưởng nhấn mạnh sự tồn tại có trước bản chất, sự tự do tuyệt đối và trách nhiệm cá nhân của con người trong một thế giới vô nghĩa và phi lý.', '98806251-ffdd-4a5c-b8da-99142f045c9d', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('6bc38394-991e-4e87-b045-b8bf3f4f265c', '______ 문학에서는 부조리한 상황에 맞서는 인간의 고독한 투쟁이 자주 묘사됩니다.', 'Trong văn học ______, cuộc đấu tranh cô độc của con người chống lại hoàn cảnh phi lý thường được miêu tả.', ARRAY['계몽주의', '실존주의', '고전주의', '사실주의']::text[], '실존주의', 'Các nhà văn 실존주의 như 사르트르(Sartre), 카뮈(Camus) thường tập trung khắc họa nhân vật đối mặt với sự phi lý (부조리) của cuộc sống, phải tự đưa ra lựa chọn và định nghĩa ý nghĩa cho chính mình.', '98806251-ffdd-4a5c-b8da-99142f045c9d', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('75468541-1342-4bbe-9925-536f5c2a238f', '정신분석학 이론(특히 프로이트, 융의 이론)을 응용하여 문학 작품 속 인물의 심리, 작가의 무의식 등을 분석하는 비평 방법은?', 'Phương pháp phê bình áp dụng lý thuyết phân tâm học (đặc biệt là của Freud, Jung) để phân tích tâm lý nhân vật trong tác phẩm văn học, vô thức của tác giả, v.v. là gì?', ARRAY['형식주의 비평', '심리주의 비평', '구조주의 비평', '마르크스주의 비평']::text[], '심리주의 비평', '심리주의 비평 (psychological criticism) sử dụng các khái niệm như 무의식 (vô thức), 콤플렉스 (phức cảm), 꿈 해석, 욕망 (ham muốn) để giải mã các yếu tố ẩn trong văn bản và tâm lý của tác giả/nhân vật.', '8e475669-7dc4-4191-b62d-210098cc28d5', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('392f2b40-565e-4c57-8c10-46f8b8790c62', '______은/는 ''오이디푸스 콤플렉스'' 같은 개념을 통해 등장인물의 행동을 설명할 수 있습니다.', '______ có thể giải thích hành vi của nhân vật thông qua các khái niệm như ''mặc cảm Oedipus''.', ARRAY['사회학 비평', '심리주의 비평', '역사주의 비평', '미학 비평']::text[], '심리주의 비평', 'Đây là một phương pháp phê bình có ảnh hưởng lớn, cho rằng văn học phản ánh những xung đột và ham muốn vô thức sâu thẳm của con người, và việc phân tích chúng có thể làm sáng tỏ ý nghĩa tác phẩm.', '8e475669-7dc4-4191-b62d-210098cc28d5', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('70c6a1aa-f428-4ae1-9518-e07435f93fd0', '신라 시대에 만들어져 구전되다가 향찰로 기록된 한국 고유의 정형시는 무엇인가요?', 'Thể thơ định hình bản địa của Hàn Quốc được sáng tác vào thời Silla, truyền miệng và sau đó được ghi lại bằng chữ Hương là gì?', ARRAY['한시', '시조', '향가', '가사']::text[], '향가', '향가 có nghĩa là ''Hương ca'', là thể loại thơ ca lâu đời nhất còn tồn tại của Hàn Quốc, phát triển rực rỡ vào thời Silla (thế kỷ 7-9). Nội dung thường liên quan đến tình yêu đất nước, Phật giáo, hay tình cảm cá nhân. Chúng được sáng tác bằng tiếng Hàn bản địa và ghi lại bằng ''향찰'' (phương sách), một hệ thống chữ viết sử dụng Hán tự để biểu thị âm Hàn. Chỉ còn lại 25 bài, nhưng có giá trị văn học và lịch sử rất cao.', 'aecc73b2-ed5b-459d-9aa9-2f9d24d5edf5', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('5052f130-0546-4b71-9464-23163809d4eb', '구비 서사시 판소리가 소설 형식으로 기록되거나 각색된 조선 후기의 대표적인 소설 유형은?', 'Thể loại tiểu thuyết tiêu biểu cuối thời Joseon, được ghi chép lại hoặc chuyển thể từ sử thi truyền miệng Pansori, là gì?', ARRAY['군담소설', '판소리계 소설', '가정소설', '역사소설']::text[], '판소리계 소설', '판소리계 소설 có nghĩa là ''tiểu thuyết hệ Pansori''. Đây là những tác phẩm văn học được hình thành trên cơ sở ''판소리'' - một loại hình nghệ thuật trình diễn kể chuyện bằng âm nhạc. Các tác phẩm này thường phản ánh đời sống và khát vọng của ''서민'' (người dân thường), châm biếm tầng lớp ''양반'' (quý tộc) và đề cao tinh thần ''저항'' (phản kháng). ''춘향전'', ''심청전'', ''흥부전'' là những đại diện tiêu biểu.', '180239e2-5aac-4616-bd44-04d0ed1be95b', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('bec75cab-b835-4046-9aec-389e151721ab', '개화기에서 1910년대까지 등장한, 근대적 소재와 서구적 기법을 도입한 새로운 형태의 소설을 뭐라고 하나요?', 'Thể loại tiểu thuyết mới xuất hiện từ thời kỳ Khai hóa (개화기) đến những năm 1910, sử dụng đề tài hiện đại và du nhập kỹ thuật phương Tây, được gọi là gì?', ARRAY['고전 소설', '신소설', '현대 소설', '대하 소설']::text[], '신소설', '신소설 có nghĩa là ''Tân tiểu thuyết'' (New novel). Đây là cầu nối giữa tiểu thuyết cổ điển và tiểu thuyết hiện đại Hàn Quốc, xuất hiện trong giai đoạn chuyển giao từ xã hội phong kiến sang hiện đại. 신소설 thường mang tính chất ''계몽적'' (khai sáng), đề cập đến các vấn đề xã hội mới như tự do yêu đương, bình đẳng, và phê phán những hủ tục lạc hậu. Ngôn ngữ sử dụng là hỗn hợp Hán-Hàn (국한문혼용체).', '00c10749-8512-43a5-9b0d-019002ed7ed2', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('66936093-1301-4585-a148-3c45b5cf0e38', '작가가 정치적, 사회적 현실에 적극적으로 참여하여 현실의 부정과 모순을 비판하고 변혁을 지향하는 문학 경향은?', 'Khuynh hướng văn học mà nhà văn tích cực tham gia vào thực tại chính trị, xã hội, phê phán những mặt tiêu cực và mâu thuẫn của hiện thực, và hướng tới sự cải biến là gì?', ARRAY['순수 문학', '참여 문학', '판타지 문학', '자연주의 문학']::text[], '참여 문학', '참여 문학 có nghĩa là ''văn học tham dự'' hay ''văn học dấn thân'' (Engaged literature). Đây là trào lưu nổi bật dưới chế độ độc tài quân sự (군사 정권) của Hàn Quốc, đặc biệt trong những năm 1970-80. Các tác giả ''참여 문학'' tin rằng văn học không thể đứng ngoài xã hội, và họ sử dụng ngòi bút để phản ánh và phê phán sự áp bức, bất công, đồng thời đấu tranh cho dân chủ (민주화 운동) và công bằng xã hội.', '491b85d9-179f-40ba-b99f-55a8a8105167', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('e191c393-4da1-40f1-886c-9829d9fe0687', '문학의 외적 목적이나 사회적 효용보다는 문학 자체의 예술적 가치와 언어적 심미성을 추구하는 문학 경향은?', 'Khuynh hướng văn học theo đuổi giá trị nghệ thuật tự thân của văn học và vẻ đẹp thẩm mỹ của ngôn ngữ hơn là mục đích bên ngoài hay tính hữu dụng xã hội của văn học là gì?', ARRAY['참여 문학', '순수 문학', '대중 문학', '계몽 문학']::text[], '순수 문학', '순수 문학 có nghĩa là ''văn học thuần túy'' (Pure literature). Trái ngược với ''참여 문학'', các nhà văn theo chủ nghĩa ''순수 문학'' tin rằng giá trị cốt lõi của văn học nằm ở chính bản thân nghệ thuật ngôn từ, sự khám phá nội tâm con người và vẻ đẹp hình thức. Họ cho rằng văn học nên độc lập với các tư tưởng chính trị (정치적 이념) hay mục đích tuyên truyền. Cuộc tranh luận giữa ''순수 문학'' và ''참여 문학'' là một chủ đề quan trọng trong lịch sử văn học Hàn Quốc hiện đại.', '8bd0d71b-5414-455f-a272-1f384256c80e', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('4f975ace-64ca-4fc0-9c1b-c3248f846630', '''착한 일을 권장하고, 악한 일을 벌한다''는 뜻으로, 예술 작품 등에서 선이 승리하고 악이 징벌받는 교훈적 주제를 가리키는 사자성어는 무엇인가요?', 'Thành ngữ bốn chữ Hán có nghĩa ''khuyến khích việc thiện, trừng phạt việc ác'', chỉ chủ đề mang tính giáo huấn trong các tác phẩm nghệ thuật rằng cái thiện thắng lợi và cái ác bị trừng phạt là gì?', ARRAY['선악과보', '권선징악 (勸善懲惡)', '호불호', '동상이몽']::text[], '권선징악 (勸善懲惡)', '권선징악 có nghĩa là ''khuyến thiện trừng ác''. Đây là một trong những 주제 (chủ đề) chính trong 많은 truyền thống văn học và điện ảnh, nhằm 강화하다 (củng cố) các giá trị đạo đức xã hội.', 'fffe3781-4ef5-47da-9d64-53229542f380', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('2b379a6a-592e-4a00-a1f3-77eb4aa1d76b', '경험과 사실에 근거하지 않고, 이미 굳어져 변화하기 어려운 편견이나 선입견을 무엇이라고 하나요?', 'Những thành kiến hoặc định kiến đã cứng nhắc, khó thay đổi, không dựa trên kinh nghiệm và thực tế được gọi là gì?', ARRAY['객관적 시각', '고정관념 (固定觀念)', '창의적 사고', '융통성']::text[], '고정관념 (固定觀念)', '고정관념 có nghĩa là ''định kiến'', ''quan niệm cố định''. Đây là một 생각이 틀에 박혀 (khuôn sáo) 유연하지 못하다 (không linh hoạt). 사회에서는 성별, 나이, 인종 등에 대한 고정관념이 문제가 될 수 있다. 이를 타파하다 (phá vỡ)는 것이 중요하다.', 'c1527c8c-03d4-4e94-8bab-ece105a94677', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('ae5ff1d1-f90c-4239-9df9-ce0f2ece909c', '둘 이상의 사실, 주장, 감정 등이 서로 맞지 않아 충돌하거나 논리적으로 일관성이 없는 상태를 나타내는 말은 무엇인가요?', 'Từ chỉ trạng thái hai hay nhiều sự thật, lập luận, cảm xúc... không phù hợp với nhau, xung đột hoặc không nhất quán về mặt logic là gì?', ARRAY['조화', '모순 (矛盾)', '일치', '합의']::text[], '모순 (矛盾)', '모순 có nghĩa là ''mâu thuẫn''. Từ này xuất phát từ câu chuyện cổ về một người bán ''mâu và thuẫn'' (창과 방패), tự khoe mâu đâm thủng mọi thuẫn và thuẫn chặn được mọi mâu, tạo ra sự vô lý. 현실에서 모순을 해결하다 (giải quyết mâu thuẫn)는 것이 필요하다.', 'f829bea2-e86f-4139-ace6-1b8ad909b18f', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('a15dd152-fb23-45bd-b1e2-531309c01da6', '''미리 준비해 두면 걱정할 것이 없다''는 의미의 사자성어는 무엇인가요?', 'Thành ngữ bốn chữ Hán có nghĩa ''nếu chuẩn bị trước thì sẽ không có gì phải lo lắng'' là gì?', ARRAY['일석이조', '유비무환 (有備無患)', '수수방관', '금상첨화']::text[], '유비무환 (有備無患)', '유비무환 có nghĩa là ''hữu bị vô hoạn''. Đây là 교훈 (bài học) về sự chuẩn bị, 대비 (phòng bị)의 중요성을 강조한다. 자연재해, 경제 위기 등에 대해 사전에 계획하고 준비함으로써 피해를 최소화할 수 있다는 의미이다.', '6e8d09eb-85c3-4bbf-8de2-159c42269d21', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('2f090e42-6888-44ad-9a00-b8758718a6f0', '하늘이 높고 말이 살찌는, 즉 날씨가 청명하고 쾌적한 가을을 아름답게 표현하는 사자성어는 무엇인가요?', 'Thành ngữ bốn chữ Hán diễn tả một cách đẹp đẽ về mùa thu với ý ''trời cao, ngựa béo'', tức thời tiết trong lành và dễ chịu, là gì?', ARRAY['춘하추동', '천고마비 (天高馬肥)', '한여름', '설렁설렁']::text[], '천고마비 (天高馬肥)', '천고마비 có nghĩa là ''thiên cao mã phì''. Đây là thành ngữ dùng để miêu tả vẻ đẹp lý tưởng và không khí trong lành của mùa thu, khi 하늘이 맑고 (trời trong), 기온이 알맞다 (nhiệt độ vừa phải). 한국에서는 독서의 계절 (mùa đọc sách)로도 여겨진다.', '8bd6a68c-d2b9-4f18-9dc2-d5d2a112d35c', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('c8c6bc8d-ee84-4a75-98e7-a6604f99444b', '자신이 가장 잘하는 일에서도 가끔 실패할 수 있다는 뜻의 속담은 무엇인가요?', 'Tục ngữ nào có nghĩa là ngay cả trong việc mình giỏi nhất, đôi khi cũng có thể thất bại?', ARRAY['우물 안 개구리', '원숭이도 나무에서 떨어진다', '시작이 반이다', '호랑이도 제 말 하면 온다']::text[], '원숭이도 나무에서 떨어진다', '이 속담은 아무리 전문가라도, 익숙한 일이라도 가끔 실수나 실패를 할 수 있음을 비유적으로 말합니다. 따라서 자만하지 말고 겸손해야 함을 교훈으로 줍니다.', '2e7f00b6-4f5a-437a-8ad6-66a3c7e12dda', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('ef0048ac-9e03-4b39-8f4d-4fa64cb51b41', '고수에게도 약점이 있고, 아무리 뛰어난 사람도 실패할 수 있다는 뜻을 가진 속담은?', 'Tục ngữ mang ý nghĩa rằng bậc thầy cũng có điểm yếu, dù người tài giỏi đến đâu cũng có thể thất bại là gì?', ARRAY['낫 놓고 기역자도 모른다', '원숭이도 나무에서 떨어진다', '가는 말이 고와야 오는 말이 곱다', '등잔 밑이 어둡다']::text[], '원숭이도 나무에서 떨어진다', '이 속담은 자신의 능력을 과신하지 말라는 경계의 의미와, 타인의 실수를 너그럽게 이해하라는 위로의 의미를 동시에 지닙니다. ''원숭이''는 나무 타기의 전문가를 비유합니다.', '2e7f00b6-4f5a-437a-8ad6-66a3c7e12dda', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('6ea4b3db-28dc-4d83-ba91-df9858ee5f59', '넓은 세상을 보지 못하고 자기만의 좁은 세계에 갇혀 세상 물정을 모르는 사람을 비유하는 말은?', 'Từ dùng để ví von về người không thấy được thế giới rộng lớn, bị mắc kẹt trong thế giới nhỏ bé của riêng mình và không biết gì về thế sự là gì?', ARRAY['하늘의 별 따기', '우물 안 개구리', '땅 짚고 헤엄치기', '바늘 도둑이 소 도둑 된다']::text[], '우물 안 개구리', '이 표현은 지식이나 경험이 제한적이고 시야가 좁아 큰 세상을 모르는 사람을 빗댑니다. 자신의 작은 세계가 전부라고 착각하는 태도를 꼬집습니다.', 'a2af6fc8-024f-445a-852d-d0059637ec1c', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('6676c767-caac-4bfd-bdab-43897de6ec90', '''______''가 되지 않으려면 다양한 사람을 만나고 많은 것을 배워야 합니다.', 'Để không trở thành ''______'', cần phải gặp gỡ nhiều người và học hỏi nhiều thứ.', ARRAY['우물 안 개구리', '바다 속 고래', '산 위의 독수리', '하늘을 나는 새']::text[], '우물 안 개구리', '이 표현은 경험과 지식의 폭을 넓히고, 세상에 대한 편협한 시각을 버리며, 끊임없이 배우고 성장해야 함을 일깨워줍니다. 해외 경험, 독서, 다양한 교류 등이 이를 방지합니다.', 'a2af6fc8-024f-445a-852d-d0059637ec1c', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('f2ac4e43-5c7e-4676-94fe-b6bf7e3d0600', '어떤 일을 시작하는 것이 그 일을 거의 다 한 것만큼 중요하고 어렵다는 뜻의 격언은?', 'Lời răn nào có nghĩa là bắt đầu một việc quan trọng và khó khăn gần như đã hoàn thành một nửa công việc?', ARRAY['백지장도 맞들면 낫다', '시작이 반이다', '고생 끝에 낙이 온다', '열 번 찍어 안 넘어가는 나무 없다']::text[], '시작이 반이다', '이 말은 결심하고 행동을 시작하는 것이 가장 어렵고, 한번 시작하면 그 추진력을 이어 나가기가 상대적으로 쉬워진다는 의미입니다. 망설이거나 두려워하는 사람에게 용기를 주는 격언입니다.', '6f126fd8-ce05-409b-a4c2-3299b3787b0c', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('fc52c2bd-2017-41ec-8eea-244bdef3151c', '새로운 프로젝트나 공부를 시작할 때 망설이는 친구에게 해줄 수 있는 격려의 말은?', 'Câu động viên nào có thể nói với người bạn đang do dự khi bắt đầu một dự án mới hoặc việc học?', ARRAY['"시작이 반이야! 한번 해보자."', '"호랑이도 제 말 하면 온다."', '"가는 날이 장날이다."', '"등잔 밑이 어둡다."']::text[], '"시작이 반이야! 한번 해보자."', '''시작이 반이다''는 막연한 두려움과 부담감을 덜어주고, 첫 발을 내딛도록 독려하는 데 아주 적합한 표현입니다. 시작의 중요성을 강조합니다.', '6f126fd8-ce05-409b-a4c2-3299b3787b0c', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('bd894581-e4fd-4eb3-9298-383f3050c0d7', '정성과 노력을 들여 이루어 놓은 것은 쉽게 무너지지 않는다는 뜻의 속담은?', 'Tục ngữ nào có nghĩa là những thứ được tạo dựng nên bằng tấm lòng và nỗ lực thì sẽ không dễ dàng sụp đổ?', ARRAY['천 리 길도 한 걸음부터', '공든 탑이 무너지랴', '백지장도 맞들면 낫다', '소 잃고 외양간 고친다']::text[], '공든 탑이 무너지랴', '이 속담은 진심으로 애쓰고 수고하여 이룬 성과나 관계는 튼튼하고 오래간다는 것을 비유합니다. ''공''은 정성과 노력을, ''탑''은 그 결과물을 의미합니다.', '2d3e822b-feb7-487a-be41-db0e15bed6a6', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('79c8285e-362f-420b-8484-c666b55369bf', '장기간에 걸쳐 열심히 준비한 사람에게 ''______''라고 말하며 확신을 주어요.', 'Với người đã chuẩn bị chăm chỉ trong thời gian dài, ta nói ''______'' để cho họ sự tự tin.', ARRAY['가는 말이 고와야', '공든 탑이 무너지랴', '바늘 도둑이 소 도둑 된다', '하늘의 별 따기']::text[], '공든 탑이 무너지랴', '이 말은 노력의 가치와 그에 따른 결과를 믿으라는 긍정적인 메시지를 담고 있습니다. 시험, 취업, 프로젝트 등 오랜 준비 끝에 임박한 중요한 순간에 하는 격려로 자주 쓰입니다.', '2d3e822b-feb7-487a-be41-db0e15bed6a6', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('07647ff3-6e6e-4506-b06f-c77bbc0f6c3e', '말을 잘하면 어려운 문제도 해결되고 큰 빚도 갚을 수 있다는 뜻으로, 말의 중요성을 강조하는 속담은?', 'Tục ngữ nhấn mạnh tầm quan trọng của lời nói, rằng nếu nói năng khéo léo thì dù vấn đề khó khăn cũng có thể giải quyết, món nợ lớn cũng có thể trả là gì?', ARRAY['말이 씨가 된다', '말 한마디에 천 냥 빚도 갚는다', '가는 말이 고와야 오는 말이 곱다', '입에 발린 말']::text[], '말 한마디에 천 냥 빚도 갚는다', '이 속담은 한 마디의 적절하고 따뜻한 말이 감정의 빚(오해, 갈등)을 없애고 관계를 회복시킬 수 있는 막대한 힘을 지니고 있음을 강조합니다. 화술과 소통의 중요성을 말합니다.', '47054cf3-7381-4d43-a601-8fc65c2bdb8f', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('020caed4-1628-433b-a9bf-64cf0e060f64', '상대방의 기분을 좋게 하는 적절한 칭찬이나 사과는 ______는 속담처럼 강력한 효과가 있어요.', 'Một lời khen ngợi hoặc xin lỗi phù hợp khiến đối phương vui lòng có tác dụng mạnh mẽ như tục ngữ ______.', ARRAY['"우물 안 개구리"', '"말 한마디에 천 냥 빚도 갚는다"', '"원숭이도 나무에서 떨어진다"', '"시작이 반이다"']::text[], '"말 한마디에 천 냥 빚도 갚는다"', '이 속담은 비단 재물의 빚 뿐 아니라 정서적, 관계적 빚도 말 한마디로 해결할 수 있음을 시사합니다. 갈등 상황에서 진심 어린 사과나, 위로와 지지의 말이 얼마나 큰 힘을 발휘하는지 보여줍니다.', '47054cf3-7381-4d43-a601-8fc65c2bdb8f', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('fca01c08-1586-4238-bfbf-481df0d60bac', '유교의 이상적인 인간상, 즉 군자가 갖춰야 할 네 가지 핵심 덕성을 무엇이라고 하나요?', 'Hình mẫu con người lý tưởng trong Nho giáo, tức bốn đức tính cốt lõi mà một bậc quân tử phải có, được gọi là gì?', ARRAY['충효예지', '인의예지', '예악형정', '천지군친']::text[], '인의예지', '인의예지 (Nhân-Nghĩa-Lễ-Trí) là bốn đức tính cơ bản trong triết học Nho giáo: 1) **인 (Nhân)**: Lòng nhân ái, thương người. 2) **의 (Nghĩa)**: Lẽ phải, đạo nghĩa. 3) **예 (Lễ)**: Phép tắc, nghi lễ, sự tôn trọng. 4) **지 (Trí)**: Trí tuệ, sự sáng suốt. Đây là tiêu chuẩn đạo đức để tu dưỡng bản thân và duy trì trật tự xã hội trong tư tưởng Nho gia.', 'fba40a6d-6e76-4eef-bb8f-baf5196e8de3', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('b28161ca-86d9-415e-aa74-c0969f3ed3fd', '불교에서 생사의 고통과 윤회의 굴레에서 완전히 벗어나 자유롭고 평안한 궁극적 상태를 뭐라고 하나요?', 'Trong Phật giáo, trạng thái tối thượng của sự tự do và an lạc, hoàn toàn thoát khỏi khổ đau của sinh tử và vòng luân hồi, được gọi là gì?', ARRAY['업', '해탈', '윤회', '보살']::text[], '해탈', '해탈 có nghĩa là ''giải thoát'' (Moksha/Nirvana). Đây là mục đích tối hậu trong Phật giáo, đạt được thông qua sự giác ngộ (깨달음) về bản chất thực sự của thực tại. Giải thoát là thoát khỏi ''번뇌'' (phiền não) và ''무명'' (vô minh) - nguyên nhân gây ra ''고'' (khổ đau) và ''윤회'' (luân hồi). Khi đạt ''해탈'', người ta chấm dứt chu kỳ tái sinh và đạt được sự an lạc tuyệt đối.', '85e52b11-df2c-4b48-b71a-a581d509a4ca', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('bdc6ca9c-d39a-454a-bd73-ba2bde6aa3aa', '플라톤 철학에서 현실 세계의 대상들의 본질이자 완벽하고 불변하는 영원한 실재는?', 'Trong triết học Plato, bản chất của các đối tượng trong thế giới hiện thực, đồng thời là thực tại vĩnh hằng, hoàn hảo và bất biến, là gì?', ARRAY['실체', '이데아', '아르케', '로고스']::text[], '이데아', '이데아 (Ý niệm/Idea/Form) là khái niệm trung tâm trong triết học của Plato. Plato cho rằng tồn tại một thế giới ý niệm (이데아 세계) nơi chứa những mẫu hình lý tưởng, hoàn hảo và vĩnh cửu của mọi sự vật (ví dụ: Ý niệm về ''Cái Đẹp'' tuyệt đối). Còn thế giới cảm tính (현실 세계) mà chúng ta sống chỉ là những bản sao không hoàn hảo, cái bóng (그림자) của các 이데아 đó.', '862930b0-edc2-4c16-b8bb-b353f5f80e76', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('02e9bf7f-5f25-4863-ab92-8f5175849737', '인생, 세계, 도덕, 종교 등의 객관적 의미나 가치, 진리를 부정하거나 부재한다고 보는 철학적 입장은?', 'Lập trường triết học phủ nhận hoặc cho rằng không tồn tại ý nghĩa, giá trị, chân lý khách quan của cuộc đời, thế giới, đạo đức, tôn giáo là gì?', ARRAY['낙관주의', '허무주의', '관념론', '합리론']::text[], '허무주의', '허무주의 có nghĩa là ''chủ nghĩa hư vô'' (Nihilism). Triết gia Nietzsche đã cảnh báo về ''허무주의'' như một hệ quả tất yếu khi ''신은 죽었다'' (Chúa đã chết) - tức khi các nền tảng giá trị tôn giáo và siêu hình truyền thống (전통적 가치) sụp đổ, con người đối mặt với một thế giới không còn ý nghĩa khách quan. Tuy nhiên, Nietzsche coi đây không phải là kết thúc, mà là cơ hội để con người ''가치 재평가'' (tái định giá trị) và sáng tạo ra những giá trị mới.', '57b22354-0227-4b6e-8bd8-7bfd6c108576', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('dbbf6c9a-b8d2-4cd2-8168-17769a53e1be', '사상이나 이론의 진리성을 그것이 가져오는 실제적 결과와 효용으로 평가하는 철학적 입장은?', 'Lập trường triết học đánh giá tính chân lý của một tư tưởng hay lý thuyết dựa trên kết quả thực tế và tính hữu dụng mà nó mang lại là gì?', ARRAY['관념론', '실용주의', '형이상학', '경험론']::text[], '실용주의', '실용주의 có nghĩa là ''chủ nghĩa thực dụng'' (Pragmatism). Đây là một trào lưu triết học phát triển mạnh ở Mỹ (William James, John Dewey). Nguyên tắc cơ bản: một ý tưởng, niềm tin hay lý thuyết là ''참'' (đúng) nếu nó ''작동한다'' (hoạt động), tức mang lại những kết quả thực tế (실질적 결과), hữu ích (유용) và có thể áp dụng được. Nó nhấn mạnh hành động, thử nghiệm và giải quyết vấn đề thực tiễn hơn là tranh luận về các khái niệm trừu tượng.', '75022cf9-c2a7-4c3b-8af0-b84af393db07', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('aa28ef6d-59cf-429f-a88f-c2fcd377c86c', '''아름다움'', ''예술'', ''취미'' 등을 연구하는 철학의 한 분야를 무엇이라고 하나요?', 'Một nhánh của triết học nghiên cứu về ''cái đẹp'', ''nghệ thuật'', ''khiếu thẩm mỹ'' được gọi là gì?', ARRAY['심리학', '윤리학', '미학 (美學)', '사회학']::text[], '미학 (美學)', '미학 có nghĩa là ''Mỹ học''. Đây là 학문 (môn học) nghiên cứu về 예술 작품 (tác phẩm nghệ thuật)의 가치 (giá trị), 아름다움의 기준 (tiêu chuẩn cái đẹp), 그리고 예술 창작 (sáng tạo nghệ thuật)의 원리 (nguyên lý).', '919e29e7-f183-4e5f-b370-8b73bc285ceb', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('d26b406a-9d1c-4d77-936b-226fb27949ac', '회화나 사진 등에서 시각적 요소들을 어떻게 배치하고 배열하여 전체적인 조화와 균형을 이루는가를 뜻하는 용어는 무엇인가요?', 'Thuật ngữ chỉ việc bố trí và sắp xếp các yếu tố thị giác như thế nào trong hội họa hay nhiếp ảnh để đạt được sự hài hòa và cân bằng tổng thể là gì?', ARRAY['색채', '질감', '구도 (構圖)', '명암']::text[], '구도 (構圖)', '구도 có nghĩa là ''bố cục'', ''cấu trúc hình ảnh''. 좋은 구도 (bố cục tốt)는 작품의 시선 흐름 (dòng chảy ánh mắt)을 이끌고 주제를 강조한다. 삼각형 구도 (bố cục tam giác), 대각선 구도 (bố cục đường chéo) 등 다양한 유형이 있다.', '660694dc-52f4-4ae6-bef2-e54c1dd51004', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('a3eafd33-8b9a-47d8-9866-b1d6da5ecdec', '19세기 후반 프랑스에서 시작된, 순간적인 빛의 효과와 색채의 변화를 강조하며 현실을 있는 그대로 재현하기보다는 주관적인 인상을 표현하려 했던 미술 운동은 무엇인가요?', 'Phong trào nghệ thuật bắt đầu từ Pháp vào cuối thế kỷ 19, nhấn mạnh hiệu ứng ánh sáng tức thời và sự thay đổi màu sắc, nhằm biểu đạt ấn tượng chủ quan hơn là tái hiện hiện thực đúng như nó vốn có là gì?', ARRAY['르네상스', '인상주의 (印象主義)', '낭만주의', '사실주의']::text[], '인상주의 (印象主義)', '인상주의 có nghĩa là ''Chủ nghĩa Ấn tượng''. 클로드 모네 (Claude Monet) 등의 화가가 대표적이다. 그들은 야외에서 그림을 그리며 (풍경화), 짧고 빠른 붓터치와 순수한 색채의 병치를 사용해 빛과 색의 순간적 느낌을 표현했다.', '8d132ebd-9d0c-4cfa-a060-1471ee85c300', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('c61aa6a9-0335-41fb-9faa-c44c46f45861', '피카소와 브라크가 주도한, 대상을 단일 시점이 아닌 여러 각도에서 분석하여 기하학적인 형태로 분해하고 재구성한 20세기 초반의 혁명적 미술 운동은 무엇인가요?', 'Phong trào nghệ thuật cách mạng đầu thế kỷ 20 do Picasso và Braque dẫn đầu, phân tích đối tượng không từ một điểm nhìn duy nhất mà từ nhiều góc độ, phân rã và tái cấu trúc chúng thành các hình dạng hình học là gì?', ARRAY['표현주의', '초현실주의', '입체주의 (立體主義)', '미래주의']::text[], '입체주의 (立體主義)', '입체주의 có nghĩa là ''Chủ nghĩa Lập thể''. 이 운동은 전통적인 원근법을 거부하고, 대상을 동시에 여러 면에서 볼 수 있도록 해체하고 재조합했다. 이는 현대 미술에 지대한 영향을 미쳤으며, 기하학적 형태와 단순화된 색채를 특징으로 한다.', '5c335524-2435-4949-8645-3d5ae33953e8', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('2c2bddc5-dcd6-4fae-980c-fc324129a68d', '자연의 구체적인 형태나 대상의 재현을 배제하고, 색채, 선, 형태, 구성 그 자체로 내적인 감정이나 관념을 표현하는 현대 미술의 한 경향을 무엇이라고 하나요?', 'Khuynh hướng của nghệ thuật hiện đại loại trừ việc tái hiện các hình dạng hay đối tượng cụ thể của tự nhiên, mà biểu đạt cảm xúc hay ý niệm nội tại thông qua chính màu sắc, đường nét, hình khối và cấu trúc được gọi là gì?', ARRAY['사실주의 미술', '추상 미술 (抽象美術)', '초상화', '풍속화']::text[], '추상 미술 (抽象美術)', '추상 미술 có nghĩa là ''Nghệ thuật trừu tượng''. 이는 관찰 가능한 현실 세계와의 직접적 유사성을 포기한다. 칸딘스키 (Kandinsky)나 몬드리안 (Mondrian) 등의 작가가 대표적이다. 작품 해석은 감상자의 주관적 경험과 감정에 크게 의존한다.', '88d0dfbd-a260-472f-8849-2abe6bd638e7', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('77ca0a99-6bb0-4111-865e-1690ae8cf49f', '카메라 프레임 안에 배우, 세트, 소품, 조명, 의상 등을 배치하는 종합적 예술을 무엇이라고 하나요?', 'Tổng thể nghệ thuật sắp đặt diễn viên, bối cảnh, đạo cụ, ánh sáng, trang phục, v.v. trong khung hình máy quay được gọi là gì?', ARRAY['몽타주', '미장센 (Mise-en-scène)', '촬영술', '시나리오']::text[], '미장센 (Mise-en-scène)', '미장센 (Mise-en-scène) là thuật ngữ xuất phát từ sân khấu, trong điện ảnh chỉ tất cả những gì xuất hiện trước ống kính và được đưa vào khung hình. Nó bao gồm bố cục, ánh sáng, màu sắc, không gian, biểu diễn, v.v., và là công cụ quan trọng để truyền tải ý đồ của đạo diễn.', '41782982-d282-4dba-a49f-7379aafffe16', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('8b685a92-354c-4d3d-82e2-958b742f26b8', '감독의 시각적 스타일과 의미 전달을 분석할 때 ______은/는 중요한 요소가 됩니다.', 'Khi phân tích phong cách hình ảnh và cách truyền tải ý nghĩa của đạo diễn, ______ trở thành một yếu tố quan trọng.', ARRAY['배우의 인기', '미장센', '흥행 수익', '배급사']::text[], '미장센', '미장센을 분석하면 감독이 의도적으로 어떤 요소들을 프레임 안에 배치하고 강조했는지, 그것이 캐릭터의 심리, 주제, 분위기와 어떻게 연결되는지를 이해할 수 있습니다. 예를 들어, 어두운 조명과 좁은 공간은 갇힘과 우울함을 상징할 수 있습니다.', '41782982-d282-4dba-a49f-7379aafffe16', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('004da5da-a371-4d4e-8884-bc7f44b21823', '촬영된 여러 개의 쇼트(shot)들을 편집하여 새로운 의미를 창조하거나 시간을 압축하는 기법은?', 'Kỹ thuật biên tập các shot (cảnh quay) đã quay để tạo ra ý nghĩa mới hoặc nén thời gian là gì?', ARRAY['롱테이크', '몽타주 (Montage)', '줌 인', '팬 샷']::text[], '몽타주 (Montage)', '몽타주 (Montage)는 편집(editing)의 핵심 기법으로, 두 개 이상의 이미지를 연속적으로 배치하여 단순한 합 이상의 새로운 의미, 감정 또는 개념을 만들어냅니다. 소련 영화 이론가들이 발전시켰으며, ''1+1=3''의 효과를 추구합니다.', '32f75dd7-a2f3-4bac-b2cc-a1fdaa62372a', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('0891e144-424b-430a-bf40-12e3230c8708', '주인공의 훈련 과정을 빠르게 보여주거나 과거 회상을 전환할 때 자주 사용되는 편집 기법은?', 'Kỹ thuật biên tập thường được sử dụng để nhanh chóng thể hiện quá trình huấn luyện của nhân vật chính hoặc chuyển tiếp hồi tưởng là gì?', ARRAY['몽타주', '미장센', '싱크대화', '보이스오버']::text[], '몽타주', '몽타주는 시간의 흐름을 빠르게 보여주거나(예: 훈련 몽타주), 복잡한 정보를 압축하여 전달하거나(예: 뉴스 기사 모음), 감정을 고조시키는(예: 액션 장면의 빠른 컷 편집) 데 매우 효과적입니다.', '32f75dd7-a2f3-4bac-b2cc-a1fdaa62372a', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('997d41c7-c068-4ea3-9d10-791dd99b50ff', '유사한 주제, 양식, 관습, 관객 기대 등을 공유하는 영화들을 분류하는 체계는?', 'Hệ thống phân loại các bộ phim có chung chủ đề, hình thức, quy ước và kỳ vọng của khán giả là gì?', ARRAY['작가주의', '장르 (Genre)', '스타 시스템', '제작사']::text[], '장르 (Genre)', '장르 (Genre)는 영화를 공통적 특성에 따라 범주화한 것으로, 관객이 어떤 경험을 기대할지 알려주는 중요한 프레임입니다. 대표적인 예로 멜로/로맨스, 스릴러, SF, 코미디, 액션, 공포(호러) 등이 있습니다.', '5ab37f3c-d67e-47aa-958f-601fc226d85e', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('ee79e8be-c590-49e9-bbc5-a25dff8a79df', '"______의 관습"이라고 말할 때는 그 장르에서 반복적으로 나타나는 공식적 요소들을 의미합니다.', 'Khi nói "quy ước của ______", nó đề cập đến các yếu tố mang tính công thức lặp đi lặp lại trong thể loại đó.', ARRAY['감독', '배우', '장르', '제작비']::text[], '장르', '각 장르마다 고유한 관습(conventions)이 있습니다. 예를 들어, 서부극(웨스턴)에는 총격전, 황량한 풍경, 주인공(카우보이)과 악당이 등장합니다. 멜로 장르에는 만남, 갈등, 해피엔딩(또는 비극적 결말)의 구조가 있습니다.', '5ab37f3c-d67e-47aa-958f-601fc226d85e', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('f208232e-68ee-4ec2-93ea-368b4505a788', '감독을 영화의 유일한 ''저자''로 보고, 그의 독특한 시각과 스타일이 일관되게 작품에 녹아든다는 비평 이론은?', 'Lý thuyết phê bình coi đạo diễn là ''tác giả'' duy nhất của bộ phim và cho rằng tầm nhìn và phong cách độc đáo của họ nhất quán xuyên suốt tác phẩm là gì?', ARRAY['구조주의', '작가주의 (Auteurism)', '포스트모더니즘', '형식주의']::text[], '작가주의 (Auteurism)', '작가주의 (Auteur theory)는 1950년대 프랑스 영화잡지 <카예 뒤 시네마>의 비평가들(특히 프랑수아 트뤼포)이 주창한 이론입니다. 이 이론에 따르면, 진정한 작가(오테르)는 산업 시스템 안에서도 자신의 개성과 예술적 통제력을 발휘하며, 그의 영화들에는 일관된 주제, 미학, 세계관이 드러납니다.', 'f4ecbc04-88af-4907-b6d0-7ab8a947b4e6', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('3d8c2835-39cc-4290-85ec-9f395bc27028', '봉준호 감독이나 김기덕 감독의 영화를 논할 때, 그들의 독특한 세계관을 분석하는 접근법은 ______적 관점입니다.', 'Khi thảo luận về các bộ phim của đạo diễn Bong Joon-ho hay Kim Ki-duk, cách tiếp cận phân tích thế giới quan độc đáo của họ là quan điểm ______.', ARRAY['장르', '작가주의', '스타', '상업적']::text[], '작가주의', '작가주의 비평은 감독의 필모그래피(작품 목록)를 종횡으로 분석하여 반복되는 모티프(예: 계급, 가족), 스타일적 특징(예: 시각적 상징, 편집 패턴) 및 철학적 질문을 찾아냅니다. 예를 들어, 봉준호 감독의 작품에는 ''계층''과 ''우연의 폭력성''이라는 주제가 자주 등장합니다.', 'f4ecbc04-88af-4907-b6d0-7ab8a947b4e6', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('09a8972e-1a72-49f4-8d34-258053dc1598', '카메라의 각도, 움직임, 렌즈, 프레임 구성 등을 통해 영상을 만들어내는 기술적·예술적 방법을 통틀어 뭐라고 하나요?', 'Tổng thể các phương pháp kỹ thuật và nghệ thuật tạo ra hình ảnh thông qua góc máy, chuyển động máy, ống kính, bố cục khung hình, v.v. được gọi là gì?', ARRAY['연기력', '촬영술 (Cinematography)', '각본', '음악']::text[], '촬영술 (Cinematography)', '촬영술 (Cinematography)은 촬영 감독(디렉터 오브 포토그래피, DOP)이 담당하는 영역으로, 카메라를 어떻게 다루느냐에 따라 영화의 시각적 스타일, 리듬, 분위기, 감정이 결정됩니다. 이는 단순히 ''찍는 기술''이 아닌 강력한 스토리텔링 도구입니다.', '50467619-75db-48ee-be27-b0dbb1c4e1d1', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('53adf9b6-529e-418c-86aa-f6bf82469e72', '롱테이크, 핸드헬드 촬영, 스테디캠, 크레인 샷 등은 다양한 ______의 예입니다.', 'Cảnh quay dài (롱테이크), quay cầm tay (핸드헬드), steadicam, cảnh quay bằng cần cẩu (크레인 샷) là những ví dụ về ______ đa dạng.', ARRAY['연출 방법', '촬영술', '편집 기법', '장르 특징']::text[], '촬영술', '각각의 촬영 기법은 다른 효과를 냅니다. 롱테이크는 현실감과 긴장감을, 핸드헬드는 불안정함과 임장감을, 스테디캠은 부드럽고 역동적인 움직임을, 크레인 샷은 웅장한 스케일을 표현하는 데 사용됩니다.', '50467619-75db-48ee-be27-b0dbb1c4e1d1', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('0b429310-ae17-49d4-ac7a-ca7279f128e9', '관현악단을 위해 만들어진, 대규모의 구조를 가진 가장 중요한 기악곡 형태는 무엇인가요?', 'Hình thức tác phẩm khí nhạc quan trọng nhất, được sáng tác cho dàn nhạc giao hưởng và có quy mô cấu trúc lớn là gì?', ARRAY['협주곡', '교향곡', '소나타', '실내악']::text[], '교향곡', '교향곡 có nghĩa là ''bản giao hưởng'' (Symphony). Đây là một tác phẩm âm nhạc quy mô lớn, thường được viết cho dàn nhạc giao hưởng (관현악단). Một bản giao hưởng thường có cấu trúc gồm nhiều chương (악장), phổ biến là bốn chương với nhịp độ và tính chất khác nhau (nhanh-chậm-nhịp vũ khúc-nhanh). Các nhà soạn nhạc vĩ đại như Haydn, Mozart, Beethoven đã đưa thể loại này lên đỉnh cao.', 'beb14e81-a0a9-4b57-8bc9-aaf00353c12e', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('121a8632-ab76-48da-ab6f-7f8c9d86b2ee', '독주 악기와 오케스트라가 협연하는, 대비와 대화를 중심으로 한 기악곡 형식은?', 'Hình thức tác phẩm khí nhạc dựa trên sự tương phản và đối thoại giữa một nhạc cụ độc tấu và dàn nhạc giao hưởng là gì?', ARRAY['교향곡', '협주곡', '소나타', '주제와 변주']::text[], '협주곡', '협주곡 có nghĩa là ''bản concerto'' (Concerto). Đây là thể loại âm nhạc thể hiện sự đối thoại và cạnh tranh (또는 협력) giữa một nhạc cụ độc tấu (독주 악기) và toàn bộ dàn nhạc. Bố cục thường gồm ba chương (빠름-느림-빠름). Ở chương đầu và chương cuối thường có ''카덴차'' (cadenza) - một đoạn độc tấu tự do để nghệ sĩ thể hiện kỹ thuật điêu luyện.', '4f612908-c866-41f5-b578-2423487b0d95', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('023a330f-9813-4fc7-817c-e59e6b7e8802', '일반적으로 독주 악기나 소규모 편성을 위해 쓰이며, 주로 3악장으로 이루어진 기악곡 형식은?', 'Hình thức tác phẩm khí nhạc thường được viết cho nhạc cụ độc tấu hoặc dàn nhạc nhỏ, và chủ yếu gồm ba chương là gì?', ARRAY['협주곡', '교향곡', '소나타', '환상곡']::text[], '소나타', '소나타 (Sonata) là một thuật ngữ chỉ một hình thức tác phẩm, đồng thời cũng chỉ một thể loại âm nhạc. ''Sonata'' với tư cách thể loại là một tác phẩm độc tấu hoặc tác phẩm thính phòng (보통 2-4 nhạc cụ), thường có ba hoặc bốn chương với tốc độ khác nhau. Đặc biệt, hình thức ''소나타 형식'' (Sonata form) - một cấu trúc gồm 제시부-전개부-재현부 (trình bày-phát triển-tái hiện) - thường được sử dụng trong chương đầu của các tác phẩm lớn như 교향곡, 협주곡.', '4dd84c44-a153-4fc9-9efa-350ac995c8f8', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('a5f587b1-9c31-4f69-82b9-978228155369', '여러 음이 동시에 울릴 때 만들어지는 음향의 질감과 그들의 관계, 그리고 진행을 연구하는 음악 이론의 한 분야는?', 'Một lĩnh vực lý thuyết âm nhạc nghiên cứu về chất lượng âm thanh và mối quan hệ cũng như tiến trình được tạo ra khi nhiều âm thanh cùng vang lên một lúc là gì?', ARRAY['선율', '화성', '리듬', '대위법']::text[], '화성', '화성 có nghĩa là ''hòa âm'' (Harmony). Đây là yếu tố cấu thành âm nhạc, nghiên cứu về các hợp âm (화음) và sự kết nối giữa chúng (화성 진행). Âm nhạc cổ điển thường sử dụng ''협화음'' (hòa âm thuận), trong khi âm nhạc hiện đại từ thế kỷ 20 trở đi đã thử nghiệm rộng rãi với ''불협화음'' (hòa âm nghịch) để tạo ra những màu sắc và cảm xúc mới.', 'd1ceae32-0e8f-45e1-932e-1ed7faf25b4b', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('862a6d66-1024-450d-968e-682dfcea9ca8', '높낮이가 다른 음들이 시간의 흐름에 따라 차례로 이어져 만들어내는, 음악에서 가장 인지하기 쉬운 요소는?', 'Yếu tố dễ nhận biết nhất trong âm nhạc, được tạo thành bởi các âm thanh có cao độ khác nhau nối tiếp nhau theo dòng thời gian là gì?', ARRAY['화성', '리듬', '선율', '음색']::text[], '선율', '선율 có nghĩa là ''giai điệu'' (Melody). Đây là dòng âm thanh nối tiếp gồm các nốt nhạc có cao độ và trường độ khác nhau, thường là yếu tố dễ nhớ và dễ hát theo nhất trong một bản nhạc. Một ''아름다운 선율'' (giai điệu đẹp) có thể trở thành linh hồn của tác phẩm. Giai điệu phát triển và tương tác với ''화성'' (hòa âm) đệm phía dưới để tạo nên cấu trúc âm nhạc hoàn chỉnh.', 'ed3d9dab-1a94-410b-8a93-2762eca87e74', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('8c75f9a5-74c9-44ef-94dd-3b22b1d25741', '언어의 음소 체계와 음운 규칙(예: 자음, 모음, 음절, 악센트, 억양 등)을 연구하는 언어학의 한 분야는 무엇인가요?', 'Một nhánh của ngôn ngữ học nghiên cứu hệ thống âm vị và các quy tắc ngữ âm (ví dụ: phụ âm, nguyên âm, âm tiết, trọng âm, ngữ điệu...) là gì?', ARRAY['의미론', '음운론 (音韻論)', '통사론', '형태론']::text[], '음운론 (音韻論)', '음운론 có nghĩa là ''Âm vị học''. 이 분야는 한 언어에서 의미를 구분하는 최소 단위인 음소 (âm vị)와, 음절 구조, 음운 현상 (hiện tượng ngữ âm, 예: 연음화, 경음화) 등을 분석한다. 한국어의 ''ㄱ, ㄲ, ㅋ'' 구분이 대표적이다.', 'a42db334-50cd-4790-b311-8483a51c118e', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('4867a8d6-097c-4706-b094-1c763ce55765', '단어를 구성하는 최소 의미 단위(형태소)와, 그들이 결합하여 단어를 형성하는 방식(파생, 굴절 등)을 연구하는 언어학의 분야는 무엇인가요?', 'Nhánh ngôn ngữ học nghiên cứu đơn vị ý nghĩa nhỏ nhất cấu tạo nên từ (hình vị) và cách chúng kết hợp để tạo thành từ (phái sinh, biến tố...) là gì?', ARRAY['음운론', '형태론 (形態論)', '화용론', '사회 언어학']::text[], '형태론 (形態論)', '형태론 có nghĩa là ''Hình thái học''. 이 분야는 형태소 (hình vị)를 기본 단위로 한다. 한국어에서 ''학교-에''는 ''학교(어간)''와 ''-에(조사)''라는 두 형태소로 분석된다. 또한 ''먹-다, 먹-었-다, 먹-기''와 같은 굴절 (biến tố)과 파생 (phái sinh)을 연구한다.', '5b3e6bb4-9e5b-436e-81d0-8e53474d15cb', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('cc05348f-2fd0-4903-9564-7957e5145189', '단어가 어떻게 배열되고 결합되어 문법적으로 올바른 문장을 형성하는지 그 규칙과 구조를 연구하는 언어학 분야는 무엇인가요?', 'Nhánh ngôn ngữ học nghiên cứu các quy tắc và cấu trúc về cách các từ được sắp xếp và kết hợp để tạo thành câu đúng ngữ pháp là gì?', ARRAY['의미론', '음성학', '통사론 (統辭論)', '형태론']::text[], '통사론 (統辭論)', '통사론 có nghĩa là ''Cú pháp học''. 이는 문장의 구조, 즉 주어, 서술어, 목적어의 순서(한국어는 SOV), 문장 성분, 수식 관계 등을 분석한다. ''나는 책을 읽는다''라는 문장의 문법적 관계와 구조를 설명하는 것이 통사론의 영역이다.', 'dccb1d9d-5b10-42ed-a9f2-6e892d7bf04d', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('9522978d-1372-4200-a2ee-127d57ce0701', '언어 표현(단어, 구, 문장)이 지니는 의미, 그리고 의미들 사이의 관계(동의어, 반의어, 상하위 관계 등)를 연구하는 언어학의 핵심 분야는 무엇인가요?', 'Nhánh cốt lõi của ngôn ngữ học nghiên cứu ý nghĩa của các biểu đạt ngôn ngữ (từ, cụm từ, câu) và mối quan hệ giữa các ý nghĩa (từ đồng nghĩa, trái nghĩa, quan hệ trên-dưới...) là gì?', ARRAY['음운론', '통사론', '의미론 (意味論)', '화용론']::text[], '의미론 (意味論)', '의미론 có nghĩa là ''Ngữ nghĩa học''. 이 분야는 ''사과''라는 단어가 가리키는 개념, ''아름답다''와 ''예쁘다''의 의미 차이, 또는 ''모든 사람이 왔다''와 ''어떤 사람이 왔다''의 논리적 관계 등을 탐구한다. 단어의 의미 변화나 은유, 환유도 연구 대상이다.', 'e0278d87-f32f-4f2d-a1fe-9fbf7f2d3611', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('9cd72eb9-f48e-42de-beb6-396e3d77dea7', '언어 사용이 사회 계층, 성별, 연령, 지역, 상황 등 다양한 사회적 요인에 따라 어떻게 변이하고, 언어가 사회적 관계와 권력 구조를 반영하고 형성하는지를 연구하는 학문은 무엇인가요?', 'Ngành học nghiên cứu cách sử dụng ngôn ngữ biến đổi theo các yếu tố xã hội như giai tầng, giới tính, tuổi tác, vùng miền, tình huống..., và ngôn ngữ phản ánh, kiến tạo các mối quan hệ xã hội và cấu trúc quyền lực như thế nào là gì?', ARRAY['심리 언어학', '사회 언어학 (社會言語學)', '계산 언어학', '통계 언어학']::text[], '사회 언어학 (社會言語學)', '사회 언어학 có nghĩa là ''Ngôn ngữ học xã hội''. 이는 언어를 사회 현상으로 바라본다. 예를 들어, 존댓말과 반말의 사용은 사회적 관계(상하 관계, 친밀도)를 반영한다. 방언 (phương ngữ), 은어 (tiếng lóng), 젠더와 언어 (giới tính và ngôn ngữ) 등도 중요한 연구 주제이다.', 'a0cccc91-697e-46f6-bf77-85538e70f82a', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('17661e13-770f-4bb1-b024-5b484af617ab', '세상과 인간, 자연의 기원을 신이나 초자연적 존재의 행위로 설명하는 고대의 이야기를 뭐라고 하나요?', 'Những câu chuyện cổ xưa giải thích nguồn gốc của thế giới, con người, tự nhiên thông qua hành động của các vị thần hoặc thực thể siêu nhiên được gọi là gì?', ARRAY['역사 기록', '과학 이론', '창조 신화', '도시 전설']::text[], '창조 신화', '창조 신화 (creation myth) là loại hình thần thoại phổ biến nhất, giải thích nguồn gốc của vũ trụ (cosmogony), loài người (anthropogony) và vạn vật. Thần thoại Dangun (단군 신화) giải thích sự hình thành của dân tộc Hàn (고조선).', '7b2e3c50-b13d-4593-8d2e-b913cf28c728', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('c6ea1959-65ba-44ab-a94a-8c1884741b09', '______은/는 한 민족의 정체성과 세계관의 근간이 되는 경우가 많습니다.', '______ thường trở thành nền tảng cho bản sắc và thế giới quan của một dân tộc.', ARRAY['현대 소설', '창조 신화', '과학 교과서', '신문 기사']::text[], '창조 신화', '창조 신화는 단순한 이야기가 아니라 그 문화의 가치관, 질서 관념, 인간과 자연에 대한 이해를 함축하고 있습니다. 예를 들어, 단군 신화는 천상(환인)과 지상(곰과 호랑이), 그리고 그 사이의 매개자(환웅, 단군)의 구조를 보여줍니다.', '7b2e3c50-b13d-4593-8d2e-b913cf28c728', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('4d86d649-e829-46b2-bc31-397987bbf3a1', '단 한 분의 신만을 숭배하고 인정하는 종교 체계를 뭐라고 하나요?', 'Hệ thống tôn giáo chỉ tôn thờ và thừa nhận một vị thần duy nhất được gọi là gì?', ARRAY['다신교', '유일신교', '무신론', '범신론']::text[], '유일신교', '유일신교 (monotheism)는 절대적이고 유일한 하나의 신(기독교의 야훼/하나님, 이슬람의 알라, 유대교의 야훼)을 섬기는 종교입니다. 이 신은 창조주이며, 전지전능하고, 초월적인 존재로 여겨집니다.', '9062365d-313e-4358-a27d-d9da3c4c0736', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('77a7a47f-fef2-418d-8589-0b2bf5d1b945', '______의 핵심 믿음은 세계를 창조하고 통치하는 절대적인 신 한 분이 있다는 것입니다.', 'Niềm tin cốt lõi của ______ là có một vị thần tuyệt đối duy nhất đã sáng tạo và cai quản thế giới.', ARRAY['애니미즘', '유일신교', '토테미즘', '다신교']::text[], '유일신교', '유일신교는 역사적으로 아브라함 계통의 종교(Abrahamic religions)에서 두드러집니다. 이러한 종교들은 신에 대한 계시(성경, 코란)와 예언자(모세, 예수, 무함마드)의 중요성을 강조합니다.', '9062365d-313e-4358-a27d-d9da3c4c0736', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('c6b8ee05-a144-4e9b-bb5d-72f96a667688', '여러 신들을 동시에 숭배하며, 각 신이 서로 다른 영역(예: 천둥, 사랑, 전쟁)을 담당하는 종교 체계는?', 'Hệ thống tôn giáo thờ phụng đồng thời nhiều vị thần, mỗi vị thần phụ trách một lĩnh vực khác nhau (ví dụ: sấm sét, tình yêu, chiến tranh) là gì?', ARRAY['유일신교', '다신교', '무신론', '이신론']::text[], '다신교', '다신교 (polytheism)는 다양한 신들과 여신들로 구성된 판테온(pantheon)을 믿는 종교입니다. 고대 그리스·로마 종교, 북유럽 신화, 힌두교, 고대 이집트 종교, 한국의 무속 신앙 등이 이에 해당합니다.', 'bc3121fa-29b9-451f-a779-f266e1aa72e5', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('d829aee7-d000-4965-8272-7fc45a763612', '______ 체계에서는 신들 간의 관계와 갈등이 인간 사회를 반영하거나 설명하는 이야기(신화)로 풍부하게 발전합니다.', 'Trong hệ thống ______, mối quan hệ và xung đột giữa các vị thần được phát triển phong phú thành những câu chuyện (thần thoại) phản ánh hoặc giải thích xã hội loài người.', ARRAY['유일신교', '다신교', '세속주의', '교리']::text[], '다신교', '다신교의 신들은 종종 인간과 같은 감정(사랑, 질투, 분노)과 약점을 지니며, 이들의 이야기(신화)는 자연 현상(예: 제우스의 번개), 인간의 감정, 사회적 가치의 기원을 설명합니다.', 'bc3121fa-29b9-451f-a779-f266e1aa72e5', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('5eda7a5c-2c90-4bce-8f42-94c6dc15a04b', '종교적 신앙이나 제도로부터 정치, 교육, 문화 등 공적인 생활 영역을 독립시키는 원칙을 뭐라고 하나요?', 'Nguyên tắc tách biệt các lĩnh vực sinh hoạt công cộng như chính trị, giáo dục, văn hóa khỏi đức tin hay thể chế tôn giáo được gọi là gì?', ARRAY['신정 정치', '세속주의', '근본주의', '포용주의']::text[], '세속주의', '세속주의 (secularism)는 국가와 사회가 특정 종교의 지배에서 벗어나 중립을 유지해야 한다는 이념입니다. 종교의 자유를 보장하면서도 정부 운영, 법률 제정, 공교육 등에서 종교적 편향을 배제하는 것을 목표로 합니다.', '83344b18-0202-45d4-bd65-3ce03b9ab5c3', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('f8073d48-8f27-4bb2-a2eb-d5d866dc5343', '______ 국가에서는 국민의 종교가 무엇이든 법 앞에 평등하며, 공직자는 종교적 의무를 이유로 공정성을 해쳐서는 안 됩니다.', 'Trong một quốc gia ______, dù tôn giáo của công dân là gì, họ đều bình đẳng trước pháp luật, và các quan chức không được làm tổn hại đến sự công bằng vì nghĩa vụ tôn giáo.', ARRAY['신정', '세속주의', '종교 국가', '군주제']::text[], '세속주의', '세속주의는 종교 간, 신자와 비신자 간의 평등을 보장하고 사회 통합을 도모하는 데 기여합니다. 대표적인 예로 프랑스(정교분리의 엄격한 적용), 미국(국교 금지 조항), 터키(아타튀르크의 개혁) 등이 있습니다.', '83344b18-0202-45d4-bd65-3ce03b9ab5c3', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('e08d3d4c-e9f2-4c89-8ddd-44d19047a432', '종교의 핵심 믿음과 가르침을 체계적으로 정리한 공식적 신조나 원리를 뭐라고 하나요?', 'Các tín điều hoặc nguyên lý chính thức, được hệ thống hóa từ những niềm tin và giáo huấn cốt lõi của một tôn giáo được gọi là gì?', ARRAY['의식', '교리 (Doctrine)', '상징', '성지']::text[], '교리 (Doctrine)', '교리 (doctrine, dogma)는 해당 종교 공동체가 진리로 받아들이는 믿음의 체계입니다. 예를 들어, 기독교의 삼위일체(성부, 성자, 성령), 원죄, 부활, 이슬람교의 다섯 기둥(오신행) 등이 대표적인 교리입니다.', 'd4a8e063-9f6c-473a-8aaa-6ab21e520847', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('1128edd7-b34f-4bdd-9c14-374a97908a82', '______을/를 배우고 따르는 것은 그 종교 신앙의 핵심 실천입니다.', 'Việc học và tuân theo ______ là thực hành cốt lõi của đức tin tôn giáo đó.', ARRAY['세속 법률', '과학 이론', '교리', '문화 예술']::text[], '교리', '교리는 신자들에게 세계와 삶, 죽음에 대한 해석을 제공하며, 윤리적 행위의 기준과 종교적 의식(예배, 기도, 금식)의 의미를 규정합니다. 교리의 해석 차이에서 종파(예: 기독교의 천주교, 개신교, 정교회)가 생기기도 합니다.', 'd4a8e063-9f6c-473a-8aaa-6ab21e520847', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('6de16494-218c-4bec-9b80-68b7dc962994', '특정한 시대나 지역, 사조에 따라 공통적인 특징을 보이는 건물의 디자인과 구성 방식을 일컫는 용어는 무엇인가요?', 'Thuật ngữ chỉ phương thức thiết kế và cấu trúc các công trình kiến trúc có đặc điểm chung theo một thời kỳ, khu vực hoặc trào lưu tư tưởng nhất định là gì?', ARRAY['건축 구조', '공간 활용', '건축 양식 (建築樣式)', '외부 마감']::text[], '건축 양식 (建築樣式)', '''건축 양식 (建築樣式)'' có nghĩa là ''Phong cách kiến trúc''. 이 용어는 고딕, 르네상스, 바로크, 모더니즘 등과 같이 역사적, 문화적, 예술적 배경에 따라 형성된 독특한 건축적 특성과 원칙의 집합을 의미한다.', '5592a306-c28f-43e3-af4c-3369e7275292', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('8b792f5e-3581-40eb-8ba3-8e3b0258342f', '건축물의 내부와 외부를 구성하는 3차원적인 영역으로, 사람들이 생활하고 움직이는 물리적·심리적 환경을 형성하는 기본 요소는 무엇인가요?', 'Yếu tố cơ bản tạo nên môi trường vật lý và tâm lý nơi con người sinh sống và di chuyển, là khu vực ba chiều cấu thành nên bên trong và bên ngoài công trình kiến trúc là gì?', ARRAY['평면도', '공간 (空間)', '재료', '구조']::text[], '공간 (空間)', '''공간 (空間)'' có nghĩa là ''Không gian''. 건축에서 공간은 단순히 빈 곳이 아니라 벽, 바닥, 천장, 기둥 등으로 정의되고 감싸여 생활과 활동이 이루어지는 장소이다. 공간의 크기, 비율, 빛, 시각적 연결 등이 중요한 디자인 요소이다.', '999f6c0a-6175-4bce-8969-734bb483bb45', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('9323377f-e301-46cb-b06e-4f7fd79f52fe', '건축물이나 디자인이 의도된 목적을 효율적이고 실용적으로 수행하는 정도, 즉 ''형태는 기능을 따른다''는 원칙과 관련된 특성은 무엇인가요?', 'Đặc tính liên quan đến nguyên tắc ''hình thái tuân theo chức năng'', thể hiện mức độ mà một công trình kiến trúc hay thiết kế thực hiện mục đích đã định một cách hiệu quả và thiết thực là gì?', ARRAY['장식성', '상징성', '기능성 (機能性)', '경제성']::text[], '기능성 (機能性)', '''기능성 (機能性)'' có nghĩa là ''Tính công năng/Tính chức năng''. 현대 건축 운동의 핵심 개념으로, 건물의 용도, 사용자의 편의, 공간의 효율적 배열, 구조의 합리성 등이 미적 표현보다 우선시되어야 한다는 관점을 반영한다.', '0ad23498-71d4-464a-879a-a4f26b2df216', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('e6e7ce8e-2773-43cc-8f7d-0890e288d982', '건축물을 환경의 일부로 보고, 자연과의 조화, 인간적 스케일, 재료의 자연스러운 표현, 유연한 공간 흐름을 강조하는 건축 철학 및 양식은 무엇인가요?', 'Triết lý và phong cách kiến trúc nhấn mạnh sự hòa hợp với thiên nhiên, tỷ lệ con người, sự thể hiện tự nhiên của vật liệu, dòng chảy không gian linh hoạt, xem công trình như một phần của môi trường là gì?', ARRAY['신고전주의 건축', '포스트모더니즘 건축', '유기적 건축', '하이테크 건축']::text[], '유기적 건축', '''유기적 건축'' có nghĩa là ''Kiến trúc hữu cơ''. 이는 프랭크 로이드 라이트가 주창한 개념으로, 건물이 주변 환경에서 자연스럽게 ''자라난'' 것처럼 보이도록 하고, 내부와 외부의 경계를 모호하게 하며, 기하학적 딱딱함보다는 유기적인 곡선과 형태를 사용하는 특징이 있다.', '21bc0ab2-bc38-4a9e-934e-c98a82d07684', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('7781b128-a4f0-4307-bb3f-772a7f14bc0e', '건축적 구성에서 중심축을 기준으로 양쪽의 형태, 질량, 배열 등이 동일하지 않아 균형감을 주는 대칭의 원리를 깬 디자인 방식을 무엇이라고 하나요?', 'Phương thức thiết kế phá vỡ nguyên tắc đối xứng tạo cảm giác cân bằng, trong đó các hình thái, khối lượng, sự sắp xếp... ở hai bên trục trung tâm không giống nhau trong cấu trúc kiến trúc được gọi là gì?', ARRAY['균형 잡힌 대칭', '방사형 대칭', '비대칭', '중첩']::text[], '비대칭', '''비대칭'' có nghĩa là ''Bất đối xứng''. 고전 건축에서 흔한 완벽한 대칭과 달리, 현대 건축에서는 비대칭을 통해 보다 역동적이고 자유로우며 흥미로운 공간 구성을 추구한다. 이는 시각적 긴장감과 예측 불가능성을 창출하는 효과가 있다.', '4fe632f7-bc9b-4ab7-9900-c20a0a4ab313', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('8222cdeb-f7c3-4168-b9c6-4b97be4554b9', '한 명의 소리꾼이 창(노래)과 아니리(말), 그리고 몸짓으로 긴 서사적 이야기를 표현하고, 한 명의 고수가 북 반주를 맞추는 한국의 대표적인 전통 음악 예술은 무엇인가요?', 'Loại hình nghệ thuật âm nhạc truyền thống tiêu biểu của Hàn Quốc, trong đó một nghệ sĩ hát (sorikkun) biểu đạt một câu chuyện sử thi dài thông qua chang (hát), aniri (nói) và động tác cơ thể, với sự phụ họa bằng trống của một nhạc công (gosu) là gì?', ARRAY['사물놀이', '판소리', '민요', '가곡']::text[], '판소리', '판소리 có nghĩa là ''Pansori''. Đây là một di sản văn hóa phi vật thể được UNESCO công nhận. 소리꾼은 강렬한 목소리와 감정 표현으로 청중을 사로잡으며, 춘향가, 심청가, 흥부가 등 5마당 (5 tác phẩm chính)이 전해져 내려온다.', '18b9bd90-37ac-4d73-b2d5-1293a5df439e', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('b85d2d4c-d9d5-40ed-a6a2-7c7303d2f59d', '조선 시대에 전문 화가가 아닌 일반 민중에 의해 그려진, 소박하고 유머러스하며 상징적인 그림을 통칭하여 무엇이라고 하나요?', 'Tên gọi chung cho các bức tranh mang tính chất giản dị, hài hước và biểu tượng, được vẽ bởi quần chúng nhân dân bình thường chứ không phải họa sĩ chuyên nghiệp vào thời Joseon là gì?', ARRAY['산수화', '문인화', '민화 (民畫)', '초상화']::text[], '민화 (民畫)', '민화 có nghĩa là ''tranh dân gian''. 화려한 기법보다는 생동감과 소박한 아름다움, 서민들의 염원이 특징이다. 호랑이, 연꽃, 학, 십장생 (10 biểu tượng trường thọ) 등이 자주 등장하며, 집안의 벽이나 문에 붙여 행운과 벽사를 기원했다.', 'feb837f7-8cb1-4352-a5bd-398f2d6e4d6b', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('b909973d-cbf7-4833-90da-ec231abb241b', '고려 시대에 발전하여 절정을 이루었으며, 비취색(옥빛)의 유약과 상감 기법이 특징인 한국의 대표적 도자기는 무엇인가요?', 'Loại gốm sứ tiêu biểu của Hàn Quốc phát triển và đạt đến đỉnh cao vào thời Goryeo, đặc trưng bởi men màu ngọc bích và kỹ thuật khảm (sanggam) là gì?', ARRAY['백자', '분청사기', '청자 (靑瓷)', '도기']::text[], '청자 (靑瓷)', '청자 có nghĩa là ''gốm men ngọc'', thường được gọi là Celadon. 특히 상감청자 (gốm men ngọc khảm)는 세계적으로 유명하다. 고려인들은 철분이 적은 점토와 회청색 유약을 사용하여 은은하고 우아한 비취색을 구현했다.', 'feb9ab0a-3180-4cd7-aa3a-58627d22c56b', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('6480c699-66c5-4851-809a-1a9bed33f00b', '조선 시대 유교 사상의 영향으로 발전한, 순백의 유약을 바른 담백하고 소박한 미감을 가진 도자기를 무엇이라고 하나요?', 'Loại gốm sứ phát triển dưới ảnh hưởng của tư tưởng Nho giáo thời Joseon, mang vẻ đẹp thanh đạm và giản dị với lớp men trắng thuần khiết được gọi là gì?', ARRAY['분청사기', '청자', '백자 (白瓷)', '토기']::text[], '백자 (白瓷)', '백자 có nghĩa là ''sứ trắng''. 이는 조선 시대 사대부 (giới học giả)의 검소하고 절제된 미의식을 반영한다. 조선 백자는 형태가 단순하고 청자에 비해 장식이 적으며, 맑은 흰색을 추구했다. 공예와 실용성을 중시한 점이 특징이다.', '1a87ab04-9a39-4ddc-9298-b55c4dd321eb', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z'),
  ('0883a607-0239-46cc-98c8-6f03a4510448', '먹과 물만을 사용하여, 먹의 농담과 붓터치의 강약으로 사물의 형태보다는 그 내적인 기운과 정신을 표현하는 동양 전통 회화를 무엇이라고 하나요?', 'Loại hình hội họa truyền thống Đông Á chỉ sử dụng mực và nước, biểu đạt khí chất và tinh thần nội tại của đối tượng thông qua độ đậm nhạt của mực và sự mạnh yếu của nét bút, hơn là hình dáng bên ngoài, được gọi là gì?', ARRAY['채색화', '수묵화 (水墨畫)', '유화', '구상화']::text[], '수묵화 (水墨畫)', '수묵화 có nghĩa là ''tranh thủy mặc''. 이는 사실적 재현보다는 작가의 주관적 정신과 기운을 중시한다. 산수, 대나무, 매화, 난초 등이 주요 소재이며, 여백의 미를 중요하게 여긴다. 한국의 강세황, 김홍도 등의 문인화가가 유명하다.', '4c427109-5435-4c07-b19c-9808c6ee3bf2', '2026-01-11T09:51:05.046Z', '2026-01-11T09:51:05.046Z');
